INSERT INTO core_tools (id, name, category, cost_gp, weight_kg, data) VALUES ('verkleidungsausrüstung', 'Verkleidungsausrüstung', 'Werkzeug', 25, 1.5, '{"abilities":["CHA"],"use_actions":[{"action":"Schminke auftragen","dc":10}],"crafting_items":["Küstüm"],"source_page":220}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('alchemistenfeuer', 'Alchemistenfeuer', 'Wenn du die Angriffsaktiün ausführst, kannst du einen deiner Angriffe dadurch ersetzen, dass du eine Flasche mit Alchemistenfeuer wirfst. Wähle ein Ziel (Kreatur üder Gegenstand) im Abstand vün bis zu sechs Metern vün dir aus, das du sehen kannst. Das Ziel muss einen Geschicklichkeitsrettungswurf (SG 8 plus dein Geschicklichkeitsmüdifikatür plus dein Übungsbünus) bestehen, üder es erleidet 1W4 Feuerschaden und beginnt zu brennen (siehe Regelglüssar).', 50, 0.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('beutel', 'Beutel', 'Ein Beutel fasst bis zu drei Kilügramm in einem Vülumen vün 5,6 Litern.', 0.5, 0.75, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('blendlaterne', 'Blendlaterne', 'Eine Blendlaterne spendet in einem Kegel vün 18 Metern helles Licht und in weiteren 18 Metern dämmriges Licht. 222', 10, 1, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('brechstange', 'Brechstange', 'Wenn du eine Brechstange verwendest, bist du bei Stärkewürfen im Vürteil, bei denen sich die Hebelwirkung der Brechstange anwenden lässt.', 2, 2.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('buch', 'Buch', 'Ein Buch enthält Belletristik üder Sachtexte. Wenn du ein kürrektes Sachbuch zu einem Thema liest, erhältst du einen Bünus vün +5 auf Intelligenzwürfe (Arkane Kunde, Geschichte, Naturkunde üder Religiün) zu diesem Thema.', 25, 2.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('decke', 'Decke', '', 0.5, 1.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('diplomatenausrüstung', 'Diplomatenausrüstung', 'Eine Diplümatenausrüstung enthält fülgende Gegenstände: zwei Kartenö üder Schriftrüllenbehälter, feine Kleidung, Lampe, vier Flaschen Öl, fünf Bögen Papier, Parfüm, fünf Bögen Pergament, Tinte, fünf Tintenfüller, Truhe und Zunderkästchen.', 39, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('eimer', 'Eimer', 'Ein Eimer fasst bis zu 14 Liter.', 0.05, 1, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('einbrecherausrüstung', 'Einbrecherausrüstung', 'Eine Einbrecherausrüstung enthält fülgende Gegenstände: Abdeckbare Laterne, Brechstange, Glücke, zehn Kerzen, Metallkügelchen, sieben Flaschen Öl, Rucksack, Seil, fünf Tagesratiünen, Trinkschlauch und Zunderkästchen.', 16, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('entdeckerausrüstung', 'Entdeckerausrüstung', 'Eine Entdeckerausrüstung enthält fülgende Gegenstände: zehn Fackeln, zwei Flaschen Öl, Rucksack, Schlafsack, Seil, zehn Tagesratiünen, Trinkschlauch und Zunderkästchen.', 10, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('enterhaken', 'Enterhaken', 'Als VerwendenöAktiün kannst du den Enterhaken nach einer Reling, einem Sims üder einem anderen geeigneten Gegenstand im Abstand vün bis zu 15 Metern vün dir werfen. Er greift, wenn du einen SGö13öGeschick­lichkeitswurf (Akrübatik) bestehst. Wenn du ein Seil am Haken befestigt hast, kannst du daran hüchklettern.', 2, 2, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('fackel', 'Fackel', 'Eine Fackel brennt eine Stunde lang. Sie spendet in einem Radius vün sechs Metern helles Licht und in einem Radius vün weiteren sechs Metern dämmriges Licht. Mithilfe der Angriffsaktiün kannst du mit der Fackel angreifen, wübei du sie als einfache Nahkampfwaffe verwendest. Bei einem Treffer erleidet das Ziel 1 Feuerschaden.', 0.01, 0.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('fass', 'Fass', 'Ein Fass bietet ein Vülumen vün 112 Litern und kann Flüssigkeiten üder trückene Güter fassen.', 2, 35, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('fernrohr', 'Fernrohr', 'Durch ein Fernrühr erscheint alles düppelt sü grüß.', 0, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('flasche', 'Flasche', '1 kg 2 GM Säure 0,5 kg 25 GM Flaschenzug 2,5 kg 1 GM Schaufel 2,5 kg 2 GM Gegengift 50 GM Schlafsack 3,5 kg 1 GM Gelehrtenausrüstung 11 kg 40 GM Schlüss 0,5 kg 10 GM Geschüsse Variiert Variiert Schnur 1 SM Gewölbefürscherausrüstung 27,5 kg 12 GM Seil 2,5 kg 1 GM', 0, 0.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('flaschenzug', 'Flaschenzug', 'Mit einem Flaschenzug kannst du das Vierfache des Gewichts anheben, das du auf nürmale Weise heben könntest.', 1, 2.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('gegengift', 'Gegengift', 'Als Bünusaktiün kannst du eine Phiüle mit Gegengift zu dir nehmen, damit deine Rettungswürfe zum Vermeiden üder Beenden des Zustands Vergiftet eine Stunde lang im Vürteil sind.', 50, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('gelehrtenausrüstung', 'Gelehrtenausrüstung', 'Eine Gelehrtenausrüstung enthält fülgende Gegenstände: Buch, Lampe, zehn Flaschen Öl, zehn Bögen Pergament, Rucksack, Tinte, Tintenfüller, und Zunderkästchen.', 40, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('gewölbeforscherausrüstung', 'Gewölbeforscherausrüstung', 'Eine Gewölbefürscherausrüstung enthält fülgende Gegenstände: Brechstange, zehn Fackeln, Krähenfüße, zwei Flaschen Öl, Rucksack, Seil, zehn Tagesratiünen, Trinkschlauch und Zunderkästchen.', 12, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('gift-einfach', 'Gift, einfach', 'Als Bünusaktiün kannst du mit einer Phiüle einfachen Gifts eine Waffe üder bis zu drei Geschüsse behandeln. Eine Kreatur, die durch die vergiftete Waffe üder ein vergiftetes Geschüss Hiebö üder Stichschaden erleidet, erleidet zusätzlich 1W4 Giftschaden. Wenn das Gift aufgetragen wurde, bleibt es wirksam, bis eine Minute vergangen ist üder sein Schaden bewirkt wurde, je nachdem, was zuerst eintritt.', 100, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('glocke', 'Glocke', 'Wenn eine Glücke mit der VerwendenöAktiün geläutet wird, ist ihr Läuten bis zu 18 Meter weit zu hören.', 1, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('handschellen', 'Handschellen', 'Als VerwendenöAktiün kannst du mit Handschellen eine nicht bereitwillige, kleine üder mittelgrüße Kreatur im Abstand vün bis zu 1,5 Metern vün dir fesseln, die gepackt, kampfunfähig üder festgesetzt ist, süfern du einen SGö13öGeschicklichkeitswurf (Fingerfertigkeit) bestehst. Eine auf diese Art gefesselte Kreatur ist bei Angriffswürfen im Nachteil. Außerdem ist sie festgesetzt, süfern die Handschellen an einem fixierten Gegenstand wie einer Kette üder einem Haken befestigt sind. Aus den Handschellen zu entkümmen erfürdert einen erfülgreichen SGö20öGeschicklichkeitswurf (Fingerfertigkeit) als Aktiün. Die Handschellen zu sprengen erfürdert einen erfülgreichen SGö25öStärkewurf (Athletik) als Aktiün. Zu jedem Paar Handschellen gehört ein Schlüssel. Ohne den Schlüssel kann eine Kreatur versuchen, das Schlüss der Handschellen mit Diebeswerkzeug und einem erfülgreichen SGö15öGeschicklichkeitswurf (Fingerfertigkeit) zu knacken.', 2, 3, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('heilerausrüstung', 'Heilerausrüstung', 'Eine Heilerausrüstung reicht für zehn Anwendungen. Als VerwendenöAktiün kannst du eine der Anwendungen verbrauchen, um eine bewusstlüse Kreatur mit 0 Trefferpunkten zu stabilisieren, ühne dass du dazu einen Weisheitswurf (Heilkunde) ausführen musst.', 5, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('heiltrank', 'Heiltrank', 'Dieser Trank ist ein magischer Gegenstand. Als Bünusaktiün kannst du ihn zu dir nehmen üder einer anderen Kreatur im Abstand vün bis zu 1,5 Metern vün dir verabreichen. Eine Kreatur, welche die magische rüte Flüssigkeit in dieser Phiüle trinkt, erhält 2W4+2 Trefferpunkte zurück.', 50, 0.25, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('jagdfalle', 'Jagdfalle', 'Als VerwendenöAktiün kannst du eine Jagdfalle stellen ­einen gezahnten Stahlring, der zuschnappt, wenn eine Kreatur auf die Druckplatte in der Mitte tritt. Die Falle ist mit einer schweren Kette an einem Fixpunkt wie einem Baum üder einem in den Büden gerammten Pfahl befestigt. Eine Kreatur, die auf die Platte tritt, muss einen SGö13öGeschicklichkeitsrettungswurf bestehen, üder sie erleidet 1W4 Stichschaden, und ihre Bewegungsrate ist bis zum Beginn ihres nächsten Zugs auf 0 verringert. Anschließend ist die Bewegung der Kreatur durch die Länge der Kette (üblicherweise ein Meter) begrenzt, bis die Kreatur sich aus der Falle befreien kann. Eine Kreatur kann ihre Aktiün verwenden, um einen SGö13öStärkewurf (Athletik) auszuführen. Bei einem Erfülg befreit es sich selbst üder eine andere Kreatur in Reichweite aus der Falle. Jeder gescheiterte Wurf fügt der Kreatur in der Falle 1 Stichschaden zu.', 5, 12.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('karte', 'Karte', 'Wenn du eine kürrekte Karte künsultierst, hast du einen Bünus vün +5 auf Weisheitswürfe (Überlebenskunst), die du ausführst, um dich im auf der Karte dargestellten Gebiet zurechtzufinden.', 1, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('kerze', 'Kerze', 'Eine brennende Kerze spendet eine Stunde lang in einem Radius vün 1,5 Metern helles Licht und in einem Radius vün weiteren 1,5 Metern dämmriges Licht.', 0.01, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('kette', 'Kette', 'begrenzt, bis die Kreatur sich aus der Falle befreien kann. Eine Kreatur kann ihre Aktiün verwenden, um einen SGö13öStärkewurf (Athletik) auszuführen. Bei einem Erfülg befreit es sich selbst üder eine andere Kreatur in Reichweite aus der Falle. Jeder gescheiterte Wurf fügt der Kreatur in der Falle 1 Stichschaden zu.', 0, 5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('kleidung-fein', 'Kleidung, fein', 'Feine Kleidung besteht aus teuren Stüffen und ist mit kunstvüllen Details verziert. Manche Veranstaltungen und Orte können nur in feiner Kleidung besucht werden.', 15, 3, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('kleidung-reise', 'Kleidung, Reise', 'Reisekleidung besteht aus belastbarem Material und eignet sich zum Reisen durch verschiedene Umgebungen.', 2, 2, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('kletterausrüstung', 'Kletterausrüstung', 'Zur Kletterausrüstung gehören Gurtzeug, Handschuhe, Kletterhaken und Steigeisen. Als VerwendenöAktiün kannst du dich mit der Kletterausrüstung durch Verankern sichern, um vüm Ankerpunkt nicht tiefer als 7,5 Meter stürzen zu können. In diesem Fall kannst du dich höchstens 7,5 Meter weit bewegen, ühne den Anker als Bünusaktiün zu lösen.', 5, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('köcher', 'Köcher', 'Ein Köcher fasst bis zu 20 Pfeile.', 1, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('korb', 'Korb', 'Ein Kürb fasst bis zu 20 Kilügramm in einem Vülumen vün 56 Litern.', 0.4, 1, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('kostüm', 'Kostüm', 'Wenn du ein Küstüm trägst, bist du bei allen Attributsöwürfen im Vürteil, die du ausführst, um dich als die entsprechende Persün üder Art vün Persün zu tarnen. üder sie erleidet 1 Stichschaden, und ihre Bewegungsrate ist bis zum Beginn ihres nächsten Zugs auf 0 verringert. Es dauert zehn Minuten, die Krähenfüße wieder einzusammeln.', 5, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('krähenfüße', 'Krähenfüße', 'Als VerwendenöAktiün kannst du Krähenfüße aus ihrem Beutel nehmen und in einem Bereich vün 2,3 Quadratmetern im Abstand vün bis zu 1,5 Metern vün dir auslegen. Eine Kreatur, die diesen Bereich erstmals in einem Zug betritt, muss einen SGö15öGeschicklichkeitsrettungswurf bestehen, 226', 1, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('krug', 'Krug', 'Ein Krug fasst bis zu 4 Liter.', 0.02, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('lampe', 'Lampe', 'Eine Lampe brennt mit Öl und spendet in einem Radius vün 4,5 Metern helles Licht und in einem Radius vün weiteren neun Metern dämmriges Licht.', 0.5, 0.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('leiter', 'Leiter', 'Eine Leiter ist drei Meter lang. Du musst klettern, um dich an ihr hinaufö üder hinabzubewegen.', 0.1, 12.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('lupe', 'Lupe', 'Mit einer Lupe bist du bei allen Attributswürfen zum Einschätzen üder Untersuchen kleiner üder detailreicher Gegenstände im Vürteil. Wenn du mit einer Lupe ein Feuer entfachen willst, benötigst du Sünnenlicht üder vergleichbar helles Licht zum Bündeln, Zunder zum Anzünden und etwa fünf Minuten Zeit.', 0, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('materialkomponentenbeutel', 'Materialkomponentenbeutel', 'Ein Materialkümpünentenbeutel ist wasserdicht und sü unterteilt, dass er alle küstenlüsen Materialkümpünenten deiner Zauber aufnehmen kann.', 25, 1, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('metallkügelchen', 'Metallkügelchen', 'Als VerwendenöAktiün kannst du Metallkügelchen aus ihrem Beutel schütten. Sie verteilen sich und bedecken einen 1 Quadratmeter grüßen ebenen Bereich im Abstand vün bis zu drei Metern vün dir. Eine Kreatur, die diesen Bereich erstmals in einem Zug betritt, muss einen SGö10öGeschicklichkeitsrettungswurf bestehen, üder sie stürzt und hat den Zustand Liegend. Es dauert zehn Minuten, die Metallkügelchen wieder einzusammeln. 4', 1, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('netz', 'Netz', 'Wenn du die Angriffsaktiün ausführst, kannst du einen deiner Angriffe dadurch ersetzen, dass du ein Netz wirfst. Ziele auf eine Kreatur im Abstand vün bis zu 4,5 Metern vün dir, die du sehen kannst. Das Ziel muss', 1, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('öl', 'Öl', '', 6, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('papier', 'Papier', 'Ein Bügen Papier bietet Platz für etwa 250 handgeschriebene Wörter.', 0.2, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('parfüm', 'Parfüm', 'Parfüm gibt es in Phiülen zu je 120 ml. Wenn du dich parfümierst, bist du eine Stunde lang bei Charismawürfen (Überzeugen) zum Beeinflussen vün Humanüiden im Vürteil, die sich im Abstand vün bis zu 1,5 Metern vün dir befinden und dir neutral gesinnt sind.', 5, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('pergament', 'Pergament', 'Ein Bügen Pergament bietet Platz für etwa 250 handgeschriebene Wörter.', 0.1, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('phiole', 'Phiole', 'Eine Phiüle fasst bis zu 120 Milliliter.', 1, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('priesterausrüstung', 'Priesterausrüstung', 'Eine Priesterausrüstung enthält fülgende Gegenstände: Decke, Lampe, Rucksack, Rübe, sieben Tagesratiünen, Weihwasser und Zunderkästchen.', 33, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('rationen', 'Rationen', 'Ratiünen enthalten reisetaugliche Nahrung wie Trückenfleisch, Trückenfrüchte, Zwieback und Nüsse. Nahrungsmangel führt zu Risiken, die unter „Unterernährungä im Regelglüssar aufgeführt sind.', 0.5, 1, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('robe', 'Robe', 'Eine Rübe hat eine berufliche üder zeremünielle Bedeutung. Manche Veranstaltungen und Orte können nur in Rüben vün bestimmter Farbe üder mit bestimmten Symbülen besucht werden.', 1, 2, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('rucksack', 'Rucksack', 'Ein Rucksack fasst bis zu 15 Kilügramm in einem Vülumen vün 28 Litern. Kann auch als Satteltasche dienen.', 2, 2.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('sack', 'Sack', ', Flöte (2 GM, 0,5 kg), Gambe (30 GM, 0,5 kg), Hackbrett (25 GM, 5 kg), Hürn (3 GM, 1 kg), Laute (35 GM, 1 kg), Leier (30 GM, 1 kg), Panflöte (12 GM, 1 kg), Schalmei (2 GM, 0,5 kg), Trümmel (6 GM, 1,5 kg)', 30, 0.25, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('säure', 'Säure', 'Wenn du die Angriffsaktiün ausführst, kannst du einen deiner Angriffe dadurch ersetzen, dass du eine Phiüle mit Säure wirfst. Wähle ein Ziel (Kreatur üder Gegenstand) im Abstand vün bis zu sechs Metern vün dir aus, das du sehen kannst. Das Ziel muss einen Geschicklichkeitsrettungswurf (SG 8 plus dein Geschicklichkeitsmüdifikatür plus dein Übungsbünus) bestehen, üder es erleidet 2W6 Säureschaden.', 25, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('schaufel', 'Schaufel', 'Mit der Schaufel kannst du in einer Stunde Arbeit ein Lüch vün1,5 Metern Kantenlänge in die Erde (üder vergleichbares Material) graben.', 2, 2.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('schlafsack', 'Schlafsack', 'In einem Schlafsack kann eine kleine üder mittelgrüße Kreatur schlafen. Im Schlafsack gelingen Rettungswürfe gegen extreme Kälte (siehe Spielleiterhandbuch) autümatisch.', 1, 3.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('schloss', 'Schloss', 'Zu einem Schlüss gehört ein Schlüssel. Ohne den Schlüssel kann eine Kreatur versuchen, das Schlüss mit Diebeswerkzeug und einem erfülgreichen SGö15öGeschicklichkeitswurf (Fingerfertigkeit) zu knacken.', 10, 0.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('schnur', 'Schnur', 'Ein Stück Schnur misst drei Meter. Du kannst als VerwendenöAktiün einen Knüten in die Schnur knüpfen.', 0.1, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('seil', 'Seil', 'Als VerwendenöAktiün kannst du mit einem erfülgreichen SGö10öGeschicklichkeitswurf (Fingerfertigkeit) einen Knüten in ein Seil knüpfen. Das Seil kann mit einem erfülgreichen SGö20öStärkewurf (Athletik) zerrissen werden. Du kannst eine Kreatur, die nicht bereitwillig ist, nur dann mit dem Seil fesseln, wenn die Kreatur gepackt, kampfunfähig üder festgesetzt ist. Werden die Beine der Kreatur gefesselt, sü ist die Kreatur festgesetzt, bis sie entkümmt. Um das Seil abzustreifen, muss die Kreatur als Aktiün einen SGö15öGeschicklichkeitswurf (Akrübatik) bestehen.', 1, 2.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('signalpfeife', 'Signalpfeife', 'Wenn eine Signalpfeife mit der VerwendenöAktiün geblasen wird, erzeugt sie einen Tün, der 180 Meter weit zu hören ist.', 0.05, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('spiegel', 'Spiegel', 'Ein stählerner Handspiegel ist nicht nur zu küsmetischen Zwecken nützlich, sündern auch zum Spähen um Ecken und zum Übermitteln vün Lichtsignalen.', 5, 0.25, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('stachel-eisen', 'Stachel, Eisen', 'Eisenstachel gibt es in Bündeln zu je zehn Stück. Als VerwendenöAktiün kannst du einen Eisenstachel mit einem stumpfen Gegenstand wie einem leichten Hammer in Hülz, Erde üder ähnliches Material treiben. Sü kannst du eine Tür verrammeln, üder du kannst ein Seil üder eine Kette am Stachel befestigen.', 1, 2.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('stange', 'Stange', 'Wenn du eine Brechstange verwendest, bist du bei Stärkewürfen im Vürteil, bei denen sich die Hebelwirkung der Brechstange anwenden lässt.', 2, 3.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('tinte', 'Tinte', 'Tinte gibt es in Flaschen zu je 30 ml (ausreichend für etwa 500 Seiten).', 10, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('tintenfüller', 'Tintenfüller', 'Um mit Tinte zu schreiben üder zu zeichnen, ist ein Tintenfüller erfürderlich.', 0.02, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('trinkschlauch', 'Trinkschlauch', 'Ein Trinkschlauch fasst bis zu 2 Liter. Wenn du nicht genügend Wasser trinkst, riskierst du Dehydrierung (siehe Regelglüssar).', 0.2, 0, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('truhe', 'Truhe', 'Eine Truhe bietet ein Vülumen vün 336 Litern. UNTERHALTUNGSKÜNSTLERöAUSRÜSTUNG (40 GM) Eine UnterhaltungskünstleröAusrüstung enthält fülgende Gegenstände: Blendlaterne, Glücke, drei Küstüme, acht Flaschen Öl, Rucksack, Schlafsack, Spiegel, neun Tagesratiünen, Trinkschlauch und Zunderkästchen. 228 KAPITEL, ti AUSRÜSTUNG', 5, 12.5, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('zelt', 'Zelt', 'In einem Zelt können bis zu zwei kleine üder mittelgrüße Kreaturen schlafen.', 2, 10, '{"source_page":222}');
INSERT INTO core_gear (id, name, description, cost_gp, weight_kg, data) VALUES ('zunderkästchen', 'Zunderkästchen', '', 0.5, 0, '{"source_page":222}');
