INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('aberration-herbeirufen', 'ABERRATION HERBEIRUFEN', 4, 'Beschwörungszauber', 'Aktion', '27 Meter', 'V, G, M (ein eingelegtes Tentakel und ein Augapfel in einer Phiole mit Platinintarsie im Wert von mindestens 400 GM)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du rufst eine geisterhafte Aberration herbei. Sie erscheint in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten einer Geisterhaften Aberration. Wenn du den Zauber wirkst, wähle zwischen Betrachter, Gedankenschinder oder Slaad aus. Die Kreatur sieht aus wie eine Aberration dieser Art. Einige Details im Wertekasten werden dadurch bestimmt. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet. Die Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.', 'Hexenmeister, Magier', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":"11 + Zaubergrad","hp":null},"summon_text":"MERKMALE\nGEISTERHAFTE ABERRATION\nMittelgroße Aberration, neutral\nRK 11 + Zaubergrad\nTP 40 + 10 für jeden Zaubergrad ab dem 5.\nBewegungsrate 9 m, Fliegen 9 m (Schweben, nur Betrachter)\nMOD\nRW\nMOD RW\nMOD RW\nSTÄ\n+3\n+3\nGES 10\n+0\n+0\nKON 15\n+2\n+2\nINT\n+3\n+3\nWEI 10\n+0\n+0\nCHA 6\n-2\n-2\nImmunitäten Psychisch\nSinne Dunkelsicht 18 m, Passive Wahrnehmung 10 Sprachen Tiefensprache, versteht die Sprachen, die du kennst HG — (EP 0, ÜB entspricht deinem Übungsbonus)\n!I\nFlüsternde Aura (nur Gedankenschinder): Zu Beginn jedes seiner Züge strahlt der Geist psionische Energie aus, sofern er nicht kampfunfähig ist. Weisheitsrettungswurf: Der SG entspricht deinem Zauberrettungswurf-SG, jede Kreatur (außer dir) im Abstand von bis zu 1,5 Metern vom Geist. Misserfolg: 2W6 psychischer Schaden. Regeneration (nur Slaad): Der Geist erhält zu Beginn seines Zugs 5 Trefferpunkte zurück, wenn er über mindestens 1 Trefferpunkt verfügt. AKTIONEN\nMehrfachangriff Der Geist führt eine Anzahl von Angriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet).\nKlaue (nur Slaad): Nahkampfangriffswurf? Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W10+3 + Zaubergrad Hiebschaden, und das Ziel kann bis zum Beginn des nächsten Zugs des Geists keine Trefferpunkte zurückerhalten.\nPsychischer Hieb (nur Gedankenschinder): Nahkampfangriffswurf? Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W8+3 + Zaubergrad psychischer Schaden.\nAugenstrahl (nur Betrachter): Fernkampfangriffswurf.• Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 45 Meter. Treffer: 1W8+3 + Zaubergrad psychischer Schaden.","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('ablenkung', 'ABLENKUNG', 5, 'Illusionszauber', 'Aktion', 'Selbst', 'S', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du wirst unsichtbar, und gleichzeitig erscheint in deinem Bereich ein illusionärer Doppelgänger von dir. Dieser bleibt für die Wirkungsdauer bestehen, doch deine Unsichtbarkeit endet, sobald du einen Angriffswurf ausführst, Schaden bewirkst oder einen Zauber wirkst. Als magische Aktion kannst du deinen illusionären Doppelgänger bis zu deiner doppelten Bewegungsrate bewegen und ihn gestikulieren, sprechen und auf eine beliebige Art handeln lassen. Er ist immateriell und unverwundbar. Du kannst durch seine Augen sehen und durch seine Ohren hören, als befändest du dich am selben Ort.', '', 'Barde, Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('alarm', 'ALARM', 1, 'Bannzauber', '1 Minute oder Ritual', '9 Meter', 'V, G, M (Glocke und Silberdraht)', NULL, '8 Stunden', 0, 1, 'Du erschaffst einen Alarm gegen Eindringen. Wähle eine Tür, ein Fenster oder einen Bereich in Reichweite aus, der nicht größer als ein Würfel mit sechs Metern Kantenlänge sein darf. Für die Wirkungsdauer warnt dich ein Alarm, wann immer eine Kreatur den geschützten Bereich berührt oder in ihn eindringt. Wenn du den Zauber wirkst, kannst du Kreaturen bestimmen, die den Alarm nicht auslösen. Außerdem wählst du aus, ob der Alarm akustisch oder mental erfolgt:
Akustischer Alarm: Der Alarm erzeugt im Abstand von bis
zu 18 Metern vom geschützten Bereich zehn Sekunden
lang das Geräusch einer Handglocke.
Mentaler Alarm: Du wirst von einem mentalen
Ping gewarnt, wenn du dich im Abstand von bis zu
1,6 Kilometern vom geschützten Bereich befindest.
Dieser Ping weckt dich, wenn du schläfst.', '', 'Magier, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('ansteckung', 'ANSTECKUNG', 5, 'Nekromantiezauber', 'Aktion', 'Berührung', 'V, G', NULL, '7 Tage', 0, 0, 'Deine Berührung verursacht eine magische Ansteckung. Das Ziel muss einen Konstitutionsrettungswurf bestehen, oder es erleidet 11W8 nekrotischen Schaden und ist vergiftet • Wähle außerdem ein Attribut aus, wenn du den Zauber wirkst. Das vergiftete Ziel ist bei Rettungswürfen mit dem ausgewählten Attribut im Nachteil. Das Ziel muss den Rettungswurf am Ende jedes seiner Züge wiederholen, bis es insgesamt drei Erfolge oder drei Misserfolge hatte. Bei drei Erfolgen endet der Zauber bei dem Ziel. Bei drei Misserfolgen wirkt der Zauber sieben Tage lang auf das Ziel. Wann immer das vergiftete Ziel einen Effekt erhält, der den Zustand Vergiftet beenden würde, muss es einen Konstitutionsrettungswurf bestehen, oder der Zustand Vergiftet endet nicht. ANTIMAGISCHES FELD Bannzauber 8. Grades (Kleriker, Magier) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G, M (Eisenspäne) Wirkungsdauer: Konzentration, bis zu 1 Stunde Du wirst von einer antimagischen Aura mit einer Aus­strömung von drei Metern umgeben. Im Bereich der Aura kann niemand Zauber wirken, magische Aktionen ausführen oder andere magische Effekte erzeugen, und Ziele innerhalb der Aura können nicht von Zaubern, magischen Aktionen und Effekten betroffen werden. Magische Eigenschaften von magischen Gegenständen funktionieren innerhalb der Aura nicht und haben auf nichts darin eine Wirkung. Wirkungsbereiche von Zaubern oder anderer Magie können sich nicht in die Aura erstrecken. Niemand kann sich in die Aura oder aus ihr heraus teleportieren, und auch Ebenenreisen sind in der Aura blockiert. Portale schließen sich, solange sie sich innerhalb der Aura befinden. Bereits wirksame Zauber werden im Bereich unterdrückt, sofern sie nicht von Artefakten oder Gottheiten gewirkt wurden. Solange ein Effekt unterdrückt wird, wirkt er nicht. Die Zeit, in der er unterdrückt ist, wird dennoch von seiner Wirkungsdauer abgezogen. Magie bannen hat innerhalb der Aura keinen Effekt. Auren, die durch mehrere Antimagisches Feld-Zauber erzeugt wurden, heben einander nicht auf.', '', 'Druide, Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('antipathie-sympathie', 'ANTIPATHIE/SYMPATHIE', 8, 'Verzauberungszauber', '1 Stunde', '18 Meter', 'V, G, M (eine Mischung aus Essig und Honig)', NULL, '10 Tage', 0, 0, 'Wähle beim Wirken des Zaubers aus, ob er Antipathie oder Sympathie erzeugen soll, und ziele auf eine Kreatur oder einen Gegenstand von höchstens riesiger Größe. Bestimme dann einen Kreaturentyp (beispielsweise rote Drachen, Goblins oder Vampire). Kreaturen der ausgewählten Art führen einen Weisheitsrettungswurf aus, wenn sie sich dem Ziel auf höchstens 36 Meter nähern. Deine Entscheidung (Antipathie oder Sympathie) bestimmt, was mit einer Kreatur geschieht, wenn der Rettungswurf scheitert: Antipathie: Die Kreatur ist verängstigt. Die verängstigte Kreatur muss in ihren Zügen ihre Bewegung nutzen, um sich so weit wie möglich vom Ziel zu entfernen. Dabei nimmt sie die sicherste Route. Sympathie: Die Kreatur ist bezaubert. Die bezauberte Kreatur muss in ihren Zügen ihre Bewegung nutzen, um sich dem Ziel so weit wie möglich zu nähern. Dabei nimmt sie die sicherste Route. Befindet sich die Kreatur im Abstand von bis zu 1,5 Metern vom Ziel, so kann sie sich nicht freiwillig entfernen. Wenn das Ziel der bezauberten Kreatur Schaden zufügt, kann diese einen Weisheitsrettungswurf ausführen, um den Effekt wie unten beschrieben zu beenden. Den Effekt beenden: Wenn die verängstigte oder bezauberte Kreatur ihren Zug weiter als 36 Meter vom Ziel entfernt beendet, führt sie einen Weisheitsrettungswurf aus. Bei einem erfolgreichen Rettungswurf endet der Effekt des Ziels auf die Kreatur. Nach einem erfolgreichen Rettungswurf ist eine Kreatur eine Minute lang gegen den Effekt immun. Danach kann er erneut auf sie gewirkt werden.', '', 'Barde, Druide, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('arkane-spiegelung', 'ARKANE SPIEGELUNG', 7, 'Illusionszauber', '10 Minuten', 'Sicht', 'V, G', NULL, '10 Tage', 0, 0, 'Du lässt Gelände in einem Bereich von 2,6 Quadrat­kilometern wie eine andere Art von Gelände aussehen, riechen und sich sogar anfühlen. Weite Felder oder eine Straße können somit wie ein Sumpf, ein Hügel, eine Erdspalte oder anderes unwegsames oder unpassierbares Gelände erscheinen. Ein Tümpel kann wie eine grüne Wiese wirken, ein Steilhang wie ein sanftes Gefälle, ein Graben voller Felsbrocken wie eine breite Straße. Du kannst auch das Aussehen von Gebäuden ändern oder sie dort platzieren, wo es keine gibt. Der Zauber kann keine Kreaturen verändern, verbergen oder hinzufügen. Die Illusion umfasst hörbare, visuelle, berührbare und olfaktorische Elemente, sodass sie sicheres Gelände in schwieriges verwandeln (und umgekehrt) oder anderweitig die Bewegung durch das Gebiet beeinträchtigen kann. Jeder Teil des illusionären Geländes wie ein Fels oder ein Zweig, der aus dem Bereich des Zaubers entfernt wird, verschwindet sofort. Kreaturen mit Wahrer Blick können die Illusion durchschauen und sehen das echte Gelände. Alle anderen Elemente der Illusion bleiben jedoch bestehen. Selbst wenn sich eine Kreatur der Illusion bewusst ist, kann sie physisch mit ihr interagieren.', '', 'Barde, Druide, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('arkane-vitalit-t', 'ARKANE VITALITÄT', 2, 'Bannzauber', 'Bonusaktion', 'Selbst', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du zapfst deine Lebenskraft an, um dich zu heilen. Würfle mit einem oder zwei deiner unverbrauchten Trefferpunktewürfel, und du erhältst eine Anzahl von Trefferpunkten in Höhe des Gesamtergebnisses plus deines Zauberwirken-Attributsmodifikators zurück. Die Würfel werden dabei verbraucht.', 'Für jeden Zauberplatzgrad über dem 2. wird die Anzahl von unverbrauchten Trefferwürfeln, mit denen du würfeln kannst, um eins erhöht.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird die Anzahl von unverbrauchten Trefferwürfeln, mit denen du würfeln kannst, um eins erhöht."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('arkanes-auge', 'ARKANES AUGE', 4, 'Erkenntniszauber', 'Aktion', '9 Meter', 'V, G, M (ein Stück Fledermausfell)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du erschaffst ein unsichtbares und unverwundbares Auge in Reichweite, das für die Wirkungsdauer in der Luft schwebt Es kann in jede Richtung blicken und übermittelt dir mental seine visuellen Informationen. Außerdem verfügt es über Dunkelsicht mit einer Reichweite von neun Metern. Als Bonusaktion kannst du das Auge bis zu neun Meter weit in jede Richtung bewegen. Außerdem kann es nicht durch massive Barrieren gelangen, jedoch Öffnungen mit mindestens 2,5 Zentimetern Durchmesser passieren. ARKANES SCHLOSS Bannzauber 2. Grades (Magier) Zeitaufwand: Aktion Reichweite: Berührung Komponenten: V, G, M (Goldstaub im Wert von mindestens 25 GM, den der Zauber verbraucht) Wirkungsdauer: Bis der Zauber gebannt wird Du berührst eine geschlossene Tür, ein Fenster, ein Tor, einen Behälter oder eine Luke und verschließt diesen Gegenstand für die Wirkungsdauer auf magische Art. Dieses Schloss kann mit nichtmagischen Methoden nicht entfernt werden. Du und alle Kreaturen, die du beim Wirken des Zaubers bestimmst, könnt den Gegenstand trotz des Schlosses normal öffnen und schließen. Du kannst auch ein Kennwort festlegen, das diesen Gegenstand im Abstand von bis zu 1,5 Metern eine Minute lang öffnet.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('arkanes-tor', 'ARKANES TOR', 6, 'Beschwörungszauber', 'Aktion', '150 Meter', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erzeugst miteinander verbundene Teleportationsportale. Wähle zwei große freie Bereiche auf dem Boden aus, die du sehen kannst. Einer davon muss sich in Reichweite befinden, der andere im Abstand von bis zu drei Metern von dir. In jedem dieser Bereiche öffnet sich je ein rundes Portal und bleibt für die Wirkungsdauer bestehen. Die Portale sind zweidimensionale leuchtende Ringe, die mit Nebel gefüllt sind, der die Sichtblockiert. Sie schweben senkrecht einige Zentimeter über dem Boden. Ein Portal ist nur auf einer Seite (nach deiner Wahl) geöffnet. Was immer in die offene Seite eines der Portale gelangt, kommt aus der offenen Seite des anderen heraus, als stünden die Portale direkt nebeneinander. Als Bonusaktion kannst du ändern, in welche Richtung die offenen Seiten zeigen.', '', 'Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('arme-von-radar', 'ARME VON RADAR', 1, 'Beschwörungszauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du beschwörst Hadar und lässt Tentakel aus dir hervorbrechen. Jede Kreatur in einer Ausströmung von drei Metern, die von dir ausgeht, führt einen Stärkerettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 2W6 nekrotischen Schaden und kann bis zum Beginn seines nächsten Zugs keine Reaktionen ausführen. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur nur halb so viel Schaden.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht. ASTRALE PROJEKTION ''I. • s EIN GOLIATH-HEXENMEISTER SETZT DIE ZAUBER *Ni .', 'Hexenmeister', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht. ASTRALE PROJEKTION ''I. • s EIN GOLIATH-HEXENMEISTER SETZT DIE ZAUBER *Ni .","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('therische-gestalten', 'ÄTHERISCHE GESTALTEN', 7, 'Beschwörungszauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Bis zu 8 Stunden', 0, 0, 'Du betrittst den Grenzbereich der Ätherebene, an dem sie sich mit deiner aktuellen Ebene überschneidet. Für die Wirkungsdauer bleibst du in der Äthergrenze. Währenddessen kannst du dich in jede Richtung bewegen. Wenn du dich nach oben oder unten bewegst, kostet dich jeder Meter Bewegung zwei Meter deiner Bewegungsrate. Du kannst die Ebene, die du verlassen hast, in Graustufen wahrnehmen. Deine Sichtweite beträgt dabei 18 Meter. Während du dich auf der Ätherebene befindest, kannst du nur Kreaturen, Objekte und Effekte beeinflussen oder von ihnen beeinflusst werden, die sich ebenfalls dort befinden. Kreaturen, die sich nicht auf der Ätherebene befinden, können dich weder wahrnehmen noch mit dir interagieren, es sei denn, ein Merkmal erlaubt es ihnen. Wenn der Zauber endet, kehrst du auf die Ebene zurück, die du verlassen hast und erscheinst in dem Bereich, der deinem Bereich in der Äthergrenze entspricht. Erscheinst 242 KAPITEL 7 ! ZAUBER EINE. TIEFLING-ZAUBERIN ENTFESSELT A,ZKUGEL AUF NOTHICS du in einem besetzten Bereich, so wirst du in den nächstliegenden freien Bereich geschoben und erleidest Energieschaden in sechsfacher Höhe der Anzahl an Metern, die du bewegt wurdest. Dieser Zauber endet sofort, wenn du dich beim Wirken bereits auf der Ätherebene oder einer Ebene befindest, die nicht an sie angrenzt (beispielsweise auf den Äußeren Ebenen).', 'Für jeden Zauberplatzgrad über dem 7. kannst du auf bis zu drei bereitwillige Kreaturen (dich eingeschlossen) zielen. Die Kreaturen müssen sich im Abstand von bis zu drei Metern von dir befinden, wenn du diesen Zauber wirkst.', 'Barde, Hexenmeister, Kleriker, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 7. kannst du auf bis zu drei bereitwillige Kreaturen (dich eingeschlossen) zielen. Die Kreaturen müssen sich im Abstand von bis zu drei Metern von dir befinden, wenn du diesen Zauber wirkst."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('attribut-verbessern', 'ATTRIBUT VERBESSERN', 2, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (etwas Fell oder eine Feder)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du berührst eine Kreatur und wählst Charisma, Geschicklichkeit, Intelligenz, Stärke oder Weisheit aus. Für die Wirkungsdauer ist das Ziel bei Attributswürfen mit dem ausgewählten Attribut im Vorteil.', 'Für jeden Zauberplatzgrad über dem 2. kannst du auf eine weitere Kreatur zielen. Du kannst für jedes Ziel ein anders Attribut bestimmen.', 'Barde, Druide, Kleriker, Magier, Waldläufer, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. kannst du auf eine weitere Kreatur zielen. Du kannst für jedes Ziel ein anders Attribut bestimmen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('atzkugel', 'ATZKUGEL', 4, 'Hervorrufungszauber', 'Aktion', '45 Meter', 'V, G, M (ein Tropfen Galle)', NULL, 'Unmittelbar', 0, 0, 'Du deutest auf einen Ort in Reichweite. Dort schlägt ein leuchtender Ball aus Säure mit einem Radius von 15 Zentimetern auf und explodiert in einer Kugel mit einem Radius von sechs Metern. Jede Kreatur in diesem Bereich führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 10W4 Säureschaden sowie weitere 5W4 Säureschaden am Ende ihres nächsten Zugs. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur nur die Hälfte des anfänglichen Schadens.', 'Für jeden Zauberplatzgrad über dem 4. wird der anfängliche Schaden um 2W4 erhöht.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der anfängliche Schaden um 2W4 erhöht.","type":"damage","dice":"2W4"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('auferstehung', 'AUFERSTEHUNG', 7, 'Nekromantiezauber', '1 Stunde', 'Berührung', 'V, G, M (ein Diamant im Wert von mindestens 1.000 GM, den der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 0, 'Du erweckst mit einer Berührung eine tote Kreatur zum Leben, die nicht länger als ein Jahrhundert tot war, nicht an Altersschwäche gestorben ist und nicht untot war, als sie starb. Die Kreatur kehrt mit all ihren Trefferpunkten ins Leben zurück. Dieser Zauber neutralisiert zudem sämtliche Gifte, die zum Zeitpunkt des Todes auf das Ziel gewirkt haben. Dieser Zauber schließt alle tödlichen Wunden und stellt sämtliche abgetrennten Gliedmaßen wieder her. Von den Toten aufzuerstehen ist eine Tortur. Das Ziel erleidet einen Malus von -4 auf W20-Prüfungen. Nach jeder langen Rast wird der Malus um 1 verringert, bis er 0 beträgt. Wirkst du diesen Zauber, um eine Kreatur zum Leben zu erwecken, die seit mindestens 365 Tagen tot ist, so belastet dich das stark. Du kannst erst nach einer langen Rast wieder Zauber wirken und bist bis dahin bei W20-Prüfungen im Nachteil.', '', 'Barde, Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('aufl-sung', 'AUFLÖSUNG', 6, 'Verwandlungszauber', 'Aktion', '18 Meter', 'V, G, M (ein Magnetstein und Staub)', NULL, 'Unmittelbar', 0, 0, 'Du verschießt einen dünnen grünen Strahl auf ein Ziel in Reichweite, das du sehen kannst. Das Ziel kann eine Kreatur, ein nichtmagischer Gegenstand oder eine Struktur aus magischer Energie sein, beispielsweise die Wand des Zaubers Energiewand. Eine Kreatur, die zum Ziel dieses Zaubers wird, führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet das Ziel 10W6+40 Energieschaden. Wenn seine Trefferpunkte durch diesen Schaden auf 0 sinken, zerfällt es mitsamt jeder nichtmagischen Ausrüstung, die es trägt oder hält, zu grauem Staub. Das Ziel kann nur durch die Zauber Wahre Auferstehung und Wunsch wiederbelebt werden. Nichtmagische Gegenstände von höchstens großer Größe sowie Strukturen aus magischer Energie werden von diesem Zauber automatisch aufgelöst. Wenn das Ziel von mindestens riesiger Größe ist, löst dieser Zauber einen Würfel mit drei Metern Kantenlänge davon auf.', 'Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 3W6 erhöht.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 3W6 erhöht.","type":"damage","dice":"3W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('aura-der-gesundheit', 'AURA DER GESUNDHEIT', 3, 'Bannzauber', 'Aktion', 'Selbst', 'V', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Für die Wirkungsdauer geht eine Aura mit einer Aus­strömung neun Metern von dir aus. Wenn du die Aura erzeugst, sowie zu Beginn jedes deiner Züge, solange sie besteht, kannst du 2W6 Trefferpunkte bei einer Kreatur innerhalb der Aura wiederherstellen. 24.4', '', 'Druide, Kleriker, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('aura-der-reinheit', 'AURA DER REINHEIT', 4, 'Bannzauber', 'Aktion', 'Selbst', 'V', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Für die Wirkungsdauer geht eine Aura mit einer Aus­strömung von neun Metern von dir aus. In der Aura seid du und deine Verbündeten gegen Giftschaden resistent und bei Rettungswürfen zum Vermeiden oder Beenden folgender Zustände im Vorteil: Betäubt, Bezaubert, Blind, Gelähmt, Taub, Verängstigt und Vergiftet.', '', 'Kleriker, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('aura-des-lebens', 'AURA DES LEBENS', 4, 'Bannzauber', 'Aktion', 'Selbst', 'V', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Für die Wirkungsdauer geht eine Aura mit einer Ausströ­mung von neun Metern von dir aus. In der Aura seid du und deine Verbündeten gegen nekrotischen Schaden resistent, und eure Trefferpunktemaxima können nicht verringert werden. Wenn ein Verbündeter mit 0 Trefferpunkten seinen Zug in der Aura beginnt, erhält er 1 Trefferpunkt zurück.', '', 'Kleriker, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('ausbessern', 'AUSBESSERN', 0, 'Verwandlungzauber', '1 Minute', 'Berührung', 'V, G, M (zwei Magnetsteine)', NULL, 'Unmittelbar', 0, 0, 'Dieser Zauber repariert einen Gegenstand, den du berührst und der eine Bruchstelle oder einen Riss hat, beispielsweise ein kaputtes Kettenglied, einen zerbrochenen Schlüssel, einen zerrissenen Umhang oder einen lecken Weinschlauch. Wenn der Riss oder die Bruchstelle in keiner Abmessung größer als 30 Zentimeter ist, wird nicht mehr zu erkennen sein, dass der Gegenstand beschädigt war. Dieser Zauber kann magische Gegenstände physisch reparieren, deren Magie jedoch nicht wiederherstellen.', '', 'Barde, Druide, Kleriker, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('usserlichkeiten', 'ÄUSSERLICHKEITEN', 5, 'Illusionszauber', 'Aktion', '9 Meter', 'V, G', NULL, '8 Stunden', 0, 0, 'Du verleihst jeder Kreatur deiner Wahl in Reichweite, die du sehen kannst, ein illusionäres Erscheinungsbild. Ein nicht bereitwilliges Ziel kann einen Charismarettungswurf ausführen. Bei einem Erfolg ist es nicht von diesem Effekt betroffen. Du kannst den Zielen dasselbe Erscheinungsbild oder unterschiedliche geben. Der Zauber kann das Erscheinungs­bild von Körpern sowie Ausrüstung der Ziele verändern. Du kannst jede Kreatur 30 Zentimeter größer oder kleiner sowie schwerer oder leichter erscheinen lassen. Das neue Erscheinungsbild eines Ziels muss die gleiche Grundanordnung von Gliedmaßen haben. Abgesehen davon sind die Einzelheiten der Illusion dir überlassen. Der Zauber bleibt für die Wirkungsdauer bestehen. Einer genauen körperlichen Untersuchung halten die Veränderungen dieser Illusion nicht stand. Nutzt du diesen Zauber beispielsweise, um einer Kreatur einen Hut aufzusetzen, so durchdringen Gegenstände diesen einfach. Eine Kreatur, die die Studieren-Aktion ausführt, um ein Ziel zu untersuchen, kann einen Intelligenzwurf (Nachforschungen) gegen deinen Zauberrettungswurf-SG ausführen. Bei einem Erfolg weiß sie, dass das Ziel verkleidet wurde.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('aussp-hung', 'AUSSPÄHUNG', 5, 'Erkenntniszauber', '10 Minuten', 'Selbst', 'V, G, M (ein Fokus im Wert von mindestens 1.000 GM, beispielsweise eine Kristallkugel, ein Spiegel oder ein mit Wasser gefülltes Becken)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du kannst eine Kreatur deiner Wahl, die sich auf derselben Existenzebene wie du befindet, hören und sehen. Das Ziel führt einen Weisheitsrettungswurf aus, der dadurch modifiziert wird, wie gut du das Ziel kennst und ob du einen Gegenstand hast, der dich mit ihm verbindet (siehe Tabellen unten). Das Ziel weiß nicht, wogegen es den Rettungswurf ausführt. Es fühlt sich nur vage unwohl. RettungswurfDein Wissen über das Ziel ist ... Modifikator aus zweiter Hand (du hast von dem Ziel gehört) +5 aus erster Hand (du bist dem Ziel begegnet) +0 umfassend (du kennst das Ziel gut) -5 RettungswurfDu hast etwas vom Ziel, und zwar ... Modifikator ein Abbild oder Porträt -2 ein Kleidungsstück oder anderen Besitz -4 ein Körperteil, Nagelstück oder eine Haarlocke -10 Bei einem erfolgreichen Rettungswurf ist das Ziel nicht betroffen, und du kannst diesen Zauber erst nach 24 Stunden erneut auf das Ziel wirken. Scheitert der Wurf, so erschafft der Zauber einen unsichtbaren, immateriellen Sensor im Abstand von bis zu drei Metern vom Ziel. Du kannst durch den Sensor hören und sehen, als ob du dich in seinem Bereich befändest. Der Sensor bewegt sich mit dem Ziel und bleibt für die Wirkungsdauer im Abstand von bis zu drei Metern von ihm. Wer den Sensor sehen kann, sieht ihn als leuchtende, etwa faustgroße Kugel. Anstelle einer Kreatur kannst du auf einen Ort zielen, den du schon einmal gesehen hast. In diesem Fall erscheint der Sensor an diesem Ort und bewegt sich nicht.', '', 'Barde, Druide, Hexenmeister, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('baumwandeln', 'BAUMWANDELN', 5, 'Beschwörungszauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du erhältst die Fähigkeit, einen Baum zu betreten und aus einem anderen Baum derselben Art im Abstand von bis zu 150 Metern wieder herauszusteigen. Beide Bäume müssen lebendig und mindestens so groß wie du sein. Es kostet 1,5 Meter Bewegung, den Baum zu betreten. Du erfährst sofort die Position aller anderen Bäume derselben Art im Abstand von bis zu 150 Metern. Als Teil der Bewegung, um den Baum zu betreten, kannst du entweder in einen dieser Bäume wechseln und oder aus dem Baum heraustreten, in dem du dich befindest. Du erscheinst in einem Bereich deiner Wahl im Abstand von bis zu 1,5 Metern vom Zielbaum, was dich weitere 1,5 Meter Bewegung kostet. Hast du keine Bewegungsrate übrig, so erscheinst du im Abstand von bis zu 1,5 Metern vom Baum, den du betreten hast. Du kannst diese Transportmöglichkeit nur einmal in jedem deiner Züge anwenden. Du musst jeden Zug außerhalb eines Baums beenden.', '', 'Druide, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('befehl', 'BEFEHL', 1, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V', NULL, 'Unmittelbar', 0, 0, 'Du gibst einer Kreatur in Reichweite, die du sehen kannst, einen ein Wort langen Befehl. Das Ziel muss einen Weisheitsrettungswurf bestehen oder den Befehl im nächsten Zug ausführen. Wähle den Befehl aus diesen Optionen aus: Anhalten: Das Ziel bewegt sich in seinem Zug nicht und führt weder eine Aktion noch eine Bonusaktion aus. Fliehen: Das Ziel nutzt seinen Zug, um sich so schnell wie möglich von dir wegzubewegen. Herkommen: Das Ziel bewegt sich auf dem kürzesten Weg in deine Richtung und beendet seinen Zug, sobald es sich auf höchstens 1,5 Meter genähert hat. Hinlegen: Das Ziel legt sich nieder und beendet dann seinen Zug. Loslassen: Das Ziel lässt fallen, was es gerade hält, und beendet seinen Zug.', 'Für jeden Zauberplatzgrad über dem 1. kannst du eine weitere Kreatur beeinflussen. -KAPITEL 7j ZAUBER II', 'Barde, Kleriker, Paladin', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du eine weitere Kreatur beeinflussen. -KAPITEL 7j ZAUBER II"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('beistand', 'BEISTAND', 2, 'Bannzauber', 'Aktion', '9 Meter', 'V, G, M (ein weißer Stoffstreifen)', NULL, '8 Stunden', 0, 0, 'Wähle bis zu drei Kreaturen in Reichweite aus. Für die Wirkungsdauer werden Trefferpunktemaximum sowie aktuelle Trefferpunkte jedes Ziels um 5 erhöht.', 'Für jeden Zauberplatzgrad über dem 2. werden die Trefferpunkte jedes Ziels um jeweils 5 erhöht.', 'Barde, Druide, Kleriker, Paladin, Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. werden die Trefferpunkte jedes Ziels um jeweils 5 erhöht."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('bewegungsfreiheit', 'BEWEGUNGSFREIHEIT', 4, 'Bannzauber', 'Aktion', 'Berührung', 'V, G, M (ein Lederriemen)', NULL, '1 Stunde', 0, 0, 'Du berührst eine bereitwillige Kreatur. Für die Wirkungs­dauer ist die Bewegungsrate des Ziels auf schwierigem Gelände nicht eingeschränkt. Zudem können Zauber und andere magische Effekte weder seine Bewegungsrate verringern noch das Ziel lähmen oder festsetzen. Das Ziel hat ferner eine Schwimmbewegungsrate in Höhe seiner Bewegungsrate. Es kann außerdem 1,5 Meter seiner Bewegungsrate verbrauchen, um nichtmagischen Fesseln (beispielsweise Handschellen oder einer Kreatur, die es gepackt hält) automatisch zu entkommen.', 'Für jeden Zauberplatzgrad über dem 4. kannst du auf eine weitere Kreatur zielen.', 'Barde, Druide, Kleriker, Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('bigbys-hand', 'BIGBYS HAND', 5, 'Hervorrufungszauber', 'Aktion', '36 Meter', 'V, G, M (eine Eierschale und ein Handschuh)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du erschaffst eine große Hand aus schimmernder magischer Energie in einem freien Bereich in Reichweite, den du sehen kannst. Die Hand bleibt für die Wirkungsdauer bestehen. Sie bewegt sich auf deinen Befehl und imitiert die Bewegungen deiner eigenen Hand. Die Hand ist ein Gegenstand mit einer RK von 20. Ihre Trefferpunkte entsprechen deinem Trefferpunktemaximum. Wenn ihre Trefferpunkte auf 0 sinken, endet der Zauber. Die Hand besetzt ihren Bereich nicht. Nach dem Wirken des Zaubers sowie als Bonusaktion in deinen folgenden Zügen kannst du die Hand bis zu 18 Meter weit bewegen und einen der folgenden Effekte bewirken: Blockierende Hand: Die Hand gewährt dir Teildeckung gegen Angriffe und andere Effekte, die von ihrem Bereich ausgehen oder ihn durchqueren. Außerdem zählt ihr Bereich für deine Gegner als schwieriges Gelände. DER MAGIER ßIGBY NUTZT SEINEN LIEBLINGSZAUBER BIGBYS HAND, UM UNGESTÖRT FORSCHEN ZU KÖNNEN. Geballte Faust: Die Hand schlägt ein Ziel im Abstand von bis zu 1,5 Metern von ihr. Führe einen Nahkampf-Zauberangriff aus. Bei einem Treffer erleidet das Ziel 5W8 Energieschaden. Greifende Hand: Die Hand versucht, eine Kreatur von höchstens riesiger Größe im Abstand von bis zu 1,5 Metern von ihr zu packen. Das Ziel muss einen Geschicklichkeitsrettungswurf bestehen, oder es wird gepackt (der Rettungswurf-SG entspricht deinem Zauberrettungswurf-SG). Solange die Hand das Ziel gepackt hat, kannst du eine Bonusaktion verwenden, damit sie es zerquetscht. Dabei fügt sie ihm Wuchtschaden in Höhe von 4W6 plus deinem Zauberwirken-Attributsmodifikator zu. Kraftvolle Hand: Die Hand versucht, eine Kreatur von höchstens riesiger Größe im Abstand von bis zu 1,5 Metern von ihr zu stoßen. Das Ziel muss einen Stärkerettungswurf bestehen, oder die Hand schiebt es bis zu 1,5 Meter plus 1,5-mal dein Zauberwirken-Attributs-modifikator (in Metern) weit in eine Richtung deiner Wahl. Die Hand bewegt sich mit dem Ziel und bleibt im Abstand von bis zu 1,5 Metern von ihm.', 'Für jeden Zauberplatzgrad über dem 5. wird der Schaden der geballten Faust um 2W8 und der Schaden der greifenden Hand um 2W6 erhöht.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. wird der Schaden der geballten Faust um 2W8 und der Schaden der greifenden Hand um 2W6 erhöht.","type":"damage","dice":"2W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('bindung-der-ebenen', 'BINDUNG DER EBENEN', 5, 'Bannzauber', '1 Stunde', '18 Meter', 'V, G, M (ein Juwel im Wert von mindestens 1.000 GM, das der Zauber verbraucht)', NULL, '24 Stunden', 0, 0, 'Du versuchst, ein celestisches Wesen, einen Elementar, ein Feenwesen oder einen Unhold in deinen Dienst zu verpflichten. Die Kreatur muss sich für die gesamte Wirkungsdauer in Reichweite befinden. (Normalerweise wird die Kreatur zuerst in einen umgekehrten Schutzkreis beschworen, wo sie gefangen bleibt, während du den Zauber wirkst.) Nach Wirken des Zaubers muss das Ziel einen Charismarettungswurf bestehen, oder es muss dir für die Wirkungsdauer dienen. Wenn die Kreatur durch einen anderen Zauber beschworen oder erschaffen wurde, verlängert sich dessen Wirkungsdauer auf die Wirkungsdauer von Bindung der Ebenen. Eine verpflichtete Kreatur muss deine Befehle nach besten Kräften befolgen. Du kannst der Kreatur befehlen, dich auf ein Abenteuer zu begleiten, einen Ort zu bewachen oder eine Botschaft zu übermitteln. Ist sie dir jedoch feindlich gesinnt, so versucht sie, deine Worte zu verdrehen, um ihre eigenen Ziele zu verfolgen. Wenn die Kreatur deine Anweisungen vollständig ausgeführt hat, bevor der Zauber endet, kehrt sie zu dir zurück und meldet Vollzug, sofern du dich auf derselben Existenzebene befindest. Falls nicht, kehrt sie an den Ort zurück, an dem du sie verpflichtet hast, und verbleibt dort, bis der Zauber endet.', 'Für jeden Zauberplatzgrad über dem 3. wird der zusätzliche Schaden um 1W8 erhöht.', 'Barde, Druide, Hexenmeister, Kleriker, Magier', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der zusätzliche Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('blindheit-taubheit', 'BLINDHEIT/TAUBHEIT', 2, 'Verwandlungszauber', 'Aktion', '36 Meter', 'V', NULL, '1 Minute', 0, 0, 'Eine Kreatur in Reichweite, die du sehen kannst, muss einen Konstitutionsrettungswurf bestehen, oder sie ist für die Wirkungsdauer blind oder taub (nach deiner Wahl). Das Ziel wiederholt den Rettungswurf am Ende jedes seiner Züge. Bei einem Erfolg endet der Zauber.', 'Für jeden Zauberplatzgrad über dem 2. kannst du auf eine weitere Kreatur zielen.', 'Barde, Kleriker, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('blitz', 'BLITZ', 3, 'Hervorrufungszauber', 'Aktion', 'Selbst', 'V, G, M (ein Stück Fell und ein Kristallstab)', NULL, 'Unmittelbar', 0, 0, 'Du schleuderst in einer 30 Meter langen und 1,5 Meter breiten Linie, die von dir ausgeht, einen Blitz in eine Richtung deiner Wahl. Jede Kreatur in der Linie führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 8W6 Blitzschaden, anderenfalls die Hälfte.', 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W6 erhöht.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('blitze-herbeirufen', 'BLITZE HERBEIRUFEN', 3, 'Beschwörungszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'An einem Punkt in Reichweite über dir, den du sehen kannst, erscheint eine Sturmwolke. Sie hat die Form eines drei Meter hohen Zylinders mit einem Radius von 18 Metern. Wenn du den Zauber wirkst, wähle einen Punkt unter der Wolke aus, den du sehen kannst. Ein Blitz schlägt von der Wolke in diesen Punkt ein. Jede Kreatur im Abstand von bis zu 1,5 Metern um diesem Punkt führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 3W10 Blitzschaden, anderenfalls die Hälfte. Für die Wirkungsdauer kannst du eine magische Aktion verwenden, um einen weiteren Blitz dieser Art einschlagen zu lassen. Dabei kannst du denselben Punkt oder einen anderen als Ziel auswählen. Wenn du dich beim Wirken des Zaubers draußen aufhältst und ein Sturm herrscht, verleiht der Zauber dir Kontrolle über diesen Sturm, anstatt einen neuen zu erschaffen. Unter diesen Umständen wird der Schaden des Zaubers um 1W10 erhöht.', 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W10 erhöht.', 'Druide', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W10 erhöht.","type":"damage","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('blitzpfeil', 'BLITZPFEIL', 3, 'Verwandlungszauber', 'Bonusaktion, die du sofort ausführst, wenn', '', '', NULL, '', 0, 0, 'du eine Kreatur mit einer Fernkampfwaffe getroffen oder verfehlt hast Reichweite: Selbst Komponenten: V, G Wirkungsdauer: Unmittelbar Wenn dein Angriff das Ziel trifft oder verfehlt, verwandeln sich die Waffe oder die Geschosse, die verwendet wurden, in einen Blitz. Anstatt Schaden oder andere Effekte durch den Angriff erleidet das Ziel bei einem Treffer 4W8 Blitzschaden, bei einem Fehlschlag die Hälfte. Jede Kreatur im Abstand von bis zu drei Metern vom Ziel führt dann einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 2W8 Blitzschaden, anderenfalls die Hälfte. Danach nehmen Waffe oder Geschosse wieder ihre normale Form an.', 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden beider Effekte jeweils um 1W8 erhöht.', 'Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden beider Effekte jeweils um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('b-ser-blick', 'BÖSER BLICK', 6, 'Nekromantiezauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Für die Wirkungsdauer werden deine Augen zu einer tiefschwarzen Leere. Eine Kreatur deiner Wahl im Abstand von bis zu 18 Metern von dir, die du sehen kannst, muss einen Weisheitsrettungswurf bestehen, oder sie erleidet für die Wirkungsdauer einen der folgenden Effekte deiner Wahl. Für die Wirkungsdauer kannst du in jedem deiner Züge eine magische Aktion ausführen, um eine andere Kreatur als Ziel auszuwählen. Du kannst jedoch keine Kreatur erneut als Ziel auswählen, die ihren Rettungswurf gegen den Zauber bestanden hat. Krank: Das Ziel ist vergiftet. Panik: Das Ziel ist verängstigt. Es muss in jedem seiner Züge die Spurt-Aktion ausführen und sich auf dem sichersten und kürzesten Weg von dir entfernen. Wenn sich das Ziel in einen Bereich mindestens 18 Meter von dir entfernt bewegt hat, von dem aus es dich nicht sehen kann, endet dieser Effekt. Schlaf: Das Ziel ist bewusstlos. Es erwacht, wenn es Schaden erleidet oder eine andere Kreatur eine Aktion ausführt, um es wachzurütteln.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('botschaft', 'BOTSCHAFT', 0, 'Verwandlungzauber', 'Aktion', '36 Meter', 'G, M (ein Stück Kupferdraht)', NULL, '1 Runde', 0, 0, 'Du deutest auf eine Kreatur in Reichweite und flüsterst eine Botschaft. Das Ziel (und nur dieses) hört die Botschaft und kann mit einem Flüstern antworten, das nur du hörst. Du kannst diesen Zauber durch massive Gegenstände hindurch wirken, wenn du mit dem Ziel vertraut bist und weißt, dass es sich hinter der Barriere befindet. Magische Stille, 30 Zentimeter Stein, Metall oder Holz sowie dünnes Bleiblech blockieren den Zauber.', '', 'Barde, Druide, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('brennende-h-nde', 'BRENNENDE HÄNDE', 1, 'Hervorrufungszauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du verschießt ein dünnes Flammenband. Jede Kreatur in einem Kegel von 4,5 Metern führt einen Geschicklich-keitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 3W6 Feuerschaden, anderenfalls die Hälfte. Brennbare Gegenstände im Kegel, die nicht getragen oder gehalten werden, beginnen zu brennen.', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. CELESTISCHER GEIST Großes celestisches Wesen, neutral RK 11 + Zaubergrad + 2 (nur Verteidiger) TP 40 + 10 für jeden Zaubergrad ab dem 6. Bewegungsrate 9 m, Fliegen 12 m MOD 5W MOD RW MOD 5W STÄ 16 +3 +3 GES 14 +2 +2 KON 16 +3 +3 INT 10 +0 +0 WEI 14 +2 +2 CHA 16 +3 +3 Resistenzen Gleißend Immunitäten Bezaubert, Verängstigt Sinne Dunkelsicht 18 m, Passive Wahrnehmung 12 Sprachen Celestisch, versteht die Sprachen, die du kennst HG — (EP 0, ÜB entspricht deinem Übungsbonus)', 'Magier, Zauberer', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. CELESTISCHER GEIST Großes celestisches Wesen, neutral RK 11 + Zaubergrad + 2 (nur Verteidiger) TP 40 + 10 für jeden Zaubergrad ab dem 6. Bewegungsrate 9 m, Fliegen 12 m MOD 5W MOD RW MOD 5W STÄ 16 +3 +3 GES 14 +2 +2 KON 16 +3 +3 INT 10 +0 +0 WEI 14 +2 +2 CHA 16 +3 +3 Resistenzen Gleißend Immunitäten Bezaubert, Verängstigt Sinne Dunkelsicht 18 m, Passive Wahrnehmung 12 Sprachen Celestisch, versteht die Sprachen, die du kennst HG — (EP 0, ÜB entspricht deinem Übungsbonus)","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":null,"hp":null},"summon_text":"AKTIONEN\nMehrfachange Der Geist führt eine Anzahl von Angriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet).\nGleißender Streitkolben (nur Verteidiger): Nahkampfangriffswwf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W10+3 + Zaubergrad gleißender Schaden. Der Geist kann sich selbst oder eine andere Kreatur, die von ihm gesehen werden kann, im Abstand von bis zu drei Metern vom Ziel auswählen. Die ausgewählte Kreatur erhält 1W10 temporäre Trefferpunkte.\nGleißender Bogen (nur Rächer): Fernkampfangriffswurf? Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 180 Meter. Treffer: 2W6+2 + Zaubergrad gleißender Schaden.\nHeilende Berührung (1-mal täglich): Der Geist berührt eine andere Kreatur. Das Ziel erhält Trefferpunkte zurück, die 2W8 + dem Zaubergrad entsprechen.\n--;","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('chromatische-kugel', 'CHROMATISCHE KUGEL', 1, 'Hervorrufungszauber', 'Aktion', '27 Meter', 'V, G, M (ein Diamant im Wert von mindestens 50 GM)', NULL, 'Unmittelbar', 0, 0, 'Du schleuderst eine Energiekugel auf ein Ziel in Reichweite. Wähle für die Art der Kugel Blitz, Feuer, Gift, Kälte, Säure oder Schall aus und führe dann einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 3W8 Schaden der ausgewählten Art. Wenn du mit mindestens zwei der W8 dasselbe Ergebnis würfelst, springt die Kugel zu einem weiteren Ziel deiner Wahl im Abstand von bis zu neun Metern vom Ziel. Führe einen Angriffswurf gegen das neue Ziel sowie einen neuen Schadenswurf aus. Die Kugel kann nicht noch einmal springen, sofern du den Zauber nicht mit einem Zauberplatz ab dem 2. Grad wirkst.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W8 erhöht. Die Kugel kann höchstens so oft springen, wie es dem Grad des verbrauchten Zauberplatzes entspricht, und jede Kreatur kann nur einmal das Ziel dieses Zaubers sein.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W8 erhöht. Die Kugel kann höchstens so oft springen, wie es dem Grad des verbrauchten Zauberplatzes entspricht, und jede Kreatur kann nur einmal das Ziel dieses Zaubers sein.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('dauerhafte-flamme', 'DAUERHAFTE FLAMME', 2, 'Hervorrufungszauber', 'Aktion', 'Berührung', 'V, G, M (Rubinstaub im Wert von mindestens 50 GM, den der Zauber verbraucht)', NULL, 'Bis der Zauber gebannt wird', 0, 0, 'Eine Flamme bricht aus einem Gegenstand hervor, den du berührst. Der Effekt spendet in einem Radius von sechs Metern helles Licht und in einem Radius von weiteren sechs Metern dämmriges Licht. Er wirkt wie eine normale Flamme, erzeugt jedoch keine Wärme und verbraucht keinen Brennstoff. Die Flamme kann verdeckt oder verborgen, aber nicht erstickt oder gelöscht werden.', '', 'Druide, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('dimensionst-r', 'DIMENSIONSTÜR', 4, 'Beschwörungszauber', 'Aktion', '150 Meter', 'V', NULL, 'Unmittelbar', 0, 0, 'Du teleportierst dich an einen Ort in Reichweite. Dabei triffst du genau am gewünschten Punkt ein. Es kann sich um einen Zielort handeln, den du sehen oder dir vorstellen kannst oder den du beschreibst, indem du Entfernung und Richtung angibst (beispielsweise „60 Meter senkrecht nach unten" oder „90 Meter nach Nordosten in einem 45-Grad-Winkel nach oben"). Du kannst zusätzlich eine bereitwillige Kreatur teleportieren. Diese muss sich im Abstand von bis zu 1,5 Metern von dir befinden, wenn du dich teleportierst. Sie gelangt in einen Bereich im Abstand von bis zu 1,5 Metern von deinem Zielbereich. EINE MENSCHLICHE ZAUBERIN LUCHTIGT GHUL.E. MIT DER UNBERECHENBAREN ENERGIE EINER CHROMATISCHEN KUGEL. Wenn mindestens einer von euch in einem Bereich eintreffen würde, der bereits vollständig von Gegenständen oder Kreaturen besetzt ist, erleidet ihr jeweils 4W6 Energieschaden, und die Teleportation scheitert. DISSONANTES FLÜSTERN Verzauberungszauber I. Grades (Barde) Zeitaufwand: Aktion Reichweite: 18 Meter Komponenten: V Wirkungsdauer: Unmittelbar Eine Kreatur deiner Wahl in Reichweite, die du sehen kannst, hört im Geiste eine misstönende Melodie. Das Ziel führt einen Weisheitsrettungswurf aus. Scheitert der Wurf, so erleidet das Ziel 3W6 psychischen Schaden und muss sofort seine Reaktion verwenden, sofern verfügbar, um sich so weit wie möglich von dir zu entfernen. Dabei nimmt es die sicherste Route. Bei einem erfolgreichen Rettungswurf erleidet das Ziel nur halb so viel Schaden.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht.', 'Barde, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('dolchwolke', 'DOLCHWOLKE', 2, 'Beschwörungszauber', 'Aktion', '18 Meter', 'V, G, M (eine Glasscherbe)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du beschwörst wirbelnde Dolche in einem Würfel mit 1,5 Metern Kantenlänge um einen Punkt in Reichweite. Jede Kreatur in diesem Bereich erleidet 4W4 Hiebschaden. Eine Kreatur erleidet diesen Schaden auch dann, wenn der Würfel sich in ihren Bereich bewegt, wenn die Kreatur den Würfel betritt oder ihren Zug darin beendet. Eine Kreatur erleidet diesen Schaden nur einmal pro Zug. In deinen folgenden Zügen kannst du eine magische Aktion ausführen, um den Würfel bis zu neun Meter weit zu teleportieren.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 2W4 erhöht.', 'Barde, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 2W4 erhöht.","type":"damage","dice":"2W4"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('donnerndes-niederstrecken', 'DONNERNDES NIEDERSTRECKEN', 1, 'Hervorrufungszauber', 'Bonusaktion, die du sofort ausführst, wenn du', '', '', NULL, '', 0, 0, 'ein Ziel mit einer Nahkampfwaffe oder einem waffenlosen Angriff getroffen hast Reichweite: Selbst Komponenten: V Wirkungsdauer: Unmittelbar Dein Hieb donnert auf eine Hörweite von bis zu 90 Metern von dir, und das Ziel erleidet zusätzlich 2W6 Schallschaden durch den Angriff. Wenn das Ziel eine Kreatur ist, muss es außerdem einen Stärkerettungswurf bestehen, oder es wird um bis zu drei Meter von dir wegund umgestoßen.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht.', 'Paladin', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('donnerschlag', 'DONNERSCHLAG', 0, 'Hervorrufungzauber', 'Aktion', 'Selbst', 'S', NULL, 'Unmittelbar', 0, 0, 'Jede Kreatur in einer Ausströmung von 1,5 Metern, die von dir ausgeht, muss einen Konstitutionsrettungswurf bestehen, oder sie erleidet 1W6 Schallschaden. Der Donnerhall des Zaubers ist bis zu 30 Meter weit zu hören.', 'Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.', 'Barde, Druide, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.","type":"cantrip_scaling","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('donnerwoge', 'DONNERWOGE', 1, 'Hervorrufungszauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du entfesselst eine Welle aus donnernder Energie. Alle Kreaturen in einem Würfel mit 4,5 Metern Kantenlänge, der von dir ausgeht, führen einen Konstitutionsrettungs-wurf aus. Scheitert der Wurf, so erleidet eine Kreatur 2W8 Schallschaden und wird drei Meter von dir weggestoßen. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur nur halb so viel Schaden. Außerdem werden Gegenstände, die sich vollständig innerhalb des Würfels befinden und nicht fixiert sind, drei Meter von dir weggestoßen, und der Donnerschlag ist im Abstand von bis zu 90 Metern zu hören.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W8 erhöht.', 'Barde, Druide, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('dornenhagel', 'DORNENHAGEL', 1, 'Beschwörungszauber', 'Bonusaktion, die du sofort ausführst, wenn', '', '', NULL, '', 0, 0, 'du eine Kreatur mit einer Fernkampfwaffe getroffen hast Reichweite: Selbst Komponenten: V Wirkungsdauer: Unmittelbar Wenn du die Kreatur triffst, lässt dieser Zauber einen Dornenhagel auf sie niederprasseln, der von deiner Fern-kampfwaffe oder deinen Geschossen ausgeht. Das Ziel des Angriffs und jede Kreatur im Abstand von bis zu 1,5 Metern von ihm führen einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 1W10 Stich­schaden, anderenfalls die Hälfte.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W10 erhöht.', 'Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W10 erhöht.","type":"damage","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('dornenpeitsche', 'DORNENPEITSCHE', 0, 'Verwandlungzauber', 'Aktion', '9 Meter', 'V, G, M (Stängel einer Dornenpflanze)', NULL, 'Unmittelbar', 0, 0, 'Du erzeugst eine rankenartige Peitsche, die mit Dornen bedeckt ist und auf deinen Befehl hin nach einer Kreatur in Reichweite schlägt. Führe einen Nahkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 1W6 Stichschaden, und wenn es von höchstens großer Größe ist, kannst du es bis zu drei Meter weit zu dir ziehen.', 'Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.', 'Druide', '{"scaling":{"text":"Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.","type":"cantrip_scaling","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('dornenwand', 'DORNENWAND', 6, 'Beschwörungszauber', 'Aktion', '36 Meter', 'V, G, M (eine Handvoll Dornen)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erschaffst eine Wand aus verworrenem Gestrüpp mit nadelspitzen Dornen. Die Wand erscheint auf einer festen Oberfläche in Reichweite und bleibt für die Wirkungsdauer bestehen. Die Wand kann bis zu 18 Meter lang, drei Meter hoch und 1,5 Meter dick sein. Alternativ kannst du eine ringförmige Wand mit einem Radius von drei Metern erschaffen, die bis zu sechs Meter hoch und 1,5 Meter dick ist. Die Wand blockiert die Sichtlinie. Wenn die Wand erscheint, führt jede Kreatur in ihrem Bereich einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 7W8 Stichschaden, anderenfalls die Hälfte. 252 KAPITEL 7 1 ZAUBER Eine Kreatur kann die Dornenwand durchdringen, jedoch nur langsam und unter Schmerzen. Für jeden Meter, den sie sich durch die Wand bewegt, muss sie vier Meter ihrer Bewegungsrate verbrauchen. Wenn eine Kreatur außerdem in einem Zug die Dornenwand erstmals betritt oder ihren Zug darin beendet, führt sie einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 7W8 Hiebschaden, anderenfalls die Hälfte. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen.', 'Für jeden Zauberplatzgrad über dem 6. werden beide Schadensarten um 1W8 erhöht.', 'Druide', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. werden beide Schadensarten um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('dornenwuchs', 'DORNENWUCHS', 2, 'Verwandlungszauber', 'Aktion', '45 Meter', 'V, G, M (sieben Dornen)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'In einem Radius von sechs Metern um einen Punkt in Reichweite wird der Boden von harten Stacheln und Dornen überwuchert. Der Bereich wird für die Wirkungsdauer zu schwierigem Gelände. Bewegt sich eine Kreatur in oder durch den Bereich, so erleidet sie pro zurückgelegten 1,5 Metern 2W4 Stichschaden. Die Verwandlung des Bodens ist als natürlich getarnt. Jede Kreatur, die den Bereich beim Wirken des Zaubers nicht sehen kann, muss die Suchen-Aktion ausführen und einen Weisheitswurf (Überlebenskunst oder Wahrnehmung) gegen deinen Zauberrettungswurf-SG ausführen. Bei einem Erfolg erkennt sie das Gelände vor dem Betreten als gefährlich. DRACHEN HERBEIRUFEN Beschwörungszauber 5. Grades (Magier) Zeitaufwand: Aktion Reichweite: 18 Meter Komponenten: V, G, M (ein Gegenstand mit eingraviertem Drachenbildnis im Wert von mindestens 500 GM) Wirkungsdauer: Konzentration, bis zu 1 Stunde Du rufst einen Drachengeist herbei. Er erscheint in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten eines Drakonischen Geists. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet. Die Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. DRAKONISCHER GEIST Großer Drache, neutral RK 14 + Zaubergrad TP 50 + 10 für jeden Zaubergrad ab dem 6. Bewegungsrate 9 m, Fliegen 18 m, Schwimmen 9 m MOD RW MOD RW MOD STÄ 19 +4 GES 14 +2 +2 KON 17 +3 +3 INT 10 +0 +0 WEI 14 CHA 14 +2 +2 Resistenzen Blitz, Feuer, Gift, Kälte, Säure Immunitäten Bezaubert, Verängstigt, Vergiftet Sinne Blindsicht 9 m, Dunkelsicht 18 m, Passive Wahrnehmung 12 Sprachen Drakonisch, versteht die Sprachen, die du kennst HG — (EP 0, ÜB entspricht deinem Übungsbonus)', 'Druide, Waldläufer', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. DRAKONISCHER GEIST Großer Drache, neutral RK 14 + Zaubergrad TP 50 + 10 für jeden Zaubergrad ab dem 6. Bewegungsrate 9 m, Fliegen 18 m, Schwimmen 9 m MOD RW MOD RW MOD STÄ 19 +4 GES 14 +2 +2 KON 17 +3 +3 INT 10 +0 +0 WEI 14 CHA 14 +2 +2 Resistenzen Blitz, Feuer, Gift, Kälte, Säure Immunitäten Bezaubert, Verängstigt, Vergiftet Sinne Blindsicht 9 m, Dunkelsicht 18 m, Passive Wahrnehmung 12 Sprachen Drakonisch, versteht die Sprachen, die du kennst HG — (EP 0, ÜB entspricht deinem Übungsbonus)","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":null,"hp":null},"summon_text":"MERKMALE\nGemeinsame Resistenzen: Wenn du den Geist beschwörst, wähle eine seiner Resistenzen aus. Du bist gegen die ausgewählte Schadensart resistent, bis der Zauber endet.\nAKTIONEN\nMehrfachangriff: Der Geist führt eine Anzahl von Zerfetzen-Angriffen in Höhe der Hälfte des Zaubergrades (abgerundet) aus und setzt seine Odemwaffe ein.\nZerfetzen: Nahkampfangriffswurf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite drei Meter. Treffer: 1W6+4 + Zaubergrad Stichschaden.\nOdemwaffe: Geschicklichkeitsrettungswurf: Der SG entspricht deinem Zauberrettungswurf-SG, alle Kreaturen in einem Kegel von neun Metern. Misserfolg: 2W6 Schaden von einer Art, gegen die dieser Geist resistent ist (nach deiner Wahl beim Wirken des Zaubers). Erfolg: Halber Schaden.","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('drachenodem', 'DRACHENODEM', 2, 'Verwandlungszauber', 'Bonusaktion', 'Berührung', 'V, G, M (eine scharfe Pfefferschote)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du berührst eine bereitwillige Kreatur und wählst Blitz, Feuer, Gift, Kälte oder Säure aus. Bis der Zauber endet, kann das Ziel eine magische Aktion ausführen, um einen Kegel von 4,5 Metern auszuatmen. Jede Kreatur in diesem Bereich führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 3W6 Schaden der ausgewählten Art, anderenfalls die Hälfte.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W6 erhöht. DRAWMIJS SOFORTIGE HERBEIZAUBERUNG Beschwörungszauber 6. Grades (Magier) Zeitaufwand: 1 Minute oder Ritual Reichweite: Berührung Komponenten: V, G, M (ein Saphir im Wert von mindestens 1.000 GM) Wirkungsdauer: Bis der Zauber gebannt wird Du berührst den Saphir, den du zum Wirken verwendest, sowie einen Gegenstand, der höchstens 4,5 Kilogramm wiegt und keine größere Abmessung als 1,8 Meter hat. Der Zauber hinterlässt eine unsichtbare Markierung am Gegenstand und schreibt den Namen des Gegenstands unsichtbar in den Saphir ein. Jedes Mal, wenn du diesen Zauber wirkst, musst du einen anderen Saphir verwenden. Danach kannst du eine magische Aktion ausführen, um den Namen des Gegenstands auszusprechen und den Saphir zu zerstören. Der Gegenstand erscheint unabhängig von seiner physischen oder planaren Entfernung sofort in deiner Hand, und der Zauber endet. Wenn eine andere Kreatur den Gegenstand hält oder trägt, wird er nicht transportiert, wenn der Saphir zerstört wird. Stattdessen erfährst du, wer die Kreatur ist, die ihn bei sich hat, und wo sie sich aktuell befindet.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W6 erhöht. DRAWMIJS SOFORTIGE HERBEIZAUBERUNG Beschwörungszauber 6. Grades (Magier) Zeitaufwand: 1 Minute oder Ritual Reichweite: Berührung Komponenten: V, G, M (ein Saphir im Wert von mindestens 1.000 GM) Wirkungsdauer: Bis der Zauber gebannt wird Du berührst den Saphir, den du zum Wirken verwendest, sowie einen Gegenstand, der höchstens 4,5 Kilogramm wiegt und keine größere Abmessung als 1,8 Meter hat. Der Zauber hinterlässt eine unsichtbare Markierung am Gegenstand und schreibt den Namen des Gegenstands unsichtbar in den Saphir ein. Jedes Mal, wenn du diesen Zauber wirkst, musst du einen anderen Saphir verwenden. Danach kannst du eine magische Aktion ausführen, um den Namen des Gegenstands auszusprechen und den Saphir zu zerstören. Der Gegenstand erscheint unabhängig von seiner physischen oder planaren Entfernung sofort in deiner Hand, und der Zauber endet. Wenn eine andere Kreatur den Gegenstand hält oder trägt, wird er nicht transportiert, wenn der Saphir zerstört wird. Stattdessen erfährst du, wer die Kreatur ist, die ihn bei sich hat, und wo sie sich aktuell befindet.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('druidenkunst', 'DRUIDENKUNST', 0, 'Verwandlungzauber', 'Aktion', '9 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du flüsterst den Geistern der Natur zu und erzeugst in Reichweite einen der folgenden magischen Effekte. Wettersensor: Du erschaffst einen winzigen, harmlosen sensorischen Effekt, der das Wetter an deinem Aufent­haltsort in den nächsten 24 Stunden vorhersagt. Der Effekt kann sich als goldene Kugel für einen wolkenlosen Himmel, als Wolke für Regen, als Schneeflocken für Schnee oder dergleichen manifestieren. Dieser Effekt hält eine Runde lang an. Erblühen: Du bewirkst, dass sofort eine Blume erblüht, sich eine Samenkapsel oder eine Blattknospe öffnet. Sensorischer Effekt: Du erzeugst einen harmlosen senso­rischen Effekt, beispielsweise fallende Blätter, geisterhafte tanzende Feenwesen, eine sanfte Brise, die Geräusche eines Tieres oder leichten Stinktiergeruch. Der Effekt muss in einen Würfel mit 1,5 Metern Kantenlänge passen. Feuerspiel: Du entzündest oder löschst eine Kerze, eine Fackel oder ein Lagerfeuer.', '', 'Druide', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('dunkelheit', 'DUNKELHEIT', 2, 'Hervorrufungszauber', 'Aktion', '18 Meter', 'V, M (Fledermausfell und ein Kohlenstück)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Für die Wirkungsdauer breitet sich magische Dunkelheit von einem Punkt in Reichweite zu einer Kugel mit einem Radius von 4,5 Metern aus. Sie kann weder mit Dunkelsicht durchdrungen noch durch nichtmagische Lichtquellen erhellt werden. Alternativ kannst du den Zauber auf einen Gegenstand wirken, der nicht getragen oder gehalten wird. In diesem Fall erfüllt die Dunkelheit eine Ausströmung von 4,5 Metern um den Gegenstand. Wird der Gegenstand mit etwas Undurchsichtigem wie einer Schüssel oder einem Helm bedeckt, so wird die Dunkelheit blockiert. Wenn sich der Wirkungsbereich dieses Zaubers mit einem Bereich hellen oder dämmrigen Lichts überschneidet, das durch einen Zauber des höchstens 2. Grades erzeugt wurde, so wird der andere Zauber gebannt.', '', 'Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('dunkelsicht', 'DUNKELSICHT', 2, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (eine getrocknete Karotte)', NULL, '8 Stunden', 0, 0, 'Für die Wirkungsdauer hat eine bereitwillige Kreatur, die du berührst, Dunkelsicht mit einer Reichweite von 45 Metern.', '', 'Druide, Magier, Waldläufer, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('d-rre', 'DÜRRE', 4, 'Nekromantiezauber', 'Aktion', '9 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Eine Kreatur in Reichweite, die du sehen kannst, führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet sie 8W8 nekrotischen Schaden, anderenfalls die Hälfte. Bei Pflanzenkreaturen scheitert der Rettungswurf automatisch. Ziele alternativ auf eine nichtmagische Pflanze, die keine Kreatur ist (beispielsweise Baum oder Strauch). Sie führt keinen Rettungswurf aus, sondern verdorrt und stirbt.', 'Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 1W8 erhöht.', 'Druide, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('ebenenwechsel', 'EBENENWECHSEL', 7, 'Beschwörungszauber', 'Aktion', 'Berührung', 'V, G, M (eine gegabelte Metallrute im Wert von mindestens 250 GM, die auf eine Existenzebene eingestimmt ist)', NULL, 'Unmittelbar', 0, 0, 'Dieser Zauber transportiert dich und bis zu acht bereitwillige Kreaturen, die sich in einem Kreis an den Händen halten, auf eine andere Existenzebene. Du kannst einen allgemeinen Zielort bestimmten, beispielsweise die Messingstadt auf der Elementarebene des Feuers oder den Palast von Dispater auf der zweiten Ebene der Neun Höllen. Ihr erscheint am Ziel oder in dessen Nähe nach Ermessen des SL. Wenn du die Siegelsequenz eines Kreises der Teleportation auf einer anderen Existenzebene kennst, kann dich der Zauber zu diesem Kreis bringen. Ist der Kreis der Teleportation zu klein für alle transportieren Kreaturen, so erscheinen sie im nächsten freien Bereich neben dem Kreis.', '', 'Druide, Hexenmeister, Kleriker, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('einfache-illusion', 'EINFACHE ILLUSION', 0, 'Illusionzauber', 'Aktion', '9 Meter', 'G, M (ein Stück Vlies)', NULL, '1 Minute', 0, 0, 'Du erschaffst für die Wirkungsdauer ein Geräusch oder das Abbild eines Gegenstands in Reichweite. Die jeweiligen Effekte sind unten beschrieben. Die Illusion endet, wenn du diesen Zauber erneut wirkst. Eine Kreatur, die das Geräusch oder Abbild mit der Studieren-Aktion untersucht, kann die Illusion mit einem erfolgreichen Intelligenzwurf (Nachforschungen) gegen deinen Zauberrettungswurf-SG als solche erkennen. In diesem Fall wird die Illusion für sie weniger deutlich wahrnehmbar. Geräusch: Die Lautstärke des Geräuschs kann so leise wie ein Flüstern oder so laut wie ein Schrei sein. Dies kann deine Stimme oder die einer anderen Person sein, aber auch Löwengebrüll, Trommeln oder ein anderes beliebiges Geräusch. Für die Wirkungsdauer kannst du einen durchgehenden Laut oder unterschiedliche kurze Laute erzeugen. Bild: Das Abbild eines Gegenstands — beispielsweise ein Stuhl, schlammige Fußabdrücke oder eine Truhe — darf nicht größer als ein Würfel mit 1,5 Metern Kantenlänge sein. Es kann weder Geräusche noch Gerüche, Licht oder andere sensorische Effekte erzeugen. Physische Interaktionen mit dem Abbild enttarnen es als Illusion, da Dinge es einfach durchdringen können.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('einfl-sterung', 'EINFLÜSTERUNG', 2, 'Verzauberungszauber', 'Aktion', '9 Meter', 'V, M (ein Tropfen Honig)', NULL, 'Konzentration, bis zu 8 Stunden', 1, 0, 'Du schlägst einer Kreatur in Reichweite, die du sehen kannst und die dich hören und verstehen kann, mit höchstens 25 Worten eine Vorgehensweise vor. Der Vorschlag muss realistisch klingen und darf nichts enthalten, was dem Ziel oder seinen Verbündeten Schaden zufügen würde. Du könntest beispielsweise sagen: „Holt den Schlüssel zur Schatzkammer des Kults und gebt ihn mir." Oder du könntest sagen: „Hört auf zu kämpfen, verlasst diese Bibliothek friedlich und kehrt nicht zurück." Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer - oder bis du oder deine Verbündeten ihm Schaden zufügen - bezaubert. Das bezauberte Ziel führt den Vorschlag nach besten Kräften aus. Die vorgeschlagene Aktivität kann während der gesamten Wirkungsdauer fortgesetzt werden. Wenn sie jedoch in kürzerer Zeit abgeschlossen werden kann, endet der Zauber beim Ziel, sobald es die Aktivität ausgeführt hat.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('einkerkerung', 'EINKERKERUNG', 9, 'Bannzauber', '1 Minute', '9 Meter', 'V, G, M (eine Statuette in Gestalt des Ziels im Wert von mindestens 5.000 GM)', NULL, 'Bis der Zauber gebannt wird', 0, 0, 'Du erschaffst magische Fesseln, um eine Kreatur in Reichweite festzusetzen, die du sehen kannst. Das Ziel muss einen Weisheitsrettungswurf ausführen. Bei einem erfolgreichen Rettungswurf ist das Ziel nicht betroffen, und es ist in den nächsten 24 Stunden gegen diesen Zauber immun. Scheitert der Wurf, so wird das Ziel eingesperrt. Ein eingesperrtes Ziel muss nicht atmen, essen und trinken, und es altert nicht. Erkenntniszauber können das Ziel weder orten noch wahrnehmen, und das Ziel kann sich nicht teleportieren. Bis der Zauber endet, ist das Ziel außerdem von einem der folgenden Effekte (nach deiner Wahl) betroffen: Abgesichertes Gefängnis: Das Ziel ist in einer Halbebene gefangen, die gegen Teleportation und Ebenenreisen geschützt ist. Bei der Halbebene kann es sich um ein Labyrinth, einen Käfig, einen Turm oder Ähnliches nach deiner Wahl handeln. Anketten: Fest im Boden verankerte Ketten halten das Ziel fest. Das Ziel ist festgesetzt und kann auf keine Weise bewegt werden. Begräbnis: Das Ziel wird unter der Erde in einer hohlen Kugel aus magischer Energie begraben, die gerade groß genug für das Ziel ist. Nichts kann die Kugel durchdringen. Schlummer: Das Ziel ist bewusstlos und kann nicht geweckt werden. Winziges Gefängnis: Das Ziel nimmt eine Größe von 2,5 Zentimetern an und wird in einem unzerstörbaren Edelstein oder einem ähnlichen Gegenstand eingesperrt. Licht kann den Edelstein durchdringen. So kann das Ziel nach draußen blicken, und andere Kreaturen können hineinsehen. Ansonsten kann den Edelstein jedoch nichts durchdringen, auch nicht Teleportation oder Ebenenreisen. Den Zauber beenden: Wenn du den Zauber wirkst, kannst du einen Auslöser festlegen, der ihn beendet. Der Auslöser kann so spezifisch oder komplex sein, wie du möchtest. Der SL muss jedoch bestätigen, dass sein Eintreten innerhalb der nächsten zehn Jahre wahrscheinlich ist. Es muss sich um eine beobachtbare Aktion handeln, beispielsweise eine bestimmte Opferung im Tempel deiner Gottheit, die Rettung deiner großen Liebe oder der Sieg über ein bestimmtes Monster. Der Zauber Magie bannen kann diesen Zauber nur beenden, wenn er mit einem Zauberplatz des 9. Grades entweder auf das Gefängnis oder auf die Komponente gewirkt wird, die beim Wirken dieses Zaubers verwendet wurde. EINSWERDEN MIT DER NATUR Erkenntniszauber 5. Grades (Druide, Waldläufer) Zeitaufwand: 1 Minute oder Ritual Reichweite: Selbst Komponenten: V, G Wirkungsdauer: Unmittelbar Du kommunizierst mit Naturgeistern und erlangst Wissen über deine Umgebung. Im Freien gewährt dir der Zauber Wissen über den Bereich im Abstand von bis zu 4,8 Kilometern von dir. In Höhlen und anderen natürlichen unterirdischen Umgebungen beträgt der Radius 90 Meter. Der Zauber funktioniert nicht in Gebieten, in denen die Natur durch Bauten ersetzt wurde, beispielsweise in Burgen und Ansiedlungen. Wähle drei der folgenden Optionen aus. Du erfährst diese Fakten, sofern sie zum Bereich des Zaubers gehören: Orte von Ansiedlungen Orte von Portalen oder anderen Existenzebenen Ort einer Kreatur mit einem Herausforderungsgrad von mindestens 10 (nach Wahl des SL), die ein celestisches Wesen, ein Elementar, ein Feenwesen, ein Unhold oder ein Untoter ist Die häufigste Art von Pflanze, Mineral oder Tier (nach deiner Wahl) Orte von Gewässern / EISMESSER. Du könntest beispielsweise den Ort eines mächtigen Monsters im Bereich, den Ort von Gewässern sowie den Ort von Dörfern erfahren.', '', 'Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('eismesser', 'EISMESSER', 1, 'Beschwörungszauber', 'Aktion', '18 Meter', 'G, M (ein Tropfen Wasser oder ein Stück Eis)', NULL, 'Unmittelbar', 0, 0, 'Du erzeugst eine Eisscherbe und schleuderst sie auf eine Kreatur in Reichweite. Führe einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 1W10 Stichschaden. Nach dem Wurf explodiert die Scherbe, ob sie getroffen hat oder nicht. Das Ziel und jede Kreatur im Abstand von bis zu 1,5 Metern von ihm müssen einen Geschicklichkeitsrettungswurf bestehen, oder sie erleiden 2W6 Kälteschaden.', 'Für jeden Zauberplatzgrad über dem 1. wird der Kälteschaden um 1W6 erhöht.', 'Druide, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Kälteschaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('eissturm', 'EISSTURM', 4, 'Hervorrufungszauber', 'Aktion', '90 Meter', 'V, G, M (ein Fausthandschuh)', NULL, 'Unmittelbar', 0, 0, 'In einem zwölf Meter hohen Zylinder mit einem Radius von sechs Metern um einen Punkt in Reichweite fällt Hagel. Jede Kreatur im Zylinder führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 2W10 Wuchtschaden und 4W6 Kälteschaden, anderenfalls die Hälfte. Die Hagelkörner machen den Boden im Zylinder bis zum Ende deines nächsten Zugs zu schwierigem Gelände.', 'Für jeden Zauberplatzgrad über dem 4. wird der Wuchtschaden um 1W10 erhöht.', 'Druide, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der Wuchtschaden um 1W10 erhöht.","type":"damage","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('eiswand', 'EISWAND', 6, 'Hervorrufungszauber', 'Aktion', '36 Meter', 'V, G, M (ein Stück Quarz)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erschaffst auf einer festen Oberfläche in Reichweite eine Eiswand. Du kannst sie auch zu einer halbkugelförmigen Kuppel oder zu einer Kugel mit einem Radius von bis zu drei Metern formen, oder du kannst eine flache Oberfläche aus zehn Platten mit je drei Metern Kantenlänge erschaffen. Jede Platte muss an eine andere angrenzen. Die Wand ist in jeder Form 30 Zentimeter dick und bleibt für die Wirkungsdauer bestehen. Erschaffst du die Wand so, dass sie den Bereich einer Kreatur durchdringt, so wird die Kreatur auf eine Seite der Wand (nach deiner Wahl) gestoßen und führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 10W6 Kälteschaden, anderenfalls die Hälfte. Die Wand ist ein Gegenstand, der beschädigt und somit durchbrochen werden kann. Sie besitzt eine RK von 12 und 30 Trefferpunkte pro Abschnitt von drei Metern. Sie ist gegen Giftschaden, Kälteschaden und psychischen Schaden immun und anfällig für Feuerschaden. Sinken die Trefferpunkte eines Abschnitts von drei Metern der Wand auf 0, so wird dieser Abschnitt zerstört und hinterlässt in seinem Bereich eine eisige Luftschicht. Eine Kreatur, die sich erstmals in einem Zug durch die kalte Luftschicht bewegt, führt einen Konstitutions-rettungswurf aus. Scheitert der Wurf, so erleidet sie 5W6 Kälteschaden, anderenfalls die Hälfte.', 'Für jeden Zauberplatzgrad über dem 6. wird der Schaden, den die Wand beim Erscheinen bewirkt, um 2W6 erhöht, und der Schaden beim Durchqueren der eisigen Luftschicht wird um 1W6 erhöht.', 'Magier', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. wird der Schaden, den die Wand beim Erscheinen bewirkt, um 2W6 erhöht, und der Schaden beim Durchqueren der eisigen Luftschicht wird um 1W6 erhöht.","type":"damage","dice":"2W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('elementalismus', 'ELEMENTALISMUS', 0, 'Verwandlungzauber', 'Aktion', '9 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du kontrollierst die Elemente und erzeugst in Reichweite einen der folgenden Effekte. Element formen: Du lässt eine Menge von Erde, Sand, Feuer, Rauch, Nebel oder Wasser, die in einen Würfel mit 30 Zentimetern Kantenlänge passt, eine Stunde lang eine grobe Gestalt annehmen (beispielsweise die einer Kreatur). Erde anlocken: Du erzeugst eine dünne Stauboder Sandschicht, die Oberflächen in einem Bereich von 2,3 Quadratmetern bedeckt, oder du lässt in einem Flecken Erde oder Sand ein einzelnes Wort in deiner Handschrift erscheinen. Feuer anlocken: Du erzeugst in einem Würfel mit 1,5 Metern Kantenlänge eine dünne Wolke aus harmlosen Funken und farbigem duftendem Rauch. Du wählst Farbe und Duft aus. Die Funken können Kerzen, Fackeln und Lampen im Bereich entzünden. Der Duft des Rauchs bleibt eine Minute lang erhalten. Luft anlocken: Du erzeugst in einem Würfel mit 1,5 Metern Kantenlänge einen Windstoß, der stark genug ist, um Stoff und Laub zu bewegen, Staub aufzuwirbeln und offene Türen und Fensterläden zu schließen. Türen und Fensterläden, die von jemandem oder etwas festgehalten werden, sind nicht betroffen. Wasser anlocken: Du erzeugst in einem Würfel mit 1,5 Metern Kantenlänge kühlen Nebel, der Kreaturen und Gegenstände benetzt. Alternativ erzeugst du eine Tasse sauberen Wassers entweder in einem Behälter oder auf einer Oberfläche. Das Wasser ist nach einer Minute verdampft. ELEMENTAR BESCHWÖREN Beschwörungszauber 5. Grades (Druide, Magier) Zeitaufwand: Aktion Reichweite: 18 Meter Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 10 Minuten Du beschwörst einen großen immateriellen Geist von den Elementarebenen, der in einem freien Bereich in Reichweite erscheint. Wähle das Element des Geistes aus, durch das seine Schadensart bestimmt wird: Erde (Schall), Feuer (Feuer), Luft (Blitz) oder Wasser (Kälte). Der Geist bleibt für die Wirkungsdauer bestehen. Wann immer eine Kreatur, die du sehen kannst, den Bereich des Geistes betritt oder ihren Zug im Abstand von bis zu 1,5 Metern vom Geist beginnt, kannst du die Kreatur zu einem Geschicklichkeitsrettungswurf zwingen, sofern der Geist keine Kreatur festgesetzt hat. Scheitert der Wurf, so erleidet das Ziel 8W8 Schaden je nach Art des Geists und ist festgesetzt, bis der Zauber endet. Das festgesetzte Ziel wiederholt den Rettungswurf zu Beginn jedes seiner Züge. Scheitert der Wurf, so erleidet das Ziel 4W8 Schaden je nach Art des Geists. Bei einem erfolgreichen Rettungswurf ist das Ziel nicht mehr vom Geist festgesetzt.', 'Für jeden Zauberplatzgrad über dem 5. wird der Schaden um 2W8 erhöht.', 'Druide, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. wird der Schaden um 2W8 erhöht.","type":"cantrip_scaling","dice":"2W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('elementar-herbeirufen', 'ELEMENTAR HERBEIRUFEN', 4, 'Beschwörungszauber', 'Aktion', '27 Meter', 'V, G, M (Luft, ein Kiesel, Asche und Wasser in einer Phiole mit Goldintarsie im Wert von mindestens 400 GM)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du rufst einen Elementargeist herbei. Er erscheint in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten eines Elementargeists. Wenn du den Zauber wirkst, entscheide dich für ein Element: Erde, Feuer, Luft oder Wasser. Die Kreatur hat eine zweibeinige, vom ausgewählten Element umhüllte Erscheinung. Durch das Element werden einige Details im ELEMENTARGEIST Mittelgroßer Elementar, neutral', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.', 'Druide, Magier, Waldläufer', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":"11 + Zaubergrad","hp":null},"summon_text":"RK 11 + Zaubergrad\nTP 50 + 10 für jeden Zaubergrad ab dem 5.\nBewegungsrate 12 m, Graben 12 m (nur Erde), Fliegen 12 m\n(Schweben, nur Luft), Schwimmen 12 m (nur Wasser)\nMERKMALE\nAmorphe Gestalt (nur Feuer, Luft und Wasser): Der Geist kann sich durch einen engen Bereich mit einer Breite von nur 2,5 Zentimetern bewegen, ohne dass dieser für ihn als schwieriges Gelände zählt.\nAKTIONEN\nMehrfachangriff: Der Geist führt eine Anzahl von Hiebangriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet).\nHieb: Nahkampfangriffswurf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W10+4 + Zaubergrad Wuchtschaden (nur Erde), Kälteschaden (nur Wasser), Blitzschaden (nur Luft) oder Feuerschaden (nur Feuer).\nWertekasten bestimmt. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet.\nDie Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('elementare-waffe', 'ELEMENTARE WAFFE', 3, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Durch deine Berührung wird eine nichtmagische Waffe zu einer magischen Waffe. Wähle eine der folgenden Schadensarten aus: Blitz, Feuer, Kälte, Säure oder Schall. Für die Wirkungsdauer hat die Waffe einen Bonus von +1 auf Angriffswürfe und bewirkt bei einem Treffer zusätzlich 1W4 Schaden der ausgewählten Art. - ENERGIEKÄFIG Hervorrufungszauber 7 Grades (Barde, Hexenmeister, Magier) Zeitaufwand: Aktion Reichweite: 30 Meter Komponenten: V, G, M (Rubinstaub im Wert von mindestens 1.500 GM, den der Zauber verbraucht) Wirkungsdauer: Konzentration, bis zu 1 Stunde Um einen Bereich deiner Wahl in Reichweite entsteht ein unbewegliches, unsichtbares würfelförmiges Gefängnis aus magischer Energie. Dabei kann es sich um einen Käfig oder um einen soliden Kasten handeln (nach deiner Wahl). In Käfigform kann das Gefängnis eine Seitenlänge von bis zu sechs Metern haben. Es besteht aus Stangen und Zwischenräumen von jeweils einem Zentimeter Durchmesser. In Kastenform kann das Gefängnis eine Seitenlänge von bis zu drei Metern haben. Es handelt sich um eine solide, für Materie undurchdringliche Barriere, die außerdem in den Bereich hinein oder aus ihm heraus gewirkte Zauber blockiert. Wenn du den Zauber wirkst, ist jede Kreatur gefangen, die sich vollständig im Bereich des Käfigs befindet. Kreaturen, die sich nur zum Teil im Bereich befinden oder zu groß sind, um komplett hineinzupassen, werden von der Mitte des Bereichs aus weggestoßen, bis sie sich vollständig außerhalb befinden. Eine Kreatur im Energiekäfig kann diesen nur auf magische Art verlassen. Wenn sie versucht, ihn mit Teleportation oder Ebenenreisen zu verlassen, muss sie einen Charismarettungswurf ausführen. Bei einem erfolgreichen Rettungswurf gelingt es ihr, den Käfig mit dieser Magie zu verlassen. Scheitert der Wurf, so verlässt die Kreatur den Käfig nicht, und der Zauber oder Effekt ist vergeudet. Der Käfig erstreckt sich auch auf die Ätherebene und blockiert damit Reisen über diese Ebene. Dieser Zauber kann durch Magie bannen nicht aufgehoben werden.', '', 'Druide, Paladin, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('energiewand', 'ENERGIEWAND', 5, 'Hervorrufungszauber', 'Aktion', '36 Meter', 'V, G, M (eine Glasscherbe)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'An einem Punkt deiner Wahl in Reichweite erscheint eine unsichtbare Energiewand. Es kann sich um eine senkrechte, waagerechte oder abgewinkelte Barriere in einer Ausrichtung deiner Wahl handeln. Sie kann frei schweben oder auf einer festen Oberfläche stehen. Du kannst sie auch zu einer halbkugelförmigen Kuppel oder zu einer Kugel mit einem Radius von bis zu drei Metern formen, oder du kannst eine flache Oberfläche aus zehn Platten mit je drei Metern Kantenlänge erschaffen. Jede Platte muss an eine andere angrenzen. Die Wand ist in jeder Form einen halben Zentimeter dick und bleibt für die Wirkungsdauer bestehen. Erschaffst du die Wand so, dass sie den Bereich einer Kreatur durchdringt, so wird die Kreatur auf eine Seite der Wand (nach deiner Wahl) gestoßen. Nichts kann die Wand physisch durchdringen. Sie ist gegen alle Schadensarten immun und kann durch Magie bannen nicht aufgehoben werden. Der Zauber Auflösung zerstört die Wand jedoch sofort. Die Wand erstreckt sich auch in die Ätherebene und blockiert damit Reisen über diese Ebene.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('erdbeben', 'ERDBEBEN', 8, 'Verwandlungszauber', 'Aktion', '150 Meter', 'V, G, M (ein geborstener Stein)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Wähle einen Punkt in Reichweite auf dem Boden aus, den du sehen kannst. Für die Wirkungsdauer erschüttert ein gewaltiges Beben die Erde in einem Radius von 30 Metern um diesen Punkt. Der Boden dort ist schwieriges Gelände. Wenn du diesen Zauber wirkst, sowie am Ende jedes deiner Züge während der Wirkungsdauer, führt jede Kreatur auf dem Boden in dem Bereich einen Geschicklich-keitsrettungswurf aus. Scheitert der Wurf, so wird die Kreatur umgestoßen, und ihre Konzentration ist unterbrochen. Du kannst außerdem die folgenden Effekte bewirken. Erdspalten: Am Ende des Zugs, in dem du den Zauber wirkst, öffnen sich insgesamt 1W6 Erdspalten im Wirkungs­bereich. Du wählst die Orte der Erdspalten aus. Sie können sich nicht unter Gebäuden öffnen. Jede Erdspalte ist 1W10 mal drei Meter tief, drei Meter breit und erstreckt sich von einem Rand des Wirkungsbereichs zu einem anderen. Eine Kreatur, die sich im Bereich einer Erdspalte befindet, muss einen Geschicklichkeitsrettungswurf bestehen, oder sie fällt hinein. Ist der Rettungswurf erfolgreich, so bewegt die Kreatur sich mit dem Rand der Erdspalte, während sich diese öffnet. Gebäude: Wenn du den Zauber wirkst, sowie am Ende jedes deiner Züge für die Wirkungsdauer, fügt das Beben allen Gebäuden, die den Boden im Wirkungsbereich berühren, 50 Wuchtschaden zu. Wenn die Trefferpunkte eines Gebäudes auf 0 sinken, stürzt das Gebäude ein. Jede Kreatur im Abstand von bis zur halben Höhe des Gebäudes führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 12W6 Wuchtschaden, wird umgestoßen und ist in den Trümmern gefangen. Sie muss einen SG-20-Stärkewurf (Athletik) als Aktion bestehen, um sich zu befreien. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur nur halb so viel Schaden.', '', 'Druide, Kleriker, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('erde-bewegen', 'ERDE BEWEGEN', 6, 'Verwandlungszauber', 'Aktion', '36 Meter', 'V, G, M (eine Miniaturschaufel)', NULL, 'Konzentration, bis zu 2 Stunden', 1, 0, 'Wähle einen viereckigen Bereich des Geländes in Reichweite mit einer Seitenlänge von höchstens zwölf Metern aus. Für die Wirkungsdauer kannst du Erde, Lehm oder Sand in dem Bereich beliebig umformen. Du kannst den Bereich anheben oder absenken, einen Graben erschaffen oder zuschütten, eine Mauer errichten oder niederreißen oder eine Säule errichten. Das Ausmaß dieser Änderungen darf die Hälfte der größten Abmessung des Bereichs nicht überschreiten. Beeinflusst du beispielsweise einen Bereich mit einer Seitenlänge von zwölf Metern, so kannst du eine bis zu sechs Meter hohe Säule erschaffen, den Bereich um bis zu sechs Meter anheben oder absenken, einen Graben mit bis zu sechs Metern Tiefe ausheben und so weiter. Es dauert zehn Minuten, diese Änderungen zu bewirken. Da die Veränderung des Geländes langsam erfolgt, können Kreaturen im Bereich normalerweise nicht durch die Bodenbewegung gefangen oder verletzt werden. Nach jeweils zehn Minuten Konzentration auf den Zauber kannst du einen neuen Bereich in Reichweite auswählen und beeinflussen. Dieser Zauber kann weder Naturgestein noch Steingebäude beeinflussen. Felsen und Gebäude verschieben sich, um sich dem neuen Gelände anzupassen. Wenn deine Veränderung des Geländes ein Gebäude instabil macht, so stürzt es möglicherweise ein. Dieser Zauber hat keinen direkten Einfluss auf Pflanzen­wachstum. Die bewegte Erde nimmt Pflanzen mit sich. ERINNERUNG VERÄNDERN Verzauberungszauber 5. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Minute Du versuchst, die Erinnerungen einer anderen Kreatur umzuformen. Eine Kreatur in Reichweite, die du sehen kannst, führt einen Weisheitsrettungswurf aus. Wenn du gegen die Kreatur kämpfst, ist sie bei ihrem Rettungswurf im Vorteil. Scheitert der Wurf, so ist das Ziel für die Wirkungsdauer bezaubert. Ein auf diese Art bezaubertes Ziel ist außerdem kampfunfähig und sich seiner Umgebung nicht bewusst, es kann dich jedoch hören. Erleidet es Schaden oder wird es von einem anderen Zauber betroffen, so endet dieser Zauber, und die Erinnerungen bleiben unverändert. Während diese Bezauberung anhält, kannst du die Erinnerungen des Ziels an ein Ereignis der letzten 24 Stunden verändern, das maximal zehn Minuten gedauert hat. Du kannst sämtliche Erinnerungen an das Ereignis dauerhaft entfernen, dem Ziel ermöglichen, sich mit absoluter Klarheit an das Ereignis zu erinnern, seine Erinnerungen an die Details des Ereignisses verändern oder eine andere Erinnerung erschaffen. Du musst mit dem Ziel sprechen und beschreiben, wie seine Erinnerungen beeinflusst werden. Das Ziel muss deine Sprache sprechen, damit die veränderten Erinnerungen Fuß fassen können. Sein Verstand füllt etwaige Lücken in den von dir beschriebenen Details. Endet der Zauber, bevor du die veränderten Erinnerungen beschrieben hast, so werden die Erinnerungen der Kreatur nicht modifiziert. Anderenfalls setzen sich die neuen Erinnerungen fest, wenn der Zauber endet. DER MAGIER EVARD WIRRT SEINEN BERÜCHTIGTEN ZAUBER EvAR05 SCHWARZE TENTAWEL. UM DREISTEN BANDITEN EINE LEHRE TU ERTEILEN. Veränderte Erinnerungen beeinflussen nicht notwendigerweise das Verhalten der Kreatur —insbesondere dann nicht, wenn sie mit den natürlichen Neigungen, der Gesinnung oder dem Glauben der Kreatur im Widerspruch stehen. Eine unlogische veränderte Erinnerung, beispielsweise daran, wie gerne die Kreatur in Säure gebadet hat, wird als Albtraum abgetan. Der SL kann eine veränderte Erinnerung als zu unsinnig erklären, um die Kreatur zu beeinflussen. Die Zauber Fluch brechen oder Vollständige Genesung stellen die wahren Erinnerungen der Kreatur wieder her.', 'Du kannst die Erinnerung des Ziels an ein Ereignis verändern, das in den vergangenen sieben Tagen (6. Zauberplatzgrad), in den vergangenen 30 Tagen (7. Zauberplatzgrad), den vergangenen 365 Tagen (8. Zauberplatzgrad) oder zu einem beliebigen Zeitpunkt in der Vergangenheit der Kreatur (9. Zauberplatzgrad) stattgefunden hat.', 'Druide, Magier, Zauberer', '{"scaling":{"text":"Du kannst die Erinnerung des Ziels an ein Ereignis verändern, das in den vergangenen sieben Tagen (6. Zauberplatzgrad), in den vergangenen 30 Tagen (7. Zauberplatzgrad), den vergangenen 365 Tagen (8. Zauberplatzgrad) oder zu einem beliebigen Zeitpunkt in der Vergangenheit der Kreatur (9. Zauberplatzgrad) stattgefunden hat."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('erschaffung', 'ERSCHAFFUNG', 5, 'Illusionszauber', '1 Minute', '9 Meter', 'V, G, M (ein Farbpinsel)', NULL, 'Besonders', 0, 0, 'Du ziehst Fäden aus Schattenmaterial aus dem Schattensaum und erschaffst einen Gegenstand in Reichweite. Es kann sich entweder um einen Gegenstand aus pflanzlichem Material (Textilien, Seil, Holz und dergleichen) oder um einen Gegenstand aus mineralischem Material (Stein, Kristall, Metall und dergleichen) handeln. Der Gegenstand darf nicht größer als ein Würfel mit 1,5 Metern Kantenlänge sein und muss aus einem Material bestehen sowie eine Form besitzen, die du bereits gesehen hast. Die Wirkungsdauer des Zaubers hängt vom Material des Gegenstands ab, wie in der Tabelle „Materialien" dargestellt. Besteht der Gegenstand aus mehreren Materialien, so verwende die kürzeste Wirkungsdauer. Wenn ein mit diesem Zauber erschaffener Gegenstand als Materialkomponente für einen anderen Zauber verwendet wird, scheitert der andere Zauber. MATERIALIEN Material Wirkungsdauer Pflanzliches Material 24 Stunden Stein oder Kristall 12 Stunden Edelmetalle 1 Stunde Edelsteine 10 Minuten Adamant oder Mithral 1 Minute', 'Für jeden Zauberplatzgrad über dem 5. wird der Würfel um 1,5 Meter vergrößert.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. wird der Würfel um 1,5 Meter vergrößert."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('erwecken', 'ERWECKEN', 5, 'Verwandlungszauber', '8 Stunden', 'Berührung', 'V, G, M (ein Achat im Wert von mindestens 1.000 GM, den der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 0, '260 KAPITEL 7 l ZAUBER Du verbringst den Zeitaufwand damit, magische Pfade in einen kostbaren Edelstein zu zeichnen, und berührst anschließend ein Ziel. Beim Ziel muss es sich um ein Tier, um eine Pflanzenkreatur mit einem Intelligenzwert von höchstens 3 oder um eine natürliche Pflanze handeln, die keine Kreatur ist. Das Ziel erhält einen Intelligenzwert von 10 sowie die Fähigkeit, eine dir bekannte Sprache zu sprechen. Wenn es sich um eine natürliche Pflanze handelt, wird diese zu einer Pflanzenkreatur, die ihre Wurzeln, Triebe, Ranken und Ähnliches bewegen kann und Sinne ähnlich denen eines Menschen hat. Dein SL wählt geeignete Spielwerte aus, beispielsweise die des Erwachten Buschs oder des Erwachten Baums im Monsterhandbuch. Die erweckte Kreatur ist bezaubert. Die Bezauberung endet nach 30 Tagen, oder wenn du oder deine Verbündeten der Kreatur Schaden zufügen. Wenn die Bezauberung endet, wählt die Kreatur ihre Haltung dir gegenüber aus.', '', 'Barde, Druide', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('erzwungenes-duell', 'ERZWUNGENES DUELL', 1, 'Verzauberungszauber', 'Bonusaktion', '9 Meter', 'V', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du versuchst, eine Kreatur zu einem Duell zu zwingen. Eine Kreatur in Reichweite, die du sehen kannst, führt einen Weisheitsrettungswurf aus. Scheitert der Wurf, so ist das Ziel bei Angriffswürfen gegen Kreaturen außer dir im Nachteil und kann sich nicht willentlich in einen Bereich bewegen, der weiter als neun Meter von dir entfernt ist. Der Zauber endet, wenn du einen Angriffswurf gegen eine andere Kreatur als das Ziel ausführst, wenn du einen Zauber auf einen anderen Gegner als das Ziel wirkst, wenn ein Verbündeter von dir dem Ziel Schaden zufügt oder wenn du deinen Zug weiter als neun Meter vom Ziel entfernt beendest. EVARDS SCHWARZE TENTAKEL Beschwörungszauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: 27 Meter Komponenten: V, G, M (ein Tentakel) Wirkungsdauer: Konzentration, bis zu 1 Minute Ein quadratischer Bodenbereich mit sechs Metern Kantenlänge in Reichweite, den du sehen kannst, wird von tiefschwarzen zuckenden Tentakeln erfüllt. Die Tentakel verwandeln diesen Bereich für die Wirkungsdauer in schwieriges Gelände. Jede Kreatur in diesem Bereich führt einen Stärkeret-tungswurf aus. Scheitert der Wurf, so erleidet sie 3W6 Wuchtschaden und ist festgesetzt, bis der Zauber endet. Auch Kreaturen, die sich in den Bereich begeben oder ihren Zug darin beenden, führen den Rettungswurf aus. Jede Kreatur führt den Rettungswurf nur einmal pro Zug aus. Eine festgesetzte Kreatur kann mit einer Aktion einen Stärkewurf (Athletik) gegen deinen Zauberrettungswurf-SG ausführen. Bei einem Erfolg endet der Zustand bei ihr. EXPLOSION DER ZAUBEREI Zaubertrick der Hervorrufung (Zauberer) Zeitaufwand: Aktion Reichweite: 36 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du wirkst zauberische Energie auf eine Kreatur oder einen Gegenstand in Reichweite. Führe einen Fernkampfzauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 1W8 Schaden von einer Art deiner Wahl: Blitz, Feuer, Gift, Kälte, Psychisch, Säure oder Schall. Wenn du mit einem W8 eine 8 für diesen Zauber würfelst, kannst du mit einem weiteren W8 würfeln und das Ergebnis dem Schaden hinzufügen. Wenn du diesen Zauber wirkst, entspricht die maximale Anzahl von W8, die du dem Zauberschaden hinzufügen kannst, deinem Zauberwirken-Attributsmodifikator.', 'Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.', 'Paladin', '{"scaling":{"text":"Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('fallen-finden', 'FALLEN FINDEN', 2, 'Erkenntniszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du erspürst alle Fallen, die sich in Reichweite und in deiner Sichtlinie befinden. Für diesen Zauber zählt jeder Gegenstand oder Mechanismus als Falle, der geschaffen wurde, um Schaden zu bewirken oder eine sonstige Gefahr darzustellen. Dazu zählen Zauber wie Alarm oder Glyphe des Schutzes oder mechanische Fallgruben, jedoch keine natürlichen Schwachstellen im Boden, keine instabilen Decken oder verborgene Senklöcher. Dieser Zauber enthüllt, dass eine Falle vorhanden ist, jedoch nicht, wo sie sich befindet. Du erfährst jedoch die allgemeine Art der Gefahr der Falle.', '', 'Druide, Kleriker, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('falsches-leben', 'FALSCHES LEBEN', 1, 'Nekromantiezauber', 'Aktion', 'Selbst', 'V, G, M (ein Tropfen Alkohol)', NULL, 'Unmittelbar', 0, 0, 'Du erhältst 2W4+4 temporäre Trefferpunkte.', 'Für jeden Zauberplatzgrad über dem 1. erhältst du 5 zusätzliche temporäre Trefferpunkte.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. erhältst du 5 zusätzliche temporäre Trefferpunkte."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('federfall', 'FEDERFALL', 1, 'Verwandlungszauber', 'Reaktion, die du ausführst, wenn du oder eine Kreatur im Abstand von bis zu 18 Metern von dir, die du sehen kannst, stürzt', '18 Meter', 'V, M (eine kleine Feder oder Daune)', NULL, '1 Minute', 0, 0, 'Wähle bis zu fünf Kreaturen in Reichweite aus, die gerade stürzen. Ihre Fallgeschwindigkeit verlangsamt sich für die Wirkungsdauer auf 18 Meter pro Runde. Wenn eine Kreatur während der Wirkungsdauer landet, erleidet sie keinen Schaden durch den Sturz, und der Zauber endet bei dieser Kreatur.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('feenfeuer', 'FEENFEUER', 1, 'Hervorrufungszauber', 'Aktion', '18 Meter', 'V', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Gegenstände innerhalb eines Würfels mit sechs Metern Kantenlänge in Reichweite werden von blauem, grünem oder violettem Licht (nach deiner Wahl) umrahmt. Auch Kreaturen im Würfel werden umrahmt, es sei denn, sie bestehen einen Geschicklichkeitsrettungswurf. Betroffene Gegenstände und Kreaturen spenden für die Wirkungsdauer in einem Radius von drei Metern dämmriges Licht und profitieren nicht von Unsichtbarkeit. Angreifer sind bei Angriffswürfen gegen betroffene Kreaturen oder Gegenstände im Vorteil, sofern sie ihr Ziel sehen können.', '', 'Barde, Druide', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('feenwesen-beschw-ren', 'FEENWESEN BESCHWÖREN', 6, 'Beschwörungszauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du beschwörst einen mittelgroßen Geist aus der Feenwildnis in einem freien Bereich in Reichweite, den du sehen kannst. Der Geist bleibt für die Wirkungsdauer bestehen und gleicht einem Feenwesen deiner Wahl. Wenn der Geist erscheint, kannst du einen Nahkampf-Zauberangriff gegen eine Kreatur im Abstand von bis zu 1,5 Metern vom Geist ausführen. Bei einem Treffer erleidet das Ziel psychischen Schaden in Höhe von 3W12 plus deinem Zauberwirken-Attributsmodifikator. Außerdem ist das Ziel bis zum Beginn deines nächsten Zugs verängstigt. Dabei seid sowohl du als auch der Geist die Quelle seiner Angst. Als Bonusaktion in deinen folgenden Zügen kannst du den Geist in einen freien Bereich im Abstand von bis zu neun Metern von seinem ursprünglichen Bereich teleportieren, den du sehen kannst, und den Angriff gegen eine Kreatur im Abstand von bis zu 1,5 Metern vom Geist ausführen.', 'Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 2W12 erhöht.', 'Druide', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 2W12 erhöht.","type":"damage","dice":"2W12"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('feenwesen-herbeirufen', 'FEENWESEN HERBEIRUFEN', 3, 'Beschwörungszauber', 'Aktion', '27 Meter', 'V, G, M (eine vergoldete Blume im Wert von mindestens 300 GM)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du rufst den Geist einer Fee herbei. Er erscheint in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten eines Feengeists. Wenn du den Zauber wirkst, entscheide dich für eine Stimmung: heiter, trickreich oder wütend. Die Kreatur sieht aus wie ein Feenwesen deiner Wahl, das die entsprechende Stimmung verkörpert, wodurch einige Details im Wertekasten bestimmt werden. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet. FEENCEIST Kleines Feenwesen, neutral', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.', 'Druide, Hexenmeister, Magier, Waldläufer', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":"12 + Zaubergrad","hp":null},"summon_text":"RK 12 + Zaubergrad\nTP 30 + 10 für jeden Zaubergrad ab dem 4.\nBewegungsrate 9 m, Fliegen 9 m\nMOD RW\tMOD 5W\nST); 13 +1 +1\tGES 16 +3 +3\nI INT 14 +2 +2 WEI 11 +0 +0\nImmunitäten Bezaubert\nSinne Dunkelsicht 18 m, Passive Wahrnehmung 10 Sprachen Sylvanisch, versteht die Sprachen, die du kennst HG — (EP 0, ÜB entspricht deinem Übungsbonus)\nAKTIONEN\nMehrfachangriff Der Geist führt eine Anzahl von Feenklinge-Angriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet).\nFeenklinge: Na h ka mpfa ngriff-Swu rf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 2W6+3 + Zaubergrad Energieschaden.\nBONUSAKTIONEN\nFeenschritt: Der Geist teleportiert sich auf magische Art bis zu neun Meter weit in einen freien Bereich, den er sehen kann. Dann tritt einer der folgenden Effekte auf, der auf der ausgewählten Stimmung des Geists beruht:\nHeiter: Weisheitsrettungswurf Der SG entspricht deinem Zauberrettungswurf-SG, eine Kreatur im Abstand von bis zu drei Metern vom Geist, die er sehen kann. Misserfolg: Das Ziel ist eine Minute lang—oder bis es Schaden erleidet—von dir und dem Geist bezaubert.\nTrickreich: Der Geist füllt im Abstand von bis zu 1,5 Metern von sich einen Würfel von drei Metern Kantenlänge mit magischer Dunkelheit. Diese bleibt bis zum Ende seines nächsten Zugs bestehen.\nWütend: Der Geist ist bei seinem nächsten Angriffswurf vor dem Ende dieses Zugs im Vorteil.\nMOD RW\nKON 14 +2 +2 CHA 16 +3 +3\n262\tKAPITEL 7 l ZAUBER\nDie Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.\nEINEM MARODIERENDEN SEEOGER ENTGEGENWIRFT\n240/Ir\t••","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('fesseln', 'FESSELN', 2, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du intonierst eine ablenkende Folge von Worten. Kreaturen deiner Wahl in Reichweite, die du sehen kannst, führen einen Weisheitsrettungswurf aus. Jede Kreatur, gegen die du oder deine Gefährten kämpfen, besteht diesen Rettungswurf automatisch. Scheitert der Wurf, so hat das Ziel einen Malus von -10 auf Weisheitswürfe (Wahrnehmung) sowie auf Passive Wahrnehmung, bis der Zauber endet.', '', 'Barde, Hexenmeister', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('fesselnder-schlag', 'FESSELNDER SCHLAG', 1, 'Beschwörungszauber', 'Bonusaktion, die du sofort ausführst,', '', '', NULL, '', 0, 0, 'wenn du eine Kreatur mit einer Waffe getroffen hast Reichweite: Selbst Komponenten: V Wirkungsdauer: Konzentration, bis zu 1 Minute Wenn du das Ziel triffst, erscheinen greifende Ranken an ihm, und es führt einen Stärkerettungswurf aus. Kreaturen von mindestens großer Größe sind bei diesem Rettungswurf im Vorteil. Scheitert der Wurf, so ist das Ziel festgesetzt, bis der Zauber endet. Bei einem erfolgreichen Rettungswurf verwelken die Ranken, und der Zauber endet. Während das Ziel festgesetzt ist, erleidet es zu Beginn jedes seiner Züge 1W6 Stichschaden. Das Ziel oder eine Kreatur in seiner Reichweite kann mit einer Aktion einen Stärkewurf (Athletik) gegen deinen Zauberrettungswurf-SG ausführen. Bei einem Erfolg endet der Zauber.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht.', 'Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('feuerball', 'FEUERBALL', 3, 'Hervorrufungszauber', 'Aktion', '45 Meter', 'V, G, M (eine Kugel aus Fledermaus-Guano und Schwefel)', NULL, 'Unmittelbar', 0, 0, 'Du verschießt einen hellen Lichtstrahl auf einen Punkt deiner Wahl in Reichweite, wo er unter dumpfem Grollen zu einer Flammenexplosion detoniert. Jede Kreatur in einer Kugel mit einem Radius von sechs Metern um diesen Punkt führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 8W6 Feuerschaden, anderenfalls die Hälfte. Brennbare Gegenstände im Bereich, die nicht getragen oder gehalten werden, beginnen zu brennen.', 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W6 erhöht.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('feuerpfeil', 'FEUERPFEIL', 0, 'Hervorrufungzauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du schleuderst einen Feuerfunken auf eine Kreatur oder einen Gegenstand in Reichweite. Führe einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 1W10 Feuerschaden. Der Zauber setzt brennbare Gegenstände in Brand, die er trifft und die nicht getragen oder gehalten werden.', 'Der Schaden wird um jeweils 1W10 erhöht, wenn du die 5. (2W10), die 11. (3W10) und die 17. (4W10) Stufe erreichst.', 'Magier, Zauberer', '{"scaling":{"text":"Der Schaden wird um jeweils 1W10 erhöht, wenn du die 5. (2W10), die 11. (3W10) und die 17. (4W10) Stufe erreichst.","type":"cantrip_scaling","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('feuerschild', 'FEUERSCHILD', 4, 'Hervorrufungszauber', 'Aktion', 'Selbst', 'V, G, M (etwas Phosphor oder ein Glühwürmchen)', NULL, '10 Minuten', 0, 0, 'Für die Wirkungsdauer umgeben feine Flammen deinen Körper und spenden in einem Radius von drei Metern helles Licht und in einem Radius von weiteren drei Metern dämmriges Licht. Die Flammen verleihen dir einen Wärmeschild oder einen Kälteschild (nach deiner Wahl). Der Wärmeschild gewährt dir Resistenz gegen Kälteschaden, der Kälteschild Resistenz gegen Feuerschaden. Wann immer dich eine Kreatur im Abstand von bis zu 1,5 Metern von dir mit einem Nahkampfangriffswurf trifft, geht der Schild außerdem in Flammen auf. Der Angreifer erleidet 2W8 Feuerschaden durch einen Wärmeschild oder 2W8 Kälteschaden durch einen Kälteschild.', '', 'Druide, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('feuersturm', 'FEUERSTURM', 7, 'Hervorrufungszauber', 'Aktion', '45 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Ein Feuersturm erscheint in Reichweite. Der Bereich des Sturms besteht aus bis zu zehn Würfeln mit drei Metern Kantenlänge, die du beliebig anordnest. Jeder Würfel muss an mindestens einen anderen angrenzen. Jede Kreatur im Bereich führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 7W10 Feuerschaden, anderenfalls die Hälfte. Brennbare Gegenstände im Bereich, die nicht getragen oder gehalten werden, beginnen zu brennen.', '', 'Druide, Kleriker, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('feuerwand', 'FEUERWAND', 4, 'Hervorrufungszauber', 'Aktion', '36 Meter', 'V, G, M (ein Stück Kohle)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du erschaffst auf einer festen Oberfläche in Reichweite eine Feuerwand. Du kannst eine gerade Wand mit bis zu 18 Metern Länge oder eine ringförmige Wand mit einem Radius von bis zu drei Metern erschaffen. In beiden Fällen ist die Wand sechs Meter hoch und 30 Zentimeter dick. Die Wand ist undurchsichtig und bleibt für die Wirkungsdauer bestehen. Wenn die Wand erscheint, führt jede Kreatur in ihrem Bereich einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 5W8 Feuerschaden, anderenfalls die Hälfte. Eine Seite der Wand, die du beim Wirken des Zaubers bestimmst, fügt jeder Kreatur, die ihren Zug im Abstand von bis zu drei Metern von der Seite oder in der Wand beendet, 5W8 Feuerschaden zu. Eine Kreatur erleidet den Schaden ebenfalls, wenn sie die Wand in einem Zug erstmals betritt. Die andere Seite der Wand bewirkt keinen Schaden.', 'Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 1W8 erhöht.', 'Druide, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('finger-des-todes', 'FINGER DES TODES', 7, 'Nekromantiezauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du entfesselst negative Energie auf eine Kreatur in Reichweite, die du sehen kannst. Das Ziel führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet das Ziel 7W8+30 nekrotischen Schaden, anderenfalls die Hälfte. Wenn ein Humanoide durch diesen Zauber getötet wird, steht er zu Beginn deines nächsten Zugs als Zombie (siehe Anhang B) wieder auf und folgt deinen verbalen Befehlen.', '', 'Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('flammen-erzeugen', 'FLAMMEN ERZEUGEN', 0, 'Beschwörungzauber', 'Bonusaktion', 'Selbst', 'V, G', NULL, '10 Minuten', 0, 0, 'In deiner Hand erscheint für die Wirkungsdauer eine flackernde Flamme. Sie strahlt keine Wärme aus und kann nichts entzünden, spendet jedoch in einem Radius von sechs Metern helles Licht und in einem Radius von weiteren sechs Metern dämmriges Licht. Der Zauber endet vorzeitig, wenn du ihn erneut wirkst. Bis der Zauber endet, kannst du eine magische Aktion ausführen, um Feuer auf eine Kreatur oder einen Gegenstand im Abstand von bis zu 18 Metern von dir zu schleudern. Führe einen Fernkampf-Zauberangriff aus. Bei einem Treffer erleidet das Ziel 1W8 Feuerschaden.', 'Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.', 'Druide', '{"scaling":{"text":"Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.","type":"cantrip_scaling","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('flammende-wolke', 'FLAMMENDE WOLKE', 8, 'Beschwörungszauber', 'Aktion', '45 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Eine wirbelnde Wolke aus Funken und Rauch erfüllt eine Kugel mit einem Radius von sechs Metern um einen Punkt in Reichweite. Der Bereich der Wolke ist komplett verschleiert. Die Wolke bleibt für die Wirkungsdauer bestehen, oder bis starker Wind (wie solcher durch Windstoß) sie auflöst. Wenn die Wolke erscheint, führt jede Kreatur darin einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 10W8 Feuerschaden, anderenfalls die Hälfte. Eine Kreatur muss den Konstitutionsrettungswurf auch dann ausführen, wenn die Kugel sich in ihren Bereich bewegt, wenn die Kreatur die Kugel betritt oder ihren Zug darin beendet. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen. Zu Beginn jedes deiner Züge bewegt sich die Wolke um drei Meter in einer Richtung deiner Wahl von dir weg.', '', 'Druide, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('flammenklinge', 'FLAMMENKLINGE', 2, 'Hervorrufungszauber', 'Bonusaktion', 'Selbst', 'V, G, M (ein Sumach-Blatt)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erschaffst eine feurige Klinge in deiner freien Hand. Die Klinge ähnelt einem Krummsäbel und bleibt für die Wirkungsdauer bestehen. Wenn du die Klinge loslässt, verschwindet sie. Du kannst sie jedoch als Bonusaktion erneut beschwören. Du kannst als magische Aktion einen Nahkampf-Zauberangriff mit der Flammenklinge ausführen. Bei einem Treffer erleidet das Ziel Feuerschaden in Höhe von 3W6 plus deinem Zauberwirken-Attributsmodifikator. Die Flammenklinge spendet in einem Radius von drei Metern helles Licht und in einem Radius von weiteren drei Metern dämmriges Licht.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W6 erhöht.', 'Druide, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('flammenkugel', 'FLAMMENKUGEL', 2, 'Beschwörungszauber', 'Aktion', '18 Meter', 'V, G, M (eine Kugel aus Wachs)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du erzeugst in einem freien Bereich in Reichweite auf dem Boden eine flammende Kugel mit einem Radius von 1,5 Metern. Die Kugel bleibt für die Wirkungsdauer bestehen. Jede Kreatur, die ihren Zug in im Abstand von bis zu 1,5 Metern um die Kugel beendet, führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 2W6 Feuerschaden, anderenfalls die Hälfte. Als Bonusaktion kannst du die Kugel bis zu neun Meter weit über den Boden rollen lassen. Wenn du die Kugel in den Bereich einer Kreatur bewegst, muss diese den Rettungswurf gegen die Kugel ausführen, und die Kugel beendet ihre Bewegung für diesen Zug. Bewegst du die Kugel, so kannst du sie über bis zu 1,5 Meter hohe Hindernisse dirigieren und über bis zu drei Meter breite Gruben springen lassen. Brennbare Gegenstände, die nicht getragen oder gehalten werden, beginnen zu brennen, wenn sie von der Kugel berührt werden. Die Kugel spendet in einem Radius von sechs Metern helles Licht sowie in einem Radius von weiteren sechs Metern dämmriges Licht.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W6 erhöht.', 'Druide, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('flammenschlag', 'FLAMMENSCHLAG', 5, 'Hervorrufungszauber', 'Aktion', '18 Meter', 'V, G, M (eine Prise Schwefel)', NULL, 'Unmittelbar', 0, 0, 'Eine vertikale Säule aus strahlendem Feuer fährt von oben herab. Jede Kreatur in einem zwölf Meter hohen Zylinder mit einem Radius von drei Metern um einen Punkt in Reichweite führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 5W6 Feuerschaden und 5W6 gleißenden Schaden, anderenfalls die Hälfte.', 'Für jeden Zauberplatzgrad über dem 5. werden Feuerschaden und gleißender Schaden um 1W6 erhöht.', 'Kleriker', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. werden Feuerschaden und gleißender Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('fleisch-zu-stein', 'FLEISCH ZU STEIN', 6, 'Verwandlungszauber', 'Aktion', '18 Meter', 'V, G, M (eine Schreckhahn-Feder)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du versuchst, eine Kreatur in Reichweite, die du sehen kannst, in Stein zu verwandeln. Das Ziel führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so ist es für die Wirkungsdauer festgesetzt. Bei einem erfolgreichen Rettungswurf beträgt seine Bewegungsrate bis zum Beginn deines nächsten Zugs 0. Konstrukte bestehen den Rettungswurf automatisch. Eine festgesetzte Kreatur führt am Ende jedes ihrer Züge einen weiteren Konstitutionsrettungswurf aus. Bei drei erfolgreichen Rettungswürfen endet dieser Zauber. Bei drei Misserfolgen wird sie zu Stein und hat für die Wirkungsdauer den Zustand Versteinert. Die Erfolge und Misserfolge müssen nicht aufeinanderfolgen. Notiere einfach beide, bis das Ziel drei von einem hat. Wenn du dich für die gesamte Wirkungsdauer auf diesen Zauber konzentrierst, versteinert die Kreatur, bis der Effekt durch den Zauber Vollständige Genesung oder ähnliche Magie aufgehoben wird.', '', 'Druide, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('fliegen', 'FLIEGEN', 3, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (eine Feder)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du berührst eine bereitwillige Kreatur. Für die Wirkungs­dauer erhält das Ziel eine Flugbewegungsrate von 18 Metern und kann schweben. Wenn der Zauber endet und das Ziel noch fliegt, stürzt es ab, sofern es den Sturz nicht verhindern kann.', 'Für jeden Zauberplatzgrad über dem 3. kannst du auf eine weitere Kreatur zielen.', 'Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('flimmern', 'FLIMMERN', 3, 'Verwandlungszauber', 'Aktion', 'Selbst', 'V, G', NULL, '1 Minute', 0, 0, 'Würfle während der Wirkungsdauer am Ende jedes deiner Züge mit 1W6. Bei einem Ergebnis von 4-6 verschwindest du von deiner aktuellen Existenzebene und erscheinst auf der Ätherebene (der Zauber endet sofort, wenn du dich bereits auf dieser Ebene befindest). Von der Ätherebene aus kannst du die Ebene, die du verlassen hast, wahrnehmen. Du siehst sie jedoch nur in Graustufen, und deine Sichtweite beträgt höchstens 18 Meter. Du kannst nur andere Kreaturen auf der Ätherebene beeinflussen oder von ihnen beeinflusst werden. Kreaturen auf der anderen Ebene können dich nicht wahrnehmen, es sei denn, eine Spezialfähigkeit erlaubt ihnen, Dinge auf der Ätherebene wahrzunehmen. Du kehrst zu Beginn deines nächsten Zugs - oder wenn der Zauber endet, während du dich auf der Ätherebene befindest - auf die andere Ebene zurück. Du kehrst in einen freien Bereich deiner Wahl, den du sehen kannst, im Abstand von bis zu drei Metern vom Bereich zurück, den du verlassen hast. Ist innerhalb dieser Reichweite kein freier Bereich vorhanden, so erscheinst du im nächstgelegenen freien Bereich.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('fluch', 'FLUCH', 3, 'Nekromantiezauber', 'Aktion', 'Berührung', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du berührst eine Kreatur. Diese muss einen Weisheitsret-tungswurf bestehen, oder sie ist für die Wirkungsdauer verflucht. Bis der Fluch endet, erleidet das Ziel einen der folgenden Effekte deiner Wahl: Wähle ein Attribut aus. Das Ziel ist bei allen Attributs­und Rettungswürfen mit diesem Attribut im Nachteil. Das Ziel ist bei Angriffswürfen gegen dich im Nachteil. Im Kampf muss das Ziel zu Beginn jedes seiner Züge einen Weisheitsrettungswurf bestehen, oder es ist gezwungen, die Ausweichaktion auszuführen. Wenn du dem Ziel mit einem Angriffswurf oder einem Zauber Schaden zufügst, erleidet es zusätzlich 1W8 nekrotischen Schaden.', 'Bei einem Zauberplatz des 4. Grades kannst du deine Konzentration auf diesen Zauber bis zu zehn Minuten lang aufrechterhalten. Bei einem Zauberplatz ab dem 5. Grad erfordert der Zauber keine Konzentration, und die Wirkungsdauer beträgt acht Stunden (Zauberplatzgrad 5-6) oder 24 Stunden (Zauberplatzgrad 7-8). Bei einem Zauber­platz des 9. Grades hält der Zauber an, bis er gebannt wird.', 'Barde, Kleriker, Magier', '{"scaling":{"text":"Bei einem Zauberplatz des 4. Grades kannst du deine Konzentration auf diesen Zauber bis zu zehn Minuten lang aufrechterhalten. Bei einem Zauberplatz ab dem 5. Grad erfordert der Zauber keine Konzentration, und die Wirkungsdauer beträgt acht Stunden (Zauberplatzgrad 5-6) oder 24 Stunden (Zauberplatzgrad 7-8). Bei einem Zauber­platz des 9. Grades hält der Zauber an, bis er gebannt wird."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('fluch-brechen', 'FLUCH BRECHEN', 3, 'Bannzauber', 'Aktion', 'Berührung', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Auf deine Berührung hin enden alle Flüche, die auf eine Kreatur oder einen Gegenstand wirken. Ist ein verfluchter Gegenstand magisch, so bleibt der Fluch zwar bestehen, aber der Zauber bricht die Einstimmung des Besitzers, sodass der Gegenstand entfernt oder weggeworfen werden kann.', '', 'Hexenmeister, Kleriker, Magier, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('freundschaft', 'FREUNDSCHAFT', 0, 'Verzauberungzauber', 'Aktion', '3 Meter', 'G, M (etwas Schminke)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du strahlst auf magische Art ein Gefühl der Freundschaft auf eine Kreatur in Reichweite aus, die du sehen kannst. Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer bezaubert. Es besteht den Rettungswurf automatisch, wenn es kein Humanoide ist, wenn du gegen es kämpfst oder diesen Zauber bereits innerhalb der letzten 24 Stunden auf das Ziel gewirkt hast. Der Zauber endet vorzeitig, wenn das Ziel Schaden erleidet, du einen Angriffswurf ausführst, Schaden bewirkst oder eine Kreatur zu einem Rettungswurf zwingst. Nach Ende des Zaubers weiß das Ziel, dass es von dir bezaubert wurde.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('furcht', 'FURCHT', 3, 'Illusionszauber', 'Aktion', 'Selbst', 'V, G, M (eine weiße Feder)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Jede Kreatur in einem Kegel von neun Metern muss einen Weisheitsrettungswurf bestehen, oder sie lässt fallen, was auch immer sie hält, und ist für die Wirkungsdauer verängstigt. Eine verängstigte Kreatur führt in jedem ihrer Züge die Spurt-Aktion aus und entfernt sich auf dem sichersten Weg von dir, sofern möglich. Beendet eine Kreatur ihren Zug in einem Bereich, in dem du nicht in ihrer Sichtlinie bist, führt sie einen Weisheitsrettungswurf aus. Bei einem erfolgreichen Rettungswurf endet der Zauber bei der Kreatur.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gasf-rmige-gestalt', 'GASFÖRMIGE GESTALT', 3, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (ein Stück Gaze)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Für die Wirkungsdauer verwandelt sich eine bereitwillige Kreatur, die du berührst, mitsamt ihrer Ausrüstung in eine Nebelwolke. Der Zauber endet beim Ziel, wenn seine Trefferpunkte auf 0 sinken oder es den Zauber mithilfe einer magischen Aktion selbst beendet. In dieser Gestalt verfügt das Ziel als einzige Bewegungsrate über eine Flugbewegungsrate von drei Metern, und es kann schweben. Das Ziel kann den Bereich einer anderen Kreatur betreten und besetzen. Es ist gegen Hieb-, Stichund Wuchtschaden resistent, gegen den Zustand Liegend immun und bei Stärke-, Geschicklichkeitsund Konstitutionsrettungswürfen im Vorteil. Es kann kleine Öffnungen passieren, behandelt jedoch Flüssigkeiten wie solide Oberflächen. Das Ziel kann weder sprechen noch mit Gegenständen interagieren. Es kann Gegenstände, die es getragen oder gehalten hat, nicht fallenlassen, verwenden oder anderweitig mit ihnen interagieren. Außerdem kann es weder angreifen noch Zauber wirken.', 'Für jeden Zauberplatzgrad über dem 3. kannst du auf eine weitere Kreatur zielen.', 'Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('geas', 'GEAS', 5, 'Verzauberungszauber', '1 Minute', '18 Meter', 'V', NULL, '30 Tage', 0, 0, 'Du erteilst einer Kreatur in Reichweite, die du sehen kannst, den verbalen Befehl, eine Aufgabe zu erfüllen oder eine bestimmte Aktion oder Handlungsrichtung deiner Wahl zu unterlassen. Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer bezaubert. Es besteht den Rettungswurf automatisch, wenn es deinen Befehl nicht verstehen kann. Die bezauberte Kreatur erleidet 5W10 psychischen Schaden, wenn sie sich deinem Befehl gegensätzlich verhält. Sie kann diesen Schaden höchstens einmal täglich erleiden. Du kannst einen beliebigen Befehl erteilen, jedoch keine Handlungen, die zum sicheren Tod führen würden. Erteilst du einen selbstmörderischen Befehl, so endet der Zauber. Auch die Zauber Fluch brechen, Vollständige Genesung oder Wunsch beenden den Zauber.', 'Bei einem Zauberplatz des 7. oder 8. Grades beträgt die Wirkungsdauer 365 Tage. Bei einem Zauberplatz des 9. Grades hält der Zauber an, bis er durch einen der oben genannten Zauber gebannt wird.', 'Barde, Druide, Kleriker, Magier, Paladin', '{"scaling":{"text":"Bei einem Zauberplatz des 7. oder 8. Grades beträgt die Wirkungsdauer 365 Tage. Bei einem Zauberplatz des 9. Grades hält der Zauber an, bis er durch einen der oben genannten Zauber gebannt wird."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gebet-der-heilung', 'GEBET DER HEILUNG', 2, 'Bannzauber', '10 Minuten', '9 Meter', 'V', NULL, 'Unmittelbar', 0, 0, 'Bis zu fünf Kreaturen deiner Wahl, die während des gesamten Zeitaufwands in Reichweite verbleiben, erhalten die Vorzüge einer kurzen Rast. Außerdem erhalten sie jeweils 2W8 Trefferpunkte zurück. Eine Kreatur kann erst nach einer langen Rast erneut Ziel dieses Zaubers werden.', 'Für jeden Zauberplatzgrad über dem 2. wird die Heilung um 1W8 Trefferpunkte erhöht.', 'Kleriker, Paladin', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird die Heilung um 1W8 Trefferpunkte erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gedanken-wahrnehmen', 'GEDANKEN WAHRNEHMEN', 2, 'Erkenntniszauber', 'Aktion', 'Selbst', 'V, G, M (1 Kupfermünze)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du aktivierst einen der nachstehenden Effekte. Bis der Zauber endet, kannst du jeweils einen der Effekte als magische Aktion in deinen folgenden Zügen aktivieren. Gedanken spüren: Du spürst Gedanken von Kreaturen im Abstand von bis zu neun Metern von dir, die Sprachen oder Telepathie beherrschen. Du kannst die Gedanken zwar nicht lesen, weißt aber, dass eine denkende Kreatur anwesend ist. Der Zauber wird von 30 Zentimetern Stein, Erde oder Holz sowie von 2,5 Zentimetern Metall oder einer dünnen Bleischicht blockiert. Gedanken lesen: Ziele auf eine Kreatur im Abstand von bis zu neun Metern von dir, die du sehen kannst oder mit der Option Gedanken spüren entdeckt hast. Du erfährst, was die Kreatur im Augenblick am meisten beschäftigt. Wenn das Ziel weder Sprachen noch Telepathie beherrscht, erfährst du nichts. Als magische Aktion im nächsten Zug kannst du versuchen, tiefer in den Verstand des Ziels einzudringen. In diesem Fall führt das Ziel einen Weisheitsrettungswurf aus. Scheitert der Wurf, so erhältst du Einsicht in seine Beweggründe und Emotionen sowie in etwas, das seine Gedanken beherrscht (beispielsweise Sorgen, Liebe oder Hass) Bei einem erfolgreichen Rettungswurf endet der Zauber. In jedem Fall merkt das Ziel, dass du in seinen Verstand eindringst. Solange du deine Aufmerksamkeit auf seinen Verstand richtest, kann es als Aktion in seinem Zug einen Intelligenzwurf (Arkane Kunde) gegen deinen Zauberrettungswurf-SG ausführen. Bei einem Erfolg endet der Zauber.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gedankendorn', 'GEDANKENDORN', 2, 'Erkenntniszauber', 'Aktion', '36 Meter', 'S', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du treibst einer Kreatur in Reichweite, die du sehen kannst, einen desorientierenden Splitter psionischer Energie in den Geist. Das Ziel führt einen Weisheitsrettungswurf aus. Scheitert der Wurf, so erleidet es 3W8 psychischen Schaden, anderenfalls die Hälfte. Scheitert der Wurf, so kennst du außerdem stets den Aufenthaltsort des Ziels, bis der Zauber endet, sofern ihr euch beide auf derselben Existenzebene befindet. Solange du über dieses Wissen verfügst, kann das Ziel sich nicht vor dir verstecken, und wenn es unsichtbar ist, gibt ihm dieser Zustand gegen dich keine Vorzüge.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W8 erhöht.', 'Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gedankenleere', 'GEDANKENLEERE', 8, 'Bannzauber', 'Aktion', 'Berührung', 'V, G', NULL, '24 Stunden', 0, 0, 'Eine bereitwillige Kreatur, die du berührst, ist für die Wirkungsdauer gegen psychischen Schaden und den Zustand Bezaubert immun. Das Ziel ist außerdem nicht von Effekten zum Erkennen seiner Gefühle und Gedanken, seiner Gesinnung sowie zum Bestimmen seines Aufenthaltsorts auf magische Art betroffen. Kein Zauber - auch nicht Wunsch - kann Informationen über das Ziel sammeln, es aus der Ferne beobachten oder seinen Verstand kontrollieren.', '', 'Barde, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gedankensplitter', 'GEDANKENSPLITTER', 0, 'Verzauberungzauber', 'Aktion', '18 Meter', 'V', NULL, '1 Runde', 0, 0, 'Du versuchst, den Verstand einer Kreatur in Reichweite, die du sehen kannst, vorübergehend zu zersplittern. Das Ziel muss einen Intelligenzrettungswurf bestehen, oder es erleidet 1W6 psychischen Schaden und muss 1W4 von seinem nächsten Rettungswurf vor Ende deines nächsten Zugs abziehen.', 'Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.', 'Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.","type":"cantrip_scaling","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gef-hle-bes-nftigen', 'GEFÜHLE BESÄNFTIGEN', 2, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Jeder Humanoide innerhalb einer Kugel mit einem Radius von sechs Metern um einen Punkt deiner Wahl in Reichweite muss einen Charismarettungswurf bestehen, oder einer der folgenden Effekte wirkt auf ihn (wähle für jede Kreatur einen Effekt aus): Die Kreatur ist für die Wirkungsdauer gegen die Zustände Bezaubert und Verängstigt immun. Wenn die Kreatur bereits bezaubert oder verängstigt war, werden diese Zustände für die Wirkungsdauer unterdrückt. Die Kreatur wird Kreaturen deiner Wahl gegenüber gleichgültig, denen es feindlich gesinnt war. Diese Gleichgültigkeit endet, wenn das Ziel Schaden erleidet oder sieht, dass seine Verbündeten Schaden erleiden. Wenn der Zauber endet, wird die Haltung der Kreatur wieder normal.', '', 'Barde, Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gegenstand-aufsp-ren', 'GEGENSTAND AUFSPÜREN', 2, 'Erkenntniszauber', 'Aktion', 'Selbst', 'V, G, M (ein gegabelter Zweig)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Beschreibe oder nenne einen Gegenstand, mit dem du vertraut bist. Sofern der Gegenstand sich im Abstand von bis zu 300 Metern von dir befindet, spürst du, in welcher Richtung sein Aufenthaltsort liegt. Wenn sich der Gegenstand in Bewegung befindet, weißt du, in welche Richtung er bewegt wird. Der Zauber kann einen bestimmtem dir bekannten Gegenstand aufspüren, sofern du diesen mindestens einmal aus der Nähe — aus höchstens neun Metern Entfernung - gesehen hast. Alternativ kann der Zauber den nächstgelegenen Gegenstand einer bestimmten Art aufspüren, wie eine bestimmte Art von Kleidung, Möbel, Schmuck, Waffe oder Werkzeug. Dieser Zauber kann den Gegenstand nicht aufspüren, wenn Blei den direkten Weg zwischen dir und dem Gegenstand blockiert — egal, welchen Durchmesser es hat.', '', 'Barde, Druide, Kleriker, Magier, Paladin, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gegenst-nde-beleben', 'GEGENSTÄNDE BELEBEN', 5, 'Verwandlungszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Auf deinen Befehl hin werden Gegenstände belebt. Wähle eine Anzahl nichtmagischer Gegenstände in Reichweite aus, die nicht getragen oder gehalten werden, nicht an einer Oberfläche fixiert und kleiner als gigantisch sind. Die maximale Anzahl von Gegenständen entspricht deinem Zauberwirken-Attributsmodifikator. Dabei zählt ein Ziel von höchstens mittelgroßer Größe als ein Gegenstand, ein großes Ziel zählt als zwei Gegenstände, und ein riesiges Ziel zählt als drei Gegenstände. Jedes Ziel wird belebt, es erhält Beine und wird zu einem Konstrukt, das den Wertekasten eines belebten Gegenstands verwendet. Diese Kreatur steht unter deiner Kontrolle, bis der Zauber endet oder seine Trefferpunkte auf 0 sinken. Jede Kreatur, die du mit diesem Zauber erzeugst, ist mit dir und deinen Verbündeten verbündet. Im Kampf teilt sie deinen Initiativewert und ist unmittelbar nach dir am Zug. BELEBTER GEGENSTAND Konstrukt von höchstens riesiger Größe, gesinnungslos', 'Für jeden Zauberplatzgrad über dem 5. wird der Hiebschaden der Kreatur um 1W4 (höchstens mittelgroß), 1W6 (groß) oder 1W12 (riesig) erhöht.', 'Barde, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. wird der Hiebschaden der Kreatur um 1W4 (höchstens mittelgroß), 1W6 (groß) oder 1W12 (riesig) erhöht.","type":"damage","dice":"1W4"},"summon":{"ac":null,"hp":null},"summon_text":"RK 15\nTP 10 (höchstens mittelgroß), 20 (groß), 40 (riesig)\nBewegungsrate 9 m\nMOD RW\tMOD\nMOD\nBW\nSTÄ\n+3\n+3\nGES 10\n+0\n+0\nKON 10\n+0\n+0\nINT\n-4\n-4\nWEI 3\n-4\n-4\nCHA 1\n-5\n-5\nImmunitäten Gift, Psychisch; Bezaubert, Erschöpft, Gelähmt,\nVerängstigt, Vergiftet\nSinne Blindsicht 9 m, Passive Wahrnehmung 6\nSprachen Versteht die Sprachen, die du sprichst\nHG - (EP 0, ÜB entspricht deinem Übungsbonus)\nAKTIONEN\nHieb: Nahkampfangriffswurf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: Energieschaden in Höhe von 1W4+3 (höchstens mittelgroß), 2W6+3 + deinem Zauberwirken-Attributsmodifikator (groß) oder 2W12+3 + deinem Zauberwirken-Attributsmodifikator (riesig).\nBis der Zauber endet, kannst du als Bonusaktion jede Kreatur, die du mit diesem Zauber erschaffen hast und die sich im Abstand von bis zu 150 Metern von dir befindet, mental befehligen. Handelt es sich um mehrere Kreaturen, so kannst du beliebig viele von ihnen gleichzeitig befehligen, wobei jeweils der gleiche Befehl erteilt wird. Wenn du keine Befehle erteilst, führt die Kreatur die Ausweichaktion aus und bewegt sich nur, um Schaden zu vermeiden. Wenn die Trefferpunkte der Kreatur auf 0 sinken, nimmt sie wieder ihre unbelebte Form an. Überschüssiger Schaden wird auf diese Form übertragen.","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gegenzauber', 'GEGENZAUBER', 3, 'Bannzauber', 'Reaktion, die du ausführst, wenn du siehst, wie', '', '', NULL, '', 0, 0, 'eine Kreatur im Abstand von bis zu 18 Metern von dir einen Zauber mit Gesten-, Materialoder Verbalkomponenten wirkt Reichweite: 18 Meter Komponenten: S Wirkungsdauer: Unmittelbar Du versuchst, eine Kreatur beim Wirken eines Zaubers zu unterbrechen. Die Kreatur führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so löst der Zauber sich ohne Effekt auf, und die Aktion, Bonusaktion oder Reaktion, mit der er gewirkt wurde, ist vergeudet. Wenn der Zauber mit einem Zauberplatz gewirkt wurde, so wird dieser nicht verbraucht.', '', 'Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('geh-ssiger-spott', 'GEHÄSSIGER SPOTT', 0, 'Verzauberungzauber', 'Aktion', '18 Meter', 'V', NULL, 'Unmittelbar', 0, 0, 'Du entfesselst eine Reihe von Beleidigungen mit subtilen Verzauberungen auf eine Kreatur in Reichweite, die du hören oder sehen kannst. Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es erleidet 1W6 psychischen Schaden und ist beim nächsten Angriffswurf vor dem Ende seines nächsten Zugs im Nachteil.', 'Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.', 'Barde', '{"scaling":{"text":"Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.","type":"cantrip_scaling","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('geisterross', 'GEISTERROSS', 3, 'Illusionszauber', '1 Minute oder Ritual', '9 Meter', 'V, G', NULL, '1 Stunde', 0, 1, '270 KAPITEL 7 1 ZAUBER GEIS rERRO55, In einem freien Bereich deiner Wahl in Reichweite, den du sehen kannst, erscheint eine große, quasi reale, pferdeartige Kreatur. Du bestimmst das Aussehen der Kreatur. Sie ist stets mit einem Sattel und Zaumzeug ausgerüstet. Durch diesen Zauber erschaffene Ausrüstung löst sich in Rauch auf, wenn sie weiter als drei Meter vom Ross entfernt wird. Für die Wirkungsdauer kann eine Kreatur deiner Wahl (auch du selbst) auf dem Ross reiten. Die Kreatur verwendet den Wertekasten eines Reitpferds (siehe Anhang B), verfügt jedoch über eine Bewegungsrate von 30 Metern und kann in einer Stunde 20,8 Kilometer zurücklegen. Wenn der Zauber endet, verblasst das Ross allmählich, und der Reiter hat eine Minute zum Absteigen Zeit. Wenn das Ross Schaden erleidet, endet der Zauber vorzeitig.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gestalt-ver-ndern', 'GESTALT VERÄNDERN', 2, 'Verwandlungszauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du veränderst deine physische Gestalt. Entscheide dich für eine der folgenden Optionen. Die Effekte bleiben für die Wirkungsdauer bestehen. Währenddessen kannst du eine magische Aktion ausführen, um die ausgewählte Option durch eine andere zu ersetzen. Aquatische Anpassung: Dir wachsen Kiemen und Schwimmhäute. Du kannst unter Wasser atmen und erhältst eine Schwimmbewegungsrate in Höhe deiner Bewegungsrate. Aussehen ändern: Du veränderst dein Aussehen. Du bestimmst, wie du aussiehst: Größe, Gewicht, Gesichtszüge, Stimme, Haarlänge, Haarfarbe und andere besondere Merkmale. Du kannst dich auch als Mitglied einer anderen Spezies ausgeben. Deine Spielwerte bleiben jedoch unverändert. Du kannst nicht als Kreatur einer anderen Größenkategorie erscheinen. Deine grundlegende Gestalt bleibt ebenfalls gleich, beispielsweise kannst du dich mit diesem Zauber nicht von einem Zweibeiner in einen Vierbeiner verwandeln. Für die Wirkungsdauer kannst du eine magische Aktion ausführen, um dein Erscheinungsbild erneut auf diese Art zu ändern. Natürliche Waffen: Dir wachsen Klauen (Hieb), Fangzähne (Stich), Hörner (Stich) oder Hufe (Wucht). Verwendest du deinen waffenlosen Angriff, um mit diesen natürlichen Waffen Schaden zu bewirken, so bewirkt dieser Angriff 1W6 Schaden der in Klammern angegebenen Art statt des normalen Schadens deines waffenlosen Angriffs, und du verwendest für die Angriffsund Schadenswürfe deinen Zauberwirken-Attributsmodifikator statt Stärke.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gestaltwandel', 'GESTALTWANDEL', 9, 'Verwandlungszauber', 'Aktion', 'Selbst', 'V, G, M (ein Jadediadem im Wert von mindestens 1.500 GM)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du nimmst die Gestalt einer anderen Kreatur an, bis der Zauber endet oder du eine magische Aktion ausführst, um eine andere mögliche Gestalt anzunehmen. Bei der neuen Gestalt muss es sich um eine Kreatur handeln, deren Herausforderungsgrad höchstens deiner Stufe oder deinem Herausforderungsgrad entspricht. Du musst diese Art von Kreatur mindestens einmal gesehen haben, und es darf sich weder um ein Konstrukt noch um einen Untoten handeln. Wenn du dich verwandelst, erhältst du eine Anzahl von temporären Trefferpunkten, die der Anzahl von Trefferpunkten der ersten neuen Gestalt entspricht. Wenn nach dem Gestaltwandel von diesen temporären Trefferpunkten noch welche übrig sind, verschwinden sie. Deine Spielwerte werden durch den Wertekasten der ausgewählten Gestalt ersetzt. Du behältst jedoch Folgendes bei: Kreaturentyp, Gesinnung, Persönlichkeit, Werte für Intelligenz, Weisheit und Charisma, Trefferpunkte, Trefferpunktewürfel, Übungen sowie die Fähigkeit zu kommunizieren. Auch das Merkmal Zauberwirken behältst du bei, sofern du darüber verfügst. Bei der Verwandlung entscheidest du, ob deine Ausrüstung zu Boden fällt oder ihre Form und Größe ändert, sodass sie zu deiner neuen Gestalt passt, solange du diese beibehältst. ^ ^ GIFT UND KRANKHEIT ENTDECKEN Erkenntniszauber I. Grades (Druide, Kleriker, Paladin, Waldläufer)', 'Der Schaden wird um jeweils 1W12 erhöht, wenn du die 5. (2W12), die 11. (3W12) und die 17. (4W12) Stufe erreichst. RIVALISIERENDE MAGIER NUTZEN GESTALTWANDEL, UM SICH ALS BETRACHTER UND ES EHIR MAGISCH 1U DUELLIEREN. Zeitaufwand: Aktion oder Ritual Reichweite: Selbst Komponenten: V, G, M (ein Eibenblatt) Wirkungsdauer: Konzentration, bis zu 10 Minuten Für die Wirkungsdauer nimmst du die Position von Giften, giftigen Kreaturen und magischen Ansteckungen im Abstand von bis zu neun Metern von dir wahr. Du erkennst auch die jeweilige Art des Gifts, der giftigen Kreatur oder der Ansteckung. Der Zauber wird von 30 Zentimetern Stein, Erde oder Holz sowie von 2,5 Zentimetern Metall oder einer dünnen Bleischicht blockiert.', 'Druide, Magier', '{"scaling":{"text":"Der Schaden wird um jeweils 1W12 erhöht, wenn du die 5. (2W12), die 11. (3W12) und die 17. (4W12) Stufe erreichst. RIVALISIERENDE MAGIER NUTZEN GESTALTWANDEL, UM SICH ALS BETRACHTER UND ES EHIR MAGISCH 1U DUELLIEREN. Zeitaufwand: Aktion oder Ritual Reichweite: Selbst Komponenten: V, G, M (ein Eibenblatt) Wirkungsdauer: Konzentration, bis zu 10 Minuten Für die Wirkungsdauer nimmst du die Position von Giften, giftigen Kreaturen und magischen Ansteckungen im Abstand von bis zu neun Metern von dir wahr. Du erkennst auch die jeweilige Art des Gifts, der giftigen Kreatur oder der Ansteckung. Der Zauber wird von 30 Zentimetern Stein, Erde oder Holz sowie von 2,5 Zentimetern Metall oder einer dünnen Bleischicht blockiert.","type":"damage","dice":"1W12"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('gift-verspr-hen', 'GIFT VERSPRÜHEN', 0, 'Nekromantiezauber', 'Aktion', '9 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du sprühst giftigen Nebel auf eine Kreatur in Reichweite. Führe einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 1W12 Giftschaden.', '', 'Druide, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('glyphe-des-schutzes', 'GLYPHE DES SCHUTZES', 3, 'Bannzauber', '1 Stunde', 'Berührung', 'V, G, M (Diamantpulver im Wert von', NULL, 'Bis der Zauber gebannt oder die Glyphe', 0, 0, 'ausgelöst wird Du erzeugst eine Glyphe, die später einen magischen Effekt bewirkt. Diese Glyphe zeichnest du entweder auf eine Oberfläche (wie einen Tisch oder den Fußboden) oder in einen Gegenstand (wie ein Buch oder eine Truhe), der geschlossen werden kann, um die Glyphe zu verbergen. Die Glyphe kann höchstens in einem Radius von 1,5 Metern wirken. Wenn die Oberfläche oder der Gegenstand um mehr als drei Meter von der Stelle entfernt wird, an der du den Zauber gewirkt hast, wird die Glyphe zerstört, und der Zauber endet, ohne ausgelöst zu werden. Die Glyphe ist kaum wahrnehmbar. Es ist ein erfolgreicher Weisheitswurf (Wahrnehmung) gegen deinen Zauberrettungswurf-SG erforderlich, um sie zu bemerken. 27 KAPITEL 7 I ZAUBER Wenn du die Glyphe zeichnest, bestimmst du ihren Auslöser und legst fest, ob es sich um eine explosive Rune oder eine Zauberglyphe wie unten beschrieben handelt. Auslöser bestimmen: Wenn du den Zauber wirkst, bestimmst du einen Auslöser für die Glyphe. Glyphen auf Oberflächen werden meist dadurch ausgelöst, dass jemand sie berührt oder betritt, einen Gegenstand bewegt, der die Glyphe verdeckt, oder einen Mindestabstand zur Glyphe unterschreitet. Glyphen in Gegenständen werden meist dadurch ausgelöst, dass jemand den Gegenstand öffnet oder die Glyphe sieht. Sobald die Glyphe ausgelöst wurde, endet der Zauber. Du kannst den Auslöser verfeinern, sodass die Glyphe nur von bestimmten Kreaturentypen ausgelöst wird (beispielsweise könnte die Glyphe nur auf Aberrationen wirken). Du kannst auch Bedingungen festlegen, unter denen die Glyphe nicht ausgelöst wird, beispielsweise wenn ein bestimmtes Kennwort ausgesprochen wird. Explosive Rune: Wird die Glyphe ausgelöst, so explodiert ihre magische Energie in einer Kugel mit einem Radius von sechs Metern um sie. Jede Kreatur in diesem Bereich führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 5W8 Blitz-, Feuer-, Kälte-, Säureoder Schallschaden (nach deiner Wahl beim Schreiben der Glyphe), anderenfalls die Hälfte. Zauberglyphe: Beim Erschaffen der Glyphe kannst du einen vorbereiteten Zauber des höchstens 3. Grades wirken und in der Glyphe speichern. Der Zauber muss eine einzelne Kreatur oder einen Bereich zum Ziel haben. Wenn er auf diese Weise gewirkt wird, hat der Zauber keinen unmittelbaren Effekt. Er wirkt erst, wenn die Glyphe ausgelöst wird. Wenn der Zauber ein Ziel braucht, wird die Kreatur ausgewählt, welche die Glyphe auslöst. Wirkt der Zauber auf einen Bereich, so ist dieser auf die Kreatur zentriert. Wenn der Zauber feindlich gesinnte Kreaturen beschwört oder schädliche Gegenstände oder Fallen erschafft, erscheinen sie so nahe wie möglich beim Eindringling und greifen ihn an. Erfordert der Zauber Konzentration, so hält diese bis zum Ende der vollen Wirkungsdauer an.', 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden einer explosiven Rune um 1W8 erhöht. In einer Zauberglyphe kannst du Zauber bis zum Grad des Zauberplatzes speichern, den du zum Wirken der Glyphe des Schutzes verwendet hast.', 'Barde, Kleriker, Magier', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden einer explosiven Rune um 1W8 erhöht. In einer Zauberglyphe kannst du Zauber bis zum Grad des Zauberplatzes speichern, den du zum Wirken der Glyphe des Schutzes verwendet hast.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('g-ttliche-f-hrung', 'GÖTTLICHE FÜHRUNG', 0, 'Erkenntniszauber', 'Aktion', 'Berührung', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du berührst eine bereitwillige Kreatur und wählst eine Fertigkeit aus. Bis der Zauber endet, fügt die Kreatur jedem Attributswurf, der die ausgewählte Fertigkeit verwendet, 1W4 hinzu.', '', 'Druide, Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('g-ttliche-gunst', 'GÖTTLICHE GUNST', 1, 'Verwandlungszauber', 'Bonusaktion', 'Selbst', 'V, G', NULL, '1 Minute', 0, 0, 'Für die Wirkungsdauer bewirkst du mit Angriffen, die du mit Waffen ausführst, bei jedem Treffer zusätzlich 1W4 gleißenden Schaden. GÖTTLICHES NIEDERSTRECKEN Hervorrufungszauber 1. Grades (Paladin) Zeitaufwand: Bonusaktion, die du sofort ausführst, wenn du ein Ziel mit einer Nahkampfwaffe oder einem waffenlosen Angriff getroffen hast Reichweite: Selbst Komponenten: V Wirkungsdauer: Unmittelbar Das Ziel erleidet zusätzlich 2W8 gleißenden Schaden durch den Angriff. Der zusätzliche Schaden wird um 1W8 erhöht, wenn das Ziel ein Unhold oder ein Untoter ist.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W8 erhöht.', 'Paladin', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('g-ttliches-wort', 'GÖTTLICHES WORT', 7, 'Hervorrufungszauber', 'Bonusaktion', '9 Meter', 'V', NULL, 'Unmittelbar', 0, 0, 'Du sprichst ein Wort, das von der Macht der Oberen Ebenen erfüllt ist. Jede Kreatur deiner Wahl in Reichweite führt einen Charismarettungswurf aus. Scheitert der Wurf, so erleidet ein Ziel mit höchstens 50 Trefferpunkten einen Effekt gemäß seiner aktuellen Trefferpunkte wie in der Tabelle „Effekte von Göttliches Wort" gezeigt. Unabhängig von ihren Trefferpunkten werden Ziele, die celestische Wesen, Elementare, Feenwesen oder Unholde sind und deren Rettungswurf scheitert, auf ihre Heimatebene verbannt, sofern sie sich nicht bereits dort befinden. In den nächsten 24 Stunden können sie nur mit dem Zauber Wunsch auf deine aktuelle Ebene zurückkehren. EFFEKTE VON GÖTTLICHES WORT Trefferpunkte Effekt 0-20 Das Ziel stirbt. 21-30 Das Ziel ist eine Stunde lang betäubt, blind und taub. 31-40 Das Ziel ist zehn Minuten lang blind und taub. 41-50 Das Ziel ist eine Minute lang taub. rtGUTE BEEREN', '', 'Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('ein-weiblicher-zwergenpalaoin-erm-chtigt-seine-waffe-mit-g-ttlichem-niederstrecken-und-entfesselt-heiligen-zorn-auf-diabolische-mezzolothen', 'EIN WEIBLICHER ZWERGENPALAOIN ERMÄCHTIGT SEINE WAFFE MIT GÖTTLICHEM NIEDERSTRECKEN UND ENTFESSELT HEILIGEN ZORN AUF DIABOLISCHE MEZZOLOTHEN.', 1, 'Beschwörungszauber', 'Aktion', 'Selbst', 'V, G, M (ein Mistelzweig)', NULL, '24 Stunden', 0, 0, 'In deiner Hand erscheinen zehn Beeren, die für die Wirkungsdauer von Magie erfüllt sind. Kreaturen können eine Bonusaktion ausführen, um eine Beere zu verzehren. Dadurch wird 1 Trefferpunkt wiederhergestellt, und die Beere bietet der Kreatur genügend Nahrung für einen Tag. Wenn der Zauber endet, verschwinden Beeren, die nicht verzehrt wurden. GUTES UND BÖSES BANNEN Bannzauber 5. Grades (Kleriker, Paladin) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G, M (Silberund Eisenpulver) Wirkungsdauer: Konzentration, bis zu 1 Minute Für die Wirkungsdauer sind celestische Wesen, Elementare, Feenwesen, Unholde und Untote bei Angriffswürfen gegen dich im Nachteil. Du kannst den Zauber vorzeitig beenden, indem du einen der folgenden besonderen Effekte verwendest: Vertreiben: Als magische Aktion zielst du auf eine Kreatur im Abstand von bis zu 1,5 Metern von dir, die du sehen kannst und die einen der genannten Kreaturentypen hat. Das Ziel muss einen Charismarettungswurf bestehen, oder es wird auf seine Heimatebene verbannt, sofern es sich nicht bereits dort befindet. Untote werden in den Schatten­saum und Feenwesen in die Feenwildnis verbannt, sofern sie sich nicht schon auf ihrer Heimatebene befinden. Verzauberungsmagie brechen: Als magische Aktion berührst du eine Kreatur in Reichweite, die von mindestens einer solchen Kreatur bezaubert, verängstigt oder besessen ist. Das Ziel ist nun nicht mehr von solchen Kreaturen besessen, bezaubert oder verängstigt. GUTES UND BÖSES ENTDECKEN Erkenntniszauber 1. Grades (Kleriker, Paladin) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 10 Minuten Für die Wirkungsdauer spürst du, ob und wo genau sich Aberrationen, celestische Wesen, Elementare, Feenwesen, Unholde oder Untote im Abstand von bis zu neun Metern von dir befinden. Außerdem spürst du, ob und wo genau der Zauber Weihen in diesem Bereich aktiv ist. Der Zauber wird von 30 Zentimetern Stein, Erde oder Holz sowie von 2,5 Zentimetern Metall oder einer dünnen Bleischicht blockiert.', '', 'Druide, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('halbebene', 'HALBEBENE', 8, 'Beschwörungszauber', 'Aktion', '18 Meter', 'S', NULL, '1 Stunde', 0, 0, 'Du erschaffst auf einer festen Oberfläche in Reichweite, die du sehen kannst, eine mittelgroße schattenhafte Tür. Diese Tür kann geöffnet und geschlossen werden. Sie führt zu einer Halbebene: einem leeren Raum mit neun Metern Kantenlänge aus Holz oder Stein (nach deiner Wahl). Wenn der Zauber endet, verschwindet die Tür, und alle Gegenstände in der Halbebene verbleiben dort. Auch Kreaturen bleiben darin, sofern sie nicht entscheiden, dass sie durch die Tür geschoben werden, wenn diese verschwindet. In diesem Fall gelangen sie in freie Bereiche, die dem Bereich der verschwundenen Tür am nächsten liegen, und sind liegend. Jedes Mal, wenn du diesen Zauber wirkst, kannst du entweder eine neue Halbebene erschaffen oder die schattenhafte Tür mit einer Halbebene verbinden, die du beim vorigen Wirken des Zaubers erschaffen hast. Sind dir außerdem Natur und Inhalt einer Halbebene bekannt, die von einer anderen Kreatur mit diesem Zauber erschaffen wurde, so kannst du die schattenhafte Tür stattdessen auch mit dieser Halbebene verbinden.', '', 'Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('hast', 'HAST', 3, 'Verwandlungszauber', 'Aktion', '9 Meter', 'V, G, M (ein Span Süßholzwurzel)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Wähle eine bereitwillige Kreatur in Reichweite aus, die du sehen kannst. Für die Wirkungsdauer ist die Bewegungsrate des Ziels verdoppelt. Außerdem erhält das Ziel einen Bonus von +2 auf seine Rüstungsklasse, ist bei Geschicklichkeitsrettungswürfen im Vorteil und kann in jedem seiner Züge eine zusätzliche Aktion ausführen. Diese Aktion kann nur für Folgendes eingesetzt werden: Angriff (nur ein Angriff), Rückzug, Spurt, Verstecken, Verwenden. Wenn der Zauber endet, ist das Ziel bis zum Ende seines nächsten Zugs kampfunfähig und hat eine Bewegungsrate von 0, weil es von einer Welle der Lethargie ergriffen wird.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('heilendes-wort', 'HEILENDES WORT', 1, 'Bannzauber', 'Bonusaktion', '18 Meter', 'V', NULL, 'Unmittelbar', 0, 0, 'Eine Kreatur deiner Wahl in Reichweite, die du sehen kannst, erhält Trefferpunkte in Höhe von 2W4 plus deinem Zauberwirken-Attributsmodifikator zurück.', 'Für jeden Zauberplatzgrad über dem 1. wird die Heilung um 2W4 Trefferpunkte erhöht.', 'Barde, Druide, Kleriker', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird die Heilung um 2W4 Trefferpunkte erhöht.","type":"damage","dice":"2W4"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('heilige-aura', 'HEILIGE AURA', 8, 'Bannzauber', 'Aktion', 'Selbst', 'V, G, M (eine Reliquie im Wert von mindestens 1.000 GM)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Für die Wirkungsdauer geht von dir eine Aura mit einer Ausströmung von neun Metern aus. In der Aura sind Kreaturen deiner Wahl bei allen Rettungswürfen im Vorteil, und andere Kreaturen sind bei Angriffswürfen gegen sie im Nachteil. Wenn außerdem ein Unhold oder ein Untoter eine betroffene Kreatur mit einem Nahkampfangriffswurf trifft, muss der Angreifer einen Konstitutionsrettungswurf bestehen, oder er ist bis zum Ende seines nächsten Zugs blind.', '', 'Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('heilige-flamme', 'HEILIGE FLAMME', 0, 'Hervorrufungzauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Auf eine Kreatur in Reichweite, die du sehen kannst, schießt ein Strahl aus gleißenden Flammen herab. Das Ziel muss einen Geschicklichkeitsrettungswurf bestehen, oder es erleidet 1W8 gleißenden Schaden. Teildeckung sowie Dreivierteldeckung bieten ihm bei diesem Rettungswurf keine Vorzüge.', 'Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.', 'Kleriker', '{"scaling":{"text":"Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.","type":"cantrip_scaling","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('heiliges-gespr-ch', 'HEILIGES GESPRÄCH', 5, 'Erkenntniszauber', '1 Minute oder Ritual', 'Selbst', 'V, G, M (Weihrauch)', NULL, '1 Minute', 0, 1, 'Du nimmst mit einer Gottheit oder dem Stellvertreter einer Gottheit Kontakt auf und stellst bis zu drei Fragen, die mit Ja oder Nein beantwortet werden können. Du musst deine Fragen stellen, bevor der Zauber endet. Du erhältst auf jede Frage eine korrekte Antwort. Göttliche Wesen sind nicht unbedingt allwissend. Bezieht sich die Frage auf Informationen, die das Wissen der Gottheit übersteigen, so könntest du „Unklar" als Antwort erhalten. Falls eine Antwort mit nur einem Wort irreführend sein oder nicht dem Interesse der Gottheit entsprechen könnte, kann der SL stattdessen einen kurzen Satz als Antwort geben. Wenn du den Zauber vor einer langen Rast häufiger als einmal wirkst, erhöht dies das Risiko, keine Antwort zu erhalten, mit jedem Wirken nach dem ersten um jeweils 25 Prozent.', '', 'Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('heiligtum', 'HEILIGTUM', 1, 'Bannzauber', 'Bonusaktion', '9 Meter', 'V, G, M (eine Spiegelscherbe)', NULL, '1 Minute', 0, 0, 'Du schützt eine Kreatur in Reichweite. Für die Wirkungsdauer muss jede Kreatur, die mit einem Angriffswurf oder Schadenszauber auf die geschützte Kreatur zielt, einen Weisheitsrettungswurf bestehen. Scheitert der Wurf, so muss sie ein neues Ziel auswählen, oder sie verliert den Angriff oder Zauber. Dieser Zauber schützt das Ziel nicht vor Wirkungsbereichen. Der Zauber endet, wenn die geschützte Kreatur einen Angriffswurf ausführt, einen Zauber wirkt oder Schaden bewirkt.', '', 'Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('heilung', 'HEILUNG', 6, 'Bannzauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Wähle eine Kreatur in Reichweite aus, die du sehen kannst. Positive Energie durchströmt das Ziel, das 70 Trefferpunkte zurückerhält. Außerdem beendet dieser Zauber die Zustände Blind, Taub und Vergiftet beim Ziel.', 'Für jeden Zauberplatzgrad über dem 6. wird die Heilung um 10 Trefferpunkte erhöht.', 'Druide, Kleriker', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. wird die Heilung um 10 Trefferpunkte erhöht."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('heldenmahl', 'HELDENMAHL', 6, 'Beschwörungszauber', '10 Minuten', 'Selbst', 'V, G, M (eine mit Edelsteinen verzierte Schale im Wert von mindestens 1.000 GM, die der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 0, 'Du beschwörst ein Festmahl, das auf einer Oberfläche in einem freien Würfel von drei Metern Kantenlänge neben dir erscheint. Der Verzehr des Festmahls dauert eine Stunde. Nach dieser Dauer verschwindet das Mahl, und die positiven Effekte treten in Kraft. Bis zu zwölf Kreaturen können am Festmahl teilnehmen. Eine Kreatur, die teilnimmt, erhält mehrere Vorzüge, die 24 Stunden lang anhalten: Sie ist gegen Giftschaden resistent und gegen die Zustände Verängstigt und Vergiftet immun. Zudem wird ihr Trefferpunktemaximum um 2W10 erhöht, und sie erhält die gleiche Anzahl von Trefferpunkten dazu. DE.R ZAUBER HELOENMANL BEREITET ABENTEURER AUF RUHMREICHE TATEN VOR', '', 'Barde, Druide, Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('heldenmut', 'HELDENMUT', 1, 'Verzauberungszauber', 'Aktion', 'Berührung', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du berührst eine bereitwillige Kreatur und erfüllst sie mit Tapferkeit. Für die Wirkungsdauer ist die Kreatur gegen den Zustand Verängstigt immun und erhält zu Beginn jedes ihrer Züge temporäre Trefferpunkte in Höhe deines Zauberwirken-Attributsmodifikators.', 'Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen.', 'Barde, Paladin', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('hellsehen', 'HELLSEHEN', 3, 'Erkenntniszauber', '10 Minuten', '1,6 Kilometer', 'V, G, M (ein Fokus im Wert von mindestens 100 GM — entweder ein juwelenbesetztes Horn zum Hören oder ein Glasauge zum Sehen)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erschaffst an einem dir bekannten Ort (den du bereits gesehen oder besucht hast) oder an einem offensichtlichen Ort, der dir unbekannt ist (beispielsweise hinter einer Tür, einer Ecke oder in einem Hain), einen unsichtbaren Sensor in Reichweite. Der Sensor ist immateriell und unverwundbar. Er bleibt für die Wirkungsdauer bestehen. Wenn du den Zauber wirkst, wähle Hören oder Sehen aus. Du nimmst den ausgewählten Sinn durch den Sensor wahr, als würdest du dich in dessen Bereich befinden. Als Bonusaktion kannst du zwischen Hören und Sehen wechseln. Eine Kreatur, die den Sensor sehen kann (beispielsweise mit Wahrer Blick oder Unsichtbares sehen), sieht eine etwa faustgroße leuchtende Kugel.', '', 'Barde, Kleriker, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('hexenpfeil', 'HEXENPFEIL', 1, 'Hervorrufungszauber', 'Aktion', '18 Meter', 'V, G, M (Zweig von einem Baum, der vom Blitz getroffen wurde)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Auf eine Kreatur in Reichweite schießt ein Strahl aus knisternder Energie zu und bildet einen beständigen Lichtbogen zwischen dir und dem Ziel. Führe einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 2W12 Blitzschaden. Du kannst in jedem deiner folgenden Züge eine Bonusaktion ausführen, um dem Ziel automatisch 1W12 Blitzschaden zuzufügen, auch dann, wenn der erste Angriff gescheitert ist. EIN HALSUNG-HEXENMEISTER SET-LT EINEN ZOMBIE MIT DEN GREIFENDEN BLITZEN VON HEXENPFEIL UNTER STROM. Der Zauber endet, wenn das Ziel sich außer Reichweite des Zaubers oder in vollständige Deckung vor dir begibt.', 'Für jeden Zauberplatzgrad über dem 1. wird der anfängliche Schaden um 1W12 erhöht.', 'Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der anfängliche Schaden um 1W12 erhöht.","type":"damage","dice":"1W12"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('h-llischer-tadel', 'HÖLLISCHER TADEL', 1, 'Hervorrufungszauber', 'Reaktion, die du ausführst, wenn du Schaden', '', '', NULL, '', 0, 0, 'durch eine Kreatur im Abstand von bis zu 18 Metern von dir erlitten hast, die du sehen kannst Reichweite: 18 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Die Kreatur, die dir Schaden zugefügt hat, ist kurz von grünen Flammen umgeben. Sie führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 2W1O Feuerschaden, anderenfalls die Hälfte.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W1O erhöht. 278 KAPITEL 7 I ZAUBER', 'Hexenmeister', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W1O erhöht. 278 KAPITEL 7 I ZAUBER","type":"damage","dice":"1W1"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('hunger-von-hadar', 'HUNGER VON HADAR', 3, 'Beschwörungszauber', 'Aktion', '45 Meter', 'V, G, M (ein eingelegtes Tentakel)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du öffnest ein Tor zum Fernen Reich, einer Region voller unaussprechlicher Schrecken. Eine Kugel der Dunkelheit mit einem Radius von sechs Metern erscheint um einen Punkt in Reichweite und bleibt für die Wirkungsdauer bestehen. Die Kugel ist schwieriges Gelände und von verstörendem Gewisper und Schlürfgeräuschen erfüllt, die bis zu neun Meter weit zu hören sind. Weder magisches noch natürliches Licht kann den Bereich erhellen, und Kreaturen, die sich vollständig darin befinden, sind blind. Jede Kreatur, die ihren Zug im Bereich beginnt, erleidet 2W6 Kälteschaden. Jede Kreatur, die ihren Zug dort beendet, muss einen Geschicklichkeitsrettungswurf bestehen, oder sie erleidet 2W6 Säureschaden durch außerweltliche Tentakel.', 'Für jeden Zauberplatzgrad über dem 3. wird der Kälteoder der Säureschaden (nach deiner Wahl) um 1W6 erhöht. HÜTER DES GLAUBENS Beschwörungszauber 4. Grades (Kleriker) EIN AASIMAR-I1EXENMEISTER GIBT SEINE GEGNER OEM ZERSTÖRERISCHEN ZAUBER HUNGER VON MAOAR PREIS. Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V Wirkungsdauer: 8 Stunden In einem freien Bereich in Reichweite, den du sehen kannst, erscheint ein großer geisterhafter Wächter und schwebt dort für die Wirkungsdauer. Der Wächter besetzt diesen Bereich und ist unverwundbar. Er erscheint in einer Gestalt, die deiner Gottheit oder deinem Pantheon entspricht. Ein Gegner, der sich erstmals in seinem Zug in einen Bereich im Abstand von bis zu drei Metern vom Wächter bewegt oder seinen Zug dort beginnt, führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet er 20 gleißenden Schaden, anderenfalls die Hälfte. Wenn der Wächter insgesamt 60 Schaden bewirkt hat, verschwindet er.', 'Hexenmeister', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Kälteoder der Säureschaden (nach deiner Wahl) um 1W6 erhöht. HÜTER DES GLAUBENS Beschwörungszauber 4. Grades (Kleriker) EIN AASIMAR-I1EXENMEISTER GIBT SEINE GEGNER OEM ZERSTÖRERISCHEN ZAUBER HUNGER VON MAOAR PREIS. Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V Wirkungsdauer: 8 Stunden In einem freien Bereich in Reichweite, den du sehen kannst, erscheint ein großer geisterhafter Wächter und schwebt dort für die Wirkungsdauer. Der Wächter besetzt diesen Bereich und ist unverwundbar. Er erscheint in einer Gestalt, die deiner Gottheit oder deinem Pantheon entspricht. Ein Gegner, der sich erstmals in seinem Zug in einen Bereich im Abstand von bis zu drei Metern vom Wächter bewegt oder seinen Zug dort beginnt, führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet er 20 gleißenden Schaden, anderenfalls die Hälfte. Wenn der Wächter insgesamt 60 Schaden bewirkt hat, verschwindet er.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('hypnotisches-muster', 'HYPNOTISCHES MUSTER', 3, 'Illusionszauber', 'Aktion', '36 Meter', 'V, G, M (eine Prise Konfetti)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du erschaffst ein wirbelndes Muster aus Farben in einem Würfel mit neun Metern Kantenlänge in Reichweite. Das Muster erscheint für einen Moment und verschwindet dann. Jede Kreatur im Bereich, die das Muster sehen kann, muss einen Weisheitsrettungswurf bestehen, oder sie ist für die Wirkungsdauer bezaubert. Auf diese Art bezauberte Kreaturen sind kampfunfähig und haben eine Bewegungsrate von 0. Der Zauber endet bei einer betroffenen Kreatur, wenn sie Schaden erleidet oder jemand anders eine Aktion verwendet, um die Kreatur aus ihrer Starre zu rütteln.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('identifizieren', 'IDENTIFIZIEREN', 1, 'Erkenntniszauber', '1 Minute oder Ritual', 'Berührung', 'V, G, M (eine Perle im Wert von mindestens 100 GM)', NULL, 'Unmittelbar', 0, 1, 'Du berührst einen Gegenstand, solange du den Zauber wirkst. Falls es sich um einen magischen Gegenstand handelt, erkennst du seine Eigenschaften und ihre Anwendung, und du weißt, ob er Einstimmung erfordert und wie viele Ladungen er besitzt, falls zutreffend. Du erfährst, ob aktuell Zauber auf den Gegenstand wirken, und wenn ja, welche. Falls der Gegenstand durch einen Zauber erzeugt wurde, erfährst du dessen Namen. Wenn du stattdessen beim Wirken des Zaubers eine Kreatur durchgehend berührst, erfährst du, von welchen Zaubern sie derzeit betroffen ist, falls zutreffend.', '', 'Barde, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('illusionsschrift', 'ILLUSIONSSCHRIFT', 1, 'Illusionszauber', '1 Minute oder Ritual', 'Berührung', 'G, M (Tinte im Wert von mindestens 10 GM, die der Zauber verbraucht)', NULL, '10 Tage', 0, 1, 'Du schreibst auf Pergament, Papier oder ein anderes geeignetes Material und erfüllst die Schrift mit einer Illusion, die für die Wirkungsdauer bestehen bleibt. Für dich und alle Kreaturen, die du beim Wirken des Zaubers bestimmst, erscheint die Schrift ganz normal in deiner Handschrift und gibt den Inhalt wieder, den du niedergeschrieben hast. Für alle anderen erscheint eine unbekannte oder magische Schrift, die unverständlich ist. Alternativ kann die Illusion die Bedeutung der Nachricht, die Handschrift oder die Sprache verändern, in der die Nachricht verfasst ist. Die Sprache muss dir jedoch bekannt sein. Falls der Zauber gebannt wird, verschwinden sowohl die echte Schrift als auch die Illusion. Eine Kreatur mit Wahrer Blick kann die versteckte Nachricht lesen.', '', 'Barde, Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('insektenplage', 'INSEKTENPLAGE', 5, 'Beschwörungszauber', 'Aktion', '90 Meter', 'V, G, M (eine Heuschrecke)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'In einer Kugel mit einem Radius von sechs Metern um einen Punkt deiner Wahl in Reichweite erscheint ein Heuschreckenschwarm. Die Kugel bleibt für die Wirkungsdauer bestehen. Ihr Bereich ist leicht verschleiert sowie schwieriges Gelände. Wenn der Schwarm erscheint, führt jede Kreatur darin einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet sie 4W10 Stichschaden, anderenfalls die Hälfte. Wenn eine Kreatur den Bereich des Zaubers in einem Zug erstmals betritt oder ihren Zug darin beendet, führt sie diesen Rettungswurf ebenfalls aus. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen.', 'Für jeden Zauberplatzgrad über dem 5. werden gleißender Schaden und Schallschaden jeweils um 1W10 erhöht.', 'Druide, Kleriker, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. werden gleißender Schaden und Schallschaden jeweils um 1W10 erhöht.","type":"damage","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('kalte-hand', 'KALTE HAND', 0, 'Nekromantiezauber', 'Aktion', 'Berührung', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du kanalisierst Grabeskälte und führst einen Nahkampf-Zauberangriff auf ein Ziel in Reichweite aus. Bei einem Treffer erleidet das Ziel 1W10 nekrotischen Schaden und kann bis zum Ende deines nächsten Zugs keine Trefferpunkte zurückerhalten.', 'Der Schaden wird um jeweils 1W10 erhöht, wenn du die 5. (2W10), die 11. (3W10) und die 17. (4W10) Stufe erreichst. ri', 'Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Der Schaden wird um jeweils 1W10 erhöht, wenn du die 5. (2W10), die 11. (3W10) und die 17. (4W10) Stufe erreichst. ri","type":"cantrip_scaling","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('k-ltekegel', 'KÄLTEKEGEL', 5, 'Hervorrufungszauber', 'Aktion', 'Selbst', 'V, G, M (ein kleiner Kegel aus Kristall oder Glas)', NULL, 'Unmittelbar', 0, 0, 'Du entfesselst einen Stoß eiskalter Luft. Jede Kreatur in einem Kegel von 18 Metern, der von dir ausgeht, führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet sie 8W8 Kälteschaden, anderenfalls die Hälfte. Wird eine Kreatur durch diesen Zauber getötet, so wird sie zu einer gefrorenen Statue, bis sie auftaut.', 'Für jeden Zauberplatzgrad über dem 5. wird der Schaden um 1W8 erhöht.', 'Druide, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('k-ltestrahl', 'KÄLTESTRAHL', 0, 'Hervorrufungzauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Auf eine Kreatur in Reichweite schießt ein eisiger blau-weißer Lichtstrahl zu. Führe einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet es 1W8 Kälteschaden, und seine Bewegungsrate ist bis zum Beginn deines nächsten Zugs um drei Meter verringert.', 'Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.', 'Magier, Zauberer', '{"scaling":{"text":"Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.","type":"cantrip_scaling","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('kettenblitz', 'KETTENBLITZ', 6, 'Hervorrufungszauber', 'Aktion', '45 Meter', 'V, G, M (drei Silbernadeln)', NULL, 'Unmittelbar', 0, 0, 'Du schleuderst einen Blitz auf ein Ziel in Reichweite, das du sehen kannst. Dann springen drei Blitze von diesem Ziel auf bis zu drei weitere Ziele deiner Wahl über, die sich im Abstand von bis zu neun Metern vom ersten Ziel befinden müssen. Beim Ziel kann es sich um eine Kreatur oder einen Gegenstand handeln. Es kann nur von einem der Blitze getroffen werden. Jedes Ziel führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet es 10W8 Blitzschaden, anderenfalls die Hälfte.', 'Für jeden Zauberplatzgrad über dem 6. springt ein zusätzlicher Blitz vom ersten Ziel auf ein weiteres über. 282 KAP!TEL 7 ZAUBER KLINGENBANN Zaubertrick des Banns (Barde, Hexenmeister, Magier, Zauberer) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Minute Wann immer eine Kreatur während der Wirkungsdauer einen Angriffswurf gegen dich ausführt, zieht sie 1W4 vom Angriffswurf ab.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. springt ein zusätzlicher Blitz vom ersten Ziel auf ein weiteres über. 282 KAP!TEL 7 ZAUBER KLINGENBANN Zaubertrick des Banns (Barde, Hexenmeister, Magier, Zauberer) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Minute Wann immer eine Kreatur während der Wirkungsdauer einen Angriffswurf gegen dich ausführt, zieht sie 1W4 vom Angriffswurf ab.","type":"damage","dice":"1W4"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('klingenbarriere', 'KLINGENBARRIERE', 6, 'Hervorrufungszauber', 'Aktion', '27 Meter', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erschaffst eine Wand wirbelnder Klingen aus magischer Energie. Sie erscheint in Reichweite und bleibt für die Wirkungsdauer bestehen. Du erschaffst entweder eine gerade Wand, die bis zu 30 Meter lang ist, oder eine ringförmige Wand mit einem Radius von bis zu neun Metern. Sie ist in beiden Fällen sechs Meter hoch und 1,5 Meter dick. Die Wand bietet Dreivierteldeckung, und ihr Bereich stellt schwieriges Gelände dar. Jede Kreatur im Bereich der Wand führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 6W10 Energieschaden, anderenfalls die Hälfte. Auch Kreaturen, die sich in den Bereich der Wand begeben oder ihren Zug darin beenden, führen den Rettungswurf aus. Jede Kreatur führt den Rettungswurf nur einmal pro Zug aus.', '', 'Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('klon', 'KLON', 8, 'Nekromantiezauber', '1 Stunde', 'Berührung', 'V, G, M (ein Diamant im Wert von mindestens 1.000 GM, den der Zauber verbraucht, und ein verschließbares Gefäß im Wert von mindestens 2.000 GM, das groß genug ist, um die zu klonende Kreatur aufzunehmen)', NULL, 'Unmittelbar', 0, 0, 'Du berührst eine Kreatur oder mindestens 16 Kubikzenti­meter von ihrem Fleisch. Im Gefäß, das du für den Zauber verwendet hast, wächst ein regloses Duplikat der Kreatur. Es ist nach 120 Tagen ausgewachsen. Du bestimmst, ob es so alt wie die Kreatur oder jünger ist. Der Klon bleibt reglos und beliebig lange erhalten, sofern das Gefäß nicht geöffnet wird. Stirbt die ursprüngliche Kreatur, wenn der Klon ausgewachsen ist, so wird ihre Seele auf den Klon übertragen, sofern sie frei und bereitwillig ist. Der Klon ist körperlich mit dem Original identisch und besitzt dessen Persönlichkeit, seine Erinnerungen und Fähigkeiten, jedoch nicht seine Ausrüstung. Überreste der ursprünglichen Kreatur werden gegebenenfalls reglos und können nicht wiederbelebt werden, da sich die Seele der Kreatur anderswo befindet.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('klopfen', 'KLOPFEN', 2, 'Verwandlungszauber', 'Aktion', '18 Meter', 'V', NULL, 'Unmittelbar', 0, 0, 'Wähle einen Gegenstand in Reichweite aus, den du sehen kannst. Es kann sich um eine Tür, eine Kiste, ein Paar Handschellen, ein Vorhängeschloss oder einen anderen Gegenstand handeln, der auf magische oder gewöhnliche Art verschlossen ist. Wenn das Ziel mit einem gewöhnlichen Schloss verschlossen, verriegelt oder verklemmt ist, wird es geöffnet, entriegelt oder gelöst. Wenn der Gegenstand mehrere Schlösser besitzt, wird nur eines davon geöffnet. Ist das Ziel durch den Zauber Arkanes Schloss verriegelt, so wird dieser Zauber zehn Minuten lang unterdrückt. In dieser Zeit kann das Ziel geöffnet und geschlossen werden. Wenn du den Zauber wirkst, gibt dein Ziel ein lautes Klopfgeräusch von sich, welches bis zu 90 Meter weit zu hören ist. KONSTRUKT HERBEIRUFEN Beschwörungszauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: 27 Meter Komponenten: V, G, M (eine Schatulle im Wert von mindestens 400 GM) Wirkungsdauer: Konzentration, bis zu 1 Stunde Du rufst den Geist eines Konstrukts herbei. Er erscheint in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten eines Konstruktgeists. Wenn du den Zauber wirkst, entscheide dich für ein Material: Lehm, Eisen oder Stein. Die Kreatur sieht aus wie eine belebte Statue (du bestimmst das Erscheinungsbild) aus dem ausgewählten Material, durch welches einige Details im Wertekasten bestimmt werden. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet. Die Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. 284 KAPITEL 7 I ZAUBER KONSTRUKTGEIST (STEIN). KONSTRUKTGEIST Mittelgroßes Konstrukt, neutral RK 13 + Zaubergrad TP 40 + 15 für jeden Zaubergrad ab dem 5. Bewegungsrate 9 m MOD RW MOD RW MOD RW SU +4 +4 GES 10 +0 +0 KON 18 +4 +4 INT +2 +2 WEI 11 +0 +0 CHA 5 —3 —3 Resistenzen Gift Immunitäten Bezaubert, Erschöpft, Gelähmt, Verängstigt, Vergiftet Sinne Dunkelsicht 18 m, Passive Wahrnehmung 10 Sprachen Versteht die Sprachen, die du sprichst HG — (EP 0, ÜB entspricht deinem Übungsbonus)', 'Barde, Magier, Zauberer', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. 284 KAPITEL 7 I ZAUBER KONSTRUKTGEIST (STEIN). KONSTRUKTGEIST Mittelgroßes Konstrukt, neutral RK 13 + Zaubergrad TP 40 + 15 für jeden Zaubergrad ab dem 5. Bewegungsrate 9 m MOD RW MOD RW MOD RW SU +4 +4 GES 10 +0 +0 KON 18 +4 +4 INT +2 +2 WEI 11 +0 +0 CHA 5 —3 —3 Resistenzen Gift Immunitäten Bezaubert, Erschöpft, Gelähmt, Verängstigt, Vergiftet Sinne Dunkelsicht 18 m, Passive Wahrnehmung 10 Sprachen Versteht die Sprachen, die du sprichst HG — (EP 0, ÜB entspricht deinem Übungsbonus)","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":null,"hp":null},"summon_text":"MERKMALE\nErhitzter Körper (nur Metall): Eine Kreatur, die den Geist mit einem Nahkampfangriff trifft oder ihren Zug vom Geist gepackt beginnt, erleidet 1W10 Feuerschaden.\nSteinerne Lethargie (nur Stein): Wenn eine Kreatur ihren Zug im Abstand von bis zu drei Metern vom Geist beginnt, kann der Geist mit magischer Energie auf sie zielen, sofern er sie sehen kann. Weisheitsrettungswurf: Der SG entspricht deinem Zauberrettungswurf-SG, das Ziel. Misserfolg: Das Ziel kann bis zum Beginn seines nächsten Zugs keine Gelegenheitsangriffe ausführen, und seine Bewegungsrate ist halbiert.\nAKTIONEN\nMehrfachangriff: Der Geist führt eine Anzahl von Hiebangriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet).\nHieb: Nahkampfangriffswurf? Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W8+4 + Zaubergrad Wuchtschaden.\nREAKTIONEN\nBerserkerpeitsche (nur Ton): Auslöser: Der Geist erleidet Schaden durch eine Kreatur. Antwort: Der Geist führt einen Hiebangriff gegen die Kreatur aus, sofern möglich, oder er nähert sich der Kreatur bis zur Hälfte der Bewegungsrate, ohne Gelegenheitsangriffe zu provozieren.\nKONTAKT ZU ANDEREN EBENEN Erkenntniszauber 5. Grades (Hexenmeister, Magier)\nZeitaufwand: 1 Minute oder Ritual\nReichweite: Selbst\nKomponenten: V\nWirkungsdauer: 1 Minute\nDu trittst mental mit einem Halbgott, dem Geist eines lange verstorbenen Weisen oder einem anderen kundigen Wesen von einer anderen Ebene in Kontakt. Der Kontakt zu dieser außerweltlichen Intelligenz kann deinen Verstand brechen. Wenn du diesen Zauber wirkst, führe einen SG-15-Intelligenzrettungswurf aus. Bei einem erfolgreichen Rettungswurf kannst du dem Wesen bis zu fünf Fragen stellen. Du musst deine Fragen stellen, bevor der Zauber endet. Der SL beantwortet jede Frage mit einem einzelnen Wort wie „Ja\", „Nein\", „Vielleicht\", „Niemals\", „Irrelevant\" oder „Unklar\" (wenn das Wesen die Antwort nicht kennt). Falls eine Antwort mit nur einem Wort irreführend wäre, kann der SL stattdessen einen kurzen Satz als Antwort geben.\nScheitert der Wurf, so erleidest du 6W6 psychischen Schaden und bist kampfunfähig, bis du eine lange Rast beendest. Der Zauber Vollständige Genesung beendet diesen Effekt.","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('kreatur-aufsp-ren', 'KREATUR AUFSPÜREN', 4, 'Erkenntniszauber', 'Aktion', 'Selbst', 'V, G, M (etwas Fell von einem Bluthund)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Beschreibe oder nenne eine Kreatur, mit der du vertraut bist. Sofern die Kreatur sich im Abstand von bis zu 300 Metern von dir befindet, spürst du, in welcher Richtung ihr Aufenthaltsort liegt. Bewegt sich die Kreatur, so weißt du, in welche Richtung. Der Zauber kann eine bestimmte dir bekannte Kreatur aufspüren oder die nächste Kreatur einer bestimmten Art (wie ein Mensch oder ein Einhorn), sofern du eine solche Kreatur mindestens einmal aus der Nähe gesehen hast (aus höchstens neun Metern Entfernung). Hat die beschriebene oder genannte Kreatur eine andere Gestalt angenommen, beispielsweise durch die Zauber Fleisch zu Stein oder Verwandlung, so kann dieser Zauber die Kreatur nicht aufspüren. Der Zauber kann die Kreatur auch dann nicht aufspüren, wenn Blei den direkten Weg zwischen dir und ihr blockiert — egal, welchen Durchmesser es hat.', '', 'Barde, Druide, Kleriker, Magier, Paladin, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('kreis-der-macht', 'KREIS DER MACHT', 5, 'Bannzauber', 'Aktion', 'Selbst', 'V', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Für die Wirkungsdauer geht eine Aura mit einer Ausströ­mung von neun Metern von dir aus. In der Aura seid du und deine Verbündeten bei Rettungswürfen gegen Zauber und andere magische Effekte im Vorteil. Außerdem erleidet eine betroffene Kreatur bei einem erfolgreichen Rettungswurf gegen Zauber oder magische Effekte, der den Schaden halbiert, stattdessen gar keinen Schaden.', '', 'Kleriker, Magier, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('kreis-der-teleportation', 'KREIS DER TELEPORTATION', 5, 'Beschwörungszauber', '1 Minute', '3 Meter', 'V, M (seltene Tinten im Wert von mindestens 50 GM, die der Zauber verbraucht)', NULL, '1 Runde', 0, 0, 'Wenn du den Zauber wirkst, zeichnest du einen Kreis mit einem Radius von 1,5 Metern auf den Boden. In den Kreis arbeitest du Siegel ein, die deine aktuelle Position mit einem anderen dauerhaften Kreis der Teleportation deiner Wahl verbinden, dessen Siegelsequenz du kennst und der sich auf derselben Existenzebene befindet. Im Kreis öffnet sich ein schimmerndes Portal, das bis zum Ende deines nächsten Zugs offen bleibt. Jede Kreatur, die durch das Portal tritt, erscheint sofort in einem freien Bereich im Abstand von bis zu 1,5 Metern um den Zielkreis, falls verfügbar, ansonsten im nächsten freien Bereich. Viele größeren Tempel, Gildenhallen und weitere wichtige Orte verfügen über dauerhafte Kreise der Teleportation. Jeder Kreis besitzt eine einzigartige Siegelsequenz, eine Reihe von Runen, die in einem bestimmten Muster angeordnet sind. Wenn du diesen Zauber erlernst, erhältst du die Siegel­sequenzen zweier Zielorte auf der materiellen Ebene nach Wahl des SL. Im Laufe deiner Abenteuer könntest du weitere Siegelsequenzen erfahren. Um eine neue Siegelsequenz zu erlernen, musst du sie eine Minute lang studieren. Wenn du diesen Zauber 365 Tage lang täglich am selben Ort wirkst, erschaffst du einen dauerhaften Kreis der Teleportation. KREUZFAHRERMANTEL Hervorrufungszauber 3. Grades (Paladin) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V Wirkungsdauer: Konzentration, bis zu 1 Minute Von dir geht eine magische Aura mit einer Ausströmung von neun Metern aus. In der Aura bewirken du und deine Verbündeten jeweils zusätzlich 1W4 gleißenden Schaden, wenn ihr einen Waffenangriff oder einen waffenlosen Angriff ausführt.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('krone-des-wahnsinns', 'KRONE DES WAHNSINNS', 2, 'Verzauberungszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Eine Kreatur in Reichweite, die du sehen kannst, muss einen Weisheitsrettungswurf bestehen, oder sie ist für die Wirkungsdauer bezaubert. Wenn die Kreatur kein Humanoide ist, besteht sie den Rettungswurf automatisch. Auf dem Kopf des bezauberten Ziels erscheint eine geisterhafte Krone. Das Ziel muss seine Aktion in jedem seiner Züge vor seiner Bewegung verwenden, um einen Nahkampfangriff gegen eine Kreatur außer sich selbst auszuführen, die du mental bestimmst. Das Ziel kann in seinem Zug normal handeln, wenn du keine Kreatur auswählst oder sich keine Kreatur in seiner Reichweite befindet. Das Ziel wiederholt den Rettungswurf am Ende jedes seiner Züge. Bei einem Erfolg endet der Zauber. In deinen folgenden Zügen musst du die magische Aktion ausführen, um die Kontrolle über das Ziel zu behalten, oder der Zauber endet. KUGEL DER UNVERWUNDBARKEIT Bannzauber 6. Grades (Magier, Zauberer) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G, M (eine Glasperle) Wirkungsdauer: Konzentration, bis zu 1 Minute In einerAusströmung von drei Metern erscheint für die Wirkungsdauer eine unbewegliche schimmernde Barriere um dich herum. Jeder Zauber des höchstens 5. Grades, der von außerhalb der Barriere gewirkt wird, hat keinen Effekt auf Kreaturen oder Gegenstände im Innern. Ein solcher Zauber kann Kreaturen und Gegenstände innerhalb der Barriere zum Ziel haben, hat jedoch keinen Effekt auf sie. Gleichermaßen ist der Bereich innerhalb der Barriere von Wirkungsbereichen solcher Zauber ausgeschlossen.', 'Für jeden Zauberplatzgrad über dem 6. blockiert die Barriere einen Grad höhere Zauber.', 'Barde, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. blockiert die Barriere einen Grad höhere Zauber."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('lange-schritte', 'LANGE SCHRITTE', 1, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (eine Prise Erde)', NULL, '1 Stunde', 0, 0, 'Du berührst eine Kreatur. Für die Wirkungsdauer ist die Bewegungsrate des Ziels um drei Meter erhöht.', 'Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen. L✓.UTLOSES TRUGBILD KANN ILLUSIONEN DES WELTLICHEN UND DES WUNDERLICHEN ERZEUGEN. 286 KAPITEL 7 ! ZAUBER', 'Barde, Druide, Magier, Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen. L✓.UTLOSES TRUGBILD KANN ILLUSIONEN DES WELTLICHEN UND DES WUNDERLICHEN ERZEUGEN. 286 KAPITEL 7 ! ZAUBER"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('lautloses-trugbild', 'LAUTLOSES TRUGBILD', 1, 'Illusionszauber', 'Aktion', '18 Meter', 'V, G, M (ein Stück Vlies)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erschaffst das Abbild eines Gegenstands, einer Kreatur oder eines anderen sichtbaren Phänomens, das nicht größer als ein Würfel mit 4,5 Metern Kantenlänge sein darf. Das Abbild erscheint an einem Punkt in Reichweite und bleibt für die Wirkungsdauer bestehen. Es ist ein rein visueller Eindruck ohne Geräusche, Gerüche oder andere Sinneseffekte. Als magische Aktion kannst du das Abbild an jeden beliebigen Punkt in Reichweite bewegen. Dabei kannst du das Abbild so verändern, dass seine Bewegung natürlich erscheint. Beispiel: Wenn du das Abbild einer Kreatur erschaffst und bewegst, kannst du es so verändern, dass es zu laufen scheint. Physische Interaktionen mit dem Abbild enttarnen es als Illusion, da Dinge es einfach durchdringen können. Eine Kreatur, die das Abbild mit der Studieren-Aktion untersucht, kann mit einem erfolgreichen Intelligenzwurf (Nachforschungen) gegen deinen Zauberrettungswurf-SG erkennen, dass es sich um eine Illusion handelt. Erkennt eine Kreatur die Illusion als solche, so wird das Abbild für sie durchscheinend.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('leid', 'LEID', 6, 'Nekromantiezauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du entfesselst virulente Magie auf eine Kreatur in Reichweite, die du sehen kannst. Das Ziel führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet es 14W6 nekrotischen Schaden, und sein Trefferpunktemaximum wird um den Betrag des erlittenen nekrotischen Schadens verringert. Bei einem erfolgreichen Rettungswurf erleidet das Ziel nur halb so viel Schaden. Dieser Zauber kann das Trefferpunktemaximum des Ziels nicht unter 1 verringern. LENKENDES GESCHOSS Hervorrufungszauber 1. Grades (Kleriker) Zeitaufwand: Aktion Reichweite: 36 Meter Komponenten: V, G Wirkungsdauer: 1 Runde Du schleuderst einen Lichtblitz auf eine Kreatur in Reichweite. Führe einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet es 4W6 gleißenden Schaden, und der nächste Angriffswurf, der vor dem Ende deines nächsten Zugs gegen es ausgeführt wird, ist im Vorteil.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht. LEOMUNDS GEHEIME TRUHE Beschwörungszauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: Berührung Komponenten: V, G, M (eine Truhe in den Abmessungen 90 mal 60 mal 60 Zentimeter aus seltenen Materialien im Wert von mindestens 5.000 GM und eine winzige Nachbildung der Truhe aus den gleichen Materialien im Wert von mindestens 50 GM) Wirkungsdauer: Bis der Zauber gebannt wird Du versteckst eine Truhe mit ihrem gesamten Inhalt auf der Ätherebene. Dazu musst du beide Materialkomponenten für den Zauber berühren, die Truhe und die winzige Nachbildung. Die Truhe fasst bis zu 324 Liter anorganischen Materials (90 mal 60 mal 60 Zentimeter). Solange sich die Truhe auf der Ätherebene befindet, kannst du mit einer magischen Aktion die Nachbildung berühren, um die Truhe herbeizurufen. Sie erscheint in einem freien Bereich auf dem Boden im Abstand von bis zu 1,5 Metern von dir. Du kannst die Truhe wieder auf die Ätherebene zurückschicken, indem du mit einer magischen Aktion die Truhe und ihre Nachbildung gleichzeitig berührst. Nach 60 Tagen besteht am Ende jedes Tages eine kumulative Chance von fünf Prozent, dass der Zauber endet. Er endet auch dann, wenn du den Zauber erneut wirkst oder die kleine Nachbildung zerstört wird. Wenn der Zauber endet, während sich die große Truhe auf der Ätherebene befindet, bleibt sie dort, bis sie von dir oder jemand anderem gefunden wird. LEOMUNDS WINZIGE HÜTTE Hervorrufungszauber 3. Grades (Barde, Magier) Zeitaufwand: 1 Minute oder Ritual Reichweite: Selbst Komponenten: V, G, M (eine Kristallperle) Wirkungsdauer: 8 Stunden Um dich herum entsteht eine Ausströmung von drei Metern und bleibt für die Wirkungsdauer ortsfest. Der Zauber scheitert beim Wirken, wenn die Ausströmung nicht groß genug ist, um alle Kreaturen in ihrem Bereich vollständig aufzunehmen. Kreaturen und Gegenstände, die sich beim Wirken des Zaubers innerhalb der Ausströmung befinden, können sich frei darin bewegen oder bewegt werden. Andere Kreaturen und Gegenstände können sie nicht passieren. Zauber des höchstens 3. Grades können nicht durch die Ausströmung hindurch gewirkt werden, und ihre Effekte können sich nicht bis in die Ausströmung erstrecken. Die Atmosphäre innerhalb der Ausströmung ist unabhängig vom Wetter außerhalb angenehm und trocken. Für die Wirkungsdauer kannst du befehlen, dass innerhalb der Ausströmung dämmriges Licht oder Dunkelheit herrschen soll (keine Aktion erforderlich). Die Ausströmung ist von außen undurchsichtig und erscheint in einer Farbe deiner Wahl. Von innen ist sie durchsichtig. Der Zauber endet vorzeitig, wenn du die Ausströmung verlässt oder den Zauber erneut wirkst. LEUCHTFEUER DER HOFFNUNG Bannzauber 3. Grades (Kleriker) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Minute Wähle eine beliebige Anzahl von Kreaturen in Reichweite aus. Für die Wirkungsdauer ist jedes Ziel bei Weisheitsund Todesrettungswürfen im Vorteil und erhält bei jeder Heilung die höchstmögliche Anzahl von Trefferpunkten zurück.', 'Kleriker', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht. LEOMUNDS GEHEIME TRUHE Beschwörungszauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: Berührung Komponenten: V, G, M (eine Truhe in den Abmessungen 90 mal 60 mal 60 Zentimeter aus seltenen Materialien im Wert von mindestens 5.000 GM und eine winzige Nachbildung der Truhe aus den gleichen Materialien im Wert von mindestens 50 GM) Wirkungsdauer: Bis der Zauber gebannt wird Du versteckst eine Truhe mit ihrem gesamten Inhalt auf der Ätherebene. Dazu musst du beide Materialkomponenten für den Zauber berühren, die Truhe und die winzige Nachbildung. Die Truhe fasst bis zu 324 Liter anorganischen Materials (90 mal 60 mal 60 Zentimeter). Solange sich die Truhe auf der Ätherebene befindet, kannst du mit einer magischen Aktion die Nachbildung berühren, um die Truhe herbeizurufen. Sie erscheint in einem freien Bereich auf dem Boden im Abstand von bis zu 1,5 Metern von dir. Du kannst die Truhe wieder auf die Ätherebene zurückschicken, indem du mit einer magischen Aktion die Truhe und ihre Nachbildung gleichzeitig berührst. Nach 60 Tagen besteht am Ende jedes Tages eine kumulative Chance von fünf Prozent, dass der Zauber endet. Er endet auch dann, wenn du den Zauber erneut wirkst oder die kleine Nachbildung zerstört wird. Wenn der Zauber endet, während sich die große Truhe auf der Ätherebene befindet, bleibt sie dort, bis sie von dir oder jemand anderem gefunden wird. LEOMUNDS WINZIGE HÜTTE Hervorrufungszauber 3. Grades (Barde, Magier) Zeitaufwand: 1 Minute oder Ritual Reichweite: Selbst Komponenten: V, G, M (eine Kristallperle) Wirkungsdauer: 8 Stunden Um dich herum entsteht eine Ausströmung von drei Metern und bleibt für die Wirkungsdauer ortsfest. Der Zauber scheitert beim Wirken, wenn die Ausströmung nicht groß genug ist, um alle Kreaturen in ihrem Bereich vollständig aufzunehmen. Kreaturen und Gegenstände, die sich beim Wirken des Zaubers innerhalb der Ausströmung befinden, können sich frei darin bewegen oder bewegt werden. Andere Kreaturen und Gegenstände können sie nicht passieren. Zauber des höchstens 3. Grades können nicht durch die Ausströmung hindurch gewirkt werden, und ihre Effekte können sich nicht bis in die Ausströmung erstrecken. Die Atmosphäre innerhalb der Ausströmung ist unabhängig vom Wetter außerhalb angenehm und trocken. Für die Wirkungsdauer kannst du befehlen, dass innerhalb der Ausströmung dämmriges Licht oder Dunkelheit herrschen soll (keine Aktion erforderlich). Die Ausströmung ist von außen undurchsichtig und erscheint in einer Farbe deiner Wahl. Von innen ist sie durchsichtig. Der Zauber endet vorzeitig, wenn du die Ausströmung verlässt oder den Zauber erneut wirkst. LEUCHTFEUER DER HOFFNUNG Bannzauber 3. Grades (Kleriker) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Minute Wähle eine beliebige Anzahl von Kreaturen in Reichweite aus. Für die Wirkungsdauer ist jedes Ziel bei Weisheitsund Todesrettungswürfen im Vorteil und erhält bei jeder Heilung die höchstmögliche Anzahl von Trefferpunkten zurück.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('licht', 'LICHT', 0, 'Hervorrufungzauber', 'Aktion', 'Berührung', 'V, M (ein Glühwürmchen oder phosphoreszierendes Moos)', NULL, '1 Stunde', 0, 0, 'Du berührst einen Gegenstand von höchstens großer Größe, der nicht von jemand anderem getragen oder gehalten wird. Für die Wirkungsdauer spendet dieser Gegenstand in einem Radius von sechs Metern helles Licht und in einem Radius von weiteren sechs Metern dämmriges Licht. Du kannst die Farbe des Lichts frei wählen. Wird der Gegenstand mit etwas Undurchsichtigem abgedeckt, so wird das Licht blockiert. Der Zauber endet vorzeitig, wenn du ihn erneut wirkst. MACHT DER VORSTELLUNGSKRAFT Illusionszauber 2. Grades (Barde, Magier, Zauberer) Zeitaufwand: Aktion Reichweite: 18 Meter Komponenten: V, G, M (ein Stück Vlies) Wirkungsdauer: Konzentration, bis zu 1 Minute Du versuchst, im Verstand einer Kreatur in Reichweite, die du sehen kannst, eine Illusion zu erschaffen. Das Ziel führt einen Intelligenzrettungswurf aus. Scheitert der Wurf, so erzeugst du ein geisterhaft sichtbares Phänomen, beispielsweise einen Gegenstand oder eine Kreatur. Das Phänomen kann nicht größer sein als ein Würfel mit drei Metern Kantenlänge. Es ist nur für die Wirkungsdauer und nur für das Ziel sichtbar. Das Phänomen umfasst auch Geräusche, Temperaturen und andere Stimuli. Das Ziel kann die Studieren-Aktion ausführen, um das Phänomen mit einem Intelligenzwurf (Nachforschungen) gegen deinen Zauberrettungswurf-SG zu untersuchen. Bei einem Erfolg bemerkt das Ziel, dass es sich um eine Illusion handelt, und der Zauber endet. Solange das Ziel vom Zauber betroffen ist, behandelt es das Phänomen, als wäre dieses real. Unlogische Ergebnisse der Interaktion mit dem Phänomen rationalisiert es. Beispiel: Wenn das Ziel eine geisterhafte Brücke betreten will und den Absturz überlebt, so glaubt es weiterhin, die Brücke würde existieren, und sucht sich eine andere Erklärung für den Sturz. Das betroffene Ziel kann sogar Schaden durch das Phänomen erleiden, wenn dieses eine gefährliche Kreatur oder eine anderweitige Gefahr repräsentiert. Das Phänomen kann dem Ziel in jedem deiner Züge 2W8 psychischen Schaden zufügen, sofern das Ziel sich im Bereich des Phänomens oder im Abstand von bis zu 1,5 Metern von ihm aufhält. Das Ziel erlebt den Schaden als von einer Art, die zur Illusion passt. MÄCHTIGE', '', 'Barde, Kleriker, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('m-chtiges-trugbild', 'MÄCHTIGES TRUGBILD', 3, 'Illusionszauber', 'Aktion', '36 Meter', 'V, G, M (ein Stück Vlies)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erschaffst das Abbild eines Gegenstands, einer Kreatur oder eines anderen sichtbaren Phänomens, das nicht größer als ein Würfel mit sechs Metern Kantenlänge sein darf. Das Abbild erscheint an einem Punkt in Reichweite und bleibt für die Wirkungsdauer bestehen. Es wirkt echt, einschließlich passender Geräusche, Gerüche und Temperaturen, kann jedoch weder Schaden noch Zustände bewirken. Wenn du dich in Reichweite der Illusion befindest, kannst du sie mit einer magischen Aktion an einen anderen Ort deiner Wahl in Reichweite bewegen. Dabei kannst du das Abbild so verändern, dass seine Bewegung natürlich erscheint. Beispiel: Wenn du das Abbild einer Kreatur erschaffst und bewegst, kannst du es so verändern, dass es zu laufen scheint. Außerdem kannst du die Illusion verschiedene Geräusche erzeugen oder sogar ein Gespräch führen lassen. Physische Interaktionen mit dem Abbild enttarnen es als Illusion, da Dinge es einfach durchdringen können. Eine Kreatur, die das Abbild mit der Studieren-Aktion untersucht, kann mit einem erfolgreichen Intelligenzwurf (Nachforschungen) gegen deinen Zauberrettungswurf-SG erkennen, dass es sich um eine Illusion handelt. In diesem Fall sieht sie das Abbild als durchscheinend und nimmt auch die anderen sensorischen Eigenschaften abgeschwächt wahr.', 'Bei einem Zauberplatz ab dem 4. Grad wirkt der Zauber, bis er gebannt wird, ohne dass er Konzentration erfordert.', 'Barde, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Bei einem Zauberplatz ab dem 4. Grad wirkt der Zauber, bis er gebannt wird, ohne dass er Konzentration erfordert."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('magie-bannen', 'MAGIE BANNEN', 3, 'Bannzauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Wähle eine Kreatur, einen Gegenstand oder einen magischen Effekt in Reichweite aus. Jeder Zauber des höchstens 3. Grades, der aktuell auf das Ziel wirkt, wird beendet. Führe für jeden Zauber des mindestens 4. Grades, der auf das Ziel wirkt, einen Attributswurf mit deinem Attribut zum Zauberwirken (SG 10 plus Grad des Zaubers) aus. Bei einem Erfolg endet der Zauber.', 'Du beendest automatisch einen Zauber, der auf das Ziel wirkt, wenn dessen Grad höchstens dem des Zauberplatzes entspricht, den du verwendest.', 'Barde, Druide, Hexenmeister, Kleriker, Magier, Paladin, Waldläufer, Zauberer', '{"scaling":{"text":"Du beendest automatisch einen Zauber, der auf das Ziel wirkt, wenn dessen Grad höchstens dem des Zauberplatzes entspricht, den du verwendest."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('magie-entdecken', 'MAGIE ENTDECKEN', 1, 'Erkenntniszauber', 'Aktion oder Ritual', 'Selbst', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 1, 'Während der Wirkungsdauer nimmst du magische Effekte im Abstand von bis zu neun Metern von dir wahr. Wenn du magische Effekte wahrnimmst, kannst du die magische Aktion ausführen, um rund um sichtbare Kreaturen und Gegenstände im Bereich, auf welche die Magie wirkt, eine schwache Aura zu sehen. Wurde der Effekt durch einen Zauber hervorgerufen, so erkennst du dessen Schule der Magie. Der Zauber wird von 30 Zentimetern Stein, Erde oder Holz sowie von 2,5 Zentimetern Metall oder einer dünnen Bleischicht blockiert.', '', 'Druide, Hexenmeister, Kleriker, Magier, Paladin, Waldläufer, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('magierhand', 'MAGIERHAND', 0, 'Beschwörungzauber', 'Aktion', '9 Meter', 'V, G', NULL, '1 Minute', 0, 0, 'An einem Punkt deiner Wahl in Reichweite erscheint eine geisterhafte schwebende Hand. Sie bleibt für die Wirkungsdauer bestehen. Die Hand verschwindet, wenn sie weiter als neun Meter von dir entfernt ist oder du den Zauber erneut wirkst. Wenn du den Zauber wirkst, kannst du mithilfe der Hand mit Gegenständen interagieren, unverschlossene Türen oder Behälter öffnen, Gegenstände in einem Behälter verstauen oder daraus hervorholen oder den Inhalt einer Phiole ausgießen. In deinen folgenden Zügen kannst du die Hand als magische Aktion erneut auf diese Art kontrollieren. Als Teil dieser Aktion kannst du die Hand bis zu neun Meter weit bewegen. Die Hand kann nicht angreifen, keine magischen Gegenstände aktivieren und nicht mehr als fünf Kilogramm Gewicht tragen.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('magierr-stung', 'MAGIERRÜSTUNG', 1, 'Bannzauber', 'Aktion', 'Berührung', 'V, G, M (ein Stück gehärtetes Leder)', NULL, '8 Stunden', 0, 0, 'Du berührst eine bereitwillige Kreatur, die keine Rüstung trägt. Für die Wirkungsdauer erhält das Ziel eine Basis-RK von 13 plus ihrem Geschicklichkeitsmodifikator. Der Zauber endet vorzeitig, wenn das Ziel eine Rüstung anlegt. 288 KAPITEL. 7 I ZAUBER MAGISCHE WAFFE', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('ein-menschlicher-magier-trotzt-mit-magierr-stung-uno-den-zielsicheren-bolzen-von-magisches-geschoss-der-gefahr', 'EIN MENSCHLICHER MAGIER TROTZT MIT MAGIERRÜSTUNG UNO DEN ZIELSICHEREN BOLZEN VON MAGISCHES GESCHOSS DER GEFAHR.', 2, 'Verwandlungszauber', 'Bonusaktion', 'Berührung', 'V, G', NULL, '1 Stunde', 0, 0, 'Du berührst eine nichtmagische Waffe. Für die Wirkungsdauer ist diese Waffe eine magische Waffe mit einem Bonus von +1 auf Angriffsund Schadenswürfe. Der Zauber endet vorzeitig, wenn du ihn erneut wirkst.', 'Bei einem Zauberplatz des 3. bis 5. Grades wird der Bonus auf +2 erhöht. Bei einem Zauberplatz ab dem 6. Grad wird der Bonus auf +3 erhöht.', 'Magier, Paladin, Waldläufer, Zauberer', '{"scaling":{"text":"Bei einem Zauberplatz des 3. bis 5. Grades wird der Bonus auf +2 erhöht. Bei einem Zauberplatz ab dem 6. Grad wird der Bonus auf +3 erhöht."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('magischer-mund', 'MAGISCHER MUND', 2, 'Illusionszauber', '1 Minute oder Ritual', '9 Meter', 'V, G, M (Jadestaub im Wert von mindestens 10 GM, den der Zauber verbraucht)', NULL, 'Bis der Zauber gebannt wird', 0, 1, 'Du implantierst eine Botschaft in einen Gegenstand in Reichweite. Diese Botschaft wird bei einer bestimmten Auslösebedingung ausgesprochen. Wähle einen Gegenstand aus, den du sehen kannst und der nicht von einer anderen Kreatur getragen oder gehalten wird. Sprich dann die Botschaft aus. Sie kann höchstens 25 Worte enthalten, jedoch über einen Zeitraum von bis zu zehn Minuten übermittelt werden. Bestimme zum Schluss den Umstand, unter dem der Zauber ausgelöst und deine Botschaft übermittelt wird. Wenn dieser Auslöser eintritt, erscheint ein magischer Mund auf dem Gegenstand und spricht die Botschaft mit deiner Stimme und deiner Sprachlautstärke. Verfügt der ausgewählte Gegenstand über einen Mund oder etwas, das wie ein Mund aussieht, beispielsweise den Mund einer Statue, so erscheint der magische Mund dort, sodass die Worte aus dem Mund des Gegenstands zu kommen scheinen. Wenn du diesen Zauber wirkst, kannst du entscheiden, ob er nach einmaligem Übermitteln der Botschaft endet oder aber bestehen bleibt und die Botschaft jeweils wiederholt wird, wenn die Auslösebedingung erneut erfüllt ist. Der Auslöser kann beliebig allgemein oder detailliert sein. Er muss jedoch auf sichtbaren und hörbaren Umständen basieren, die im Abstand von bis zu neun Metern um den Gegenstand auftreten. Beispielweise könntest du bestimmen, dass die Botschaft übermittelt wird, sobald eine Kreatur sich dem Gegenstand auf neun Meter nähert oder wenn im Abstand von bis zu neun Metern vom Gegenstand eine Silberglocke geläutet wird.', '', 'Barde, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('magisches-gef-ss', 'MAGISCHES GEFÄSS', 6, 'Nekromantiezauber', '1 Minute', 'Selbst', 'V, G, M (ein Edelstein, ein Kristall oder eine Reliquie im Wert von mindestens 500 GM)', NULL, 'Bis der Zauber gebannt wird', 0, 0, 'Dein Körper verfällt in einen katatonischen Zustand, während deine Seele ihn verlässt und in den Behälter eindringt, den du als Materialkomponente des Zaubers verwendest. Während sich deine Seele im Behälter befindet, nimmst du deine Umgebung wahr, als würdest du dich im Bereich des Behälters aufhalten. Du kannst dich weder bewegen noch Reaktionen ausführen. Als einzige mögliche Aktion kannst du deine Seele bis zu 30 Meter außerhalb des Behälters projizieren und entweder in deinen lebendigen Körper zurückkehren - und damit den Zauber beenden ­oder versuchen, dich des Körpers eines Humanoiden zu bemächtigen. Du kannst versuchen, von einem beliebigen Humanoiden im Abstand von bis zu 30 Metern von dir, den du sehen kannst, Besitz zu ergreifen. Ausgenommen sind Kreaturen, die unter einem der Zauber Schutz vor Gut und Böse oder Schutzkreis stehen. Das Ziel führt einen Charismarettungswurf aus. Scheitert der Wurf, so besetzt deine Seele den Körper des Ziels, und seine Seele wird im Behälter gefangen. Bei einem erfolgreichen Rettungswurf widersteht das Ziel dir, und du kannst 24 Stunden lang keinen weiteren Versuch unternehmen. Sobald du vom Körper einer Kreatur Besitz ergriffen hast, kontrollierst du ihn. Deine Trefferpunkte und Trefferpunktewürfel, deine Werte für Geschicklichkeit, Konstitution und Stärke, deine Bewegungsrate sowie deine Sinne werden durch die der Kreatur ersetzt. Deine übrigen Spielwerte behältst du bei. Währenddessen kann die Seele der Kreatur die Umgebung um den Behälter mit ihren eigenen Sinnen wahrnehmen. Sie ist jedoch kampfunfähig und kann sich nicht bewegen. Während du von einem Körper Besitz ergriffen hast, kannst du als magische Aktion vom Wirtskörper zum Behälter zurückkehren, sofern sich dieser im Abstand von bis zu 30 Metern von dir befindet. Dadurch wird die Seele der Kreatur in ihren Körper zurückbefördert. Wenn der Wirtskörper stirbt, während du dich darin befindest, stirbt die Kreatur, und du führst einen Charismarettungswurf gegen deinen eigenen Zauberrettungswurf-SG aus. Bei einem Erfolg kehrst du in den Behälter zurück, sofern er sich im Abstand von bis zu 30 Metern von dir befindet. Anderenfalls stirbst du. Wenn der Behälter zerstört wird oder der Zauber endet, kehrt deine Seele in deinen Körper zurück. Befindet sich dein Körper weiter als 30 Meter von dir entfernt oder ist er tot, so stirbst du. Befindet sich die Seele einer anderen Kreatur im Behälter, wenn er zerstört wird, so kehrt sie in ihren Körper zurück, sofern er lebendig ist und sich im Abstand von bis zu 30 Metern von ihr befindet. Anderenfalls stirbt die Kreatur. Wenn der Zauber endet, wird der Behälter zerstört. 29O !!!', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('magisches-geschoss', 'MAGISCHES GESCHOSS', 1, 'Hervorrufungszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du erschaffst drei leuchtende Wurfpfeile aus magischer Energie. Jeder Wurfpfeil trifft eine Kreatur deiner Wahl in Reichweite, die du sehen kannst. Ein Wurfpfeil fügt seinem Ziel 1W4+1 Energieschaden zu. Die Wurfpfeile treffen alle gleichzeitig. Du kannst bestimmen, ob sie dieselbe oder verschiedene Kreaturen treffen.', 'Für jeden Zauberplatzgrad über dem 1. erzeugt der Zauber einen weiteren Wurfpfeil.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. erzeugt der Zauber einen weiteren Wurfpfeil."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('massen-einfl-sterung', 'MASSEN-EINFLÜSTERUNG', 6, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V, M (eine Schlangenzunge)', NULL, '24 Stunden', 0, 0, 'Du schlägst — mit höchstens 25 Worten - bis zu zwölf Kreaturen in Reichweite, die du sehen kannst und die dich hören und verstehen können, eine Vorgehensweise vor. Der Vorschlag muss realistisch klingen und darf nichts enthalten, was den Zielen oder ihren Verbündeten Schaden zufügen würde. Du könntest beispielsweise sagen: „Geht ins Dorf an dieser Straße und helft den Leuten dort bis zum Sonnenuntergang bei der Ernte." Oder du könntest sagen: „Dies ist nicht die Zeit für Gewalt. Lasst Eure Waffen fallen und tanzt! Hört in einer Stunde wieder auf damit." Jedes Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer — oder bis du oder deine Verbündeten ihm Schaden zufügen — bezaubert. Jedes bezauberte Ziel führt den Vorschlag nach besten Kräften aus. Die vorgeschlagene Aktivität kann während der gesamten Wirkungsdauer fortgesetzt werden. Wenn sie jedoch in kürzerer Zeit abgeschlossen werden kann, endet der Zauber bei einem Ziel, sobald es die Aktivität ausgeführt hat.', 'Für jeden Zauberplatzgrad über dem 5. wird die Heilung um 1W8 Trefferpunkte erhöht.', 'Barde, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. wird die Heilung um 1W8 Trefferpunkte erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('melfs-s-urepfeil', 'MELFS SÄUREPFEIL', 2, 'Hervorrufungszauber', 'Aktion', '27 Meter', 'V, G, M (ein zerstoßenes Rhabarberblatt)', NULL, 'Unmittelbar', 0, 0, 'Auf ein Ziel in Reichweite schießt ein schimmernder grüner Pfeil, der in einem Sprühregen aus Säure explodiert. Führe einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 4W4 Säureschaden sowie 2W4 Säureschaden am Ende seines nächsten Zugs. Bei einem Fehlschlag spritzt dennoch genügend Säure auf das Ziel, um ihm die Hälfte des anfänglichen Schadens zuzufügen.', 'Für jeden Zauberplatzgrad über dem 2. werden anfänglicher sowie späterer Schaden jeweils um 1W4 erhöht.', 'Magier', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. werden anfänglicher sowie späterer Schaden jeweils um 1W4 erhöht.","type":"damage","dice":"1W4"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('metall-erhitzen', 'METALL ERHITZEN', 2, 'Verwandlungszauber', 'Aktion', '18 Meter', 'V, G, M (ein Stück Eisen und eine Flamme)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Wähle einen verarbeiteten Gegenstand aus Metall in Reichweite aus, den du sehen kannst — beispielsweise eine metallene Waffe oder eine schwere oder mittelschwere metallene Rüstung. Du lässt den Gegenstand glühend heiß werden. Wenn du den Zauber wirkst, erleiden alle Kreaturen in physischem Kontakt mit dem Gegenstand 2W8 Feuerschaden. Für die Wirkungsdauer kannst du in jedem deiner folgenden Züge eine Bonusaktion verwenden, um diesen Schaden erneut zu bewirken, sofern der Gegenstand sich in Reichweite befindet. Trägt eine Kreatur den Gegenstand oder hält ihn in der Hand, während sie den Schaden erleidet, so muss sie einen Konstitutionsrettungswurf bestehen, um den Gegenstand fallenzulassen, falls möglich. Wenn sie den Gegenstand nicht fallen lässt, ist sie bis zum Beginn deines nächsten Zugs bei Angriffsund Attributswürfen im Nachteil.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W8 erhöht.', 'Barde, Druide', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('meteoritenschwarm', 'METEORITENSCHWARM', 9, 'Hervorrufungszauber', 'Aktion', '1,6 Kilometer', 'V, G', NULL, 'Unmittelbar', 0, 0, 'An vier verschiedenen Punkten in Reichweite, die du sehen kannst, stürzen lodernde Feuerkugeln zu Boden. Alle Kreaturen in Kugeln mit einem Radius von zwölf Metern um diese Punkte müssen einen Geschicklichkeitsrettungswurf ausführen. Scheitert der Wurf, so erleidet die jeweilige Kreatur 20W6 Feuerschaden und 20W6 Wuchtschaden, anderenfalls die Hälfte. Kreaturen im Wirkungsbereich von mehr als einer Feuerkugel sind nur einmal betroffen. Nichtmagische Gegenstände im Bereich, die nicht getragen oder gehalten werden, erleiden den gleichen Schaden und fangen Feuer, sofern sie brennbar sind.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('mit-pflanzen-sprechen', 'MIT PFLANZEN SPRECHEN', 3, 'Verwandlungszauber', 'Aktion', 'Selbst', 'V, G', NULL, '10 Minuten', 0, 0, 'In einer unbeweglichen Ausströmung von bis zu neun Metern um dich erfüllst du Pflanzen mit begrenztem Bewusstsein, sodass sie mit dir kommunizieren und einfache Befehle befolgen können. Du kannst die Pflanzen über Ereignisse des letzten Tages im Bereich des Zaubers befragen und so Informationen über vorbeiziehende Kreaturen, das Wetter und andere Umstände erhalten. Außerdem kannst du schwieriges Gelände aufgrund von Pflanzenwachstum — beispielsweise Dickicht oder Unterholz — für die Wirkungsdauer in normales Gelände verwandeln. Umgekehrt kannst du normales Gelände für die Wirkungsdauer zu schwierigem Gelände durch Pflanzenwachstum machen. Der Zauber ermöglicht es ihnen zwar nicht, sich zu entwurzeln und umherzulaufen, aber sie sind in der Lage, ihre Zweige, Ranken und Stiele für dich zu bewegen. Wenn sich eine Pflanzenkreatur in der Umgebung befindet, kannst du mit ihr kommunizieren, als würdet ihr dieselbe Sprache sprechen.', '', 'Barde, Druide, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('mit-stein-verschmelzen', 'MIT STEIN VERSCHMELZEN', 3, 'Verwandlungszauber', 'Aktion oder Ritual', 'Berührung', 'V, G', NULL, '8 Stunden', 0, 1, 'Du betrittst einen steinernen Gegenstand oder eine Oberfläche von ausreichender Größe, um deinen Körper aufzunehmen, und verschmilzt für die Wirkungsdauer mitsamt deiner Ausrüstung mit dem Stein. Dazu musst du den Stein berühren. Deine Anwesenheit ist nicht erkennbar und kann nur auf magische Weise wahrgenommen werden. Während du mit dem Stein verschmolzen bist, kannst du nicht sehen, was außerhalb vor sich geht. Du bist zudem bei Weisheitswürfen (Wahrnehmung), um Geräusche von außen zu hören, im Nachteil. Du bemerkst, wie die Zeit vergeht, und kannst Zauber auf dich selbst wirken. Zudem 292 KAPITEL 7 ZAUBER kannst du 1,5 Meter deiner Bewegungsrate verwenden, um den Stein dort zu verlassen, wo du ihn betreten hast. Dadurch endet der Zauber. Ansonsten kannst du dich nicht bewegen. Geringer physischer Schaden am Stein verletzt dich nicht. Wenn er teilweise zerstört oder seine Form verändert wird, sodass du nicht mehr hineinpasst, wirst du ausgestoßen und erleidest 6W6 Energieschaden. Wird der Stein vollständig zerstört oder in eine andere Substanz verwandelt, so wirst du ausgestoßen und erleidest 50 Energieschaden. Wenn du ausgestoßen wirst, gelangst du in den freien Bereich, der deinem Eintrittspunkt am nächsten liegt, und bist liegend.', '', 'Druide, Kleriker, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('mit-tieren-sprechen', 'MIT TIEREN SPRECHEN', 1, 'Erkenntniszauber', 'Aktion oder Ritual', 'Selbst', 'V, G', NULL, '10 Minuten', 0, 1, 'Für die Wirkungsdauer kannst du Tiere verstehen und verbal mit ihnen kommunizieren, und du kannst beliebige Fertigkeitsoptionen der Beeinflussen-Aktion auf sie anwenden. Die meisten Tiere haben zu Themen abseits von Überleben oder Gefährtenschaft nicht viel zu sagen. Sie sind jedoch zumindest in der Lage, Informationen über nahe Orte und Monster sowie über Ereignisse mitzuteilen, die sie innerhalb des letzten Tages erlebt haben.', '', 'Barde, Druide, Hexenmeister, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('mit-toten-sprechen', 'MIT TOTEN SPRECHEN', 3, 'Nekromantiezauber', 'Aktion', '3 Meter', 'V, G, M (brennender Weihrauch)', NULL, '10 Minuten', 0, 0, 'Du erfüllst einem Leichnam deiner Wahl in Reichweite mit einem Hauch von Leben, damit er Fragen beantworten kann, die du ihm stellst. Der Leichnam muss einen Mund besitzen. Wenn die Kreatur untot war, als sie starb, scheitert dieser Zauber. Er scheitert auch dann, wenn die Leiche innerhalb der letzten zehn Tage bereits Ziel dieses Zaubers war. Für die Wirkungsdauer kannst du der Leiche bis zu fünf Fragen stellen. Die Leiche besitzt nur das Wissen, das sie zu Lebzeiten hatte, und spricht nur die ihr zu Lebzeiten bekannten Sprachen. Die Antworten sind normalerweise kurz, kryptisch oder monoton. Der Leichnam muss dir nicht wahrheitsgemäß antworten, wenn du ihm feindselig gesinnt bist oder er dich als Feind erkennt. Dieser Zauber bringt nicht die Seele der Kreatur in ihren Körper zurück, nur ihren belebenden Geist. Daher kann die Leiche keine neuen Informationen erhalten, weiß nichts, was nach ihrem Tod geschehen ist, und kann nicht über zukünftige Ereignisse mutmaßen.', '', 'Barde, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('mondstrahl', 'MONDSTRAHL', 2, 'Hervorrufungszauber', 'Aktion', '36 Meter', 'V, G, M (Blatt einer Mondsamenpflanze)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'In einem zwölf Meter hohen Zylinder mit einem Radius von bis zu 1,5 Metern um einen Punkt in Reichweite scheint ein silbriger Strahl fahlen Lichts herab. Für die Wirkungsdauer ist der Zylinder von dämmrigem Licht erfüllt, und du kannst in deinen folgenden Zügen eine magische Aktion ausführen, um den Zylinder bis zu 18 Meter weit zu bewegen. Wenn der Zylinder erscheint, führt jede Kreatur darin einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet eine Kreatur 2W10 gleißenden Schaden. Wenn ihre Gestalt gewandelt ist (beispielsweise infolge des Zaubers Verwandlung), nimmt sie außerdem ihre wahre Gestalt an und kann sich erst wieder verwandeln, wenn sie den Zylinder verlässt. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur nur halb so viel Schaden. Eine Kreatur führt diesen Rettungswurf auch dann aus, wenn der Bereich des Zaubers sich in ihren Bereich bewegt, wenn die Kreatur den Bereich des Zaubers betritt oder ihren Zug darin beendet. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W10 erhöht.', 'Druide', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W10 erhöht.","type":"damage","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('monster-beherrschen', 'MONSTER BEHERRSCHEN', 8, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Eine Kreatur deiner Wahl in Reichweite, die du sehen kannst, muss einen Weisheitsrettungswurf bestehen, oder sie ist für die Wirkungsdauer bezaubert. Wenn du oder deine Verbündeten gegen das Ziel kämpfen, ist es beim Rettungswurf im Vorteil. Wann immer das Ziel Schaden erleidet, wiederholt es den Rettungswurf. Bei einem Erfolg endet der Zauber bei ihm. Du verfügst über eine telepathische Verbindung mit dem bezauberten Ziel, solange ihr euch auf derselben Existenzebene befindet. Diese Verbindung kannst du in deinem Zug nutzen, um dem Ziel Befehle zu erteilen (keine Aktion erforderlich), beispielsweise „Greife diese Kreatur an", „Laufe dorthin" oder „Hole diesen Gegenstand". Das Ziel gehorcht in seinem Zug, so gut es kann. Wenn es den Befehl ausgeführt hat und keine weiteren Anweisungen von dir erhält, verhält es sich nach seinen Wünschen und versucht vor allem, sich zu schützen. DER MAGIER MORDENKAINEN BEGRÜSST SEINE GÄSTE IN SEINEM MAGISCHEN ANWESEN: MORDENKA INENS HERRLICHEM HERRENHAUS. Du kannst dem Ziel befehlen, eine Reaktion auszuführen, musst dazu allerdings selbst eine Reaktion ausführen.', 'bei einem Zauberplatz des 9. Grades bis zu acht Stunden.', 'Barde, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"bei einem Zauberplatz des 9. Grades bis zu acht Stunden."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('monster-bezaubern', 'MONSTER BEZAUBERN', 4, 'Verzauberungszauber', 'Aktion', '9 Meter', 'V, G', NULL, '1 Stunde', 0, 0, 'Eine Kreatur in Reichweite, die du sehen kannst, führt einen Weisheitsrettungswurf aus. Dabei ist sie im Vorteil, wenn du oder deine Verbündeten gegen sie kämpfen. Scheitert der Wurf, so ist das Ziel bezaubert, bis der Zauber endet oder du oder deine Verbündeten ihm Schaden zufügen. Die bezauberte Kreatur ist dir freundlich gesinnt. Nach Ende des Zaubers weiß das Ziel, dass es von dir bezaubert wurde.', 'Für jeden Zauberplatzgrad über dem 4. kannst du auf eine weitere Kreatur zielen.', 'Barde, Druide, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('monster-festhalten', 'MONSTER FESTHALTEN', 5, 'Verzauberungszauber', 'Aktion', '27 Meter', 'V, G, M (ein gerades Stück Eisen)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Wähle eine Kreatur in Reichweite aus, die du sehen kannst. Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer gelähmt. Das Ziel wiederholt den Rettungswurf am Ende jedes seiner Züge. Bei einem Erfolg endet der Zauber.', 'Für jeden Zauberplatzgrad über dem 4. kannst du die Kantenlänge des Würfels um 30 Meter vergrößern. MORDENKAINENS SCHWERT Hervorrufungszauber 7. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: 27 Meter Komponenten: V, G, M (ein Miniaturschwert im Wert von mindestens 250 GM) Wirkungsdauer: Konzentration, bis zu 1 Minute Du erschaffst ein geisterhaftes Schwert, das in Reichweite schwebt. Die Kugel bleibt für die Wirkungsdauer bestehen. Wenn das Schwert erscheint, kannst du einen Nahkampf-Zauberangriff gegen ein Ziel im Abstand von bis zu 1,5 Metern vom Schwert ausführen. Bei einem Treffer erleidet das Ziel Energieschaden in Höhe von 4W12 plus deinem Zauberwirken-Attributsmodifikator. In deinen folgenden Zügen kannst du das Schwert mit einer Bonusaktion bis zu neun Meter weit an einen Ort bewegen, den du sehen kannst, und den Angriff gegen dasselbe oder ein anderes Ziel wiederholen. MORDENKAINENS TREUER HUND Beschwörungszauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G, M (eine Silberpfeife) Wirkungsdauer: 8 Stunden Du beschwörst in einem freien Bereich in Reichweite, den du sehen kannst, einen geisterhaften Wachhund. Dieser bleibt für die Wirkungsdauer bestehen, oder bis ihr euch weiter als 90 Meter voneinander entfernt. Niemand außer dir kann den Hund sehen, und er ist immateriell und unverwundbar. Wenn sich ihm eine Kreatur von mindestens kleiner Größe auf höchstens neun Meter nähert, ohne zuvor das Kennwort zu sprechen, das du beim Wirken des Zaubers festgelegt hast, bellt der Hund laut. Der Hund hat Wahrer Blick mit einer Reichweite von neun Metern. Zu Beginn jedes deiner Züge versucht der Hund, einen Gegner im Abstand von bis zu 1,5 Metern von sich zu beißen. Der Gegner muss einen Geschicklichkeitsrettungswurf bestehen, oder er erleidet 4W8 Energieschaden. In deinen folgenden Zügen kannst du eine magische Aktion ausführen, um den Hund bis zu neun Meter weit zu bewegen. 04 • -= • NAHRUNG UND WASSER ERSCHAFFEN Beschwörungszauber 3. Grades (Kleriker, Paladin) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du erschaffst 22,5 Kilogramm Nahrung und 120 Liter Wasser auf dem Boden oder in Behältern in Reichweite ­nützlich, um den Gefahren von Unterernährung und Dehydrierung vorzubeugen. Die Nahrung sieht aus wie Essen deiner Wahl und ist schlicht, aber nahrhaft. Das Wasser ist sauber. Die Nahrung verdirbt innerhalb von 24 Stunden, wenn sie nicht gegessen wird. NAHRUNG UND WASSER REINIGEN Verwandlungszauber 1. Grades (Druide, Kleriker, Paladin) Zeitaufwand: Aktion oder Ritual Reichweite: 3 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du entfernst Gifte und Verderbnis aus nichtmagischen Speisen und Getränken in einer Kugel mit einem Radius von 1,5 Metern um einen Punkt deiner Wahl in Reichweite.', 'Barde, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. kannst du die Kantenlänge des Würfels um 30 Meter vergrößern. MORDENKAINENS SCHWERT Hervorrufungszauber 7. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: 27 Meter Komponenten: V, G, M (ein Miniaturschwert im Wert von mindestens 250 GM) Wirkungsdauer: Konzentration, bis zu 1 Minute Du erschaffst ein geisterhaftes Schwert, das in Reichweite schwebt. Die Kugel bleibt für die Wirkungsdauer bestehen. Wenn das Schwert erscheint, kannst du einen Nahkampf-Zauberangriff gegen ein Ziel im Abstand von bis zu 1,5 Metern vom Schwert ausführen. Bei einem Treffer erleidet das Ziel Energieschaden in Höhe von 4W12 plus deinem Zauberwirken-Attributsmodifikator. In deinen folgenden Zügen kannst du das Schwert mit einer Bonusaktion bis zu neun Meter weit an einen Ort bewegen, den du sehen kannst, und den Angriff gegen dasselbe oder ein anderes Ziel wiederholen. MORDENKAINENS TREUER HUND Beschwörungszauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G, M (eine Silberpfeife) Wirkungsdauer: 8 Stunden Du beschwörst in einem freien Bereich in Reichweite, den du sehen kannst, einen geisterhaften Wachhund. Dieser bleibt für die Wirkungsdauer bestehen, oder bis ihr euch weiter als 90 Meter voneinander entfernt. Niemand außer dir kann den Hund sehen, und er ist immateriell und unverwundbar. Wenn sich ihm eine Kreatur von mindestens kleiner Größe auf höchstens neun Meter nähert, ohne zuvor das Kennwort zu sprechen, das du beim Wirken des Zaubers festgelegt hast, bellt der Hund laut. Der Hund hat Wahrer Blick mit einer Reichweite von neun Metern. Zu Beginn jedes deiner Züge versucht der Hund, einen Gegner im Abstand von bis zu 1,5 Metern von sich zu beißen. Der Gegner muss einen Geschicklichkeitsrettungswurf bestehen, oder er erleidet 4W8 Energieschaden. In deinen folgenden Zügen kannst du eine magische Aktion ausführen, um den Hund bis zu neun Meter weit zu bewegen. 04 • -= • NAHRUNG UND WASSER ERSCHAFFEN Beschwörungszauber 3. Grades (Kleriker, Paladin) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du erschaffst 22,5 Kilogramm Nahrung und 120 Liter Wasser auf dem Boden oder in Behältern in Reichweite ­nützlich, um den Gefahren von Unterernährung und Dehydrierung vorzubeugen. Die Nahrung sieht aus wie Essen deiner Wahl und ist schlicht, aber nahrhaft. Das Wasser ist sauber. Die Nahrung verdirbt innerhalb von 24 Stunden, wenn sie nicht gegessen wird. NAHRUNG UND WASSER REINIGEN Verwandlungszauber 1. Grades (Druide, Kleriker, Paladin) Zeitaufwand: Aktion oder Ritual Reichweite: 3 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du entfernst Gifte und Verderbnis aus nichtmagischen Speisen und Getränken in einer Kugel mit einem Radius von 1,5 Metern um einen Punkt deiner Wahl in Reichweite.","type":"damage","dice":"4W12"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('nebelschritt', 'NEBELSCHRITT', 2, 'Beschwörungszauber', 'Bonusaktion', 'Selbst', 'V', NULL, 'Unmittelbar', 0, 0, 'Du bist kurz von einem silbrigen Nebel umgeben und teleportierst dich bis zu neun Meter weit in einen freien Bereich, den du sehen kannst.', '', 'Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('nebelwolke', 'NEBELWOLKE', 1, 'Beschwörungszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du erschaffst eine Kugel aus Nebel mit einem Radius von sechs Metern um einen Punkt in Reichweite. Der Bereich in der Kugel ist komplett verschleiert. Die Kugel bleibt für die Wirkungsdauer bestehen, oder bis starker Wind (wie solcher durch Windstoß) sie auflöst.', 'Für jeden Zauberplatzgrad über dem 1. wird der Radius des Nebels um sechs Meter erhöht.', 'Druide, Magier, Waldläufer, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Radius des Nebels um sechs Meter erhöht."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('netz', 'NETZ', 2, 'Beschwörungszauber', 'Aktion', '18 Meter', 'V, G, M (einige Spinnweben)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du beschwörst an einem Punkt in Reichweite eine Masse aus klebrigen Netzen. Für die Wirkungsdauer füllen die Netze dort einen Würfel mit sechs Metern Kantenlänge. Die Netze sind schwieriges Gelände, und ihr Bereich ist leicht verschleiert. Wenn sich die Netze nicht zwischen zwei festen Gegenständen wie Mauern oder Bäumen befinden oder über einen Fußboden, eine Wand oder unter einer Decke gespannt sind, fallen sie in sich zusammen, und zu Beginn deines nächsten Zugs endet der Zauber. Netze, die über eine flache Oberfläche ausgebreitet sind, besitzen eine Dicke von 1,5 Metern. Wenn eine Kreatur in einem Zug die Netze erstmals betritt oder ihren Zug darin beginnt, muss sie einen Geschicklichkeitsrettungswurf bestehen, oder sie ist festgesetzt, solange sie sich in den Netzen befindet oder bis sie sich befreit. Eine von den Netzen festgesetzte Kreatur kann als Aktion einen Stärkewurf (Athletik) gegen deinen Zauberrettungswurf-SG ausführen. Bei einem Erfolg ist sie nicht länger festgesetzt. Die Netze sind brennbar. Alle Netze innerhalb eines Würfels mit 1,5 Metern Kantenlänge verbrennen innerhalb von einer Runde, wenn sie mit Feuer in Kontakt kommen, und fügen dabei allen Kreaturen, die ihren Zug im Feuer beginnen, 2W4 Feuerschaden zu.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('notfall', 'NOTFALL', 6, 'Bannzauber', '10 Minuten', 'Selbst', 'V, G, M (eine mit Edelsteinen verzierte Statuette von dir im Wert von mindestens 1.500 GM)', NULL, '10 Tage', 0, 0, 'Wähle einen Zauber des höchstens 5. Grades aus, den du auf dich selbst wirken kannst und dessen Zeitaufwand eine Aktion beträgt. Du wirkst diesen Zauber (auch Notfallzauber genannt) gemeinsam mit Notfall und verbrauchst Zauber­plätze für beide. Der Notfallzauber tritt allerdings erst auf einen bestimmten Auslöser hin in Kraft. Diesen Auslöser beschreibst du, wenn du die beiden Zauber wirkst. Beispiel: Wirkst du Notfall gemeinsam mit Wasser atmen, so kannst du bestimmen, dass Wasser atmen in Kraft tritt, wenn du in Wasser oder einer ähnlichen Flüssigkeit untertauchst. Der Notfallzauber tritt sofort in Kraft, wenn der Auslöser das erste Mal auftritt — ob du willst oder nicht. Danach endet Notfall. Der Zauber wirkt nur auf dich, selbst wenn er normale­rweise auch auf andere Ziele gewirkt werden könnte. Du kannst immer nur jeweils einen Notfall-Zauber verwenden. Wirkst du diesen Zauber erneut, so endet der Effekt eines anderen Notfall-Zaubers auf dich. Notfall endet auch dann, wenn du die Materialkomponente nicht mehr bei dir hast. NYSTULS MAGISCHE AURA Illusionszauber 2. Grades (Magier) Zeitaufwand: Aktion Reichweite: Berührung Komponenten: V, G, M (ein kleines Quadrat aus Seide) Wirkungsdauer: 24 Stunden Du berührst eine bereitwillige Kreatur oder einen Gegenstand, der nicht getragen oder gehalten wird, und platzierst eine Illusion auf dem Ziel. Eine Kreatur erhält den Effekt Maskierung, ein Gegenstand den Effekt Falsche Aura (beide Effekte siehe unten). Der Effekt bleibt für die Wirkungsdauer bestehen. Wenn du diesen Zauber 30 Tage lang täglich auf dasselbe Ziel wirkst, bleibt die Illusion bestehen, bis sie gebannt wird. Maskierung (Kreatur): Wähle einen Kreaturentyp aus, der nicht dem tatsächlichen Typ des Ziels entspricht. Andere Zauber und magische Effekte behandeln das Ziel, als wäre es eine Kreatur des ausgewählten Typs. Falsche Aura (Gegenstand): Du veränderst die Art, wie das Ziel für Zauber und magische Effekte wie Magie entdecken erscheint, die magische Auren entdecken. Du kannst einen nichtmagischen Gegenstand magisch und einen magischen Gegenstand nichtmagisch erscheinen lassen, oder die Aura des Gegenstands so manipulieren, dass er einer Schule der Magie deiner Wahl anzugehören scheint.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('otilukes-frostsph-re', 'OTILUKES FROSTSPHÄRE', 6, 'Hervorrufungszauber', 'Aktion', '90 Meter', 'V, G, M (eine Miniatu r-Kristal l kugel)', NULL, 'Unmittelbar', 0, 0, 'Du verschießt eine eisige Kugel an einen Punkt deiner Wahl in Reichweite, wo sie zu einer Kugel mit einem Radius von 18 Metern explodiert. Jede Kreatur in diesem Bereich führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet sie 10W6 Kälteschaden, anderenfalls die Hälfte. Wenn die Kugel ein Gewässer trifft, so gefriert dieses in einem quadratischen Bereich von neun Metern Kantenlänge bis in eine Tiefe von 15 Zentimetern. Das Eis bleibt eine Minute lang bestehen. Kreaturen, die an der Oberfläche des gefrorenen Wassers schwimmen, werden im Eis gefangen und sind festgesetzt. Eine gefangene Kreatur kann als Aktion einen Stärkewurf (Athletik) gegen deinen Zauberrettungswurf-SG ausführen, um sich zu befreien. Du musst die Kugel nach dem Wirken des Zaubers nicht sofort verschießen. In diesem Fall erscheint eine kleine kühle Kugel von der Größe eines Schleudersteins in deiner Hand. Du oder eine Kreatur, der du die Kugel gibst, kann sie jederzeit bis zu zwölf Meter weit werfen oder mit einer Schleuder innerhalb ihrer Grundreichweite verschießen. Die Kugel zerbirst beim Aufprall und bewirkt den gleichen Effekt wie das normale Wirken des Zaubers. Du kannst die Kugel auch ablegen, ohne dass sie zerbirst. Ist sie nach einer Minute nicht zerborsten, so explodiert sie.', 'Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 1W6 erhöht. OTILUKES UNVERWÜSTLICHE SPHÄRE Bannzauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G, M (eine Glaskugel) Wirkungsdauer: Konzentration, bis zu 1 Minute Eine schimmernde Kugel umschließt eine Kreatur oder einen Gegenstand von jeweils höchstens großer Größe in Reichweite. Eine nicht bereitwillige Kreatur muss einen Geschicklichkeitsrettungswurf bestehen, oder sie wird für die Wirkungsdauer eingeschlossen. Physische Gegenstände, Energien und Zaubereffekte können die Barriere weder von außen noch von innen durchdringen. Eine Kreatur in der Kugel kann jedoch atmen. Die Kugel ist gegen alle Schadensarten immun, und eine Kreatur oder ein Gegenstand darin kann weder durch Angriffe noch Effekte von außerhalb Schaden erleiden. Gleichermaßen kann eine Kreatur in der Kugel keinen Schaden außerhalb der Kugel bewirken. Die Kugel ist schwerelos und gerade groß genug für die Kreatur oder den Gegenstand darin. Eine gefangene Kreatur kann sich als Aktion gegen die Wand der Kugel lehnen und die Kugel so bis zur halben Bewegungsrate der Kreatur bewegen. Ebenso kann die Kugel von anderen Kreaturen aufgehoben und bewegt werden. Wird der Zauber Auflösung auf die Kugel gewirkt, so wird die Kugel zerstört, ohne dass etwas im Inneren Schaden erleidet. OTTOS UNWIDERSTEHLICHER TANZ Verzauberungszauber 6. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V Wirkungsdauer: Konzentration, bis zu 1 Minute Eine Kreatur in Reichweite, die du sehen kannst, muss einen Weisheitsrettungswurf ausführen. Bei einem erfolgreichen Rettungswurf tanzt das Ziel bis zum Ende seines nächsten Zugs albern an einer Stelle und verbraucht dafür seine gesamte Bewegungsrate. Scheitert der Wurf, so ist das Ziel für die Wirkungsdauer bezaubert. Solange es bezaubert ist, tanzt das Ziel albern an einer Stelle und benötigt dafür seine gesamte Bewegungsrate. Es ist bei Geschicklichkeitsrettungswürfen sowie bei Angriffswürfen im Nachteil, und andere Kreaturen sind bei Angriffswürfen gegen das Ziel im Vorteil. Das Ziel kann in jedem seiner Züge eine Aktion ausführen, um sich zu sammeln und den Rettungswurf zu wiederholen. Bei einem Erfolg endet der Zauber.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 1W6 erhöht. OTILUKES UNVERWÜSTLICHE SPHÄRE Bannzauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G, M (eine Glaskugel) Wirkungsdauer: Konzentration, bis zu 1 Minute Eine schimmernde Kugel umschließt eine Kreatur oder einen Gegenstand von jeweils höchstens großer Größe in Reichweite. Eine nicht bereitwillige Kreatur muss einen Geschicklichkeitsrettungswurf bestehen, oder sie wird für die Wirkungsdauer eingeschlossen. Physische Gegenstände, Energien und Zaubereffekte können die Barriere weder von außen noch von innen durchdringen. Eine Kreatur in der Kugel kann jedoch atmen. Die Kugel ist gegen alle Schadensarten immun, und eine Kreatur oder ein Gegenstand darin kann weder durch Angriffe noch Effekte von außerhalb Schaden erleiden. Gleichermaßen kann eine Kreatur in der Kugel keinen Schaden außerhalb der Kugel bewirken. Die Kugel ist schwerelos und gerade groß genug für die Kreatur oder den Gegenstand darin. Eine gefangene Kreatur kann sich als Aktion gegen die Wand der Kugel lehnen und die Kugel so bis zur halben Bewegungsrate der Kreatur bewegen. Ebenso kann die Kugel von anderen Kreaturen aufgehoben und bewegt werden. Wird der Zauber Auflösung auf die Kugel gewirkt, so wird die Kugel zerstört, ohne dass etwas im Inneren Schaden erleidet. OTTOS UNWIDERSTEHLICHER TANZ Verzauberungszauber 6. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V Wirkungsdauer: Konzentration, bis zu 1 Minute Eine Kreatur in Reichweite, die du sehen kannst, muss einen Weisheitsrettungswurf ausführen. Bei einem erfolgreichen Rettungswurf tanzt das Ziel bis zum Ende seines nächsten Zugs albern an einer Stelle und verbraucht dafür seine gesamte Bewegungsrate. Scheitert der Wurf, so ist das Ziel für die Wirkungsdauer bezaubert. Solange es bezaubert ist, tanzt das Ziel albern an einer Stelle und benötigt dafür seine gesamte Bewegungsrate. Es ist bei Geschicklichkeitsrettungswürfen sowie bei Angriffswürfen im Nachteil, und andere Kreaturen sind bei Angriffswürfen gegen das Ziel im Vorteil. Das Ziel kann in jedem seiner Züge eine Aktion ausführen, um sich zu sammeln und den Rettungswurf zu wiederholen. Bei einem Erfolg endet der Zauber.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('person-beherrschen', 'PERSON BEHERRSCHEN', 5, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Ein Humanoide deiner Wahl in Reichweite, den du sehen kannst, muss einen Weisheitsrettungswurf bestehen, oder er ist für die Wirkungsdauer bezaubert. Wenn du oder deine Verbündeten gegen das Ziel kämpfen, ist es beim Rettungswurf im Vorteil. Wann immer das Ziel Schaden erleidet, wiederholt es den Rettungswurf. Bei einem Erfolg endet der Zauber bei ihm. Du verfügst über eine telepathische Verbindung mit dem bezauberten Ziel, solange ihr euch auf derselben Existenzebene befindet. Diese Verbindung kannst du in deinem Zug nutzen, um dem Ziel Befehle zu erteilen (keine Aktion erforderlich), beispielsweise „Greife diese Kreatur an", „Laufe dorthin" oder „Hole diesen Gegenstand". Das Ziel gehorcht in seinem Zug, so gut es kann. Wenn es den Befehl ausgeführt hat und keine weiteren Anweisungen von dir erhält, verhält es sich nach seinen Wünschen und versucht vor allem, sich zu schützen. Du kannst dem Ziel befehlen, eine Reaktion auszuführen, musst dazu allerdings selbst eine Reaktion ausführen.', 'bei einem Zauberplatz des 6. Grades bis zu zehn Minuten, bei einem Zauberplatz des 7. Grades bis zu einer Stunde und bei einem Zauberplatz ab dem 8. Grad bis zu acht Stunden.', 'Barde, Magier, Zauberer', '{"scaling":{"text":"bei einem Zauberplatz des 6. Grades bis zu zehn Minuten, bei einem Zauberplatz des 7. Grades bis zu einer Stunde und bei einem Zauberplatz ab dem 8. Grad bis zu acht Stunden."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('person-bezaubern', 'PERSON BEZAUBERN', 1, 'Verzauberungszauber', 'Aktion', '9 Meter', 'V, G', NULL, '1 Stunde', 0, 0, 'Ein Humanoide in Reichweite, den du sehen kannst, führt einen Weisheitsrettungswurf aus. Dabei ist sie im Vorteil, wenn du oder deine Verbündeten gegen sie kämpfen. Scheitert der Wurf, so ist das Ziel bezaubert, bis der Zauber endet oder du oder deine Verbündeten ihm Schaden zufügen. Die bezauberte Kreatur ist dir freundlich gesinnt. Nach Ende des Zaubers weiß das Ziel, dass es von dir bezaubert wurde.', 'Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen.', 'Barde, Druide, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('person-festhalten', 'PERSON FESTHALTEN', 2, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V, G, M (ein gerades Stück Eisen)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Wähle einen Humanoiden in Reichweite aus, den du sehen kannst. Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer gelähmt. Das Ziel wiederholt den Rettungswurf am Ende jedes seiner Züge. Bei einem Erfolg endet der Zauber.', 'Für jeden Zauberplatzgrad über dem 2. kannst du auf einen weiteren Humanoiden zielen. PFEILSALVE BESCHWÖREN Beschwörungszauber 5. Grades (Waldläufer) Zeitaufwand: Aktion Reichweite: 45 Meter Komponenten: V, G, M (eine Nahoder Fernkampfwaffe im Wert von mindestens 1 KM) Wirkungsdauer: Unmittelbar Du führst die Waffe, die du zum Wirken des Zaubers verwendet hast, und wählst einen Punkt in Reichweite aus. Hunderte ähnlicher geisterhafter Waffen (oder Geschosse, die zur Waffe passen) hageln herab und verschwinden dann. In einem sechs Meter hohen Zylinder mit einem Radius von zwölf Metern um den gewählten Punkt führt jede Kreatur deiner Wahl, die du sehen kannst, einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 8W8 Energieschaden, anderenfalls die Hälfte.', 'Barde, Druide, Hexenmeister, Kleriker, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. kannst du auf einen weiteren Humanoiden zielen. PFEILSALVE BESCHWÖREN Beschwörungszauber 5. Grades (Waldläufer) Zeitaufwand: Aktion Reichweite: 45 Meter Komponenten: V, G, M (eine Nahoder Fernkampfwaffe im Wert von mindestens 1 KM) Wirkungsdauer: Unmittelbar Du führst die Waffe, die du zum Wirken des Zaubers verwendet hast, und wählst einen Punkt in Reichweite aus. Hunderte ähnlicher geisterhafter Waffen (oder Geschosse, die zur Waffe passen) hageln herab und verschwinden dann. In einem sechs Meter hohen Zylinder mit einem Radius von zwölf Metern um den gewählten Punkt führt jede Kreatur deiner Wahl, die du sehen kannst, einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 8W8 Energieschaden, anderenfalls die Hälfte.","type":"damage","dice":"8W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('pfeilspalier', 'PFEILSPALIER', 2, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (ein Ziergeflecht)', NULL, '8 Stunden', 0, 0, 'Du berührst bis zu vier nichtmagische Pfeile oder Armbrustbolzen und steckst sie in deinem Bereich in den Boden. Die Geschosse können während der Wirkungsdauer nicht aus dem Boden gezogen werden, und wann immer eine Kreatur außer dir sich dem Bereich der Geschosse erstmals in einem Zug bis auf neun Meter nähert oder ihren Zug dort beendet, erhebt sich eines der Geschosse, um sie zu treffen. Die Kreatur muss einen Geschicklichkeitsrettungswurf bestehen, oder sie erleidet 2W4 Stichschaden. Das Geschoss wird dabei zerstört. Der Zauber endet, wenn keines der Geschosse mehr im Boden steckt. Wenn du diesen Zauber wirkst, kannst du beliebig viele Kreaturen bestimmen, die der Zauber ignorieren soll.', 'Für jeden Zauberplatzgrad über dem 2. kannst du zwei weitere Geschosse einsetzen.', 'Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. kannst du zwei weitere Geschosse einsetzen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('pflanzentor', 'PFLANZENTOR', 6, 'Beschwörungszauber', 'Aktion', '3 Meter', 'V, G', NULL, '1 Minute', 0, 0, '298 KAPITEL 7 i ZAUBER •-• PFLANZENTOR UNO BnumwANoELN MACHEN AUS BÄUMEN EIN TRANSPORTNET7.WERH. Dieser Zauber erschafft eine magische Verbindung zwischen einer unbelebten Pflanze von mindestens großer Größe in Reichweite und einer anderen Pflanze in beliebiger Entfernung auf derselben Existenzebene. Du musst die Zielpflanze mindestens einmal gesehen oder berührt haben. Für die Wirkungsdauer kann jede Kreatur die verzauberte Pflanze betreten und steigt dann aus der Zielpflanze wieder heraus. Dafür benötigt sie 1,5 Meter ihrer Bewegungsrate.', '', 'Druide', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('pflanzenwachstum', 'PFLANZENWACHSTUM', 3, 'Verwandlungszauber', 'Aktion (Mehr Wachstum) oder 8 Stunden', '', '', NULL, '', 0, 0, '(Mehr Ertrag) Reichweite: 45 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Dieser Zauber kanalisiert Vitalität in Pflanzen. Der Zeitaufwand, den du verwendest, bestimmt, ob der Zauber den Effekt Mehr Wachstum oder den Effekt Mehr Ertrag (beide unten beschrieben) bewirkt. Mehr Wachstum: Wähle einen Punkt in Reichweite aus. Alle normalen Pflanzen in einem Radius von 30 Metern um diesen Punkt gedeihen und wuchern. Jede Kreatur in diesem Bereich muss für jeden Meter, den sie sich bewegt, vier Meter ihrer Bewegungsrate verbrauchen. Du kannst mindestens einen Bereich beliebiger Größe im Wirkungsbereich vom Effekt ausschließen. Mehr Ertrag: Alle Pflanzen in einem Radius von 0,8 Kilometern um einen Punkt in Reichweite werden 365 Tage lang ertragreich. Bei der Ernte spenden sie doppelt so viel Nahrung wie gewöhnlich. Sie können nur von einem Pflanzenwachstum-Zauber pro Jahr profitieren.', '', 'Barde, Druide, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('quell-des-mondlichts', 'QUELL DES MONDLICHTS', 4, 'Hervorrufungszauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Für die Wirkungsdauer umgibt kühles Licht deinen Körper und spendet in einem Radius von sechs Metern helles Licht und in einem Radius von weiteren sechs Metern dämmriges Licht. Für die Wirkungsdauer bist du gegen gleißenden Schaden resistent, und deine Nahkampfangriffe bewirken bei einem Treffer zusätzlich 2W6 gleißenden Schaden. Außerdem kannst du sofort, nachdem du Schaden durch eine Kreatur im Abstand von bis zu 18 Metern von dir erlitten hast, die du sehen kannst, eine Reaktion ausführen, um die Kreatur zu einem Konstitutionsrettungswurf zu zwingen. Scheitert der Wurf, so ist die Kreatur bis zum Ende deines nächsten Zugs blind. RARYS TELEPATHISCHES BAND Erkenntniszauber 5. Grades (Barde, Magier) Zeitaufwand: Aktion oder Ritual Reichweite: 9 Meter Komponenten: V, G, M (zwei Eier) Wirkungsdauer: 1 Stunde Du erschaffst zwischen bis zu acht bereitwilligen Kreaturen deiner Wahl in Reichweite eine telepathische Verbindung. Dadurch sind alle Kreaturen für die Wirkungsdauer psychisch miteinander verbunden. Kreaturen, die in keiner Sprache kommunizieren können, sind von diesem Zauber nicht betroffen. Bis der Zauber endet, können die Ziele telepathisch miteinander kommunizieren, unabhängig davon, ob sie eine gemeinsame Sprache sprechen. Die Kommunikation ist auf jede Entfernung möglich, reicht allerdings nicht bis auf andere Existenzebenen.', '', 'Barde, Druide', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('rascher-r-ckzug', 'RASCHER RÜCKZUG', 1, 'Verwandlungszauber', 'Bonusaktion', 'Selbst', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du führst die Spurt-Aktion aus, und bis der Zauber endet, kannst du sie erneut als Bonusaktion ausführen. EIN ZWERG HATTE EINE UNGLÜCKLICHE BEGEGNUNG MIT DEM INDIGO-STRAHL VON REGENBOGENSPIEL.', '', 'Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('redegewandtheit', 'REDEGEWANDTHEIT', 8, 'Verzauberungszauber', 'Aktion', 'Selbst', 'V', NULL, '1 Stunde', 0, 0, 'Für die Wirkungsdauer kannst du das Ergebnis deiner Charismawürfe durch eine 15 ersetzen. Magie, die ermittelt, ob du die Wahrheit sagst, zeigt stets an, dass du die Wahrheit sagst — egal, was du in Wirklichkeit von dir gibst.', '', 'Barde, Hexenmeister', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('regenbogenspiel', 'REGENBOGENSPIEL', 7, 'Hervorrufungszauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du verschießt in einem Kegel von 18 Metern acht Lichtstrahlen. Alle Kreaturen im Kegel führen einen Geschicklichkeitsrettungswurf aus. Würfle für jedes Ziel mit 1W8, um die Farbe des Strahls zu bestimmen, der es trifft (Effekt siehe Tabelle „Regenbogenstrahlen"). REGENBOGENSTRAHLEN 1W8 Strahl 1 Rot: Gescheiterter Rettungswurf 12W6 Feuerschaden. Erfolgreicher Rettungswurf Halb so viel Schaden. 2 Orange: Gescheiterter Rettungswurf 12W6 Säureschaden. Erfolgreicher Rettungswurf Halb so viel Schaden. 3 Gelb: Gescheiterter Rettungswurf 12W6 Blitzschaden. Erfolgreicher Rettungswurf: Halb so viel Schaden. 4 Grün: Gescheiterter Rettungswurf 12W6 Giftschaden. Erfolgreicher Rettungswurf Halb so viel Schaden. 5 Blau: Gescheiterter Rettungswurf 12W6 Kälteschaden. Erfolgreicher Rettungswurf: Halb so viel Schaden. 1W8 Strahl 6 Indigo: Gescheiterter Rettungswurf Das Ziel ist festgesetzt und führt am Ende jedes seiner Züge einen Konstitutionsrettungswurf aus. Bei drei erfolgreichen Rettungswürfen endet der Zustand. Bei drei gescheiterten Rettungswürfen wird das Ziel versteinert, bis es durch einen Effekt wie den des Zaubers Vollständige Genesung befreit wird. Die Erfolge und Misserfolge müssen nicht aufeinanderfolgen. Notiere einfach beide, bis das Ziel drei von einem hat. 7 Violett: Gescheiterter Rettungswurf: Das Ziel ist blind und führt zu Beginn deines nächsten Zugs einen Weisheitsrettungswurf aus. Bei einem erfolgreichen Rettungswurf endet der Zustand. Scheitert der Wurf, so endet der Zustand, und die Kreatur wird auf eine andere Existenzebene (nach Wahl des SL) teleportiert. 8 Besonders: Das Ziel wird von zwei Strahlen getroffen. Würfle zweimal. Bei einem Ergebnis von 8 würfelst du erneut.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('regenbogenwand', 'REGENBOGENWAND', 9, 'Bannzauber', 'Aktion', '18 Meter', 'V, G', NULL, '10 Minuten', 0, 0, 'Um einen Punkt in Reichweite entsteht eine undurchsichtige vertikale Wand aus bunt schimmerndem Licht. Die Wand ist bis zu 27 Meter lang, neun Meter hoch und 2,5 Zentimeter dick. Alternativ formst du eine Kugel mit einem Radius von bis zu 4,5 Metern um einen Punkt in Reichweite. Wand oder Kugel bleiben für die Wirkungsdauer bestehen. Platzierst du die Wand in einem Bereich, der von einer Kreatur besetzt ist, so endet der Zauber sofort ohne Effekt. Die Wand spendet in einem Radius von 30 Metern helles Licht und in einem Radius von weiteren 30 Metern dämmriges Licht. Du und Kreaturen, die du beim Wirken des Zaubers bestimmst, könnt die Wand durchqueren und sich in ihrer Nähe aufhalten, ohne Schaden zu erleiden. Wenn eine andere Kreatur, welche die Wand sehen kann, sich ihr auf höchstens sechs Meter nähert oder ihren Zug in diesem Bereich beginnt, muss sie einen Konstitutionsrettungswurf bestehen, oder sie ist eine Minute lang blind. Die Wand besteht aus sieben Schichten, jede in einer anderen Farbe. Wenn eine Kreatur durch die Wand greift oder geht, geschieht dies Schicht für Schicht durch alle sieben Schichten der Wand. Jede Schicht zwingt die Kreatur zu einem Geschicklichkeitsrettungswurf. Scheitert der Wurf, so ist die Kreatur von den Eigenschaften der Schicht betroffen, wie in der Tabelle „Regenbogenschichten" beschrieben. Die Wand besitzt eine RK von 10. Sie kann auch Schicht für Schicht von Rot nach Violett auf jeweils spezifische Weise zerstört werden. Eine zerstörte Schicht bleibt für die Wirkungsdauer verschwunden. Der Zauber Antimagisches Feld hat keine Wirkung auf die Mauer, und der Zauber Magie bannen kann nur auf die violette Schicht wirken. REGENBOGENSCHICHTEN Reihenfolge Effekte 1 Rot: Gescheiterter Rettungswurf 12W6 Feuers­chaden. Erfolgreicher Rettungswurf Halb so viel Schaden. Zusätzliche Effekte: Nichtmagische Fernkampfangriffe können diese Schicht nicht durchdringen. Die Schicht wird zerstört, wenn sie mindestens 25 Kälteschaden erleidet. 2 Orange: Gescheiterter Rettungswurf 12W6 Säures­chaden. Erfolgreicher Rettungswurf Halb so viel Schaden. Zusätzliche Effekte: Magische Fernkampfangriffe können diese Schicht nicht durchdringen. Die Schicht wird durch einen starken Wind (wie solchen durch Windstoß) zerstört. 3 Gelb: Gescheiterter Rettungswurf: 12W6 Blitzs­chaden. Erfolgreicher Rettungswurf: Halb so viel Schaden. Zusätzliche Effekte: Die Schicht wird zerstört, wenn sie mindestens 60 Energieschaden erleidet. 4 Grün: Gescheiterter Rettungswurf: 12W6 Gifts­chaden. Erfolgreicher Rettungswurf Halb so viel Schaden. Zusätzliche Effekte: Die Schicht wird durch den Zauber Wände passieren oder einen Zauber des mindestens gleichen Grades zerstört, der auf einer festen Oberfläche ein Portal öffnet. 5 Blau: Gescheiterter Rettungswurf: 12W6 Kältes­chaden. Erfolgreicher Rettungswurf Halb so viel Schaden. Zusätzliche Effekte: Die Schicht wird zerstört, wenn sie mindestens 25 Feuerschaden erleidet. 6 Indigo: Gescheiterter Rettungswurf Das Ziel ist festgesetzt und führt am Ende jedes seiner Züge einen Konstitutionsrettungswurf aus. Bei drei erfolgreichen Rettungswürfen endet der Zustand. Bei drei gescheiterten Rettungswürfen wird das Ziel versteinert, bis es durch einen Effekt wie den des Zaubers Vollständige Genesung befreit wird. Die Erfolge und Misserfolge müssen nicht aufeinanderfolgen. Notiere einfach beide, bis das Ziel drei von einem hat. Zusätzliche Effekte: Zauber können diese Schicht nicht durchdringen. Die Schicht wird von hellem Licht des Zaubers Tageslicht zerstört. 7 Violett: Gescheiterter Rettungswurf Das Ziel ist blind und führt zu Beginn deines nächsten Zugs einen Weisheitsrettungswurf aus. Bei einem erfolgreichen Rettungswurf endet der Zustand. Scheitert der Wurf, so endet der Zustand, und die Kreatur wird auf eine andere Existenzebene (nach Wahl des SL) teleportiert. Zusätzliche Effekte: Diese Schicht wird durch den Zauber Magie bannen zerstört. Eine Kreatur, die du berührst, erhält 4W8+15 Trefferpunkte zurück. Für die Wirkungsdauer erhält das Ziel zu Beginn jedes seiner Züge 1 Trefferpunkt zurück, und fehlende Gliedmaßen wachsen nach zwei Minuten nach.', '', 'Barde, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('reittier-finden', 'REITTIER FINDEN', 2, 'Beschwörungszauber', 'Aktion', '9 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du beschwörst ein außerweltliches Wesen, das sich als treues Reittier in einem freien Bereich deiner Wahl in Reichweite manifestiert. Es verwendet den Wertekasten eines Außerweltlichen Reittiers. Wenn du bereits über BONUSAKTIONEN Böses Starren (nur Unhold, wird nach langer Rast aufgeladen): Weisheitsrettungswurf Der SG entspricht deinem Zauber-rettungswurf-SG, eine Kreatur im Abstand von bis zu 18 Metern, die vom Reittier gesehen werden kann. Misserfolg: Das Ziel ist bis zum Ende deines nächsten Zugs verängstigt. Feenschritt (nur Feenwesen, wird nach langer Rast aufgeladen): Das Reittier teleportiert sich samt Reiter bis zu 18 Meter weit in einen freien Bereich deiner Wahl. AUSSERWELTLICHES REITTIER Großes celestisches Wesen, Feenwesen oder Unhold (nach deiner Wahl), neutral', '', 'Paladin', '{"summon":{"ac":null,"hp":null},"summon_text":"RK 10 + 1 pro Zaubergrad\nTP 5 + 10 pro Zaubergrad (das Reittier verfügt über eine\nii\tAnzahl von W10-Trefferwürfeln in Höhe des Zaubergrades) Bewegungsrate 18 m, Fliegen 18 m (erfordert einen Zauber des mindestens 4. Grades)\nMOD\nRW\nMOD\nRW\nMOD\nRW\nSTÄ\nil\n+4\n+4\nGES 12\n+1\n+1\nKON 14\n+2\n+2\n+1\nINT\n—2\n—2\nWEI 12\n+1\nCHA 8\n—1\n—1\nSinne Passive Wahrnehmung 11\nSprachen Telepathie auf 1,6 Kilometer (nur mit dir)\nHG - (EP 0, ÜB entspricht deinem Übungsbonus)\nMERKMALE\nLebensbindung: Wenn du Trefferpunkte durch einen Zauber ab dem 1. Grad zurückerhältst, erhält das Reittier die gleiche Anzahl von Trefferpunkten zurück, sofern du dich im Abstand von bis zu 1,5 Metern von ihm befindest.\nAKTIONEN\nAußerweltlicher Hieb: Nahkampfangriffswurf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W8 plus Zaubergrad gleißender (celestisches Wesen), psychischer (Feenwesen) oder nekrotischer (Unhold) Schaden.","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('regeneration', 'REGENERATION', 7, 'Verwandlungszauber', '1 Minute', 'Berührung', 'V, G, M (eine Gebetsmühle)', NULL, '1 Stunde', 0, 0, 'Heilende Berührung (nur celestisches Wesen, wird nach langer Rast aufgeladen): Eine Kreatur im Abstand von bis zu 1,5 Metern vom Reittier erhält eine Anzahl von Trefferpunkten in Höhe von 2W8 plus Zaubergrad zurück. ein Reittier durch diesen Zauber verfügst, wird dieses durch das neue ersetzt. RIESENINSEKT Großes Tier, gesinnungslos', '', 'Barde, Druide, Kleriker', '{"summon":{"ac":"11 + Zaubergrad","hp":null},"summon_text":"RK 11 + Zaubergrad\nTP 30 + 10 für jeden Zaubergrad ab dem 5.\nBewegungsrate 12 m, Klettern 12 m, Fliegen 12 m (nur Wespe)\nMOD\nRW\nMOD\nRW\nMOD\nRW\nSTÄ\n+3\n+3\nGES 13\n+1\n+1\nKON 15\n+2\n+2\nINT\n-3\n-3\nWEI 14\n+2\n+2\nCHA 3\n-4\n-4\nSinne Dunkelsicht 18 m, Passive Wahrnehmung 12 Sprachen Versteht die Sprachen, die du sprichst HG - (EP 0, ÜB entspricht deinem Übungsbonus)\nMERKMALE\nSpinnenklettern: Das Insekt kann ohne Attributswürfe schwierige Oberflächen erklimmen und sich an Decken entlang bewegen.\nAKTIONEN\nMehrfachangriff: Das Insekt führt eine Anzahl von Angriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet).\nGiftstich: Nahkampfangriffswurf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite drei Meter. Treffer: 1W6+3 plus Zaubergrad Stichschaden sowie 1W4 Giftschaden.\nNetzbolzen (nur Spinne): Fernkampfangriffswurf: Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 18 Meter. Treffer: 1W10+3 plus Zaubergrad Wuchtschaden, und die Bewegungsrate des Ziels ist bis zum Beginn seines nächsten Zugs auf° verringert.\nBONUSAKTIONEN\nGiftspeien (nur Tausendfüßler): Konstitutionsrettungswurf\nDein Zauberrettungswurf-SG, eine Kreatur im Abstand von bis zu drei Metern, die vom Insekt gesehen werden kann. Misserfolg: Das Ziel ist bis zum Beginn des nächsten Zugs des Insekts vergiftet.","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('rindenhaut', 'RINDENHAUT', 2, 'Verwandlungszauber', 'Bonusaktion', 'Berührung', 'V, G, M (eine Handvoll Rinde)', NULL, '1 Stunde', 0, 0, 'Du berührst eine bereitwillige Kreatur. Für die Wirkungsdauer nimmt die Haut des Ziels ein rindenartiges Erscheinungsbild an, und das Ziel erhält eine Rüstungsklasse von 17, sofern seine RK niedriger ist. Das Reittier sieht aus wie ein reittaugliches Tier deiner Wahl — beispielsweise ein Pferd, ein Kamel, ein Schreckenswolf oder ein Elch. Wann immer du diesen Zauber wirkst, wählst du den Kreaturentyp des Reittiers aus: celestisches Wesen, Feenwesen oder Unhold. Der Typ beeinflusst bestimmte Merkmale im Wertekasten. Kampf: Das Reittier ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt es deinen Initiativewert und fungiert als kontrolliertes Reittier, während du es reitest (wie in den Regeln zu berittenem Kampf definiert). Wenn du kampfunfähig bist, ist das Reittier unmittelbar nach dir am Zug, agiert unabhängig und fokussiert sich darauf, dich zu beschützen. Verschwinden des Reittiers: Das Reittier verschwindet, wenn seine Trefferpunkte auf 0 sinken oder wenn du stirbst. Wenn es verschwindet, hinterlässt es alles, was es getragen oder gehalten hat. Wenn du diesen Zauber erneut wirkst, entscheidest du, ob du erneut das verschwundene Reittier oder ein anderes beschwörst.', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.', 'Druide, Waldläufer', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.","type":"spell_level_ref","target":"creature_statblock"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('rieseninsekt', 'RIESENINSEKT', 4, 'Beschwörungszauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du beschwörst einen Riesentausendfüßler, eine Riesen­spinne oder eine Riesenwespe (nach deiner Wahl, wenn du den Zauber wirkst). Die Kreatur manifestiert sich in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten eines Rieseninsekts. Einige Details im Wertekasten werden von der ausgewählten Gestalt bestimmt. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet. Die Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. 3O2 KAPITEL 71 ZAUBER !14', 'Druide', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. 3O2 KAPITEL 71 ZAUBER !14","type":"spell_level_ref","target":"creature_statblock"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('r-stung-von-agathys', 'RÜSTUNG VON AGATHYS', 1, 'Bannzauber', 'Bonusaktion', 'Selbst', 'V, G, M (eine blaue Glasscherbe)', NULL, '1 Stunde', 0, 0, 'Du wirst von schützendem magischen Frost umgeben. Du erhältst 5 temporäre Trefferpunkte. Wenn eine Kreatur dich während der Wirkungsdauer mit einem Nahkampfangriffswurf trifft, erleidet sie 5 Kälteschaden. Wenn du keine temporären Trefferpunkte mehr hast, endet der Zauber vorzeitig.', 'Für jeden Zauberplatzgrad über dem 1. werden die temporären Trefferpunkte und der Kälteschaden jeweils um 5 erhöht.', 'Hexenmeister', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. werden die temporären Trefferpunkte und der Kälteschaden jeweils um 5 erhöht."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('sagenkunde', 'SAGENKUNDE', 5, 'Erkenntniszauber', '10 Minuten', 'Selbst', 'V, G, M (Weihrauch im Wert von mindestens 250 GM, den der Zauber verbraucht, und vier Stücke', NULL, 'Unmittelbar', 0, 0, 'Nenne oder beschreibe eine Person, einen Ort oder einen Gegenstand mit Berühmtheit. Der Zauber übermittelt dir mental eine Zusammenfassung der wichtigen Legenden über das Genannte wie vom SL beschrieben. Dabei kann es sich um wichtige Details, amüsante Enthüllungen oder sogar geheimes Wissen handeln. Je mehr Informationen du bereits kennst, desto präzisere und detailliertere Informationen erhältst du durch den Zauber. Die erhaltenen Informationen sind korrekt, können aber nach Ermessen des SL in bildlicher Sprache oder in Versform übermittelt werden. Wenn das Objekt deines Interesses eigentlich nicht berühmt ist, hörst du einige traurige Posaunentöne, und der Zauber scheitert.', '', 'Barde, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('sanfte-ruhe', 'SANFTE RUHE', 2, 'Nekromantiezauber', 'Aktion oder Ritual', 'Berührung', 'V, G, M (2 Kupfermünzen, die der Zauber verbraucht)', NULL, '10 Tage', 0, 1, 'Du berührst einen Leichnam oder andere sterbliche Überreste. Für die Wirkungsdauer ist das Ziel vor Verwesung geschützt und kann nicht untot werden. Der Zauber verlängert dadurch auch die Dauer, in der das Ziel wiederbelebt werden kann, da die Tage unter dem Einfluss dieses Zaubers bei Zaubern wie Tote erwecken nicht als verstrichene Zeit zählen.', '', 'Kleriker, Magier, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('s-urespritzer', 'SÄURESPRITZER', 0, 'Hervorrufungzauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du erzeugst an einem Punkt in Reichweite eine Säureblase, die in einer Kugel mit einem Radius von 1,5 Metern explodiert. Jede Kreatur in der Kugel muss einen Geschicklichkeitsrettungswurf bestehen, oder sie erleidet 1W6 Säureschaden.', 'Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.', 'Magier, Zauberer', '{"scaling":{"text":"Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.","type":"cantrip_scaling","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schauriger-strahl', 'SCHAURIGER STRAHL', 0, 'Hervorrufungzauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du schleuderst einen Strahl aus knisternder Energie. Führe einen Fernkampf-Zauberangriff gegen eine Kreatur oder einen Gegenstand in Reichweite aus. Bei einem Treffer erleidet das Ziel 1W10 Energieschaden.', 'Der Zauber erzeugt mehr Strahlen, wenn du die 5. (zwei Strahlen), die 11. (drei Strahlen) und die 17. (vier Strahlen) Stufe erreichst. Du kannst die Strahlen alle auf dasselbe Ziel oder auf jeweils unterschiedliche Ziele lenken. Führe für jeden Strahl einen eigenen Angriffswurf aus.', 'Hexenmeister', '{"scaling":{"text":"Der Zauber erzeugt mehr Strahlen, wenn du die 5. (zwei Strahlen), die 11. (drei Strahlen) und die 17. (vier Strahlen) Stufe erreichst. Du kannst die Strahlen alle auf dasselbe Ziel oder auf jeweils unterschiedliche Ziele lenken. Führe für jeden Strahl einen eigenen Angriffswurf aus."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('scheingel-nde', 'SCHEINGELÄNDE', 4, 'Illusionszauber', '10 Minuten', '90 Meter', 'V, G, M (ein Pilz)', NULL, '24 Stunden', 0, 0, 'Du lässt natürliches Gelände innerhalb eines Würfels mit 45 Metern Kantenlänge wie ein anderes natürliches Gelände aussehen, klingen und riechen. Weite Felder oder eine Straße können so als Sumpf, Berg, Erdspalte oder anderes schwieriges oder unpassierbares Gelände erscheinen. Ein Tümpel kann wie eine grüne Wiese wirken, ein Steilhang wie ein sanftes Gefälle, ein Graben voller Felsbrocken wie eine breite Straße. Gebäude, Ausrüstung und Kreaturen im Bereich verändern sich nicht. Die ertastbaren Eigenschaften des Geländes bleiben unverändert, sodass Kreaturen, die den Bereich betreten, die Illusion wahrscheinlich als solche bemerken. Ist der Unterschied bei Berührung nicht offensichtlich, so kann eine Kreatur, welche die Illusion untersucht, die Studieren-Aktion und einen Intelligenzwurf (Nachforschungen) gegen deinen Zauberrettungswurf-SG ausführen, um sie zu durchschauen. Wenn eine Kreatur die Illusion als solche erkennt, sieht sie diese als ein dem eigentlichen Gelände überlagertes vages Bild.', '', 'Barde, Druide, Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schild', 'SCHILD', 1, 'Bannzauber', 'Reaktion, die du ausführst, wenn du von', '', '', NULL, '', 0, 0, 'einem Angriffswurf getroffen oder als Ziel des Zaubers Magisches Geschoss ausgewählt wirst Reichweite: Selbst Komponenten: V, G Wirkungsdauer: 1 Runde Eine nicht wahrnehmbare Barriere aus magischer Energie schützt dich. Bis zum Beginn deines nächsten Zugs hast du einen Bonus von +5 auf deine RK (auch gegen den auslösenden Angriff), und du erleidest keinen Schaden durch Magisches Geschoss. SCHILD DES GLAUBENS Bannzauber 1. Grades (Kleriker, Paladin) Zeitaufwand: Bonusaktion Reichweite: 18 Meter Komponenten: V, G, M (eine Gebetsschriftrolle) Wirkungsdauer: Konzentration, bis zu 10 Minuten Ein schimmerndes Feld umgibt eine Kreatur deiner Wahl in Reichweite und gewährt ihr für die Wirkungsdauer einen Bonus von +2 auf ihre RK.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schlaf', 'SCHLAF', 1, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V, G, M (eine Prise Sand oder Rosenblütenblätter)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Jede Kreatur deiner Wahl in einer Kugel mit einem Radius von 1,5 Metern um einen Punkt in Reichweite muss einen Weisheitsrettungswurf bestehen, oder es ist bis zum Ende seines nächsten Zugs kampfunfähig und muss den Rettungswurf dann wiederholen. Wenn das Ziel auch beim zweiten Rettungswurf scheitert, ist es für die Wirkungsdauer bewusstlos. Der Zauber endet bei einem Ziel, wenn es Schaden erleidet oder jemand im Abstand von bis zu 1,5 Metern von ihm eine Aktion ausführt, um es zu schütteln. Kreaturen, die wie Elfen nicht schlafen, oder die gegen den Zustand Erschöpft immun sind, bestehen Rettungswürfe gegen diesen Zauber automatisch.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schlingranke', 'SCHLINGRANKE', 4, 'Beschwörungszauber', 'Bonusaktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du beschwörst eine Ranke, die in einem freien Bereich in Reichweite, den du sehen kannst, aus einer Oberfläche sprießt. Die Ranke bleibt für die Wirkungsdauer bestehen. Führe einen Nahkampf-Zauberangriff gegen eine Kreatur im Abstand von bis zu neun Metern von der Ranke aus. Bei einem Treffer erleidet das Ziel 4W8 Wuchtschaden und wird um bis zu neun Meter zur Ranke gezogen. Wenn das Ziel von höchstens riesiger Größe ist, wird es gepackt (Rettungswurf-SG entspricht deinem Zauberrettungswurf-SG). Die Ranke kann immer nur jeweils eine Kreatur gepackt halten, und du kannst die Ranke veranlassen, eine gepackte Kreatur loszulassen (keine Aktion erforderlich). Als Bonusaktion in deinen folgenden Zügen kannst du den Angriff auf eine Kreatur im Abstand von bis zu neun Metern von der Ranke wiederholen.', 'Für jeden Zauberplatzgrad über dem 4. wird die Anzahl von Kreaturen, die von der Ranke gepackt werden können, um eins erhöht.', 'Druide, Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird die Anzahl von Kreaturen, die von der Ranke gepackt werden können, um eins erhöht."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schmieren', 'SCHMIEREN', 1, 'Beschwörungszauber', 'Aktion', '18 Meter', 'V, G, M (ein Stück Schweineschwarte oder Butter)', NULL, '1 Minute', 0, 0, 'In einem quadratischen Bereich mit drei Metern Kantenlänge um einen Punkt in Reichweite bedeckt nichtbrennbares Fett den Boden und macht ihn für die Wirkungsdauer zu schwierigem Gelände. Wenn das Schmierfett erscheint, muss jede Kreatur in diesem Bereich einen Geschicklichkeitsrettungswurf bestehen, oder sie rutscht aus und stürzt hin, sodass sie liegend ist. Auch Kreaturen, die den Bereich betreten oder ihren Zug darin beginnen, müssen diesen Rettungswurf bestehen, um nicht auszurutschen.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schneesturm', 'SCHNEESTURM', 3, 'Beschwörungszauber', 'Aktion', '45 Meter', 'V, G, M (ein Miniaturregenschirm)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Für die Wirkungsdauer fallen in einem zwölf Meter hohen Zylinder mit einem Radius von sechs Metern um einen Punkt deiner Wahl in Reichweite Schnee und Graupel. Der Bereich ist komplett verschleiert, und offene Flammen erlöschen darin. Der Boden im Zylinder ist schwieriges Gelände. Wenn eine Kreatur den Zylinder erstmals in ihrem Zug betritt oder ihren Zug darin beginnt, muss sie einen Geschicklichkeitsrettungswurf bestehen, oder sie stürzt hin, sodass sie liegend ist, und verliert ihre Konzentration.', '', 'Druide, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schneller-k-cher', 'SCHNELLER KÖCHER', 5, 'Verwandlungszauber', 'Bonusaktion', 'Selbst', 'V, G, M (ein Köcher im Wert von mindestens 1 GM)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du kannst beim Wirken des Zaubers sowie als Bonusaktion während der Wirkungsdauer zwei Angriffe mit einer Waffe ausführen, die Pfeile oder Bolzen verschießt, beispielsweise mit einem Langbogen oder einer leichten Armbrust. Der Zauber erzeugt die für jeden Angriff benötigten Geschosse auf magische Art. Alle vom Zauber erzeugten Pfeile oder Bolzen bewirken den gleichen Schaden wie ihre nichtmagischen Gegenstücke und lösen sich auf, sobald sie getroffen oder verfehlt haben.', '', 'Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schockgriff', 'SCHOCKGRIFF', 0, 'Hervorrufungzauber', 'Aktion', 'Berührung', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Von dir springt ein Blitz auf eine Kreatur über, die du zu berühren versuchst. Führe einen Nahkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 1W8 Blitzschaden und kann bis zum Beginn seines nächsten Zugs keine Gelegenheitsangriffe ausführen.', 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W8 erhöht.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W8 erhöht.","type":"cantrip_scaling","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schutz-vor-energie', 'SCHUTZ VOR ENERGIE', 3, 'Bannzauber', 'Aktion', 'Berührung', 'V, G', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Für die Wirkungsdauer ist eine bereitwillige Kreatur, die du berührst, gegen eine Schadensart deiner Wahl resistent: Blitz, Feuer, Kälte, Säure oder Schall.', '', 'Druide, Kleriker, Magier, Waldläufer, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schutz-vor-gift', 'SCHUTZ VOR GIFT', 2, 'Bannzauber', 'Aktion', 'Berührung', 'V, G', NULL, '1 Stunde', 0, 0, 'Du berührst eine Kreatur und beendest bei ihr den Zustand Vergiftet. Für die Wirkungsdauer ist das Ziel bei Rettungswürfen zum Vermeiden oder Beenden des Zustands Vergiftet im Vorteil und gegen Giftschaden resistent.', '', 'Druide, Kleriker, Paladin, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schutz-vor-gut-und-b-se', 'SCHUTZ VOR GUT UND BÖSE', 1, 'Bannzauber', 'Aktion', 'Berührung', 'V, G, M (eine Flasche Weihwasser im Wert von mindestens 25 GM, die der Zauber verbraucht)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Für die Wirkungsdauer ist eine bereitwillige Kreatur, die du berührst, vor bestimmten Kreaturentypen geschützt: vor Aberrationen, celestischen Wesen, Elementaren, Feenwesen, Unholden und Untoten. Der Schutz gewährt mehrere Vorzüge: Angriffswürfe von Kreaturen dieses Typs sind gegen das Ziel im Nachteil. Außerdem kann das Ziel nicht von Kreaturen dieses Typs besessen, bezaubert oder verängstigt werden. Wenn das Ziel bereits von einer solchen Kreatur besessen, bezaubert oder verängstigt ist, so ist es bei jedem neuen Rettungswurf gegen den entsprechenden Effekt im Vorteil.', '', 'Druide, Hexenmeister, Kleriker, Magier, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('sch-tzendes-band', 'SCHÜTZENDES BAND', 2, 'Bannzauber', 'Aktion', 'Berührung', 'V, G, M (ein Paar Platinringe im Wert von jeweils mindestens 50 GM, die du und das Ziel für die Wirkungsdauer tragen müsst)', NULL, '1 Stunde', 0, 0, 'Du berührst eine bereitwillige Kreatur und erzeugst eine mystische Verbindung zwischen euch, die bestehen bleibt, bis der Zauber endet. Solange sich das Ziel im Abstand von bis zu 18 Metern von dir befindet, erhält es einen Bonus 3O5 von +1 auf seine RK und auf Rettungswürfe und ist gegen alle Schadensarten resistent. Allerdings erleidest du jedes Mal, wenn dem Ziel Schaden zugefügt wird, die gleiche Menge von Schaden. 3O6 Der Zauber endet, falls deine Trefferpunkte auf 0 sinken oder ihr euch weiter als 18 Meter voneinander entfernt. Der Zauber endet auch, wenn er auf einen der beiden Beteiligten erneut gewirkt wird.', '', 'Kleriker, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schutzgeister', 'SCHUTZGEISTER', 3, 'Beschwörungszauber', 'Aktion', 'Selbst', 'V, G, M (eine Gebetsschriftrolle)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Beschützende Naturgeister umschwirren dich für die Wirkungsdauer in einer Ausströmung von 4,5 Metern. Wenn du guter oder neutraler Gesinnung bist, erscheint ihre Spektralform engeloder feenhaft (nach deiner Wahl). Wenn du böser Gesinnung bist, erscheinen sie als Unholde. Wenn du diesen Zauber wirkst, kannst du Kreaturen bestimmen, die vom Zauber nicht betroffen sind. Die Bewegungsrate betroffener Kreaturen ist innerhalb der Ausströmung halbiert, und wann immer die Aus­strömung in den Bereich einer Kreatur gelangt, eine Kreatur die Ausströmung betritt oder ihren Zug darin beendet, muss die Kreatur einen Weisheitsrettungswurf ausführen. Scheitert der Wurf, so erleidet die Kreatur 3W8 gleißenden Schaden (wenn du guter oder neutraler Gesinnung bist) oder 3W8 nekrotischen Schaden (wenn du böser Gesinnung bist). Bei einem erfolgreichen Rettungswurf erleidet die Kreatur halb so viel Schaden. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen.', 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W8 erhöht. SCHUTZHÜLLE GEGEN LEBENDES Bannzauber 5. Grades (Druide) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Stunde Für die Wirkungsdauer geht eine Aura mit einer Ausströ­mung von drei Metern von dir aus. Die Aura verhindert, dass Kreaturen außer Konstrukten und Untoten sich hineinbewegen oder hineingreifen. Sie können jedoch Zauber wirken und mit Fernkampfwaffen oder Waffen mit Reichweite durch die Barriere angreifen. Wenn du dich so bewegst, dass eine betroffene Kreatur die Barriere passieren muss, endet der Zauber.', 'Kleriker', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W8 erhöht. SCHUTZHÜLLE GEGEN LEBENDES Bannzauber 5. Grades (Druide) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Stunde Für die Wirkungsdauer geht eine Aura mit einer Ausströ­mung von drei Metern von dir aus. Die Aura verhindert, dass Kreaturen außer Konstrukten und Untoten sich hineinbewegen oder hineingreifen. Sie können jedoch Zauber wirken und mit Fernkampfwaffen oder Waffen mit Reichweite durch die Barriere angreifen. Wenn du dich so bewegst, dass eine betroffene Kreatur die Barriere passieren muss, endet der Zauber.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schutzkreis', 'SCHUTZKREIS', 3, 'Bannzauber', '1 Minute', '3 Meter', 'V, G, M (Salz und Silberpulver im Wert von mindestens 100 GM, das der Zauber verbraucht)', NULL, '1 Stunde', 0, 0, 'Du erschaffst einen sechs Meter hohen Zylinder aus magischer Energie mit einem Radius von drei Metern um einen Punkt auf dem Boden in Reichweite, den du sehen kannst. Wo der Zylinder den Boden oder eine andere Oberfläche schneidet, erscheinen leuchtende Runen. Wähle mindestens einen der folgenden Kreaturentypen aus: celestische Wesen, Elementare, Feenwesen, Unholde oder Untote. Der Kreis beeinflusst eine Kreatur des ausgewählten Typs wie folgt: Die Kreatur kann den Zylinder nicht freiwillig auf nichtMagische Weise betreten. Versucht die Kreatur, Teleportation oder Ebenenreisen zu verwenden, so muss sie zuerst einen Charismarettungswurf bestehen. Die Kreatur ist bei Angriffswürfen gegen Ziele innerhalb des Zylinders im Nachteil. Ziele innerhalb des Zylinders können von der Kreatur nicht besessen, bezaubert oder verängstigt werden. Jedes Mal, wenn du diesen Zauber wirkst, kannst du seine Wirkung umkehren, sodass Kreaturen des ausgewählten Typs den Zylinder nicht verlassen können und Ziele außerhalb des Zylinders geschützt sind.', 'Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 2W8 erhöht.', 'Hexenmeister, Kleriker, Magier, Paladin', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 2W8 erhöht.","type":"damage","dice":"2W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schwache-genesung', 'SCHWACHE GENESUNG', 2, 'Bannzauber', 'Bonusaktion', 'Berührung', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du berührst eine Kreatur und beendest einen Zustand bei ihr: Blind, Gelähmt, Taub oder Vergiftet.', '', 'Barde, Druide, Kleriker, Paladin, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schw-chestrahl', 'SCHWÄCHESTRAHL', 2, 'Nekromantiezauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du verschießt einen Strahl aus entkräftender Energie auf eine Kreatur in Reichweite. Das Ziel muss einen Konstitutionsrettungswurf ausführen. Bei einem erfolgreichen Rettungswurf ist das Ziel bei seinem nächsten Angriffswurf bis zum Beginn deines nächsten Zugs im Nachteil. Scheitert der Wurf, so ist das Ziel für die Wirkungsdauer bei W20-Prüfungen, die auf Stärke basieren, im Nachteil. Während dieser Dauer zieht es außerdem 1W8 von all seinen Schadenswürfen ab. Das Ziel wiederholt den Rettungswurf am Ende jedes seiner Züge. Bei einem Erfolg endet der Zauber.', '', 'Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schweben', 'SCHWEBEN', 2, 'Verwandlungszauber', 'Aktion', '18 Meter', 'V, G, M (eine Metallfeder)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Ein Ziel (eine Kreatur oder ein loser Gegenstand) deiner Wahl in Reichweite, das du sehen kannst, steigt senkrecht sechs Meter empor und schwebt für die Wirkungsdauer in der Luft. Der Zauber kann Gegenstände mit einem Maximalgewicht von 250 Kilogramm anheben. Besteht eine nicht bereitwillige Kreatur einen Konstitutionsrettungswurf, so ist sie nicht betroffen. Das Ziel kann sich nur bewegen, indem es sich von festen Gegenständen oder Oberflächen wie Wänden oder Decken in Reichweite abstößt oder sich daran entlanghangelt. Dabei gilt die Kletterbewegungsrate. Während deines Zugs kannst du das Ziel um bis zu sechs Meter anheben oder absenken. Wenn du selbst das Ziel bist, kannst du dich im Rahmen deiner Bewegung anheben oder absenken. Anderenfalls kannst du das Ziel mit einer magischen Aktion bewegen. Dabei musst du die Reichweite des Zaubers einhalten. Wenn der Zauber endet und das Ziel noch schwebt, sinkt es sanft zu Boden. Der Raum fasst bis zu acht Kreaturen von höchstens mittelgroßer Größe. Angriffe, Zauber und andere Effekte können den extradimensionalen Raum weder von innen noch von außen durchdringen. Kreaturen im Raum können jedoch durch das Portal nach draußen blicken. Alles, was im Raum enthalten ist, fällt heraus, wenn der Zauber endet.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('schwerkraft-umkehren', 'SCHWERKRAFT UMKEHREN', 7, 'Verwandlungszauber', 'Aktion', '30 Meter', 'V, G, M (ein Magnetstein und Eisenspäne)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Dieser Zauber kehrt in einem 30 Meter hohen Zylinder mit einem Radius von 15 Metern um einen Punkt in Reichweite die Schwerkraft um. Alle Kreaturen und Gegenstände im Bereich, die nicht mit dem Boden verbunden sind, stürzen nach oben bis zur Oberseite des Zylinders. Eine Kreatur kann einen Geschicklichkeitsrettungswurf ausführen, um sich an einem verankerten Gegenstand in Reichweite festzuhalten und den Sturz nach oben zu vermeiden. Wenn eine Decke oder ein fixer Gegenstand bei dem umgekehrten Sturz im Weg ist, kollidieren fallende Gegenstände und Kreaturen mit ihm wie bei einem normalen Sturz. Erreicht eine betroffene Kreatur oder ein Gegenstand die Oberseite des Zylinders, ohne mit etwas zu kollidieren, so verbleibt das Ziel für die Wirkungsdauer schwebend dort. Wenn der Zauber endet, fallen betroffene Gegenstände und Kreaturen nach unten.', '', 'Druide, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('segnen', 'SEGNEN', 1, 'Verzauberungszauber', 'Aktion', '9 Meter', 'V, G, M (ein heiliges Symbol im Wert von mindestens 5 GM)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du segnest bis zu drei Kreaturen in Reichweite. Wann immer ein Ziel während der Wirkungsdauer einen Angriffs­oder Rettungswurf ausführt, fügt es dem Ergebnis 1W4 hinzu.', 'Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen.', 'Kleriker, Paladin', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('seiltrick', 'SEILTRICK', 2, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (ein Stück Seil)', NULL, '1 Stunde', 0, 0, 'Du berührst ein Seil. Ein Seilende erhebt sich in die Luft, bis das gesamte Seil senkrecht zum Boden in der Luft hängt oder das Seilende eine Decke erreicht. Am oberen Ende des Seils öffnet sich ein unsichtbares Portal zu einem extradimensionalen Raum. Das Portal ist einen Meter mal 1,5 Meter groß und bleibt für die Wirkungsdauer bestehen. Der Raum kann über das Seil erreicht werden, das in den Raum hineingezogen werden oder heraushängen kann.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('selbstverkleidung', 'SELBSTVERKLEIDUNG', 1, 'Illusionszauber', 'Aktion', 'Selbst', 'V, G', NULL, '1 Stunde', 0, 0, 'Du veränderst dein Aussehen einschließlich Kleidung, Rüstung, Waffen und anderer Habseligkeiten, bis der Zauber endet oder du ihn mit einer Aktion beendest. Du kannst 30 Zentimeter größer oder kleiner sowie schwerer oder leichter erscheinen. Allerdings musst du eine Gestalt mit der gleichen Grundanordnung von Gliedmaßen annehmen. Abgesehen davon sind die Einzelheiten der Illusion dir überlassen. Einer genauen körperlichen Untersuchung halten die Veränderungen dieser Illusion nicht stand. Wenn du diesen Zauber beispielsweise nutzt, um dir einen Hut aufzusetzen, durchdringen Gegenstände diesen einfach. Will jemand den Hut berühren, so fühlt er dort nichts. Um deine Verkleidung als solche zu erkennen, muss eine Kreatur die Studieren-Aktion ausführen, um dein Aussehen zu untersuchen, und einen Intelligenzwurf (Nachforschungen) gegen deinen Zauberrettungswurf-SG bestehen.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('sengender-strahl', 'SENGENDER STRAHL', 2, 'Hervorrufungszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du schleuderst drei feurige Strahlen. Du kannst sie auf ein Ziel oder auf mehrere Ziele in Reichweite schleudern. Führe für jeden Strahl einen Fernkampf-Zauberangriff aus. Bei einem Treffer erleidet das Ziel 2W6 Feuerschaden pro Strahl.', 'Für jeden Zauberplatzgrad über dem 1. wird jeder Schaden um 1W6 erhöht.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird jeder Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('shillelagh', 'SHILLELAGH', 0, 'Verwandlungzauber', 'Bonusaktion', 'Selbst', 'V, G, M (ein Mistelzweig)', NULL, '1 Minute', 0, 0, 'Das Holz eines Knüppels oder Kampfstabs, den du in der Hand hältst, wird von der Macht der Natur erfüllt. Für die Wirkungsdauer kannst du bei Angriffsund Schadens-würfen von Nahkampfangriffen mit dieser Waffe dein Attribut zum Zauberwirken anstatt Stärke verwenden, und der Schadenswürfel der Waffe wird zu einem W8. Wenn der Angriff Schaden bewirkt, kann dies Energieschaden oder die normale Schadensart der Waffe sein (nach deiner Wahl). Der Zauber endet vorzeitig, wenn du ihn erneut wirkst oder die Waffe loslässt.', 'Der Schadenswürfel ändert sich, wenn du die 5. (W10), die 11. (W12) und die 17. (2W6) Stufe erreichst. SIMULAKRUM Illusionszauber Z Grades (Magier) Zeitaufwand: 12 Stunden Reichweite: Berührung Komponenten: V, G, M (Rubinpulver im Wert von mindestens 1.500 GM, das der Zauber verbraucht) Wirkungsdauer: Bis der Zauber gebannt wird Du erschaffst das Abbild eines Tieres oder eines Humanoiden. Das Original muss sich während des gesamten Zeitaufwands im Abstand von bis zu drei Metern von dir befinden. Du schließt das Zauberwirken ab, indem du sowohl die Kreatur als auch einen Haufen Eis oder Schnee von gleicher Größe berührst. Der Haufen verwandelt sich in das Simulakrum, welches eine Kreatur ist. Es verwendet die Spielwerte der ursprünglichen Kreatur zum Zeitpunkt des Zauberwirkens, ist allerdings ein Konstrukt, sein Trefferpunktemaximum beträgt die Hälfte, und es kann diesen Zauber nicht wirken. Das Simulakrum ist dir und allen von dir bestimmten Kreaturen freundlich gesinnt. Es gehorcht deinen Befehlen und agiert im Kampf in deinem Zug. Ihm sind weder Stufenaufstiege noch kurze oder lange Rasten möglich. Wenn das Simulakrum Schaden erleidet, besteht die einzige Möglichkeit, seine Trefferpunkte wiederherzustellen, in einer Reparatur während deiner langen Rast, wobei du Komponenten im Wert von 100 GM pro wiederherge­stelltem Trefferpunkt aufwenden musst. Bei der Reparatur muss es im Abstand von bis zu 1,5 Metern von dir bleiben. Das Simulakrum bleibt bestehen, bis seine Trefferpunkte auf 0 sinken. Dann wird es wieder zu Schnee oder Eis und schmilzt. Wirkst du diesen Zauber erneut, so wird das vorige Simulakrum sofort zerstört.', 'Druide', '{"scaling":{"text":"Der Schadenswürfel ändert sich, wenn du die 5. (W10), die 11. (W12) und die 17. (2W6) Stufe erreichst. SIMULAKRUM Illusionszauber Z Grades (Magier) Zeitaufwand: 12 Stunden Reichweite: Berührung Komponenten: V, G, M (Rubinpulver im Wert von mindestens 1.500 GM, das der Zauber verbraucht) Wirkungsdauer: Bis der Zauber gebannt wird Du erschaffst das Abbild eines Tieres oder eines Humanoiden. Das Original muss sich während des gesamten Zeitaufwands im Abstand von bis zu drei Metern von dir befinden. Du schließt das Zauberwirken ab, indem du sowohl die Kreatur als auch einen Haufen Eis oder Schnee von gleicher Größe berührst. Der Haufen verwandelt sich in das Simulakrum, welches eine Kreatur ist. Es verwendet die Spielwerte der ursprünglichen Kreatur zum Zeitpunkt des Zauberwirkens, ist allerdings ein Konstrukt, sein Trefferpunktemaximum beträgt die Hälfte, und es kann diesen Zauber nicht wirken. Das Simulakrum ist dir und allen von dir bestimmten Kreaturen freundlich gesinnt. Es gehorcht deinen Befehlen und agiert im Kampf in deinem Zug. Ihm sind weder Stufenaufstiege noch kurze oder lange Rasten möglich. Wenn das Simulakrum Schaden erleidet, besteht die einzige Möglichkeit, seine Trefferpunkte wiederherzustellen, in einer Reparatur während deiner langen Rast, wobei du Komponenten im Wert von 100 GM pro wiederherge­stelltem Trefferpunkt aufwenden musst. Bei der Reparatur muss es im Abstand von bis zu 1,5 Metern von dir bleiben. Das Simulakrum bleibt bestehen, bis seine Trefferpunkte auf 0 sinken. Dann wird es wieder zu Schnee oder Eis und schmilzt. Wirkst du diesen Zauber erneut, so wird das vorige Simulakrum sofort zerstört.","type":"cantrip_scaling","dice":"2W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('sonnenfeuer', 'SONNENFEUER', 8, 'Hervorrufungszauber', 'Aktion', '45 Meter', 'V, G, M (ein Stück Sonnenstein)', NULL, 'Unmittelbar', 0, 0, 'Eine Kugel mit einem Radius von 18 Metern um einen Punkt deiner Wahl in Reichweite wird von gleißendem Sonnenlicht erfüllt. Jede Kreatur in der Linie führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 12W6 gleißenden Schaden und ist eine Minute lang blind. Bei einem erfolgreichen Rettungswurf erleidet das Ziel nur halb so viel Schaden. Eine von diesem Zauber geblendete Kreatur führt am Ende jedes ihrer Züge einen weiteren Konstitutions-rettungswurf aus. Bei einem Erfolg endet der Effekt bei ihr. Dieser Zauber bannt durch beliebige Zauber erschaffene Dunkelheit im Bereich.', '', 'Druide, Kleriker, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('sonnenstrahl', 'SONNENSTRAHL', 6, 'Hervorrufungszauber', 'Aktion', 'Selbst', 'V, G, M (eine Lupe)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du verschießt einen Sonnenstrahl in einer 1,5 Meter breiten und 18 Meter langen Linie. Jede Kreatur in der Linie führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 6W8 gleißenden Schaden und ist bis zum Beginn deines nächsten Zugs blind. Bei einem erfolgreichen Rettungswurf erleidet das Ziel nur halb so viel Schaden. Bis der Zauber endet, kannst du eine magische Aktion ausführen, um eine neue gleißende Linie zu erschaffen. Für die Wirkungsdauer erstrahlt ein heller Funke über dir. Er spendet in einem Radius von neun Metern helles Licht und in einem Radius von weiteren neun Metern dämmriges Licht. Beim Licht handelt es sich um Sonnenlicht. SPÄTZÜNDENDER', 'Für jeden Zauberplatzgrad über dem 7. wird der Grundschaden um 1W6 erhöht.', 'Druide, Kleriker, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 7. wird der Grundschaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('spiegelbilder', 'SPIEGELBILDER', 2, 'Illusionszauber', 'Aktion', 'Selbst', 'V, G', NULL, '1 Minute', 0, 0, 'In deinem Bereich erscheinen drei illusionäre Duplikate deiner selbst. Für die Wirkungsdauer bewegen sich die Duplikate mit dir und imitieren deine Aktionen sowie deine Position, sodass die reale Version unmöglich zu erkennen ist. Wann immer dich eine Kreatur während der Wirkungs­dauer des Zaubers mit einem Angriffswurf trifft, würfle für jedes verbleibende Duplikat mit einem 1W6. Würfelst du jeweils mindestens eine 3, wird statt dir das entsprechende Duplikat getroffen und dabei zerstört. Die Duplikate ignorieren alle anderen Schadensquellen und Effekte. Wenn alle drei Duplikate zerstört wurden, endet der Zauber. Eine Kreatur ist von diesem Zauber nicht betroffen, wenn sie den Zustand Blind hat oder aber über Blindsicht oder Wahrer Blick verfügt.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('spinnenklettern', 'SPINNENKLETTERN', 2, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (etwas Pech und eine Spinne)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Eine bereitwillige Kreatur, die du berührst, erhält für die Wirkungsdauer die Fähigkeit, sich in alle Richtungen und mit freien Händen über senkrechte Oberflichen sowie an Decken zu bewegen. Außerdem erhält das Ziel eine Kletterbewegungsrate in Höhe seiner Bewegungsrate.', 'Für jeden Zauberplatzgrad über dem 2. kannst du auf eine weitere Kreatur zielen.', 'Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('sprachen-verstehen', 'SPRACHEN VERSTEHEN', 1, 'Erkenntniszauber', 'Aktion oder Ritual', 'Selbst', 'V, G, M (je eine Prise Ruß und Salz)', NULL, '1 Stunde', 0, 1, 'Für die Wirkungsdauer verstehst du die wörtliche Bedeutung jeder Sprache, die du hörst oder liest. Außerdem verstehst du jede Schriftsprache, die du liest, sofern du die Oberfläche berührst, auf der die Worte geschrieben stehen. Eine Seite Text zu lesen dauert etwa eine Minute. Dieser Zauber entschlüsselt weder Symbole noch Geheimbotschaften.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('springen', 'SPRINGEN', 1, 'Verwandlungszauber', 'Bonusaktion', 'Berührung', 'V, G, M (Hinterbein einer Heuschrecke)', NULL, '1 Minute', 0, 0, 'Du berührst eine bereitwillige Kreatur. Für die Wirkungs­dauer kann diese Kreatur in jedem ihrer Züge bis zu neun Meter weit springen, indem sie drei Meter ihrer Bewegungsrate verbraucht.', 'Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen.', 'Druide, Magier, Waldläufer, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('spr-hende-farben', 'SPRÜHENDE FARBEN', 1, 'Illusionszauber', 'Aktion', 'Selbst', 'V, G, M (eine Prise bunter Sand)', NULL, 'Unmittelbar', 0, 0, 'Du entfesselst schillernde bunte Lichter. Jede Kreatur in einem von dir ausgehenden Kegel von 4,5 Metern muss einen Konstitutionsrettungswurf bestehen, oder sie ist bis zum Ende deines nächsten Zugs blind. 3W', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('spurloses-gehen', 'SPURLOSES GEHEN', 2, 'Bannzauber', 'Aktion', 'Selbst', 'V, G, M (Asche eines Mistelzweigs)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Von dir geht für die Wirkungsdauer eine verhüllende Aura mit einer Ausströmung von neun Metern aus. Du und jede Kreatur deiner Wahl habt in der Aura einen Bonus von +10 auf Geschicklichkeitswürfe (Heimlichkeit), und ihr hinterlasst keine Spuren.', '', 'Druide, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('stahlwindschlag', 'STAHLWINDSCHLAG', 5, 'Beschwörungszauber', 'Aktion', '9 Meter', 'G, M (eine Nahkampfwaffe im Wert von mindestens 1 SM)', NULL, 'Unmittelbar', 0, 0, 'Du schwingst die Waffe, die du zum Wirken des Zaubers verwendet hast, und verschwindest dann, um zuzuschlagen wie der Wind. Wähle bis zu fünf Kreaturen in Reichweite aus, die du sehen kannst. Führe einen Nahkampf-Zauberangriff gegen jedes Ziel aus. Bei einem Treffer erleidet ein Ziel 6W10 Energieschaden. Dann teleportierst du dich in einen freien Bereich, den du sehen kannst und der im Abstand von bis zu 1,5 Metern von einem der Ziele liegt.', '', 'Magier, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('stein-formen', 'STEIN FORMEN', 4, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (weicher Lehm)', NULL, 'Unmittelbar', 0, 0, 'Du berührst einen steinernen Gegenstand von höchstens mittelgroßer Größe oder einen Abschnitt aus Stein, der in keiner Abmessung größer als 1,5 Meter sein darf, und verformst ihn nach deinen Wünschen. Du könntest beispielsweise eine Waffe, eine Statue oder eine Truhe aus einem Felsbrocken formen oder einen kleinen Durchgang in einer Mauer schaffen, die höchstens 1,5 Meter dick ist. Du könntest auch eine Steintür oder ihren Rahmen teilweise umformen, um sie zu versiegeln. Der von dir erschaffene Gegenstand kann bis zu zwei Scharniere und einen Riegel besitzen, feinere mechanische Details sind jedoch nicht möglich.', '', 'Druide, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('steinhaut', 'STEINHAUT', 4, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (Diamantstaub im Wert von mindestens 100 GM, den der Zauber verbraucht)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Bis der Zauber endet, ist eine bereitwillige Kreatur, die du berührst, gegen Hieb-, Stichund Wuchtschaden resistent.', '', 'Druide, Magier, Waldläufer, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('steinwand', 'STEINWAND', 5, 'Hervorrufungszauber', 'Aktion', '36 Meter', 'V, G, M (ein Granitwürfel)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'An einem Punkt deiner Wahl in Reichweite erscheint eine nichtmagische Wand aus massivem Stein. Sie ist 15 Zentimeter dick und besteht aus zehn quadratischen Platten mit drei Metern Kantenlänge. Jede Platte muss an eine andere angrenzen. Alternativ kannst du drei Meter mal sechs Meter große Platten erschaffen, die nur 7,5 Zentimeter dick sind. Erschaffst du die Wand so, dass sie den Bereich einer Kreatur durchdringt, so wird die Kreatur auf eine Seite der Wand (nach deiner Wahl) gestoßen. Wenn eine Kreatur auf allen Seiten von der Wand umschlossen (oder zwischen der Wand und einer anderen soliden Oberfläche eingeschlossen) wäre, kann diese Kreatur einen Geschicklichkeitsrettungswurf ausführen. Bei einem Erfolg kann sie ihre Reaktion verwenden, um sich bis zu ihrer Bewegungsrate zu bewegen, sodass sie nicht länger eingeschlossen ist. Die Wand kann jede gewünschte Form annehmen, jedoch nicht denselben Bereich wie eine Kreatur oder ein Gegenstand einnehmen. Sie muss nicht senkrecht sein oder auf festem Untergrund stehen. Allerdings muss sie mit bestehendem Stein verschmelzen und von ihm getragen werden. Somit kannst du diesen Zauber verwenden, um eine Schlucht zu überwinden oder eine Rampe zu erschaffen. Wenn du eine Brücke von mehr als sechs Metern Länge erschaffen willst, musst du die halbe Größe jeder Platte für Stützpfeiler verbrauchen. Du kannst die Steinwand grob formen, um Wehranlagen und dergleichen zu erschaffen. Die Wand ist ein Gegenstand aus Stein, der beschädigt und somit durchbrochen werden kann. Jede Platte besitzt eine RK von 15, 30 Trefferpunkte pro 2,5 Zentimetern Dicke und ist gegen Giftschaden und psychischen Schaden immun. Wenn die Trefferpunkte einer Platte auf 0 sinken, wird sie zerstört, was angrenzende Platten nach Ermessen des SL zum Einsturz bringen könnte. Konzentrierst du dich für die gesamte Wirkungsdauer auf diesen Zauber, so wird die Wand dauerhaft und kann nicht gebannt werden. Anderenfalls verschwindet sie, wenn der Zauber endet.', '', 'Druide, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('sternenfunke', 'STERNENFUNKE', 0, 'Hervorrufungzauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du schleuderst einen Lichtfunken auf eine Kreatur oder einen Gegenstand in Reichweite. Führe einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 1W8 gleißenden Schaden, und bis zum Ende deines nächsten Zugs spendet es in einem Radius von drei Metern dämmriges Licht und kann nicht unsichtbar werden.', 'Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.', 'Barde, Druide', '{"scaling":{"text":"Der Schaden wird um jeweils 1W8 erhöht, wenn du die 5. (2W8), die 11. (3W8) und die 17. (4W8) Stufe erreichst.","type":"cantrip_scaling","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('stille', 'STILLE', 2, 'Illusionszauber', 'Aktion oder Ritual', '36 Meter', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 1, 'Für die Wirkungsdauer kann in einer Kugel mit einem Radius von sechs Metern kein Geräusch erzeugt werden, und kein Geräusch kann in die Kugel dringen. Die Kugel wird an einem Ort deiner Wahl in Reichweite erzeugt. Alle Kreaturen und Gegenstände, die sich vollständig in der Kugel befinden, sind taub und gegen Schallschaden immun. In der Kugel können keine Zauber mit Verbalkomponenten gewirkt werden.', '', 'Barde, Kleriker, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('strahl-der-belkeit', 'STRAHL DER ÜBELKEIT', 1, 'Nekromantiezauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du verschießt einen grünlichen Strahl auf eine Kreatur in Reichweite. Führe einen Fernkampf-Zauberangriff gegen das Ziel aus. Bei einem Treffer erleidet das Ziel 2W8 Giftschaden und ist bis zum Ende deines nächsten Zugs vergiftet.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W6 erhöht. •', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W6 erhöht. •","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('stinkende-wolke', 'STINKENDE WOLKE', 3, 'Beschwörungszauber', 'Aktion', '27 Meter', 'V, G, M (ein faules Ei)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du erschaffst eine Kugel mit einem Radius von sechs Metern um einen Punkt in Reichweite, die mit gelbem, übelriechendem Gas gefüllt ist. Die Wolke ist komplett verschleiert. Sie bleibt für die Wirkungsdauer in der Luft, oder bis starker Wind (wie solcher durch Windstoß) sie auflöst. Jede Kreatur, die ihren Zug in der Kugel beginnt, muss einen Konstitutionsrettungswurf bestehen, oder sie ist bis zum Ende des aktuellen Zugs vergiftet. Eine auf diese Art vergiftete Kreatur kann weder eine Aktion noch eine Bonusaktion ausführen. 312 KAPITEL 7 I ZAUBER STURM DER VERGELTUNG Beschwörungszauber 9. Grades (Druide) Zeitaufwand: Aktion Reichweite: 1,6 Kilometer Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Minute Für die Wirkungsdauer bildet sich um einen Punkt in Reichweite eine brodelnde Sturmwolke und breitet sich in einem Radius von 90 Metern aus. Jede Kreatur unter der Wolke muss einen Konstitutionsrettungswurf bestehen, oder sie erleidet 2W6 Schallschaden und ist für die Wirkungsdauer taub. Zu Beginn jedes deiner folgenden Züge bringt die Sturmwolke andere Effekte hervor wie unten ausgeführt. Runde 2: Es fällt Säureregen. Alle Kreaturen und Gegenstände unter der Wolke erleiden 4W6 Säureschaden. Runde 3: Du rufst sechs Blitze aus der Wolke hervor, die sechs verschiedene Kreaturen oder Gegenstände darunter treffen. Die Kreatur muss einen Geschicklich-keitsrettungswurf ausführen. Scheitert der Wurf, so erleidet sie 10W6 Blitzschaden, anderenfalls die Hälfte. Runde 4: Hagelkörner prasseln herab. Jede Kreatur unter der Wolke erleidet 2W6 Wuchtschaden. Runden 5-10: Durch den Bereich unter der Wolke peitschen Windstöße und Eisregen. Jede Kreatur im Bereich erleidet 1W6 Kälteschaden. Bis der Zauber endet, ist der Bereich schwieriges Gelände sowie komplett verschleiert, Fernkampfwaffenangriffe sind darin nicht möglich, und starke Stürme fegen hindurch. SYMBOL Bannzauber Z Grades (Barde, Druide, Kleriker, Magier) Zeitaufwand: 1 Minute Reichweite: Berührung Komponenten: V, G, M (Diamantpulver im Wert von mindestens 1.000 GM, das der Zauber verbraucht) Wirkungsdauer: Bis der Zauber gebannt oder die Glyphe ausgelöst wird Du zeichnest eine schädliche Glyphe entweder auf eine Oberfläche (wie den Fußboden oder eine Wand) oder in einen Gegenstand (wie ein Buch oder eine Truhe), der geschlossen werden kann. Die Glyphe kann höchstens in einem Radius von 1,5 Metern wirken. Wählst du einen Gegenstand aus, so muss dieser an seinem Standort bleiben. Wenn er um mehr als drei Meter von der Stelle entfernt wird, an der du den Zauber gewirkt hast, wird die Glyphe zerstört, und der Zauber endet, ohne ausgelöst zu werden. Die Glyphe ist kaum wahrnehmbar. Es ist ein erfolgreicher Weisheitswurf (Wahrnehmung) gegen deinen Zauberrettungswurf-SG erforderlich, um sie zu bemerken. Wenn du die Glyphe zeichnest, bestimmst du ihren Auslöser und entscheidest, welchen Effekt das Symbol trägt: Betäubung, Furcht, Schlaf, Schmerz, Tod oder Zwietracht. Die Effekte sind unten erläutert. Auslöser bestimmen: Wenn du den Zauber wirkst, bestimmst du einen Auslöser für die Glyphe. Glyphen auf Oberflächen werden meist dadurch ausgelöst, dass jemand sie berührt oder betritt, einen Gegenstand bewegt, der die Glyphe verdeckt, oder einen Mindestabstand zur Glyphe unterschreitet. Glyphen in Gegenständen werden meist dadurch ausgelöst, dass jemand den Gegenstand öffnet oder die Glyphe sieht. Du kannst den Auslöser verfeinern, sodass die Glyphe nur von bestimmten Kreaturentypen ausgelöst wird (beispielsweise könnte die Glyphe nur auf Aberrationen wirken). Du kannst auch Bedingungen festlegen, unter denen die Glyphe nicht ausgelöst wird, beispielsweise wenn ein bestimmtes Kennwort ausgesprochen wird. Sobald die Glyphe ausgelöst wurde, beginnt sie zu leuchten und erfüllt eine Kugel mit einem Radius von 18 Metern zehn Minuten lang mit dämmrigem Licht. Danach endet der Zauber. Jede Kreatur, die sich beim Aktivieren der Glyphe in der Kugel befindet, diese in ihrem Zug erstmals betritt oder ihren Zug darin beendet, wird zum Ziel des vorher festgelegten Effekts. Eine Kreatur kann nur einmal pro Zug zum Ziel werden. Betäubung: Jedes Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist eine Minute lang betäubt. Furcht: Jedes Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist eine Minute lang verängstigt. Ein verängstigtes Ziel muss sich in jedem seiner Züge um mindestens neun Meter von der Glyphe entfernen, sofern dies möglich ist. Schlaf: Jedes Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist zehn Minuten lang bewusstlos. Eine Kreatur erwacht, wenn sie Schaden erleidet oder jemand eine Aktion ausführt, um sie wachzurütteln. Schmerz: Jedes Ziel muss einen Konstitutionsrettungswurf bestehen, oder es ist eine Minute lang kampfunfähig. Tod: Jedes Ziel führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet das Ziel 10W10 nekrotischen Schaden, anderenfalls die Hälfte. Zwietracht: Jedes Ziel führt einen Weisheitsrettungswurf aus. Scheitert der Wurf, so streitet sich das Ziel eine Minute lang mit anderen Kreaturen. Während dieser Zeit ist es nicht zu sinnvollen Gesprächen in der Lage und bei Angriffssowie Attributswürfen im Nachteil.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('synaptisches-rauschen', 'SYNAPTISCHES RAUSCHEN', 5, 'Verzauberungszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du lässt psychische Energie an einem Punkt in Reichweite explodieren. Jede Kreatur in einer Kugel mit einem Radius von sechs Metern um diesen Punkt führt einen Intelligenzrettungswurf aus. Scheitert der Wurf, so erleidet sie 8W6 psychischen Schaden, anderenfalls die Hälfte. Scheitert der Wurf, so sind außerdem die Gedanken des Ziels eine Minute lang verwirrt. Während dieser Dauer zieht es 1W6 von all seinen Angriffsund Attributswürfen sowie von Konstitutionsrettungswürfen ab, die es ausführt, um seine Konzentration aufrechtzuerhalten. Das Ziel führt am Ende jedes seiner Züge einen Intelligenzrettungswurf aus. Bei einem Erfolg endet der Effekt.', '', 'Barde, Hexenmeister, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tageslicht', 'TAGESLICHT', 3, 'Hervorrufungszauber', 'Aktion', '18 Meter', 'V, G', NULL, '1 Stunde', 0, 0, 'Für die Wirkungsdauer breitet sich Sonnenlicht von einem Punkt in Reichweite zu einer Kugel mit einem Radius von 18 Metern aus. Beim Bereich des Sonnenlichts handelt es sich um helles Licht. Er spendet im Radius von weiteren 18 Metern dämmriges Licht. Alternativ kannst du den Zauber auf einen Gegenstand wirken, der nicht getragen oder gehalten wird. In diesem Fall erfüllt das Sonnenlicht eine Ausströmung von 18 Metern um den Gegenstand. Wird der Gegenstand mit etwas Undurchsichtigem wie einer Schüssel oder einem Helm bedeckt, so wird das Sonnenlicht blockiert. Wenn sich der Wirkungsbereich dieses Zaubers mit einem Bereich von Dunkelheit überschneidet, der durch einen Zauber des höchstens 3. Grades erzeugt wurde, so wird der andere Zauber gebannt.', '', 'Druide, Kleriker, Paladin, Waldläufer, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tanzende-lichter', 'TANZENDE LICHTER', 0, 'Illusionzauber', 'Aktion', '36 Meter', 'V, G, M (etwas Phosphor)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du kannst bis zu vier Lichter in Fackelgröße in Reichweite erzeugen und als Fackeln, Laternen oder Leuchtkugeln erscheinen lassen, die für die Wirkungsdauer in der Luft schweben. Alternativ kannst du die vier Lichter zu einer leuchtenden, entfernt humanoiden Gestalt mittlerer Größe kombinieren. Unabhängig von der Form spendet jedes Licht dämmriges Licht in einem Radius von drei Metern. Als Bonusaktion kannst du die Lichter bis zu 18 Meter weit in einen Bereich in Reichweite bewegen. Jedes Licht muss sich im Abstand von bis zu sechs Metern von einem anderen durch diesen Zauber erzeugten Licht befinden. Wenn ein Licht die Reichweite des Zaubers verlässt, erlischt es.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('taschenspielerei', 'TASCHENSPIELEREI', 0, 'Verwandlungzauber', 'Aktion', '3 Meter', 'V, G', NULL, 'Bis zu 1 Stunde', 0, 0, 'Du erzeugst einen magischen Effekt in Reichweite. Wähle den Effekt aus den Optionen unten aus. Wenn du diesen Zauber mehrmals wirkst, können bis zu drei der langfristigen Effekte gleichzeitig aktiv sein. Sensorischer Effekt: Du erzeugst einen unmittelbaren, harmlosen sensorischen Effekt, beispielsweise einen Funkenregen, einen Windstoß, eine leise Melodie oder einen merkwürdigen Geruch. Feuerspiel: Du entzündest oder löschst unmittelbar eine Kerze, eine Fackel oder ein kleines Lagerfeuer. Sauber oder schmutzig: Du kannst unmittelbar einen Gegenstand, der nicht größer als ein Würfel mit 30 Zentimetern Metern Kantenlänge ist, säubern oder verschmutzen. Einfache Sinneswahrnehmung: Du kannst unbelebtes Material, das nicht größer als ein Würfel mit 30 Zentimetern Metern Kantenlänge ist, abkühlen, erhitzen oder würzen. Dies hält eine Stunde lang an. P4 l''e Magisches Zeichen: Du lässt eine Stunde lang einen Farbfleck, ein Mal oder ein Symbol auf einem Gegenstand oder einer Oberfläche erscheinen. Einfache Schöpfung: Du erzeugst ein nichtmagisches Schmuckstück oder ein illusionäres Bild, das in deine Hand passt. Es bleibt bis zum Ende deines nächsten Zugs bestehen. Diese Schöpfung kann keinen Schaden bewirken und hat keinen Geldwert. TASHAS BRODELNDER KESSEL Beschwörungszauber 6. Grades (Hexenmeister, Magier) Zeitaufwand: Aktion Reichweite: 1,5 Meter Komponenten: V, G, M (eine vergoldete Schöpfkelle im Wert von mindestens 500 GM) Wirkungsdauer: 10 Minuten Du beschwörst einen klauenfüßigen Kessel, der mit einer blubbernden Flüssigkeit gefüllt ist. Der Kessel erscheint in einem freien Bereich auf dem Boden im Abstand von bis zu 1,5 Metern von dir und bleibt für die Wirkungsdauer bestehen. Der Kessel kann nicht bewegt werden, und er verschwindet mitsamt seinem Inhalt, wenn der Zauber endet. Die Flüssigkeit im Kessel hat die Eigenschaften eines gewöhnlichen oder eines ungewöhnlichen Tranks deiner Wahl (beispielsweise die von einem Heiltrank). Als Bonusaktion kannst du - oder ein Verbündeter ­in den Kessel greifen und einen Trank dieser Art herausholen. Der Trank ist in einer Phiole enthalten, die verschwindet, wenn der Trank konsumiert wurde. Der Kessel kann eine Anzahl dieser Tränke in Höhe deines Zauberwirken-Attributsmodifikators (mindestens 1) erzeugen. Wenn der letzte dieser Tränke aus dem Kessel geholt wurde, verschwindet der Kessel, und der Zauber endet. Tränke, die aus dem Kessel geholt, aber nicht konsumiert wurden, verschwinden, wenn du diesen Zauber erneut wirkst. TASHAS FÜRCHTERLICHER LACHANFALL Verzauberungszauber 1. Grades (Barde, Hexenmeister, Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G, M (eine Torte und eine Feder) Wirkungsdauer: Konzentration, bis zu 1 Minute Eine Kreatur deiner Wahl in Reichweite, die du sehen kannst, führt einen Weisheitsrettungswurf aus. Scheitert der Wurf, so hat sie für die Wirkungsdauer die Zustände Liegend sowie Kampfunfähig. Während dieser Dauer lacht sie unkontrollierbar, sofern sie lachen kann, und kann den Zustand Liegend bei sich nicht beenden. Am Ende jedes ihrer Züge und jedes Mal, wenn sie Schaden erleidet, führt die Kreatur einen weiteren Weisheitsrettungswurf aus. Dabei ist sie im Vorteil, wenn der Rettungswurf durch Schaden ausgelöst wurde. Bei einem erfolgreichen Rettungswurf endet der Zauber.', 'Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen.', 'Barde, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('telekinese', 'TELEKINESE', 5, 'Verwandlungszauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erhältst die Fähigkeit, Kreaturen oder Gegenstände mit deinen Gedanken zu bewegen oder zu manipulieren. Wenn du den Zauber wirkst - und als magische Aktion in deinen folgenden Zügen, ehe der Zauber endet —, kannst du deinen Willen auf ein Ziel (Kreatur oder Gegenstand) in Reichweite richten, das du sehen kannst, und den entsprechenden Effekt auslösen. Du kannst in jeder Runde dasselbe Ziel beeinflussen oder ein neues auswählen. Wenn du das Ziel wechselst, ist das vorige nicht mehr vom Zauber betroffen. Kreatur: Du kannst versuchen, eine Kreatur von höchstens riesiger Größe zu bewegen. Das Ziel muss einen Stärkerettungswurf bestehen, oder du bewegst es um bis zu neun Meter in eine beliebige Richtung in Reichweite des Zaubers. Die Kreatur ist bis zum Ende deines nächsten Zugs festgesetzt, und wenn du sie in die Luft hebst, schwebt sie und verbleibt dort. Am Ende deines nächsten Zugs fällt sie herunter, sofern du diese Option nicht erneut auf sie anwendest und ihr Rettungswurf scheitert. Gegenstand: Du kannst versuchen, einen Gegenstand von höchstens riesiger Größe zu bewegen. Wird der Gegenstand weder getragen noch gehalten, so bewegst du ihn automatisch um bis zu neun Meter in eine beliebige Richtung in Reichweite des Zaubers. Wenn der Gegenstand von einer Kreatur getragen oder gehalten wird, muss diese Kreatur einen Stärkerettungs-wurf bestehen, oder du entwindest ihr den Gegenstand und bewegst ihn um bis zu neun Meter in eine beliebige Richtung in Reichweite des Zaubers. Du kannst mit deinem telekinetischen Griff Gegenstände präzise steuern, beispielsweise ein einfaches Werkzeug verwenden, eine Tür oder einen Behälter öffnen, einen Gegenstand aus einem offenen Behälter holen, einen Gegenstand in einem offenen Behälter verstauen oder den Inhalt einer Phiole ausgießen.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('telepathie', 'TELEPATHIE', 8, 'Erkenntniszauber', 'Aktion', 'Unbegrenzt', 'V, G, M (ein Paar verbundene Silberringe)', NULL, '24 Stunden', 0, 0, 'Du erzeugst eine telepathische Verbindung zwischen dir und einer bereitwilligen Kreatur, mit der du vertraut bist. Die Kreatur kann sich an einem beliebigen Ort auf derselben Existenzebene wie du befinden. Der Zauber endet, wenn du und das Ziel euch nicht mehr auf der gleichen Ebene befindet. Für die Wirkungsdauer könnt du und das Ziel euch über die Verbindung Worte, Bilder, Geräusche und andere sensorische Botschaften übermitteln. Das Ziel erkennt dich als Kommunikationspartner. Der Zauber ermöglicht einer Kreatur, die Bedeutung deiner übermittelten Worte und sensorischen Botschaften zu verstehen.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('teleportieren', 'TELEPORTIEREN', 7, 'Beschwörungszauber', 'Aktion', '3 Meter', 'V', NULL, 'Unmittelbar', 0, 0, 'Dieser Zauber transportiert dich und bis zu acht bereitwillige Kreaturen (oder aber einen Gegenstand) in Reichweite, die du sehen kannst, sofort zu einem von dir ausgewählten Ort. Ein Gegenstand muss von höchstens großer Größe sein und darf nicht von einer Kreatur gehalten oder getragen werden, die nicht bereitwillig ist. Der Zielort muss dir bekannt sein und sich auf derselben Existenzebene wie du befinden. Deine Vertrautheit mit dem Zielort bestimmt, ob du erfolgreich dort eintriffst. Der SL würfelt mit 1W100 und konsultiert die Tabelle „Ergebnis der Teleportation" sowie die entsprechenden Erläuterungen. ERGEBNIS DER TELEPORTATION MissgeÄhnliches Abseits des Am Vertrautheit schick Gebiet Zielorts Zielort Dauerhafter Kreis 1-100 Bezugsgegenstand 1-100 Sehr vertraut 1-5 6-13 14-24 25-100 Mehrfach gesehen 1-33 34-43 44-53 54-100 Einmal gesehen oder Beschreibung erhalten Falscher Zielort 1-43 44-53 1-50 51-100 54-73 74-100 Vertrautheit: Hier werden die Bedeutungen der Begriffe in der Spalte ,Vertrautheit" der Tabelle erläutert: Mit „dauerhafter Kreis" ist ein dauerhafter Kreis der Teleportation gemeint, dessen Siegelsequenz dir bekannt ist. „Bezugsgegenstand" bedeutet, dass du einen Gegenstand besitzt, der innerhalb der letzten sechs Monate vom gewünschten Zielort genommen wurde, beispielsweise ein Buch aus der Bibliothek eines Magiers. „Sehr vertraut" beschreibt einen Ort, den du oft aufgesucht oder genau studiert hast oder den du beim Wirken des Zaubers sehen kannst. „Mehrfach gesehen" bezeichnet einen Ort, den du mehr als einmal gesehen hast, mit dem du jedoch nicht sehr vertraut bist. „Einmal gesehen oder Beschreibung erhalten" beschreibt einen Ort, den du einmal gesehen hast, wahrscheinlich durch Magie, oder der dir beschrieben wurde, vielleicht anhand einer Karte. „Falscher Zielort" bedeutet, dass ein Ort nicht existiert. Vielleicht hast du versucht, das Heiligtum eines Gegners magisch auszuspähen, konntest jedoch nur eine Illusion sehen. Oder du versuchst, dich ein einen Ort zu teleportieren, der nicht mehr existiert. , EINE ROTE MAGIERIN VON THAV BEFIEHLT IHREN SKELETTDIENERN, SCHÄTZE AUF TFNSERS SCHWEBENDE SCHEIBE ZU HÄUFEN 4 " Missgeschick: Die unberechenbare Magie des Zaubers führt zu einer holprigen Reise. Jede teleportierte Kreatur (oder der Zielgegenstand) erleidet 3W10 Energieschaden. Zudem würfelt der SL anhand der Tabelle, um zu bestimmen, wo ihr landet. Es kann zu mehreren Unglücken kommen, die jeweils Schaden bewirken. Ähnliches Gebiet: Du erscheinst mit deiner Gruppe (oder dem Gegenstand) in einem anderen Gebiet, das dem Zielort visuell oder thematisch ähnlich ist. Du erscheinst am nächstgelegenen ähnlichen Ort. Wenn du beispielsweise in dein Labor teleportieren wolltest, könntest du in das Labor einer anderen Person in der gleichen Stadt gelangen. Abseits des Zielorts: Du erscheinst mit deiner Gruppe (oder dem Gegenstand) in 2W12 mal 1,6 Kilometern abseits des Zielorts. Würfle mit 1W8, um die Richtung zu bestimmen:1: Osten, 2: Südosten, 3: Süden, 4: Südwesten, 5: Westen, 6: Nordwesten, 7: Norden, 8: Nordosten. Am Zielort: Du erscheinst mit deiner Gruppe (oder dem Gegenstand) dort, wo du hinwolltest. TENSERS', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('thaumaturgie', 'THAUMATURGIE', 0, 'Verwandlungzauber', 'Aktion', '9 Meter', 'V', NULL, 'Bis zu 1 Minute', 0, 0, 'Du manifestierst in Reichweite ein kleines Wunder. Du bewirkst in Reichweite einen der Effekte unten. Wenn du diesen Zauber mehrmals wirkst, können bis zu drei seiner einminütigen Effekte gleichzeitig wirken. Du kannst einen solchen Effekt verwerfen (keine Aktion erforderlich). Beben: Du bewirkst ein harmloses Erdbeben, das eine Minute lang andauert. Dröhnende Stimme: Deine Stimme ertönt eine Minute lang bis zu dreimal so laut wie sonst. Für die Wirkungsdauer bist du bei Charismawürfen (Einschüchtern) im Vorteil. Feuerspiel: Du lässt eine Minute lang Flammen auflodern, stärker oder schwächer leuchten oder ihre Farbe ändern. Phantomgeräusch: Du erzeugst ein unmittelbares Geräusch, das von einem Punkt deiner Wahl in Reichweite ausgeht, beispielsweise Donnergrollen, Rabenkrächzen oder bedrohliches Flüstern. Unsichtbare Hand: Du lässt unmittelbar eine unver­schlossene Tür oder ein unverschlossenes Fenster auffliegen oder zuschlagen. Verändete Augen: Du veränderst für eine Minute das Aussehen deiner Augen.', '', 'Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tier-beherrschen', 'TIER BEHERRSCHEN', 4, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Ein Tier in Reichweite, das du sehen kannst, muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer bezaubert. Wenn du oder deine Verbündeten gegen das Ziel kämpfen, ist es beim Rettungswurf im Vorteil. Wann immer das Ziel Schaden erleidet, wiederholt es den Rettungswurf. Bei einem Erfolg endet der Zauber bei ihm. Du verfügst über eine telepathische Verbindung mit dem bezauberten Ziel, solange ihr euch auf derselben Existenzebene befindet. Diese Verbindung kannst du in deinem Zug nutzen, um dem Ziel Befehle zu erteilen (keine Aktion erforderlich), beispielsweise „Greife diese Kreatur an", „Laufe dorthin" oder „Hole diesen Gegenstand". Das Ziel gehorcht in seinem Zug, so gut es kann. Wenn es den Befehl ausgeführt hat und keine weiteren Anweisungen von dir erhält, verhält es sich nach seinen Wünschen und versucht vor allem, sich zu schützen. Du kannst dem Ziel befehlen, eine Reaktion auszuführen, musst dazu allerdings selbst eine Reaktion ausführen.', 'bei einem Zauberplatz des 5. Grades bis zu zehn Minuten, bei einem Zauberplatz des 6. Grades bis zu einer Stunde und bei einem Zauberplatz ab dem 7. Grad bis zu acht Stunden.', 'Druide, Waldläufer, Zauberer', '{"scaling":{"text":"bei einem Zauberplatz des 5. Grades bis zu zehn Minuten, bei einem Zauberplatz des 6. Grades bis zu einer Stunde und bei einem Zauberplatz ab dem 7. Grad bis zu acht Stunden."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tier-herbeirufen', 'TIER HERBEIRUFEN', 2, 'Beschwörungszauber', 'Aktion', '27 Meter', 'V, G, M (eine Feder, ein Fellbüschel und ein Fischschwanz in einer vergoldeten Eichel im Wert von mindestens 200 GM)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du rufst einen Tiergeist herbei. Er erscheint in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten eines Tiergeists. Wenn du den Zauber wirkst, wähle eine der folgenden Umgebungen aus: Land, TIERGEIST Kleines Tier, neutral', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.', 'Druide, Waldläufer', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":"11 + Zaubergrad","hp":null},"summon_text":"RK 11 + Zaubergrad\nTP 20 (nur Luft) oder 30 (nur Land und Wasser) + 5 für jeden\nZaubergrad ab dem 3.\nBewegungsrate 9 m, Klettern 9 m (nur Land), Fliegen 18 m\n(nur Luft), Schwimmen 9 m (nur Wasser)\nMOD RW\tMOD RAR''\tMOD 5W\nS''rÄ 18 +4 +4\tGES 11 +0 +0\tKON 16 +3 +3\nINT 4 -3 -3\tWEI 14 +2 +2\tCHA 5 —3 —3\nSinne Dunkelsicht 18 m, Passive Wahrnehmung 12 Sprachen Versteht die Sprachen, die du sprichst HG — (EP 0, ÜB entspricht deinem Übungsbonus)\nMERKMALE\nRudeltaktik (nur Land und Wasser): Der Geist ist beim Angriffswurf gegen eine Kreatur im Vorteil, wenn sich mindestens einer seiner Verbündeten, der nicht kampfunfähig ist, im Abstand von bis zu 1,5 Metern von der Kreatur befindet.\nVorbeifliegen (nur Luft): Der Geist provoziert keine Gelegenheitsangriffe, wenn er die Reichweite eines Gegners verlässt.\nWasser atmen (nur Wasser): Der Geist kann nur unter Wasser atmen.\nAKTIONEN\nMehtfachange Der Geist führt eine Anzahl von Zerfetzen-Angriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet).\nZerfetzen: Nahkampfangriffswurfi Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W8+4 + Zaubergrad-Stichschaden.\nLuft oder Wasser. Die Kreatur sieht aus wie ein Tier, das in dieser Umgebung zu Hause ist, wodurch einige Details im Wertekasten bestimmt werden. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet.\nDie Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tierbote', 'TIERBOTE', 2, 'Verzauberungszauber', 'Aktion oder Ritual', '9 Meter', 'V, G, M (ein Happen Nahrung)', NULL, '24 Stunden', 0, 1, '318 KAPITEL 7 i ZAUBER Ein winziges Tier deiner Wahl in Reichweite, das du sehen kannst, muss einen Charismarettungswurf bestehen. Scheitert der Wurf, so muss das Ziel versuchen, in deinem Namen eine Botschaft zu überbringen (dies gelingt dem Ziel automatisch, sofern sein Herausforderungsgrad nicht gleich null ist). Bestimme einen Ort, an dem du schon einmal warst, und einen Empfänger, der einer allgemeinen Beschreibung wie „eine Person in Stadtwachenuniform" oder „ein rothaariger Zwerg mit spitzem Hut" entspricht. Dann kommunizierst du eine Botschaft mit bis zu 25 Worten. Das Tier reist für die Wirkungsdauer in Richtung Zielort, wobei es in 24 Stunden etwa 40 Kilometer zurücklegt ­oder 80 Kilometer, wenn es fliegen kann. Am Zielort übermittelt das Tier der Kreatur, die du beschrieben hast, deine Botschaft und imitiert dabei deine Art der Kommunikation. Erreicht das Tier während der Wirkungsdauer sein Ziel nicht, so geht die Botschaft verloren, und das Tier kehrt an den Ort zurück, an dem du den Zauber gewirkt hast.', 'Für jeden Zauberplatzgrad über dem 2. wird die Wirkungsdauer des Zaubers um 48 Stunden erhöht.', 'Barde, Druide, Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird die Wirkungsdauer des Zaubers um 48 Stunden erhöht."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tiere-beschw-ren', 'TIERE BESCHWÖREN', 3, 'Beschwörungszauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du beschwörst Naturgeister. Diese nehmen die Form eines großen Rudels geisterhafter, immaterieller Tiere in einem freien Bereich in Reichweite an, den du sehen kannst. Das Rudel bleibt für die Wirkungsdauer bestehen. Du wählst die Tierform der Geister aus, beispielsweise Wölfe, Schlangen oder Vögel. Du bist bei Stärkerettungswürfen im Vorteil, solange du dich im Abstand von bis zu 1,5 Metern vom Rudel befindest. Wenn du dich in deinem Zug bewegst, kannst du auch das Rudel um bis zu neun Meter in einen freien Bereich bewegen, den du sehen kannst. Wann immer das Rudel sich einer Kreatur, die du sehen kannst, auf höchstens drei Meter nähert, und wann immer eine Kreatur, die du sehen kannst, einen Bereich im Abstand von bis zu drei Metern vom Rudel betritt oder ihren Zug dort beendet, kannst du die Kreatur zu einem Geschicklichkeitsrettungswurf zwingen. Scheitert der Wurf, so erleidet die Kreatur 3W10 Hiebschaden. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen.', 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W10 erhöht. Beschreibe oder nenne eine bestimmte Art von Tier, Pflanzenkreatur oder nichtmagischer Pflanze. Du erfährst, in welcher Entfernung und Richtung im Abstand von bis zu acht Kilometern sich die nächste Kreatur dieser Art befindet, sofern vorhanden.', 'Druide, Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W10 erhöht. Beschreibe oder nenne eine bestimmte Art von Tier, Pflanzenkreatur oder nichtmagischer Pflanze. Du erfährst, in welcher Entfernung und Richtung im Abstand von bis zu acht Kilometern sich die nächste Kreatur dieser Art befindet, sofern vorhanden.","type":"damage","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tierform', 'TIERFORM', 8, 'Verwandlungszauber', 'Aktion', '9 Meter', 'V, G', NULL, '24 Stunden', 0, 0, 'Wähle eine beliebige Anzahl bereitwilliger Kreaturen in Reichweite aus, die du sehen kannst. Jedes Ziel verwandelt sich in ein Tier deiner Wahl von höchstens großer Größe mit einem Herausforderungsgrad von höchstens 4. Du kannst für jedes Ziel eine andere Gestalt bestimmen. In deinen folgenden Zügen kannst du eine magische Aktion ausführen, um die Ziele erneut zu verwandeln. Die Spielwerte eines Ziels werden durch die Werte des jeweiligen Tieres ersetzt. Das Ziel behält jedoch seinen Kreaturentyp, seine Trefferpunkte sowie Trefferpunkte-würfel, seine Gesinnung, seine Kommunikationsfähigkeit sowie seine Werte für Intelligenz, Weisheit und Charisma bei. Die Aktionen des Ziels sind durch die Anatomie der Tiergestalt begrenzt. Das Ziel kann keine Zauber wirken. Seine Ausrüstung verschmilzt mit der neuen Gestalt, und das Ziel kann seine Ausrüstung in dieser Gestalt nicht verwenden. Das Ziel erhält eine Anzahl von temporären Treffer­punkten, die der Anzahl von Trefferpunkten der ersten Tiergestalt entspricht, in die es sich verwandelt. Wenn nach dem Ende der Verwandlung von diesen temporären Trefferpunkten noch welche übrig sind, verschwinden sie. Die Verwandlung bleibt bei jedem Ziel bestehen, bis der Zauber endet oder bis es die Tiergestalt als Bonusaktion aufgibt.', '', 'Druide', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tierfreundschaft', 'TIERFREUNDSCHAFT', 1, 'Verzauberungszauber', 'Aktion', '9 Meter', 'V, G, M (ein Happen Nahrung)', NULL, '24 Stunden', 0, 0, 'Ziele auf ein Tier in Reichweite, das du sehen kannst. Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer bezaubert. Wenn du oder einer deiner Verbündeten dem Ziel Schaden zufügt, endet der Zauber.', 'Für jeden Zauberplatzgrad über dem 1. kannst du auf ein weiteres Tier zielen. TIERE ODER PFLANZEN AUFSPÜREN Erkenntniszauber 2. Grades (Barde, Druide, Waldläufer) Zeitaufwand: Aktion oder Ritual Reichweite: Selbst Komponenten: V, G, M (etwas Fell von einem Bluthund) Wirkungsdauer: Unmittelbar', 'Barde, Druide, Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du auf ein weiteres Tier zielen. TIERE ODER PFLANZEN AUFSPÜREN Erkenntniszauber 2. Grades (Barde, Druide, Waldläufer) Zeitaufwand: Aktion oder Ritual Reichweite: Selbst Komponenten: V, G, M (etwas Fell von einem Bluthund) Wirkungsdauer: Unmittelbar"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tiersinn', 'TIERSINN', 2, 'Erkenntniszauber', 'Aktion oder Ritual', 'Berührung', '5', NULL, 'Konzentration, bis zu 1 Stunde', 1, 1, 'Du berührst ein bereitwilliges Tier. Für die Wirkungs­dauer kannst du nicht nur mit deinen eigenen Sinnen wahrnehmen, sondern auch mit denen des Tieres. Dabei kannst du auch alle Spezialsinne nutzen, die das Tier möglicherweise hat.', '', 'Druide, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('todeskreis', 'TODESKREIS', 6, 'Nekromantiezauber', 'Aktion', '45 Meter', 'V, G, M (Pulver einer zerstoßenen schwarzen Perle im Wert von mindestens 500 GM)', NULL, 'Unmittelbar', 0, 0, 'Von einem Punkt deiner Wahl in Reichweite breitet sich negative Energie zu einer Kugel mit einem Radius von 18 Metern aus. Jede Kreatur in diesem Bereich führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet sie 8W8 nekrotischen Schaden, anderenfalls die Hälfte.', 'Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 2W8 erhöht.', 'Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 2W8 erhöht.","type":"damage","dice":"2W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('todesschutz', 'TODESSCHUTZ', 4, 'Bannzauber', 'Aktion', 'Berührung', 'V, G', NULL, '8 Stunden', 0, 0, 'Du berührst eine Kreatur und verleihst ihr einen gewissen Schutz vor dem Tod. Wenn die Trefferpunkte des Ziels während der Wirkungsdauer erstmals auf 0 sinken würden, sinken sie stattdessen auf 1 Trefferpunkt, und der Zauber endet. Erleidet das Ziel während der Wirkungsdauer einen Effekt, der es sofort töten würde, ohne Schaden zu bewirken, so ist das Ziel nicht von diesem Effekt betroffen, und der Zauber endet.', '', 'Kleriker, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('todeswolke', 'TODESWOLKE', 5, 'Beschwörungszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Du erschaffst eine Kugel aus gelbgrünem Nebel mit einem Radius von sechs Metern um einen Punkt in Reichweite. 320 KAPITEL 7 j ZAUBER Der Nebel bleibt für die Wirkungsdauer bestehen, oder bis starker Wind (wie solcher durch Windstoß) ihn auflöst, wodurch der Zauber endet. Der Bereich der Kugel ist komplett verschleiert. Jede Kreatur in der Kugel führt einen Konstitutions-rettungswurf aus. Scheitert der Wurf, so erleidet sie 5W8 Giftschaden, anderenfalls die Hälfte. Eine Kreatur muss den Konstitutionsrettungswurf auch dann ausführen, wenn die Kugel sich in ihren Bereich bewegt, wenn die Kreatur die Kugel betritt oder wenn sie ihren Zug darin beendet. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen. Zu Beginn jedes deiner Züge bewegt sich die Kugel um drei Meter von dir weg.', 'Für jeden Zauberplatzgrad über dem 5. wird der Schaden um 1W8 erhöht.', 'Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('t-dliches-phantom', 'TÖDLICHES PHANTOM', 4, 'Illusionszauber', 'Aktion', '36 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du zapfst die Alpträume einer Kreatur in Reichweite an, die du sehen kannst, und erschaffst eine Illusion ihrer tiefsten Ängste, die nur für diese Kreatur sichtbar ist. Das Ziel führt einen Weisheitsrettungswurf aus. Scheitert der Wurf, so erleidet das Ziel 4W10 psychischen Schaden und ist für die Wirkungsdauer bei Attributswürfen sowie Angriffswürfen im Nachteil. Bei einem erfolgreichen Rettungswurf erleidet das Ziel halb so viel Schaden, und der Zauber endet. Für die Wirkungsdauer kann das Ziel am Ende jedes seiner Züge einen Weisheitsrettungswurf ausführen. Scheitert der Wurf, so erleidet es den psychischen Schaden erneut. Bei einem erfolgreichen Rettungswurf endet der Zauber.', 'Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 1W10 erhöht. To R Beschwörungszauber 9. Grades (Hexenmeister, Kleriker, Magier, Zauberer) Zeitaufwand: Aktion Reichweite: 18 Meter Komponenten: V, G, M (ein Diamant im Wert von mindestens 5.000 GM) Wirkungsdauer: Konzentration, bis zu 1 Minute Du beschwörst ein Portal, das einen freien Bereich in Reichweite, den du sehen kannst, mit einem bestimmten Ort auf einer anderen Existenzebene verbindet. Bei dem Portal handelt es sich um eine kreisrunde Öffnung mit einem Radius von 1,5 bis sechs Metern. Du kannst das Portal in jede beliebige Richtung ausrichten. Es bleibt für die Wirkungsdauer bestehen, und der Zielort ist durch das Portal hindurch sichtbar. Das Portal hat auf beiden Ebenen eine Vorderund eine Rückseite. Reisen durch das Portal sind nur durch die Vorderseite möglich. Alles, was das Portal passiert, wird sofort auf die andere Ebene transportiert und erscheint in einem freien Bereich, der dem Portal am nächsten liegt. PORTAL.IN DIE AUSSENLANDE T.0 ÖFFNEN 4011 Gottheiten und andere Herrscher über die Ebenen können verhindern, dass sich Portale dieses Zaubers in ihrer Gegenwart oder in ihren Domänen öffnen. Wenn du diesen Zauber wirkst, kannst du den Namen einer bestimmten Kreatur aussprechen (Pseudonyme, Titel und Spitznamen funktionieren nicht). Befindet sich die Kreatur auf einer anderen Ebene als du, so öffnet sich das Portal neben ihr und transportiert sie in den nächstgelegenen freien Bereich auf deiner Seite des Portals. Du erlangst keine besondere Macht über die Kreatur, und sie handelt nach Ermessen des SL. Sie kann verschwinden, dich angreifen oder dir helfen.', 'Barde, Magier', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 1W10 erhöht. To R Beschwörungszauber 9. Grades (Hexenmeister, Kleriker, Magier, Zauberer) Zeitaufwand: Aktion Reichweite: 18 Meter Komponenten: V, G, M (ein Diamant im Wert von mindestens 5.000 GM) Wirkungsdauer: Konzentration, bis zu 1 Minute Du beschwörst ein Portal, das einen freien Bereich in Reichweite, den du sehen kannst, mit einem bestimmten Ort auf einer anderen Existenzebene verbindet. Bei dem Portal handelt es sich um eine kreisrunde Öffnung mit einem Radius von 1,5 bis sechs Metern. Du kannst das Portal in jede beliebige Richtung ausrichten. Es bleibt für die Wirkungsdauer bestehen, und der Zielort ist durch das Portal hindurch sichtbar. Das Portal hat auf beiden Ebenen eine Vorderund eine Rückseite. Reisen durch das Portal sind nur durch die Vorderseite möglich. Alles, was das Portal passiert, wird sofort auf die andere Ebene transportiert und erscheint in einem freien Bereich, der dem Portal am nächsten liegt. PORTAL.IN DIE AUSSENLANDE T.0 ÖFFNEN 4011 Gottheiten und andere Herrscher über die Ebenen können verhindern, dass sich Portale dieses Zaubers in ihrer Gegenwart oder in ihren Domänen öffnen. Wenn du diesen Zauber wirkst, kannst du den Namen einer bestimmten Kreatur aussprechen (Pseudonyme, Titel und Spitznamen funktionieren nicht). Befindet sich die Kreatur auf einer anderen Ebene als du, so öffnet sich das Portal neben ihr und transportiert sie in den nächstgelegenen freien Bereich auf deiner Seite des Portals. Du erlangst keine besondere Macht über die Kreatur, und sie handelt nach Ermessen des SL. Sie kann verschwinden, dich angreifen oder dir helfen.","type":"damage","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tote-beleben', 'TOTE BELEBEN', 3, 'Nekromantiezauber', '1 Minute', '3 Meter', 'V, G, M (ein Tropfen Blut, ein Stück Fleisch und eine Prise Knochenstaub)', NULL, 'Unmittelbar', 0, 0, 'Wähle einen Knochenhaufen oder die Leiche eines kleinen oder mittelgroßen Humanoiden in Reichweite aus. Das Ziel wird zu einer untoten Kreatur: zu einem Skelett, wenn du Knochen ausgewählt hast, oder zu einem Zombie, wenn du eine Leiche ausgewählt hast (jeweilige Wertekästen siehe Anhang B). Du kannst in jedem deiner Züge eine Bonusaktion ausführen, um jede Kreatur, die du mit diesem Zauber erschaffen hast und die sich im Abstand von bis zu 18 Metern von dir befindet, mental zu befehligen. Handelt es sich um mehrere Kreaturen, so kannst du beliebig viele von ihnen gleichzeitig befehligen, wobei jeweils der gleiche Befehl erteilt wird. Du entscheidest über die Aktion und die Bewegung der Kreatur in ihrem nächsten Zug. Alternativ kannst du einen allgemeinen Befehl erteilen, beispielsweise eine bestimmte Kammer oder einen Korridor zu bewachen. Wenn du keine Befehle erteilst, führt die Kreatur die Ausweichaktion aus und bewegt sich nur, um Schaden zu vermeiden. Sobald die Kreatur einen Befehl erhalten hat, führt sie ihn aus, bis die Aufgabe abgeschlossen ist. Die Kreatur steht 24 Stunden lang unter deiner Kontrolle. Danach folgt sie keinem deiner Befehle mehr. Um für weitere 24 Stunden die Kontrolle über die Kreatur zu behalten, musst du diesen Zauber erneut auf die Kreatur wirken, ehe die ersten 24 Stunden abgelaufen sind. In diesem Fall verlängerst du die Kontrolle über bis zu vier Kreaturen, die du mit diesem Zauber erschaffen hast, anstatt eine neue Kreatur zu erschaffen.', 'Für jeden Zauberplatzgrad über dem 3. erschaffst du zwei zusätzliche untote Kreaturen oder verlängerst die Kontrolle über zwei zusätzliche untote Kreaturen. Du benötigst für jede Kreatur eine separate Leiche oder einen separaten Knochenhaufen. KAPITEL, 7 1 ZAUBER r', 'Kleriker, Magier', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. erschaffst du zwei zusätzliche untote Kreaturen oder verlängerst die Kontrolle über zwei zusätzliche untote Kreaturen. Du benötigst für jede Kreatur eine separate Leiche oder einen separaten Knochenhaufen. KAPITEL, 7 1 ZAUBER r"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tote-erwecken', 'TOTE ERWECKEN', 5, 'Nekromantiezauber', '1 Stunde', 'Berührung', 'V, G, M (ein Diamant im Wert von mindestens 500 GM, den der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 0, 'Du erweckst eine tote Kreatur durch eine Berührung wieder zum Leben, sofern sie höchstens zehn Tage lang tot und nicht untot war, als sie starb. Sie wird mit 1 Trefferpunkt wieder lebendig. Dieser Zauber neutralisiert zudem sämtliche Gifte, die zum Zeitpunkt des Todes auf das Ziel gewirkt haben. Der Zauber schließt alle tödlichen Wunden, kann jedoch keine abgetrennten Gliedmaßen wiederherstellen. Fehlen der Kreatur lebensnotwendige Körperteile oder Organe, beispielsweise ihr Kopf, so scheitert der Zauber automatisch. Von den Toten aufzuerstehen ist eine Tortur. Das Ziel erleidet einen Malus von -4 auf W20-Prüfungen. Nach jeder langen Rast wird der Malus um 1 verringert, bis er 0 beträgt.', '', 'Barde, Kleriker, Paladin', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('totenl-uten', 'TOTENLÄUTEN', 0, 'Nekromantiezauber', 'Aktion', '18 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du deutest auf eine Kreatur in Reichweite, die du sehen kannst. Ein einzelnes Läuten einer Totenglocke ist im 322 KAPITEL ''7 I ZAUBER Abstand von bis zu drei Metern um das Ziel hörbar. Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es erleidet 1W8 nekrotischen Schaden. Wenn dem Ziel Trefferpunkte fehlen, erleidet es stattdessen 1W12 nekrotischen Schaden.', 'Der Schaden wird um jeweils einen Würfel erhöht, wenn du die 5. (2W8 oder 2W12), die 11. (3W8 oder 3W12) und die 17. (4W8 oder 4W12) Stufe erreichst.', 'Hexenmeister, Kleriker, Magier', '{"scaling":{"text":"Der Schaden wird um jeweils einen Würfel erhöht, wenn du die 5. (2W8 oder 2W12), die 11. (3W8 oder 3W12) und die 17. (4W8 oder 4W12) Stufe erreichst.","type":"cantrip_scaling","dice":"2W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('totstellen', 'TOTSTELLEN', 3, 'Nekromantiezauber', 'Aktion oder Ritual', 'Berührung', 'V, G, M (eine Prise Friedhofserde)', NULL, '1 Stunde', 0, 1, 'Du berührst eine bereitwillige Kreatur und versetzt sie in einen Zustand der Starre, der vom Tod nicht zu unterscheiden ist. Für die Wirkungsdauer erscheint das Ziel bei äußerlichen Untersuchungen sowie bei Zaubern zum Bestimmen seines Status als tot. Das Ziel ist blind und kampfunfähig, und seine Bewegungsrate beträgt 0. Es ist gegen alle Schadensarten bis auf psychischen Schaden resistent und gegen den Zustand Vergiftet immun.', '', 'Barde, Druide, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('traum', 'TRAUM', 5, 'Illusionszauber', '1 Minute', 'Besonders', 'V, G, M (eine Handvoll Sand)', NULL, '8 Stunden', 0, 0, 'er Du zielst auf eine Kreatur, die du kennst und die sich auf derselben Existenzebene befindet wie du. Der Zauber versetzt dich selbst oder eine bereitwillige Kreatur, die du berührst, in eine Trance, um als Traumbote zu fungieren. In der Trance ist der Bote kampfunfähig und hat eine Bewegungsrate von O. Wenn das Ziel schläft, erscheint der Bote in seinen Träumen und kann für die Wirkungsdauer des Zaubers mit ihm sprechen. Der Bote kann zudem die Umgebung des Traums gestalten und Landschaften, Gegenstände oder andere Bilder erschaffen. Der Bote kann die Trance und damit den Zauber jederzeit beenden. Das Ziel erinnert sich beim Aufwachen genau an den Traum. Ist das Ziel wach, wenn du den Zauber wirkst, so weiß der Bote dies und kann entweder die Trance (und den Zauber) beenden oder warten, bis das Ziel schläft, und dann in dessen Träumen erscheinen. Du kannst den Boten für das Ziel furchteinflößend erscheinen lassen. In diesem Fall kann der Bote eine Nachricht von höchstens zehn Worten übermitteln. Dann führt das Ziel einen Weisheitsrettungswurf aus. Scheitert der Wurf, so gewährt die Rast dem Ziel keine Vorzüge, und es erleidet beim Erwachen 3W6psychischen Schaden. TRUGBILD PROJIZIEREN Illusionszauber 7. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: 800 km Komponenten: V, G, M (eine Statuette von dir im Wert von mindestens 5 GM) Wirkungsdauer: Konzentration, bis zu 1 Tag Du erschaffst ein illusionäres Abbild von dir selbst, das für die Wirkungsdauer bestehen bleibt. Das Abbild kann unabhängig von Hindernissen an einem beliebigen Ort in Reichweite erscheinen, den du schon einmal gesehen hast. Die Illusion sieht aus und klingt wie du, ist aber immateriell. Erleidet die Illusion Schaden, so verschwindet sie, und der Zauber endet. Du kannst durch ihre Augen sehen und durch ihre Ohren hören, als befändest du dich in ihrem Bereich. Als magische Aktion kannst du die Illusion bis zu 18 Meter weit bewegen und sie gestikulieren, sprechen und auf eine beliebige Art handeln lassen. Sie ahmt dein Verhalten perfekt nach. Physische Interaktionen mit dem Abbild enttarnen es als illusionär, da Dinge es einfach durchdringen können. Eine Kreatur, die das Abbild mit der Studieren-Aktion untersucht, kann mit einem erfolgreichen Intelligenzwurf (Nachforschungen) gegen deinen Zauberrettungswurf-SG erkennen, dass es sich um eine Illusion handelt. Erkennt eine Kreatur die Illusion als solche, so wird das Abbild für sie durchscheinend, und sämtliche Geräusche klingen für sie hohl.', '', 'Barde, Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('tsunami', 'TSUNAMI', 8, 'Beschwörungszauber', '1 Minute', '1,6 Kilometer', 'V, G', NULL, 'Konzentration, bis zu 6 Runden', 1, 0, 'An einem Punkt deiner Wahl in Reichweite erscheint eine Wasserwand. Du kannst sie bis zu 9O Meter lang, 9O Meter hoch und 15 Meter dick sein lassen. Die Wand bleibt für die Wirkungsdauer bestehen. Wenn die Wand erscheint, führt jede Kreatur in ihrem Bereich einen Stärkerettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 6W1O Wuchtschaden, anderenfalls die Hälfte. Zu Beginn jedes deiner Züge nach Erscheinen der Wand bewegt diese sich und alle Kreaturen darin 15 Meter von dir weg. Alle Kreaturen von höchstens riesiger Größe, die sich in der Wand befinden oder in deren Bereiche die Wand sich bewegt, müssen einen Stärkerettungswurf bestehen, oder sie erleiden 5W1O Wuchtschaden. Eine Kreatur kann diesen Schaden nur einmal pro Runde erleiden. Am Ende des Zugs ist die Höhe der Wand um 15 Meter verringert, und der Schaden, den die Wand in späteren Runden bewirkt, ist um 1W1O verringert. Wenn die Höhe der Wand auf O sinkt, endet der Zauber. Eine Kreatur in der Wand kann sich schwimmend bewegen. Aufgrund der Wucht der Welle muss die Kreatur jedoch einen Stärkewurf (Athletik) gegen deinen Zauberrettungswurf-SG bestehen, um sich überhaupt bewegen zu können. Scheitert der Wurf, so kann die Kreatur sich nicht bewegen. Wenn eine Kreatur die Wand verlässt, fällt sie zu Boden.', '', 'Druide', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('unauffindbarkeit', 'UNAUFFINDBARKEIT', 3, 'Bannzauber', 'Aktion', 'Berührung', 'V, G, M (eine Prise Diamantstaub im Wert von mindestens 25 GM, den der Zauber verbraucht)', NULL, '8 Stunden', 0, 0, 'Du berührst ein Ziel, um es für die Wirkungsdauer vor Erkenntniszaubern zu verstecken. Das Ziel kann eine bereitwillige Kreatur, ein Ort oder ein Gegenstand sein. Es darf in keiner Abmessung größer als drei Meter sein. Es kann weder durch Erkenntniszauber noch durch magische Ausspähungssensoren wahrgenommen werden. UNHEIMLICHES SCHICKSAL Illusionszauber 9. Grades (Hexenmeister, Magier) Zeitaufwand: Aktion Reichweite: 36 Meter Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Minute Du versuchst, illusionäre Schrecken im Geiste anderer zu erzeugen. Jede Kreatur deiner Wahl in einer Kugel mit einem Radius von neun Metern um einen Punkt in Reichweite führt einen Weisheitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 1OW1O psychischen Schaden und ist für die Wirkungsdauer verängstigt. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur nur halb so viel Schaden. Ein verängstigtes Ziel führt am Ende jedes seiner Züge einen Weisheitsrettungswurf aus. Scheitert der Wurf, so erleidet es 5W1O psychischen Schaden. Bei einem erfolg­reichen Rettungswurf endet der Zauber bei diesem Ziel. UNHOL0GEIST (TEUFEL). UNHOLD HERBEIRUFEN Beschwörungszauber 6. Grades (Hexenmeister, Magier) Zeitaufwand: Aktion Reichweite: 27 Meter Komponenten: V, G, M (eine blutige Phiole im Wert von mindestens 600 GM) Wirkungsdauer: Konzentration, bis zu 1 Stunde Du rufst einen Unholdgeist herbei. Er erscheint in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten eines Unholdgeists. Wenn du den Zauber wirkst, wähle zwischen Dämon, Teufel und Yugoloth. Die Kreatur sieht aus wie ein Unhold des ausgewählten Typs, wodurch einige Details im Wertekasten bestimmt werden. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet. Die Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. e'' UNHOLDGEIST Großer Unhold, neutral RK 12 + Zaubergrad TP 50 (nur Dämon) oder 40 (nur Teufel) oder 60 (nur Yugoloth) + 15 für jeden Zaubergrad ab dem 7. Bewegungsrate 12 m, Klettern 12 m (nur Dämon), Fliegen 18 m (nur Teufel) MOD RW MOD RW MOD RW S-rii +1 +1 GES 16 +3 +3 KON 15 +2 +2 INT +0 +0 WEI 10 +0 +0 CHA 16 +3 +3 Resistenzen Feuer Immunitäten Gift, Vergiftet Sinne Dunkelsicht 18 m, Passive Wahrnehmung 10 Sprachen Abyssisch, Infernalisch, Telepathie auf 18 m HG - (EP 0, ÜB entspricht deinem Übungsbonus)', 'Barde, Magier, Waldläufer', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. e'' UNHOLDGEIST Großer Unhold, neutral RK 12 + Zaubergrad TP 50 (nur Dämon) oder 40 (nur Teufel) oder 60 (nur Yugoloth) + 15 für jeden Zaubergrad ab dem 7. Bewegungsrate 12 m, Klettern 12 m (nur Dämon), Fliegen 18 m (nur Teufel) MOD RW MOD RW MOD RW S-rii +1 +1 GES 16 +3 +3 KON 15 +2 +2 INT +0 +0 WEI 10 +0 +0 CHA 16 +3 +3 Resistenzen Feuer Immunitäten Gift, Vergiftet Sinne Dunkelsicht 18 m, Passive Wahrnehmung 10 Sprachen Abyssisch, Infernalisch, Telepathie auf 18 m HG - (EP 0, ÜB entspricht deinem Übungsbonus)","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":null,"hp":null},"summon_text":"MERKMALE\nMagieresistenz: Der Geist ist bei Rettungswürfen gegen Zauber und andere magische Effekte im Vorteil.\nTeufelssicht (nur Teufel): Magische Dunkelheit beeinträchtigt die Dunkelsicht des Geists nicht.\nTodeszuckungen (nur Dämon): Wenn die Trefferpunkte des Geists auf° sinken oder der Zauber endet, explodiert\nder Geist. Geschicklichkeitsrettungswurf: Der SG entspricht deinem Zauberrettungswurf-SG, alle Kreaturen in einer Ausströmung von drei Metern um den Geist. Misserfolg: 2W10 plus Zaubergrad Feuerschaden. Erfolg: Halber Schaden.\nAKTIONEN\nMehrfachange Der Geist führt eine Anzahl von Angriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet).\nBiss (nur Dämon): Nahkampfangriffswurf: Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W12+3 + Zaubergrad nekrotischer Schaden.\nKlauen (nur Yugoloth): Nahkampfangrewurf: Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W8+3 + Zaubergrad Hiebschaden. Sofort, nachdem der Angriff getroffen oder verfehlt hat, kann sich der Geist bis zu neun Meter weit in einen freien Bereich teleportieren, den er sehen kann.\nFeuriger Schlag (nur Teufel): Nahkampf- oder Fernkampf angriffswurf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter oder 45 Meter. Treffer: 2W6+3 + Zaubergrad Feuerschaden.","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('unsichtbarer-diener', 'UNSICHTBARER DIENER', 1, 'Beschwörungszauber', 'Aktion oder Ritual', '18 Meter', 'V, G, M (ein Stück Schnur und etwas Holz)', NULL, '1 Stunde', 0, 1, 'Dieser Zauber erschafft eine mittelgroße unsichtbare, geistund formlose Kraft, die auf deinen Befehl einfache Aufgaben erfüllt, bis der Zauber endet. Der Diener erscheint in Reichweite in einem freien Bereich auf dem Boden. Er besitzt eine RK von 10, einen Stärkewert von 2, 1 Trefferpunkt und kann nicht angreifen. Wenn seine Trefferpunkte auf 0 sinken, endet der Zauber. Als Bonusaktion kannst du in jedem deiner Züge dem Diener mental befehlen, sich um bis zu 4,5 Meter zu bewegen und mit einem Gegenstand zu interagieren. Der Diener kann einfache Aufgaben übernehmen, die auch ein Mensch ausführen könnte: Dinge holen, reinigen und reparieren, Kleidung zusammenlegen, Feuer entfachen, Speisen servieren und Getränke einschenken. Sobald du einen Befehl erteilst, erfüllt der Diener diesen nach Kräften, bis die Aufgabe erledigt ist. Dann wartet er auf deinen nächsten Befehl. Wenn du dem Diener befiehlst, sich für eine Aufgabe mehr als 18 Meter weit von dir zu entfernen, endet der Zauber.', '', 'Barde, Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('unsichtbares-sehen', 'UNSICHTBARES SEHEN', 2, 'Erkenntniszauber', 'Aktion', 'Selbst', 'V, G, M (eine Prise Talk)', NULL, '1 Stunde', 0, 0, 'Für die Wirkungsdauer siehst du unsichtbare Kreaturen und Gegenstände, als wären sie sichtbar. Du kannst zudem in die Ätherebene blicken. Kreaturen und Gegenstände dort erscheinen geisterhaft.', '', 'Barde, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('unsichtbarkeit', 'UNSICHTBARKEIT', 2, 'Illusionszauber', 'Aktion', 'Berührung', 'V, G, M (eine Wimper in Gummiarabikum)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Eine Kreatur, die du berührst, wird unsichtbar, bis der Zauber endet. Der Zauber endet vorzeitig, sobald das Ziel einen Angriffswurf ausgeführt, Schaden bewirkt oder selbst einen Zauber gewirkt hat.', 'Für jeden Zauberplatzgrad über dem 2. kannst du auf eine weitere Kreatur zielen.', 'Barde, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('untote-erschaffen', 'UNTOTE ERSCHAFFEN', 6, 'Nekromantiezauber', '1 Minute', '3 Meter', 'V, G, M (je ein schwarzer Onyx im Wert von mindestens 150 GM für jeden Leichnam)', NULL, 'Unmittelbar', 0, 0, 'Du kannst diesen Zauber nur nachts wirken. Wähle bis zu drei Leichen von Humanoiden kleiner oder mittelgroßer Größe in Reichweite aus. Jede Leiche wird zu einem Ghul unter deiner Kontrolle (den Wertekasten findest du im Monsterhandbuch). Als Bonusaktion in jedem deiner Züge kannst du jede untote Kreatur, die du mit diesem Zauber erschaffen hast und die sich im Abstand von bis zu 36 Metern von dir befindet, mental befehligen. Handelt es sich um mehrere Kreaturen, so kannst du beliebig viele von ihnen gleichzeitig befehligen, wobei jeweils der gleiche Befehl erteilt wird. Du entscheidest über die Aktion und die Bewegung der Kreatur in ihrem nächsten Zug. Alternativ kannst du einen allgemeinen Befehl erteilen, beispielsweise einen bestimmten Ort zu bewachen. Wenn du keine Befehle erteilst, führt die Kreatur die Ausweichaktion aus und bewegt sich nur, um Schaden zu vermeiden. Sobald die Kreatur einen Befehl erhalten hat, führt sie ihn aus, bis die Aufgabe abgeschlossen ist. Die Kreatur steht 24 Stunden lang unter deiner Kontrolle. Danach folgt sie keinem deiner Befehle mehr. Um die Kontrolle über die Kreatur für weitere 24 Stunden zu behalten, musst du diesen Zauber erneut auf die Kreatur wirken, ehe die ersten 24 Stunden abgelaufen sind. In diesem Fall verlängerst du die Kontrolle über bis zu drei Kreaturen, die du mit diesem Zauber erschaffen hast, anstatt eine neue Kreatur zu erschaffen.', 'Bei einem Zauberplatz des 7. Grades kannst du vier Ghule erschaffen oder wieder unter deine Kontrolle bringen. Bei einem Zauberplatz des 8. Grades kannst du fünf Ghule oder zwei Grule oder zwei Gruftschrecken erschaffen oder wieder unter deine Kontrolle bringen. Bei einem Zauberplatz des 9. Grades kannst du sechs Ghule oder drei Grule oder drei Gruftschrecken oder zwei Mumien erschaffen oder wieder unter deine Kontrolle bringen. Die jeweiligen Wertekästen findest du im Monsterhandbuch.', 'Hexenmeister, Kleriker, Magier', '{"scaling":{"text":"Bei einem Zauberplatz des 7. Grades kannst du vier Ghule erschaffen oder wieder unter deine Kontrolle bringen. Bei einem Zauberplatz des 8. Grades kannst du fünf Ghule oder zwei Grule oder zwei Gruftschrecken erschaffen oder wieder unter deine Kontrolle bringen. Bei einem Zauberplatz des 9. Grades kannst du sechs Ghule oder drei Grule oder drei Gruftschrecken oder zwei Mumien erschaffen oder wieder unter deine Kontrolle bringen. Die jeweiligen Wertekästen findest du im Monsterhandbuch."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('untoten-herbeirufen', 'UNTOTEN HERBEIRUFEN', 3, 'Nekromantiezauber', 'Aktion', '27 Meter', 'V, G, M (einvergoldeter Schädel im Wert von mindestens 300 GM)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du rufst einen untoten Geist herbei. Er erscheint in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten eines Untoten Geists. Wenn du den Zauber wirkst, wähle die Gestalt der Kreatur aus: geisterhaft, skelettartig oder verwesend. Der Geist sieht aus wie ein Untoter der ausgewählten Gestalt, wodurch einige Details im Wertekasten bestimmt werden. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet. Die Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.', 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. UNTOTER GEIST (GEISTERHAFT). UNTOTER GEIST Mittelgroßer Untoter, neutral RK 11 + Zaubergrad TP 30 (nur Geisterhaft und Verwesend) oder 20 (nur Skelettartig) + 10 für jeden Zaubergrad ab dem 4. Bewegungsrate 9 m, Fliegen 12 m (Schweben, nur Geisterhaft) M 0 D RW MOD RW MOD RV,/ STÄ 12 +1 +1 GES 16 +3 +3 KON 15 +2 +2 INT 4 -3 -3 WEI 10 +0 +0 CHA 9 -1 -1 Immunitäten Gift, Nekrotisch; Erschöpft, Gelähmt, Verängstigt, Vergiftet Sinne Dunkelsicht 18 m, Passive Wahrnehmung 10 Sprachen Versteht die Sprachen, die du sprichst HG — (EP 0, ÜB entspricht deinem Übungsbonus)', 'Hexenmeister, Magier', '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. UNTOTER GEIST (GEISTERHAFT). UNTOTER GEIST Mittelgroßer Untoter, neutral RK 11 + Zaubergrad TP 30 (nur Geisterhaft und Verwesend) oder 20 (nur Skelettartig) + 10 für jeden Zaubergrad ab dem 4. Bewegungsrate 9 m, Fliegen 12 m (Schweben, nur Geisterhaft) M 0 D RW MOD RW MOD RV,/ STÄ 12 +1 +1 GES 16 +3 +3 KON 15 +2 +2 INT 4 -3 -3 WEI 10 +0 +0 CHA 9 -1 -1 Immunitäten Gift, Nekrotisch; Erschöpft, Gelähmt, Verängstigt, Vergiftet Sinne Dunkelsicht 18 m, Passive Wahrnehmung 10 Sprachen Versteht die Sprachen, die du sprichst HG — (EP 0, ÜB entspricht deinem Übungsbonus)","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":null,"hp":null},"summon_text":"MERKMALE\nFaulige Aura (nur Verwesend): Konstitutionsrettungswurf: Der SG entspricht deinem Zauberrettungswurf-SG, eine Kreatur (außer dir), die ihren Zug in einer Ausströmung von 1,5 Metern beginnt, die vom Geist ausgeht. Misserfolg: Die Kreatur ist bis zum Beginn ihres nächsten Zugs vergiftet.\nKörperloser Durchgang (nur Geisterhaft): Der Geist kann sich durch andere Kreaturen und Gegenstände bewegen, als wären sie schwieriges Gelände. Wenn er seinen Zug\nin einem Gegenstand beendet, wird er zum nächsten freien Bereich geschoben und erleidet pro zurückgelegten 1,5 Metern 1W10 Energieschaden.\nAKTIONEN\nMehrfachangriff Der Geist führt eine Anzahl von Angriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet).\nTodesberührung (nur Geisterhaft): Nahkampfangriffswurf: Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W8+3 + Zaubergrad nekrotischer Schaden, und das Ziel ist bis zum Ende seines nächsten Zugs verängstigt.\nVerrottende Klaue (nur Verwesend): Nahkampfangriffswurf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W6+3 + Zaubergrad Hiebschaden. Wenn das Ziel vergiftet ist, so ist es bis zum Ende seines nächsten Zugs gelähmt.\nGrabesblitz (nur Skelettartig): Fernkampfangriffswurf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 45 Meter. Treffer: 2W4+3 + Zaubergrad nekrotischer Schaden.\n326\tKAPITEL:7 ZAUBER","source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('vampirgriff', 'VAMPIRGRIFF', 3, 'Nekromantiezauber', 'Aktion', 'Selbst', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Die Berührung deiner in Schatten gehüllten Hand kann anderen Kreaturen Lebenskraft entziehen, um dich zu heilen. Führe einen Nahkampf-Zauberangriff gegen eine Kreatur in Reichweite aus. Bei einem Treffer erleidet das Ziel 3W6 nekrotischen Schaden, und du erhältst Trefferpunkte in Höhe der Hälfte des nekrotischen Schadens zurück. Für die Wirkungsdauer kannst du in jedem deiner Züge den Angriff als magische Aktion wiederholen und dabei auf dieselbe oder auf eine andere Kreatur zielen.', 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W6 erhöht.', 'Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verarbeitung', 'VERARBEITUNG', 4, 'Verwandlungszauber', '10 Minuten', '36 Meter', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Du wandelst Rohmaterial in Erzeugnisse aus dem gleichen Material um. Du kannst beispielsweise aus einem Stück Baum eine Holzbrücke erschaffen, aus etwas Hanf ein Seil, aus Flachs oder Wolle Kleidung. Wähle Rohmaterial in Reichweite aus, das du sehen kannst. Du kannst einen Gegenstand von höchstens großer Größe herstellen, der in einen Würfel mit drei Metern Kantenlänge oder in acht verbundene Würfel mit jeweils 1,5 Metern Kantenlänge passt, sofern du über genügend Material verfügst. Wenn du mit Metall, Stein oder einem anderen Mineral arbeitest, kann der hergestellte Gegenstand von höchstens mittelgroßer Größe sein. Er muss in einen Würfel mit 1,5 Metern Kantenlänge passen. Die Qualität der mit dem Zauber erschaffenen Gegenstände hängt von der Qualität des Rohmaterials ab. Mit diesem Zauber können weder Kreaturen noch magische Gegenstände erschaffen werden. Du kannst zudem keine Gegenstände wie Schmuck, Waffen, Glas oder Rüstung erschaffen, die ein hohes Maß an Können erfordern, es sei denn, du bist im Umgang mit dem passenden Handwerkszeug geübt. VERBANNENDES NIEDERSTRECKEN Beschwörungszauber 5. Grades (Paladin) Zeitaufwand: Bonusaktion, die du sofort ausführst, wenn du eine Kreatur mit einer Nahkampfwaffe oder einem waffenlosen Angriff getroffen hast Reichweite: Selbst Komponenten: V Wirkungsdauer: Konzentration, bis zu 1 Minute Das Ziel des Angriffswurfs erleidet zusätzlich 5W10 Energieschaden. Wenn der Angriff die Trefferpunkte des Ziels auf höchstens 50 verringert, muss das Ziel einen Charismarettungswurf bestehen, oder es wird für die Wirkungsdauer auf eine harmlose Halbebene transportiert. Solange das Ziel sich dort befindet, ist es kampfunfähig. Wenn der Zauber endet, erscheint das Ziel in dem Bereich, aus dem es wegteleportiert wurde, oder im nächstgelegenen freien Bereich wieder, falls der ursprüngliche inzwischen besetzt ist.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verbannung', 'VERBANNUNG', 4, 'Bannzauber', 'Aktion', '9 Meter', 'V, G, M (ein Pentakel)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Eine Kreatur in Reichweite, die du sehen kannst, muss einen Charismarettungswurf bestehen, oder sie wird für die Wirkungsdauer auf eine harmlose Halbebene transportiert. Solange das Ziel sich dort befindet, ist es kampfunfähig. Wenn der Zauber endet, erscheint das Ziel in dem Bereich, aus dem es wegteleportiert wurde, oder im nächstgelegenen freien Bereich wieder, falls der ursprüngliche inzwischen besetzt ist. Falls das Ziel eine Aberration, ein celestisches Wesen, ein Elementar, ein Feenwesen oder ein Unhold ist, kehrt es nicht zurück, sofern der Zauber eine Minute lang wirkt. Stattdessen gelangt es an einen zufälligen Ort auf einer Ebene (nach Wahl des SL), die mit seinem Kreaturentyp assoziiert ist.', 'Für jeden Zauberplatzgrad über dem 4. kannst du auf eine weitere Kreatur zielen.', 'Hexenmeister, Kleriker, Magier, Paladin, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verbergen', 'VERBERGEN', 7, 'Verwandlungszauber', 'Aktion', 'Berührung', 'V, G, M (Edelsteinpulver im Wert von mindestens 5.000 GM, das der Zauber verbraucht)', NULL, 'Bis der Zauber gebannt wird', 0, 0, 'Du verbirgst einen Gegenstand oder eine bereitwillige Kreatur auf magische Art, indem du das Ziel berührst. Für die Wirkungsdauer ist das Ziel unsichtbar und kann nicht Ziel von Erkenntniszaubern werden, von Magie aufgespürt oder magisch aus der Ferne betrachtet werden. Wenn das Ziel eine Kreatur ist, fällt es in einen scheintoten Zustand: Es ist bewusstlos, altert nicht und benötigt weder Nahrung noch Wasser noch Luft. Du kannst eine Bedingung festlegen, durch die der Zauber vorzeitig endet. Dabei hast du freie Wahl, solange die Bedingung im Abstand von bis zu 1,6 Kilometern um das Ziel eintritt oder sichtbar ist. Beispiele dafür wären „Nach 1.000 Jahren" oder „Wenn die Tarraske erwacht". Dieser Zauber endet auch dann, wenn das Ziel Schaden erleidet. VERBÜNDETER AUS DEN EBENEN Beschwörungszauber 6. Grades (Kleriker) Zeitaufwand: 10 Minuten Reichweite: 18 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du erflehst den Beistand eines außerweltlichen Wesens. Dieses Wesen muss dir bekannt sein — eine Gottheit, ein Dämonenprinz oder ein anderes Wesen von kosmischer Macht. Es schickt ein ihm treu ergebenes celestisches Wesen, einen Elementar oder einen Unhold, um dir zu helfen. Diese Kreatur erscheint in einem freien Bereich in Reichweite. Kennst du den Namen einer bestimmten Kreatur, so kannst du ihn beim Wirken des Zaubers auss­prechen und so um diese Kreatur bitten. Nach Wahl des SL erhältst du möglicherweise trotzdem eine andere Kreatur. Wenn die Kreatur erscheint, ist sie nicht gezwungen, sich auf eine bestimmte Weise zu verhalten. Du kannst sie bitten, einen Dienst gegen Bezahlung auszuführen. Sie ist dazu jedoch nicht verpflichtet. Die Aufgabe kann einfach oder komplex sein, von „Fliege uns über den Abgrund!" oder „Hilf uns im Kampf!" bis zu „Spioniere unsere Feinde aus!" oder „Beschütze uns, während wir in das Gewölbe vordringen!". Du musst mit der Kreatur kommunizieren können, damit du um ihre Dienste feilschen kannst. Die Bezahlung kann auf verschiedene Arten erfolgen. Ein celestisches Wesen verlangt möglicherweise eine beträchtliche Spende an Gold oder magischen Gegen­ständen, während ein Unhold ein lebendiges Opfer oder einen Schatz erwartet. Manche Kreaturen erwarten für ihre Dienste, dass du im Gegenzug eine Mission für sie erfüllst. Eine Aufgabe, die in Minuten gemessen wird, erfordert eine Bezahlung von 100 GM pro Minute. Eine Aufgabe, die in Stunden gemessen wird, kostet 1.000 GM pro Stunde. Eine Aufgabe, die in Tagen gemessen wird (höchstens zehn Tage), erfordert 10.000 GM pro Tag. Der SL kann die Bezahlung an die Umstände anpassen, unter denen du den Zauber wirkst. Entspricht die Aufgabe dem Ethos der Kreatur, so kann die Bezahlung halbiert werden oder ganz entfallen. Ungefährliche Aufgaben erfordern meist nur die Hälfte der genannten Bezahlung, während besonders gefährliche Aufgaben eine größere Spende benötigen. Kreaturen nehmen selten Aufgaben an, die selbstmörderisch erscheinen. Wenn die Kreatur den Auftrag abgeschlossen hat oder die vereinbarte Dauer des Dienstes endet, erstattet die Kreatur dir Bericht, sofern möglich, und kehrt dann auf ihre Heimatebene zurück. Wenn du dich mit der Kreatur nicht auf einen Preis für ihren Dienst einigen kannst, kehrt sie sofort auf ihre Heimatebene zurück.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verderben', 'VERDERBEN', 1, 'Verzauberungszauber', 'Aktion', '9 Meter', 'V, G, M (ein Tropfen Blut)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Bis zu drei Kreaturen deiner Wahl in Reichweite, die du sehen kannst, führen jeweils einen Charismarettungswurf aus. Wenn ein Ziel, dessen Rettungswurf scheitert, während der Wirkungsdauer einen Angriffsoder Rettungswurf ausführt, muss es 1W4 von seinem Angriffsoder Rettungswurf abziehen.', 'Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen.', 'Barde, Hexenmeister, Kleriker', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du auf eine weitere Kreatur zielen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('vergr-ssern-verkleinern', 'VERGRÖSSERN/VERKLEINERN', 2, 'Verwandlungszauber', 'Aktion', '9 Meter', 'V, G, M (eine Prise Eisenpulver)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Für die Wirkungsdauer vergrößert oder verkleinert der Zauber ein Ziel (Kreatur oder Gegenstand) in Reichweite, das du sehen kannst (ausgewählter Effekt siehe unten). Ein Gegenstand als Ziel darf nicht getragen oder gehalten werden. Wenn das Ziel eine nicht bereitwillige Kreatur ist, kann es einen Konstitutionsrettungswurf ausführen. Bei einem erfolgreichen Rettungswurf hat der Zauber keine Wirkung. Alles, was eine Zielkreatur trägt oder hält, verändert mit ihr die Größe. Lässt sie einen Gegenstand fallen, so nimmt dieser sofort seine normale Größe an. Wurfwaffen und Geschosse nehmen ihre normale Größe an, sobald sie ein Ziel getroffen oder verfehlt haben. Vergrößern: Die Größe des Ziels steigt um eine Kategorie, beispielsweise von Mittelgroß auf Groß. Das Ziel ist außerdem bei Stärkewürfen und Stärkerettungswürfen im Vorteil. Die Angriffe des Ziels mit seinen vergrößerten Waffen sowie seine waffenlosen Angriffe bewirken bei einem Treffer zusätzlich 1W4 Schaden. Verkleinern: Die Größe des Ziels sinkt um eine Kategorie, beispielsweise von Mittelgroß auf Klein. Das Ziel ist außerdem bei Stärkewürfen und Stärkerettungswürfen im Nachteil. Die Angriffe des Ziels mit seinen verkleinerten Waffen sowie seine waffenlosen Angriffe bewirken bei einem Treffer 1W4 Schaden weniger (der Schaden kann jedoch nicht unter 1 verringert werden).', '', 'Barde, Druide, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verlangsamen', 'VERLANGSAMEN', 3, 'Verwandlungszauber', 'Aktion', '36 Meter', 'V, G, M (ein Tropfen Melasse)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du verlangsamst um bis zu sechs Kreaturen deiner Wahl in einem Würfel mit zwölf Metern Kantenlänge in Reichweite die Zeit. Jedes Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer von diesem Zauber betroffen. Die Bewegungsrate eines betroffenen Ziels wird halbiert, es erleidet einen Malus von -2 auf seine RK und auf Geschicklichkeitsrettungswürfe. Außerdem kann es keine Reaktionen ausführen. Es kann in seinem Zug 328 KAPITEL 7 I ZAUBER eine Aktion oder eine Bonusaktion verwenden, jedoch nicht beides. Wenn es die Angriffsaktion ausführt, kann es nur einen Angriff ausführen. Wenn es einen Zauber mit Gestenkomponente wirkt, besteht eine Chance von 25 Prozent, dass der Zauber scheitert, weil das Ziel die Gesten zu langsam ausführt. Ein betroffenes Ziel wiederholt den Rettungswurf am Ende jedes seiner Züge. Bei einem Erfolg endet der Effekt. VERSCHONUNG DER STERBENDEN Zaubertrick der Nekromantie (Druide, Kleriker) Zeitaufwand: Aktion Reichweite: 4,5 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Wähle eine Kreatur in Reichweite aus, deren Treffer­punkte auf 0 gesunken sind, die jedoch noch nicht tot ist. Die Kreatur wird stabil.', 'Die Reichweite wird jeweils verdoppelt, wenn du die 5. (neun Meter), die 11. (18 Meter) und die 17. (36 Meter) Stufe erreichst.', 'Barde, Magier, Zauberer', '{"scaling":{"text":"Die Reichweite wird jeweils verdoppelt, wenn du die 5. (neun Meter), die 11. (18 Meter) und die 17. (36 Meter) Stufe erreichst."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verschwimmen', 'VERSCHWIMMEN', 2, 'Illusionszauber', 'Aktion', 'Selbst', 'V', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Dein Körper wird unscharf. Für die Wirkungsdauer sind Kreaturen bei Angriffswürfen gegen dich im Nachteil. Angreifer, die dich mit Blindsicht oder Wahrer Blick wahrnehmen, sind gegen diesen Effekt immun.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verst-ndigung', 'VERSTÄNDIGUNG', 3, 'Erkenntniszauber', 'Aktion', 'Unbegrenzt', 'V, G, M (ein Stück Kupferdraht)', NULL, 'Unmittelbar', 0, 0, 'Du sendest eine kurze Botschaft von höchstens 25 Worten an eine Kreatur, der du begegnet bist oder die dir von jemandem beschrieben wurde, der ihr begegnet ist. Das Ziel hört die Botschaft in seinem Kopf, erkennt dich als Absender, sofern es dich kennt, und kann auf gleiche Weise sofort antworten. Der Zauber ermöglicht dem Ziel, die Bedeutung deiner Botschaft zu verstehen. Du kannst die Botschaft über beliebige Entfernungen und sogar auf andere Existenzebenen senden. Wenn sich das Ziel jedoch auf einer anderen Ebene befindet als du, besteht ein Risiko von fünf Prozent, dass die Botschaft nicht ankommt. Ist dies der Fall, so weißt du, dass sie nicht angekommen ist. Wenn eine Kreatur deine Botschaft erhalten hat, kann sie für die nächsten acht Stunden verhindern, dass du sie erneut mit diesem Zauber erreichst. Versuchst du in diesem Fall, ihr eine weitere Botschaft zu senden, so erfährst du, dass du blockiert wurdest, und der Zauber scheitert.', '', 'Barde, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verstricken', 'VERSTRICKEN', 1, 'Beschwörungszauber', 'Aktion', '27 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'In einem quadratischen Bereich mit sechs Metern Kantenlänge in Reichweite wachsen greifende Pflanzen aus dem Boden. Diese Pflanzen verwandeln den Boden im Bereich für die Wirkungsdauer in schwieriges Gelände. Wenn der Zauber endet, verschwinden sie. Jede Kreatur außer dir, die sich in dem Bereich befindet, wenn du den Zauber wirkst, muss einen Stärkerettungswurf bestehen, oder sie ist festgesetzt, bis der Zauber endet. Eine festgesetzte Kreatur kann mit einer Aktion einen Stärkewurf (Athletik) gegen deinen Zauberrettungswurf-SG ausführen. Bei einem Erfolg befreit sie sich von den greifenden Pflanzen und ist nicht mehr von ihnen festgesetzt.', '', 'Druide, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('vertrauten-finden', 'VERTRAUTEN FINDEN', 1, 'Beschwörungszauber', '1 Stunde oder Ritual', '3 Meter', 'V, G, M (brennender Weihrauch im Wert von mindestens 10 GM, den der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 1, 'Du erhältst die Dienste eines Vertrauten — eines Geistes, der eine Tiergestalt deiner Wahl annimmt: Eidechse, Eule, Falke, Fledermaus, Frosch, Katze, Oktopus, Rabe, Ratte, Spinne, Wiesel oder ein anderes Tier mit einem Herausforderungsgrad von 0. Der Vertraute erscheint in einem freien Bereich in Reichweite. Er hat die Spielwerte der gewählten Gestalt (siehe Anhang B), ist jedoch tatsächlich ein celestisches Wesen, ein Feenwesen oder ein Unhold (nach deiner Wahl) und kein Tier. Dein Vertrauter agiert unabhängig von dir, befolgt aber deine Befehle. Telepathische Verbindung: Solange sich dein Vertrauter im Abstand von bis zu 30 Metern von dir befindet, kannst du telepathisch mit ihm kommunizieren. Zudem kannst du als Bonusaktion bis zum Beginn deines nächsten Zugs durch die Sinne des Vertrauten hören und sehen. Dabei kannst du auch etwaige Spezialsinne des Vertrauten nutzen. Wenn du einen Zauber mit Berührungsreichweite wirkst, kann dein Vertrauter die Berührung ausführen. Dazu muss er sich im Abstand von bis zu 30 Metern von dir befinden und die Berührung als Reaktion ausführen, wenn du den Zauber wirkst. Kampf: Der Vertraute ist mit dir und deinen Verbündeten verbündet. Er würfelt seine eigene Initiative aus und handelt in seinem eigenen Zug. Ein Vertrauter kann nicht angreifen, jedoch andere Aktionen normal ausführen. Verschwinden des Vertrauten: Sinken die Trefferpunkte des Vertrauten auf 0, so verschwindet er. Er erscheint wieder, wenn du den Zauber erneut wirkst. Als magische Aktion kannst du den Vertrauten vorübergehend verwerfen und in eine Taschendimension schicken. Alternativ kannst du ihn für immer verwerfen. Wenn du deinen Vertrauten EIN DRACHENBLUTIGER DRUIDE HÄLT TOBENDE GNOLLE MIT VERSTRICKEN AUF vorübergehend verworfen hast, kannst du ihn als magische Aktion in einem freien Bereich im Abstand von bis zu neun Metern von dir wieder erscheinen lassen. Wann immer die Trefferpunkte des Vertrauten auf 0 sinken oder der Vertraute in einer Taschendimension verschwindet, bleibt alles, was er getragen oder gehalten hat, in seinem Bereich liegen. Nur ein Vertrauter: Du kannst nur jeweils einen Vertrauten haben. Wirkst du diesen Zauber, während du einen Vertrauten hast, so lässt du diesen stattdessen eine neue verfügbare Gestalt annehmen.', '', 'Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verwandlung', 'VERWANDLUNG', 4, 'Verwandlungszauber', 'Aktion', '18 Meter', 'V, G, M (Kokon einer Raupe)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du versuchst, eine Kreatur in Reichweite, die du sehen kannst, in ein Tier zu verwandeln. Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es nimmt für die Wirkungsdauer eine Tiergestalt an. Dabei kann es sich um ein beliebiges Tier deiner Wahl handeln, dessen Herausforderungsgrad nicht höher als der des Ziels ist (wenn das Ziel keinen Herausforderungsgrad hat, verwende seine Stufe). Die Spielwerte des Ziels werden vom Wertekasten des Tieres ersetzt. Das Ziel behält jedoch Gesinnung, Persönlichkeit, Kreaturentyp, Trefferpunkte sowie Trefferpunktewürfel bei. Beispiele für Wertekästen von Tieren findest du in Anhang B. Das Ziel erhält eine Anzahl von temporären Treffer­punkten, die der Anzahl von Trefferpunkten der Tiergestalt entspricht. Der Zauber endet beim Ziel vorzeitig, wenn es keine temporären Trefferpunkte übrig hat. Wenn nach dem Ende der Verwandlung von diesen temporären Trefferpunkten noch welche übrig sind, verschwinden sie. Das Ziel kann nur Aktionen ausführen, die in seiner neuen Gestalt anatomisch möglich sind. Es kann weder sprechen noch Zauber wirken. Die Ausrüstung des Ziels verschmilzt mit seiner neuen Gestalt. Die Kreatur kann die Ausrüstung nicht verwenden oder anderweitig nutzen.', '', 'Barde, Druide, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verwirrung', 'VERWIRRUNG', 4, 'Verzauberungszauber', 'Aktion', '27 Meter', 'V, G, M (drei Nussschalen)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Jede Kreatur in einer Kugel mit einem Radius von drei Metern um einen Punkt deiner Wahl in Reichweite muss einen Weisheitsrettungswurf bestehen, oder sie kann weder Bonusaktionen noch Reaktionen ausführen und muss zu Beginn jedes ihrer Züge mit 1W10 würfeln, um anhand der nachstehenden Tabelle ihr Verhalten in diesem Zug zu bestimmen. 1W10 Verhalten im Zug DIE GESTALTEN DER GEISTER, DIE MIT VERTRAUTEN FINDEN BESCHWOREN WURDEN, SIND VON DEN BESCHWÖRERN INSPIRIERT, 1 Das Ziel führt keine Aktion aus und verwendet seine gesamte Bewegungsrate, um sich zu bewegen. Würfle die Richtung mit 1W4 aus: 1: Norden, 2: Osten; 3: Süden, 4: Westen. 2-6 Das Ziel bewegt sich nicht und führt keine Aktionen aus. 7—8 Das Ziel bewegt sich nicht. Es führt die Angriffsaktion aus, um einen NahkampfangrifF gegen eine zufällige Kreatur in Reichweite auszuführen. Wenn sich keine Kreaturen in Reichweite befinden, führt das Ziel keine Aktionen aus. 9-10 Das Ziel wählt sein Verhalten selbst aus. Ein betroffenes Ziel wiederholt den Rettungswurf am Ende jedes seiner Züge. Bei einem Erfolg endet der Zauber bei diesem Ziel.', 'Für jeden Zauberplatzgrad über dem 4. wird der Radius der Kugel um 1,5 Meter erhöht.', 'Barde, Druide, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der Radius der Kugel um 1,5 Meter erhöht."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('verw-nschen', 'VERWÜNSCHEN', 1, 'Verzauberungszauber', 'Bonusaktion', '27 Meter', 'V, G, M (versteinertes Auge eines Molchs)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du verfluchst eine Kreatur in Reichweite, die du sehen kannst. Für die Wirkungsdauer fügst du dem Ziel, wann immer du es mit einem Angriffswurf triffst, zusätzlich 1W6 nekrotischen Schaden zu. Wähle außerdem ein Attribut aus, wenn du den Zauber wirkst. Das Ziel ist bei allen entsprechenden Attributswürfen im Nachteil. Wenn die Trefferpunkte des Ziels auf 0 sinken, bevor der Zauber endet, kannst du in deinen folgenden Zügen eine Bonusaktion ausführen, um eine andere Kreatur zu verfluchen.', 'bei einem Zauberplatz des 2. Grades bis zu vier Stunden, bei einem Zauberplatz des 3. und 4. Grades bis zu acht Stunden und bei einem Zauberplatz ab dem 5. Grad bis zu 24 Stunden.', 'Hexenmeister', '{"scaling":{"text":"bei einem Zauberplatz des 2. Grades bis zu vier Stunden, bei einem Zauberplatz des 3. und 4. Grades bis zu acht Stunden und bei einem Zauberplatz ab dem 5. Grad bis zu 24 Stunden."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('vollst-ndige-genesung', 'VOLLSTÄNDIGE GENESUNG', 5, 'Bannzauber', 'Aktion', 'Berührung', 'V, G, M (Diamantstaub im Wert von mindestens 100 GM, den der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 0, 'Du berührst eine Kreatur und hebst auf magische Art einen der folgenden Effekte bei ihr auf: 1 Erschöpfungsstufe Einen der Zustände Bezaubert oder Versteinert Einen Fluch oder die Einstimmung des Ziels auf einen verfluchten magischen Gegenstand Jegliche Verringerung eines der Attributswerte des Ziels Jegliche Verringerung des Trefferpunktemaximums des Ziels VORAHNUNG', '', 'Barde, Druide, Kleriker, Paladin, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('ein-zauberwirker-k-nnte-vorbestimmtes-trugbild-verwenden-damit-ein-wachhund-erscheint-der-eindringlinge-h-flich-zum-gehen-auffordert', 'EIN ZAUBERWIRKER KÖNNTE VORBESTIMMTES TRUGBILD VERWENDEN, DAMIT EIN WACHHUND ERSCHEINT, DER EINDRINGLINGE HÖFLICH ZUM GEHEN AUFFORDERT.', 2, 'Erkenntniszauber', '1 Minute oder Ritual', 'Selbst', 'V, G, M (besonders markierte Stöcke, Knochen, Karten oder andere Weissagungsgegenstände im Wert von mindestens 25 GM)', NULL, 'Unmittelbar', 0, 1, 'Du erhältst ein Omen von einem außerweltlichen Wesen zum Ergebnis eines Vorhabens, das innerhalb der nächsten 30 Minuten geplant ist. Der SL wählt das Omen aus der Tabelle „Omen" aus. OMEN Omen Wohl Wehe Wohl und Wehe Gleichgültig Ergebnis des Vorhabens Gut Schlecht Gut und schlecht Weder gut noch schlecht Der Zauber berücksichtigt keinerlei Umstände wie andere Zauber, die das Ergebnis ändern könnten. Wenn du den Zauber vor einer langen Rast häufiger als einmal wirkst, erhöht dies das Risiko, keine Antwort zu erhalten, mit jedem Wirken nach dem ersten um jeweils 25 Prozent.', '', 'Druide, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('voraussicht', 'VORAUSSICHT', 9, 'Erkenntniszauber', '1 Minute', 'Berührung', 'V, G, M (eine Kolibrifeder)', NULL, '8 Stunden', 0, 0, 'Du berührst eine bereitwillige Kreatur und verleihst ihr die eingeschränkte Fähigkeit, in die unmittelbare Zukunft zu blicken. Für die Wirkungsdauer ist das Ziel bei W20-Prüfungen im Vorteil, und andere Kreaturen sind bei Angriffswürfen gegen das Ziel im Nachteil. Der Zauber endet vorzeitig, wenn du ihn erneut wirkst. VORBESTIMMTES TRUGBILD Illusionszauber 6. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: 36 Meter Komponenten: V, G, M (Jadestaub im Wert von mindestens 25 GM) Wirkungsdauer: Bis der Zauber gebannt wird Du erschaffst die Illusion eines Gegenstands, einer Kreatur oder eines anderen sichtbaren Phänomens in Reichweite, die von einem bestimmten Auslöser aktiviert wird. Bis dahin ist die Illusion nicht wahrnehmbar. Sie darf nicht größer als ein Würfel mit neun Metern Kantenlänge sein. Beim Wirken des Zaubers bestimmst du, wie sich die Illusion verhält und welche Geräusche sie macht. Diese vorbestimmte Darbietung kann bis zu fünf Minuten andauern. Tritt der festgelegte Auslöser auf, so entsteht die Illusion und verhält sich auf die von dir beschriebene Weise. Sobald die Illusion ihre Darbietung beendet hat, verschwindet sie und ist zehn Minuten lang inaktiv. Danach kann sie erneut aktiviert werden. Der Auslöser kann beliebig allgemein oder detailliert sein. Er muss jedoch auf sichtbaren und hörbaren Phänomenen basieren, die im Abstand von bis zu neun Metern um den Bereich auftreten. Beispielsweise könntest du eine Illusion deiner selbst erschaffen, die andere davor warnt, eine Tür mit einer Falle zu öffnen. Physische Interaktionen mit dem Abbild enttarnen es als illusionär, da Dinge es einfach durchdringen können. Eine Kreatur, die das Abbild mit der Studieren-Aktion untersucht, kann mit einem erfolgreichen Intelligenzwurf (Nachforschungen) gegen deinen Zauberrettungswurf-SG erkennen, dass es sich um eine Illusion handelt. Erkennt eine Kreatur die Illusion als solche, so wird das Abbild für sie durchscheinend, und sämtliche Geräusche klingen für sie hohl. WÄCHTER UND HÜTER Bannzauber 6. Grades (Barde, Magier) Zeitaufwand: 1 Stunde Reichweite: Berührung Komponenten: V, G, M (ein Silberstab im Wert von mindestens 10 GM) Wirkungsdauer: 24 Stunden Du erschaffst einen Schutz, der einen Bereich von bis zu 232 Quadratmetern auf dem Boden abdeckt. Der geschützte Bereich kann bis zu sechs Meter hoch sein. Du kannst ihn als Quadrat mit 15 Metern Kantenlänge, als hundert aneinandergrenzende Quadrate mit 1,5 Metern Kantenlänge oder als 25 aneinandergrenzende Quadrate mit drei Metern Kantenlänge formen. Wenn du diesen Zauber wirkst, kannst du Individuen bestimmen, die von seinen Effekten nicht betroffen sind. Du kannst auch ein Kennwort festlegen, das den Sprecher gegen die Effekte immunisiert, wenn es im Abstand von bis zu 1,5 Metern vom geschützten Bereich ausgesprochen wird. Der Zauber erzeugt im geschützten Bereich die unten genannten Effekte. Magie bannen hat keinen Effekt auf Wächter und Hüter selbst, aber jeder der folgenden Effekte kann gebannt werden. Werden alle vier Effekte gebannt, so endet Wächter und Hüter. Wenn du den Zauber 365 Tage lang täglich auf denselben Bereich wirkst, bleibt er wirksam, bis seine Effekte aufgelöst werden. Korridore: Alle geschützten Korridore sind mit Nebel erfüllt und dadurch komplett verschleiert. Außerdem besteht an jeder Kreuzung oder Abzweigung eine Chance von 50 Prozent, dass eine Kreatur (außer dir selbst) glaubt, in die entgegengesetzte Richtung zu gehen. Treppen: Alle Treppen im geschützten Bereich werden von der Decke bis zum Boden von Spinnennetzen blockiert, so wie beim Zauber Netz. Wenn die Fäden zerstört werden, wachsen sie für die Wirkungsdauer von Wächter und Hüter innerhalb von zehn Minuten nach. Türen: Alle Türen im geschützten Bereich sind auf magische Art verschlossen, wie durch den Zauber Arkanes Schloss. Außerdem kannst du bis zu zehn Türen mit einer Illusion verbergen, sodass sie als normale Wand erscheinen. Andere Zaubereffekte: Platziere einen der folgenden magischen Effekte im geschützten Bereich: Einflüsterung in einem Würfel mit 1,5 Metern Kantenlänge. Jede Kreatur, die den Würfel betritt, erhält die Einflüsterung mental Magischer Mund an zwei Orten Stinkende Wolke an zwei Orten (solange Wächter und Hüter wirkt, kehren die Dämpfe nach zehn Minuten zurück, wenn sie aufgelöst werden) Tanzende Lichter in vier Korridoren mit einem einfachen Muster, das die Lichter für die Wirkungsdauer von Wächter und Hüter wiederholen Windstoß in einem Korridor oder Raum (der Wind weht für die Wirkungsdauer kontinuierlich)', '', 'Barde, Druide, Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('waffe-des-glaubens', 'WAFFE DES GLAUBENS', 2, 'Hervorrufungszauber', 'Bonusaktion', '18 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Du erzeugst eine schwebende geisterhafte Kraft, die wie eine Waffe deiner Wahl aussieht und für die Wirkungsdauer bestehen bleibt. Die Kraft erscheint in einem Bereich deiner Wahl in Reichweite, und du kannst sofort einen Nahkampf-Zauberangriff gegen eine Kreatur im Abstand von bis zu 1,5 Metern von der Kraft ausführen. Bei einem Treffer erleidet das Ziel Energieschaden in Höhe von 1W8 plus deinem Zauberwirken-Attributsmodifikator. In deinen folgenden Zügen kannst du die Waffe als Bonusaktion um bis zu sechs Meter bewegen und einen weiteren Angriff gegen eine Kreatur im Abstand von bis zu 1,5 Metern von ihr ausführen.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W8 erhöht.', 'Kleriker', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wahre-auferstehung', 'WAHRE AUFERSTEHUNG', 9, 'Nekromantiezauber', '1 Stunde', 'Berührung', 'V, G, M (Diamanten im Wert von 25.000 GM, die der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 0, 'Du berührst eine tote Kreatur. Ihr Tod darf höchstens 200 Jahre zurückliegen, und sie darf nicht an Alters­schwäche gestorben sein. Die Kreatur wird mit allen Trefferpunkten wiederbelebt. Dieser Zauber schließt alle Wunden, neutralisiert jedes Gift, heilt alle magischen Ansteckungen und bannt sämtliche Flüche, unter denen die Kreatur zum Zeitpunkt ihres Todes gelitten hat. Beschädigte oder fehlende Organe und Gliedmaßen werden ersetzt. Wenn die Kreatur untot war, wird ihre nichtuntote Gestalt wiederhergestellt. Der Zauber kann einen neuen Körper erschaffen, wenn der ursprüngliche Körper nicht mehr existiert. In diesem Fall musst du den Namen der Kreatur aussprechen. Die Kreatur erscheint dann in einem freien Bereich deiner Wahl im Abstand von bis zu drei Metern von dir.', '', 'Druide, Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wahre-verwandlung', 'WAHRE VERWANDLUNG', 9, 'Verwandlungszauber', 'Aktion', '9 Meter', 'V, G, M (ein Tropfen Quecksilber, ein Stück Gummiarabikum und eine Rauchschwade)', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Wähle ein Ziel - eine Kreatur oder einen nichtmagischen Gegenstand - in Reichweite aus, das du sehen kannst. Eine Kreatur verwandelt sich in eine andere Kreatur oder einen nichtmagischen Gegenstand. Ein Gegenstand, der weder getragen noch gehalten werden darf, verwandelt sich in eine Kreatur. Die Verwandlung bleibt für die Wirkungsdauer bestehen, sofern das Ziel nicht stirbt oder zerstört wird. Wenn du allerdings deine Konzentration auf diesen Zauber für die gesamte Wirkungsdauer aufrechterhältst, wirkt der Zauber, bis er gebannt wird. Eine nicht bereitwillige Kreatur kann einen Weisheits-rettungswurf ausführen. Bei einem Erfolg ist sie nicht von diesem Zauber betroffen. Kreatur in Kreatur: Verwandelst du eine Kreatur in eine andere Kreatur, so kannst du eine beliebige Gestalt wählen, deren Herausforderungsgrad nicht höher ist als der Herausforderungsgrad oder die Stufe des Ziels. Die Spielwerte des Ziels werden durch den Wertekasten der neuen Gestalt ersetzt. Das Ziel behält jedoch Trefferpunkte, Trefferpunktewürfel, Gesinnung und Persönlichkeit bei. Das Ziel erhält eine Anzahl von temporären Treffer­punkten, die der Anzahl von Trefferpunkten der neuen Gestalt entspricht. Wenn nach dem Ende der Verwandlung von diesen temporären Trefferpunkten noch welche übrig sind, verschwinden sie. Das Ziel kann nur Aktionen ausführen, die in seiner neuen Gestalt anatomisch möglich sind. Es kann weder sprechen noch Zauber wirken. Die Ausrüstung des Ziels verschmilzt mit seiner neuen Gestalt. Die Kreatur kann die Ausrüstung nicht verwenden oder anderweitig nutzen. Gegenstand in Kreatur: Du kannst einen Gegenstand in eine beliebige Kreatur verwandeln, sofern die Kreatur nicht größer als der Gegenstand ist und ihr Herausforderungsgrad höchstens 9 beträgt. Die Kreatur ist dir und deinen Verbündeten gegenüber freundlich gesinnt. Im Kampf ist sie unmittelbar nach dir am Zug, und sie gehorcht deinen Befehlen. Wenn der Zauber länger als eine Stunde andauert, kannst du die Kreatur nicht mehr kontrollieren. Sie kann dir freundlich gesinnt bleiben, je nachdem, wie du sie behandelt hast. Kreatur in Gegenstand: Verwandelst du eine Kreatur in einen Gegenstand, so verwandelt sie sich mit allem, was sie trägt und in der Hand hält. Der Gegenstand darf nicht größer sein als die Kreatur. Die Spielwerte der Kreatur werden zu denen des Gegenstands. Wenn der Zauber endet und das Ziel wieder seine ursprüngliche Gestalt annimmt, besitzt es keine Erinnerungen an die Zeit, die es als Gegenstand verbracht hat.', '', 'Barde, Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wahrer-blick', 'WAHRER BLICK', 6, 'Erkenntniszauber', 'Aktion', 'Berührung', 'V, G, M (Pilzpulver im Wert von mindestens 25 GM, das der Zauber verbraucht)', NULL, '1 Stunde', 0, 0, 'Für die Wirkungsdauer hat eine bereitwillige Kreatur, die du berührst, Wahrer Blick mit einer Reichweite von 36 Metern.', '', 'Barde, Hexenmeister, Kleriker, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('w-nde-passieren', 'WÄNDE PASSIEREN', 5, 'Verwandlungszauber', 'Aktion', '9 Meter', 'V, G, M (eine Prise Sesamkörner)', NULL, '1 Stunde', 0, 0, 'An einem Punkt in Reichweite auf einer Oberfläche aus Holz, Gips oder Stein (beispielsweise eine Mauer, eine Decke oder ein Fußboden), den du sehen kannst, erscheint ein Durchgang, der für die Wirkungsdauer bestehen bleibt. Du bestimmst die Ausmaße des Durchgangs: höchstens 1,5 Meter breit, 2,4 Meter hoch und sechs Meter tief. Der Durchgang macht das umgebende Gebäude nicht instabil. Wenn der Durchgang verschwindet, gelangen sämtliche Kreaturen und Gegenstände, die sich noch darin befinden, in den freien Bereich, welcher der Oberfläche, auf die der Zauber gewirkt wurde, am nächsten liegt. WANKENDMACHENDES NIEDERSTRECKEN Verzauberungszauber 4. Grades (Paladin) Zeitaufwand: Bonusaktion, die du sofort ausführst, wenn du eine Kreatur mit einer Nahkampfwaffe oder einem waffenlosen Angriff getroffen hast Reichweite: Selbst Komponenten: V Wirkungsdauer: Unmittelbar Das Ziel erleidet durch den Angriff zusätzlich 4W6 psychischen Schaden und muss einen Weisheitsrettungswurf bestehen, oder es ist bis zum Ende deines nächsten Zugs betäubt.', 'Für jeden Zauberplatzgrad über dem 4. wird der zusätzliche Schaden um 1W6 erhöht.', 'Magier', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der zusätzliche Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wasser-atmen', 'WASSER ATMEN', 3, 'Verwandlungszauber', 'Aktion oder Ritual', '9 Meter', 'V, G, M (ein kurzes Schilfrohr)', NULL, '24 Stunden', 0, 1, 'Bis zu zehn bereitwillige Kreaturen deiner Wahl in Reichweite können für die Wirkungsdauer unter Wasser atmen. Betroffene Kreaturen verfügen weiterhin über ihre normale Atmung. ZAUBER WIE WASSER ATMEN UND', 'Für jeden Zauberplatzgrad über dem 1. kannst du zusätzliche 40 Liter Wasser erschaffen oder zerstören oder die Kantenlänge des Würfels um 1,5 Meter erhöhen.', 'Druide, Magier, Waldläufer, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du zusätzliche 40 Liter Wasser erschaffen oder zerstören oder die Kantenlänge des Würfels um 1,5 Meter erhöhen."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wasser-kontrollieren', 'WASSER KONTROLLIEREN', 4, 'Verwandlungszauber', 'Aktion', '90 Meter', 'V, G, M (eine Mischung aus Wasser und Staub)', NULL, 'Konzentration, bis zu 10 Minuten', 1, 0, 'Für die Wirkungsdauer kontrollierst du jedes Gewässer in einem Bereich deiner Wahl, der einem Würfel mit bis zu 30 Metern Kantenlänge entspricht, und nutzt dabei einen der folgenden Effekte. Als magische Aktion in deinen folgenden Zügen kannst du den gleichen Effekt wiederholen oder einen anderen auswählen. Fluss umlenken: Du bewirkst, dass sich fließendes Wasser im Bereich in eine Richtung deiner Wahl bewegt, selbst wenn es dafür über Hindernisse, an Wänden empor oder in andere unwahrscheinliche Richtungen strömen muss. Das Wasser im Bereich bewegt sich nach deinen Vorgaben. Sobald es jedoch den Bereich des Zaubers verlässt, fließt es wieder je nach Gelände. Das Wasser strömt in die Richtung deiner Wahl, bis der Zauber endet oder du einen anderen Effekt auswählst. Flut: Du lässt den Wasserspiegel aller stehenden Gewässer im Bereich um bis zu sechs Meter ansteigen. Wenn du einen Bereich in einem größeren Gewässer auswählst, erschaffst du stattdessen eine sechs Meter hohe Welle, die sich von einer Seite des Bereichs zum anderen bewegt und dann bricht. Alle Fahrzeuge von höchstens riesiger Größe im Weg der Welle werden mit ihr zur anderen Seite bewegt. Wenn ein solches Fahrzeug von der Welle getroffen wird, besteht eine Chance von 25 Prozent, dass es kentert. Der Wasserspiegel bleibt erhöht, bis der Zauber endet oder du einen anderen Effekt auswählst. Wenn dieser Effekt eine Welle erzeugt, wiederholt sich diese zu Beginn deines nächsten Zugs, solange der Flut-Effekt anhält. Wasser teilen: Du teilst das Wasser im Bereich und erschaffst einen Graben, der sich durch den Wirkungs­bereich zieht. Das Wasser ragt auf beiden Seiten wie eine Mauer auf. Der Graben bleibt bestehen, bis der Zauber endet oder du einen anderen Effekt auswählst. Anschließend füllt das Wasser im Verlauf der nächsten Runde langsam den Graben, bis der normale Wasserspiegel wiederhergestellt ist. Wasserstrudel: Du erschaffst einen Wasserstrudel in der Mitte eines Bereichs von mindestens 15 mal 15 Metern und einer Tiefe von 7,5 Metern. Der Strudel bleibt bestehen, bis du einen anderen Effekt auswählst oder der Zauber endet. Er ist 7,5 Meter hoch, an der Basis 1,5 Meter breit und an der Spitze bis zu 15 Meter breit. Alle Kreaturen, die sich im Wasser im Abstand von bis zu 7,5 Metern um den Strudel befinden, werden drei Meter weit in seine Richtung gezogen. Wenn eine Kreatur in einem Zug erstmals in den Bereich des Strudels gelangt oder ihren Zug dort beendet, führt sie einen Stärkerettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 2W8 Wuchtschaden. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur halb so viel Schaden. Eine Kreatur kann nur dann vom Strudel wegschwimmen, wenn sie zunächst eine Aktion ausführt, um sich zu entfernen, und einen Stärkewurf (Athletik) gegen deinen Zauberrettungswurf-SG besteht.', '', 'Druide, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wasserwandeln', 'WASSERWANDELN', 3, 'Verwandlungszauber', 'Aktion oder Ritual', '9 Meter', 'V, G, M (ein Stück Kork)', NULL, '1 Stunde', 0, 1, 'Dieser Zauber verleiht die Fähigkeit, sich über flüssige Oberflächen zu bewegen, darunter Wasser, Säure, Schlamm, Schnee, Treibsand oder Lava, als handele es sich um ungefährlichen, festen Boden (Kreaturen, die sich über geschmolzene Lava bewegen, können jedoch trotzdem Hitzeschaden erleiden). Bis zu zehn bereitwillige Kreaturen deiner Wahl in Reichweite erhalten für die Wirkungsdauer diese Fähigkeit. Ein betroffenes Ziel muss eine Bonusaktion ausführen, um von der Oberfläche der Flüssigkeit in die Flüssigkeit zu gelangen und umgekehrt. Wenn das Ziel jedoch in den Bereich fällt, gelangt es automatisch durch die Oberfläche in die Flüssigkeit darunter.', '', 'Druide, Kleriker, Waldläufer, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('weg-finden', 'WEG FINDEN', 6, 'Erkenntniszauber', '1 Minute', 'Selbst', 'V, G, M (ein Satz Weissagungsgegenstände wie Karten oder Runen im Wert von mindestens 100 GM)', NULL, 'Konzentration, bis zu 1 Tag', 1, 0, 'Du spürst auf magische Art den direktesten physischen Weg zu einem Ort, den du nennst. Der Ort muss dir bekannt sein. Der Zauber scheitert, wenn du ein Ziel auf einer anderen Existenzebene, ein bewegtes Ziel (beispielsweise eine mobile Festung) oder ein unspezifisches Ziel (beispielsweise „Hort eines grünen Drachen") nennst. Während der Wirkungsdauer weißt du, wie weit der Zielort entfernt ist und in welcher Richtung er liegt, sofern du dich auf derselben Existenzebene befindest. Wann immer du unterwegs eine Wahl zwischen verschiedenen Wegen hast, weißt du, welcher Weg der direkteste ist.', '', 'Barde, Druide, Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('weihen', 'WEIHEN', 5, 'Bannzauber', '24 Stunden', 'Berührung', 'V, G, M (Weihrauch im Wert von mindestens 1.000 GM, den der Zauber verbraucht)', NULL, 'Bis der Zauber gebannt wird', 0, 0, 'Du berührst einen Punkt und erfüllst den Bereich um ihn herum mit heiliger oder unheiliger Macht. Der Bereich kann einen Radius von bis zu 18 Metern haben. Wenn dazu ein Bereich gehört, der bereits unter dem Effekt von Weihen steht, scheitert der Zauber. Im betroffenen Bereich wirken folgende Effekte. Geheiligter Schutz: Wähle beliebige der folgenden Kreaturentypen aus: Aberration, celestisches Wesen, Elementar, Feenwesen, Unhold oder Untoter. Kreaturen des ausgewählten Typs können den Bereich nicht willentlich betreten, und Kreaturen innerhalb des Bereichs, die von solchen Wesen besessen, bezaubert oder verängstigt sind, sind innerhalb des Bereichs nicht von ihnen besessen, bezaubert oder verängstigt. Zusätzlicher Effekt: Du bindest einen zusätzlichen Effekt aus der folgenden Liste an den Bereich: Anfälligkeit: Kreaturen der von dir ausgewählten Typen sind anfällig für eine Schadensart deiner Wahl, solange sie sich im Bereich aufhalten. Dunkelheit: Dunkelheit erfüllt den Bereich. Weder natürliches noch magisches Licht durch Zauber eines niedrigeren Grades als dem dieses Zaubers kann den Bereich erhellen. Extradimensionale Interferenz: Kreaturen der von dir ausgewählten Typen können nicht mittels Teleportation oder auf extradimensionale oder interplanare Art in den Bereich oder aus ihm heraus gelangen. Friedliche Ruhe: Leichen innerhalb des Bereichs können nicht in Untote verwandelt werden. Furcht: Kreaturen der von dir ausgewählten Typen sind verängstigt, solange sie sich im Bereich aufhalten. Mut: Kreaturen der von dir ausgewählten Typen können nicht verängstigt werden, solange sie sich im Bereich aufhalten. Resistenz: Kreaturen der von dir ausgewählten Typen sind resistent gegen eine Schadensart deiner Wahl, solange sie sich im Bereich aufhalten. Stille: Geräusche können weder in den Bereich hineinnoch aus ihm herausdringen. Tageslicht: Helles Licht erfüllt den Bereich. Magische Dunkelheit durch Zauber eines niedrigeren Grades als dem dieses Zaubers kann den Bereich nicht verdunkeln. STURM IU BEWAHREN. IIIP Zungen: Kreaturen der von dir ausgewählten Typen können mit allen anderen Kreaturen im Bereich kommunizieren, auch wenn sie keine gemeinsame Sprache sprechen.', '', 'Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('weissagung', 'WEISSAGUNG', 4, 'Erkenntniszauber', 'Aktion oder Ritual', 'Selbst', 'V, G, M (Weihrauch im Wert von mindestens 25 GM, den der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 1, 'Dieser Zauber bringt dich mit einem Gott oder dem Diener eines Gottes in Kontakt. Du stellst eine Frage zu einem bestimmten Ziel, einem Ereignis oder einer Aktivität der kommenden sieben Tage. Der SL antwortet wahrheitsgemäß in Form eines kurzen Satzes oder eines kryptischen Reims. Der Zauber berücksichtigt keinerlei Umstände, die das Ergebnis ändern könnten, beispielsweise das Wirken anderer Zauber. Wenn du den Zauber vor einer langen Rast häufiger als einmal wirkst, erhöht dies das Risiko, keine Antwort zu erhalten, mit jedem Wirken nach dem ersten um jeweils 25 Prozent. WESEN DES WALDES BESCHWÖREN Beschwörungszauber 4. Grades (Druide, Waldläufer) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 10 Minuten Du beschwörst Naturgeister, die dich für die Wirkungs­dauer in einer Ausströmung von drei Metern umschwirren. Wann immer die Ausströmung in den Bereich einer Kreatur gelangt, die du sehen kannst, und wann immer eine Kreatur, die du sehen kannst, die Ausströmung betritt oder ihren Zug dort beendet, kannst du die Kreatur zu einem Weisheitsrettungswurf zwingen. Scheitert der Wurf, so erleidet die Kreatur 5W8 Energieschaden, anderenfalls die Hälfte. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen. Außerdem kannst du für die Wirkungsdauer des Zaubers die Rückzug-Aktion als Bonusaktion ausführen.', 'Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 1W8 erhöht.', 'Druide, Kleriker, Magier', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wetterkontrolle', 'WETTERKONTROLLE', 8, 'Verwandlungszauber', '10 Minuten', 'Selbst', 'V, G, M (brennender Weihrauch)', NULL, 'Konzentration, bis zu 8 Stunden', 1, 0, 'Für die Wirkungsdauer kontrollierst du das Wetter im Abstand von bis zu acht Kilometern. Du musst dich im Freien befinden, wenn du diesen Zauber wirkst. Der Zauber endet vorzeitig, wenn du nach drinnen gehst. Wenn du den Zauber wirkst, änderst du die aktuellen Wetterbedingungen, die vom SL bestimmt werden. Du kannst Niederschlag, Temperatur und Wind beeinflussen. Es dauert 1W4 mal zehn Minuten, bis die neuen Bedingungen in Kraft treten. Danach kannst du die Bedingungen erneut ändern. Wenn der Zauber endet, normalisiert sich das Wetter allmählich. Finde zum Ändern des Wetters eine aktuelle Wetterbedingung in der folgenden Tabelle und ändere sie eine Stufe nach oben oder unten. Wenn du den Wind änderst, kannst du auch seine Richtung ändern. NIEDERSCHLAG Phase Bedingung 1 Wolkenlos 2 Leicht bewölkt 3 Bewölkt oder Bodennebel 4 Regen, Hagel oder Schnee 5 Starkregen, Hagelsturm oder Schneesturm TEMPERATUR WIND Phase Bedingung Phase Bedingung Extrem heiß Flaute Heiß Mäßiger Wind Warm Starker Wind Kühl Starke Böen Kalt Sturm Eisig WIDERSTAND Zaubertrick des Banns (Druide, Kleriker) Zeitaufwand: Aktion Reichweite: Berührung Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Minute Du berührst eine bereitwillige Kreatur und wählst eine Schadensart aus: Blitz, Feuer, Gift, Gleißend, Hieb, Kälte, Nekrotisch, Säure, Schall, Stich oder Wucht. Wenn die Kreatur während der Wirkungsdauer Schaden des ausgewählten Typs erleidet, verringert sie den erlittenen Gesamtschaden um 1W4. Eine Kreatur kann von diesem Zauber nur einmal pro Zug profitieren.', '', 'Druide, Kleriker, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wiederbeleben', 'WIEDERBELEBEN', 3, 'Nekromantiezauber', 'Aktion', 'Berührung', 'V, G, M (ein Diamant im Wert von mindestens 300 GM, den der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 0, 'Du berührst eine Kreatur, die innerhalb der letzten Minute gestorben ist. Diese Kreatur wird mit 1 Trefferpunkt wiederbelebt. Dieser Zauber kann keine Kreaturen wiederbeleben, die an Altersschwäche gestorben sind, und stellt auch keine fehlenden Körperteile wieder her.', '', 'Druide, Kleriker, Paladin, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wiedergeburt', 'WIEDERGEBURT', 5, 'Nekromantiezauber', '1 Stunde', 'Berührung', 'V, G, M (seltene Öle im Wert von mindestens 1.000 GM, die der Zauber verbraucht)', NULL, 'Unmittelbar', 0, 0, 'Du berührst einen toten Humanoiden oder seine Überreste. Sofern die Kreatur höchstens zehn Tage lang tot war, erschafft der Zauber einen neuen Körper für sie und ruft die Seele in diesen Körper. Würfle mit 1W10 und bestimme die Spezies des Körpers anhand der Tabelle unten, oder der SL wählt eine andere spielbare Spezies aus. 1W10 Spezies 1W10 Spezies Aasimar Halbling Drachenblütiger Mensch Elf Ork Gnom Tiefling Goliath Zwerg Die reinkarnierte Kreatur trifft alle Entscheidungen, welche die Beschreibung ihrer Spezies bietet, und erinnert sich an ihr voriges Leben. Sie behält die Fähigkeiten bei, die sie in der ursprünglichen Gestalt hatte, verliert jedoch die Merkmale der bisherigen Spezies und erhält die Merkmale der neuen. 338 KAPITEL 7 ZAUBER', '', 'Druide', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('windstoss', 'WINDSTOSS', 2, 'Hervorrufungszauber', 'Aktion', 'Selbst', 'V, G, M (ein Hülsenfruchtsamen)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Für die Wirkungsdauer weht von dir ausgehend ein starker Wind in einer Linie mit 18 Metern Länge und drei Metern Breite in eine Richtung deiner Wahl. Jede Kreatur innerhalb der Linie muss einen Stärkerettungswurf bestehen, oder sie wird entlang der Linie 4,5 Meter weit von dir weggestoßen. Eine Kreatur, die ihren Zug innerhalb der Linie beendet, muss den Rettungswurf ebenfalls ausführen. Jede Kreatur innerhalb der Linie muss für jeden Meter, den sie sich auf dich zubewegt, zwei Meter ihrer Bewegungsrate verbrauchen. Der Windstoß zerstreut Gas und Dampf und löscht Kerzen und ähnliche ungeschützte Flammen in seinem Bereich. Geschützte Flammen wie die von Laternen flackern wild. Es besteht eine Chance von 50 Prozent, dass sie gelöscht werden. Für die Wirkungsdauer kannst du als Bonusaktion in deinen folgenden Zügen die Richtung ändern, in die der Wind weht.', '', 'Druide, Magier, Waldläufer, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('windwall', 'WINDWALL', 3, 'Hervorrufungszauber', 'Aktion', '36 Meter', 'V, G, M (ein Fächer und eine Feder)', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'An einem Punkt deiner Wahl in Reichweite erscheint eine Wand aus starkem Wind. Die Wand kann bis zu 15 Meter lang, 4,5 Meter hoch und 30 Zentimeter dick sein. Du kannst sie beliebig formen, solange sie einem ununterbrochenen Pfad auf dem Boden folgt. Die Wand bleibt für die Wirkungsdauer bestehen. Wenn die Wand erscheint, führt jede Kreatur in ihrem Bereich einen Stärkerettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 4W8 Wuchtschaden, anderenfalls die Hälfte. Der starke Wind hält Nebel, Rauch und andere Gase auf Abstand. Fliegende Kreaturen und Gegenstände von höchstens kleiner Größe können die Wand nicht durchdringen. Lose, leichte Materialien, die in die Wand gebracht werden, fliegen nach oben. Pfeile, Bolzen und andere gewöhnliche Geschosse, die auf Ziele hinter der Wand geschossen werden, werden nach oben abgelenkt und verfehlen ihr Ziel automatisch. Felsbrocken, die von Riesen oder Belagerungsmaschinen geschleudert werden, und ähnliche Geschosse sind hiervon nicht betroffen. Kreaturen in gasförmiger Gestalt können die Wand nicht passieren.', '', 'Druide, Waldläufer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('windwandeln', 'WINDWANDELN', 6, 'Verwandlungszauber', '1 Minute', '9 Meter', 'V, G, M (eine Kerze)', NULL, '8 Stunden', 0, 0, 'Dieser Zauber verwandelt dich und bis zu zehn bereitwillige Kreaturen deiner Wahl in Reichweite für die Wirkungsdauer in gasförmige Gestalten, die als Wolkenschwaden erscheinen. In Wolkengestalt verfügt ein Ziel über eine Flugbewegungsrate von 90 Metern und kann schweben. Es ist gegen den Zustand Liegend immun und gegen Hieb-, Stichund Wuchtschaden resistent. Die einzigen Aktionen, die ein Ziel in dieser Gestalt ausführen kann, sind die Spurt-Aktion oder eine magische Aktion, um die Rückverwandlung in seine normale Gestalt zu beginnen. Die Rückverwandlung dauert eine Minute, in der das Ziel betäubt ist. Bis der Zauber endet, kann das Ziel zur Wolkengestalt zurückkehren. Auch dies erfordert eine magische Aktion, gefolgt von einer einminütigen Verwandlung. Befindet sich ein Ziel in Wolkengestalt in der Luft, wenn der Zauber endet, so sinkt es eine Minute lang 18 Meter pro Runde hinab, bis es sicher landet. Wenn es nach einer Minute nicht landen kann, stürzt es die verbleibende Entfernung nach unten.', '', 'Druide', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wirrnis', 'WIRRNIS', 8, 'Verzauberungszauber', 'Aktion', '45 Meter', 'V, G, M (ein Schlüsselring ohne Schlüssel)', NULL, 'Unmittelbar', 0, 0, 'Du attackierst den Verstand einer Kreatur in Reichweite, die du sehen kannst. Das Ziel führt einen Intelligenzrettungs-wurf aus. Scheitert der Wurf, so erleidet das Zie110W12 psychischen Schaden und kann weder Zauber wirken noch die magische Aktion ausführen. Nach jeweils 30 Tagen wiederholt das Ziel den Rettungswurf und beendet den Effekt bei einem Erfolg. Der Effekt kann auch durch die Zauber Heilung, Vollständige Genesung oder Wunsch beendet werden. Bei einem erfolgreichen Rettungswurf erleidet das Ziel nur halb so viel Schaden. WORT DER MACHT: BETÄUBUNG Verzauberungszauber 8. Grades (Barde, Hexenmeister, Magier, Zauberer) Zeitaufwand: Aktion Reichweite: 18 Meter Komponenten: V Wirkungsdauer: Unmittelbar Du überwältigst den Verstand einer Kreatur in Reichweite, die du sehen kannst. Wenn das Ziel höchstens 150 Trefferpunkte hat, wird es betäubt. Anderenfalls beträgt seine Bewegungsrate bis zum Beginn deines nächsten Zugs 0. Das betäubte Ziel führt am Ende jedes seiner Züge einen Konstitutionsrettungswurf aus. Bei einem Erfolg endet der Zustand bei ihm. WORT DER MACHT:', '', 'Barde, Druide, Hexenmeister, Magier', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wort-des-r-ckrufs', 'WORT DES RÜCKRUFS', 6, 'Beschwörungszauber', 'Aktion', '1,5 Meter', 'V', NULL, 'Unmittelbar', 0, 0, 'Dieser Zauber teleportiert dich und bis zu fünf bereitwillige Kreaturen im Abstand von bis zu 1,5 Metern von dir sofort zu einer zuvor festgelegten Zuflucht. Du und alle Kreaturen, die mit dir teleportiert werden, erscheint in einem freien Bereich, der dem von dir als Zuflucht bestimmten Ort am nächsten liegt (siehe unten). Wenn du diesen Zauber wirkst, ohne eine Zuflucht festgelegt zu haben, hat er keinen Effekt. Du musst einen Ort, beispielsweise einen Tempel, als Zuflucht benennen, um diesen Zauber dort zu wirken.', '', 'Kleriker', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wort-des-strahlens', 'WORT DES STRAHLENS', 0, 'Hervorrufungzauber', 'Aktion', 'Selbst', 'V, M (ein Sonnenfeuer-Symbol)', NULL, 'Unmittelbar', 0, 0, 'In einer Ausströmung von 1,5 Metern bricht ein sengendes Gleißen aus dir hervor. Jede Kreatur deiner Wahl, die du darin sehen kannst, muss einen Konstitutionsrettungswurf bestehen, oder sie erleidet 1W6 gleißenden Schaden.', 'Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.', 'Kleriker', '{"scaling":{"text":"Der Schaden wird um jeweils 1W6 erhöht, wenn du die 5. (2W6), die 11. (3W6) und die 17. (4W6) Stufe erreichst.","type":"cantrip_scaling","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wunden-heilen', 'WUNDEN HEILEN', 1, 'Bannzauber', 'Aktion', 'Berührung', 'V, G', NULL, 'Unmittelbar', 0, 0, 'Eine Kreatur, die du berührst, erhält Trefferpunkte in Höhe von 2W8 plus deinem Zauberwirken-Attributsmodifikator zurück.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W10 erhöht.', 'Barde, Druide, Kleriker, Paladin, Waldläufer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W10 erhöht.","type":"damage","dice":"1W10"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('wunsch', 'WUNSCH', 9, 'Beschwörungszauber', 'Aktion', 'Selbst', 'V', NULL, 'Unmittelbar', 0, 0, 'Wunsch ist der mächtigste Zauber, den ein Sterblicher wirken kann. Durch einfaches Aussprechen kann die Realität selbst verändert werden. Die grundlegende Verwendung dieses Zaubers besteht darin, einen beliebigen anderen Zauber des höchstens 8. Grades zu kopieren. Auf diese Art musst du weder die Voraussetzungen erfüllen, um den Zauber zu wirken, noch teure Materialkomponenten bezahlen. Der Zauber tritt einfach in Kraft. Alternativ kannst du einen der folgenden Effekte erzeugen: Gegenstand erschaffen: Du erschaffst einen nicht­magischen Gegenstand im Wert von bis zu 25.000 GM. Dieser darf in keiner Abmessung größer als 90 Meter sein. Er erscheint in einem freien Bereich auf dem Boden, den du sehen kannst. Immunität gegen Zauber: Du verleihst bis zu zehn Kreaturen, die du sehen kannst, acht Stunden lang Immunität gegen einen einzelnen Zauber oder magischen Effekt. Realität umformen: Du könntest etwas wünschen, das nicht in den anderen Effekten enthalten ist. Beschreibe dazu dem SL deinen Wunsch so genau wie möglich. Der SL hat einigen Spielraum bei der Entscheidung, was in einem solchen Fall geschieht. Je größer der Wunsch, desto größer auch das Risiko, dass etwas schiefgeht. Der Zauber könnte schlicht scheitern, der gewünschte Effekt nur zum Teil erfüllt werden, oder du selbst könntest je nach Formulierung des Wunsches unvorhergesehene Nebenwirkungen erleiden. Wenn du dir beispielsweise einen Bösewicht tot wünschst, könntest du in eine Zukunft katapultiert werden, in der er nicht mehr lebt - wodurch du praktisch aus dem Spiel entfernt wirst. Wünschst du dir einen legendären magischen Gegenstand oder ein Artefakt, so könntest du zum aktuellen Besitzer des Gegenstands teleportiert werden. Wenn dein Wunsch gewährt wird und Konsequenzen für eine ganze Gemeinschaft, Region oder Welt haben, ziehst du wahrscheinlich mächtige Gegner an. Wenn dein Wunsch Auswirkungen auf einen Gott hätte, könnten dessen Diener sofort eingreifen, um den Wunsch zu verhindern, oder sie könnten dich ermuntern, ihn auf eine bestimmte Art zu wirken. Wenn dein Wunsch das Multiversum gefährden, die Stadt Sigil bedrohen oder die Fürstin der Schmerzen beeinflussen würde, siehst du kurz deren Antlitz vor dem geistigen Auge: Sie schüttelt den Kopf, und dein Wunsch scheitert. Resistenz: Du gewährst bis zu zehn Kreaturen, die du sehen kannst, Resistenz gegen eine Schadensart deiner Wahl. Diese Resistenz ist dauerhaft. Sofortige Gesundheit: Du selbst und bis zu zwanzig Kreaturen, die du sehen kannst, erhaltet all eure Trefferpunkte zurück. Du beendest zudem alle auf euch wirkenden Effekte, wie unter dem Zauber Vollständige Genesung beschrieben. Sofortiges Lernen: Du ersetzt eines deiner Talente durch ein anderes Talent, das für dich in Frage kommt. Du verlierst alle Vorzüge des bisherigen Talents und erhältst die Vorzüge des neuen. Talente, die Voraussetzungen für andere Talente oder Merkmale sind, kannst du nicht ersetzen. Würfelwurf wiederholen: Du machst ein kurz zurückliegendes Ereignis ungeschehen, indem du eine Wiederholung eines beliebigen Würfelwurfs in der letzten Runde erzwingst (einschließlich deines letzten Zugs). Die Realität passt sich an das neue Ergebnis an. Beispiel: Der Zauber Wunsch könnte den gescheiterten Rettungswurf eines Verbündeten oder den kritischen Treffer eines Gegners ungeschehen machen. Du kannst erzwingen, dass der neue Wurf im Vorteil oder Nachteil ist. Zudem hast du die Wahl, ob das ursprüngliche oder das neue Ergebnis gilt. Wirkst du Wunsch, um einen anderen Effekt zu erzielen, als einen Zauber zu kopieren, so belastet dich das sehr. Jedes Mal, wenn du nach dieser Belastung einen Zauber wirkst, erleidest du 1W10 nekrotischen Schaden pro Zaubergrad. Dieser Effekt verschwindet nach einer langen Rast. Der Schaden kann weder verringert noch verhindert werden. Außerdem hast du 2W4 Tage lang einen Stärkewert von 3. In dieser Zeit verringerst du mit jedem Tag, an dem du dich ausruhst oder nur leichten Tätigkeiten nachgehst, die verbleibende Erholungszeit um zwei Tage. Nicht zuletzt besteht ein Risiko von 33 Prozent, dass du den Zauber Wunsch aufgrund dieser Belastung nie wieder wirken kannst. YOLANDES MAJESTÄTISCHE PRÄSENZ Verzauberungszauber 5. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G, M (eine Miniaturtiara) Wirkungsdauer: Konzentration, bis zu 1 Minute Du umgibst dich in einer Ausströmung von drei Metern mit überirdischer Erhabenheit. Wann immer die Ausströmung in den Bereich einer Kreatur gelangt, die du sehen kannst, und wann immer eine Kreatur, die du sehen kannst, die Ausströmung betritt oder ihren Zug dort beendet, kannst du die Kreatur zu einem Weisheitsrettungswurf zwingen. Scheitert der Wurf, so erleidet das Ziel 4W6 psychischen Schaden und ist liegend, und du kannst es bis zu drei Meter weit wegschieben. Bei einem erfolgreichen Rettungswurf erleidet das Ziel nur halb so viel Schaden. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('zeichen-des-j-gers', 'ZEICHEN DES JÄGERS', 1, 'Erkenntniszauber', 'Bonusaktion', '27 Meter', 'V', NULL, 'Konzentration, bis zu 1 Stunde', 1, 0, 'Du markierst Kreatur in Reichweite, die du sehen kannst, auf magische Art als deine Beute. Für die Wirkungsdauer fügst du dem Ziel, wann immer du es mit einem Angriffs­wurf triffst, zusätzlich 1W6 Energieschaden zu. Außerdem bist du bei allen Weisheitswürfen (Überlebenskunst oder Wahrnehmung) im Vorteil, die du ausführst, um das Ziel zu finden. Wenn die Trefferpunkte des Ziels auf 0 sinken, bevor dieser Zauber endet, kannst du eine Bonusaktion verwenden, um das Zeichen auf eine andere Kreatur in Reichweite, die du sehen kannst, zu übertragen.', 'bei einem Zauberplatz des 3. und 4. Grades bis zu acht Stunden und bei einem Zauberplatz ab dem 5. Grad bis zu 24 Stunden.', 'Waldläufer', '{"scaling":{"text":"bei einem Zauberplatz des 3. und 4. Grades bis zu acht Stunden und bei einem Zauberplatz ab dem 5. Grad bis zu 24 Stunden."},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('zeitstopp', 'ZEITSTOPP', 9, 'Verwandlungszauber', 'Aktion', 'Selbst', 'V', NULL, 'Unmittelbar', 0, 0, 'Du stoppst kurzzeitig für alle außer dir selbst den Fluss der Zeit. Für andere Kreaturen vergeht keine Zeit, während du 1W4+1 Züge hintereinander bekommst, in denen du wie gewohnt Aktionen ausführen und dich bewegen kannst. Dieser Zauber endet, wenn eine deiner Aktionen oder ein von dir erschaffener Effekt während dieser Zeitspanne eine Kreatur außer dir selbst oder einen Gegenstand beeinflusst, den jemand anders trägt oder hält. Zudem endet der Zauber, wenn du dich um mehr als 300 Meter von dem Ort entfernst, an dem du ihn gewirkt hast.', '', 'Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('zerbersten', 'ZERBERSTEN', 2, 'Hervorrufungszauber', 'Aktion', '18 Meter', 'V, G, M (ein Glimmersplitter)', NULL, 'Unmittelbar', 0, 0, 'Von einem Punkt deiner Wahl in Reichweite geht ein lautes Geräusch aus. Jede Kreatur in einer Kugel mit einem Radius von drei Metern um den Punkt führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet sie 3W8 Schallschaden, anderenfalls die Hälfte. Konstrukte sind beim Rettungswurf im Nachteil. Nichtmagische Gegenstände im Bereich, die nicht getragen oder gehalten werden, erleiden den gleichen Schaden.', 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W8 erhöht. ZERSTÖRERISCHE WOGE Hervorrufungszauber 5. Grades (Paladin) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V Wirkungsdauer: Unmittelbar In einer Ausströmung von neun Metern geht zerstörerische Energie von dir aus. Jede Kreatur deiner Wahl in der Ausströmung führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 5W6 Schallschaden sowie 5W6 gleißenden oder nekrotischen Schaden (nach deiner Wahl) und wird umgestoßen. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur nur halb so viel Schaden.', 'Barde, Magier, Zauberer', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W8 erhöht. ZERSTÖRERISCHE WOGE Hervorrufungszauber 5. Grades (Paladin) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V Wirkungsdauer: Unmittelbar In einer Ausströmung von neun Metern geht zerstörerische Energie von dir aus. Jede Kreatur deiner Wahl in der Ausströmung führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 5W6 Schallschaden sowie 5W6 gleißenden oder nekrotischen Schaden (nach deiner Wahl) und wird umgestoßen. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur nur halb so viel Schaden.","type":"damage","dice":"1W8"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('zielsicherer-schlag', 'ZIELSICHERER SCHLAG', 0, 'Erkenntniszauber', 'Aktion', 'Selbst', 'G, M (eine Waffe, mit der du Übung hast, im Wert von mindestens 1 KM)', NULL, 'Unmittelbar', 0, 0, 'Von einem Blitz magischer Einsicht geleitet führst du einen Angriff mit der Waffe aus, die du zum Wirken des Zaubers verwendet hast. Der Zauber nutzt dein Attribut zum Zauberwirken für den Angriff und Schadenswürfe statt Stärke oder Geschicklichkeit. Wenn der Angriff Schaden bewirkt, kann dies gleißender Schaden oder die normale Schadensart der Waffe sein (nach deiner Wahl).', 'Unabhängig davon, ob du gleißenden Schaden oder die normale Schadensart der Waffe bewirkst, bewirkt der Angriff zusätzlich gleißenden Schaden, wenn du die 5. (1W6), die 11. (2W6) und die 17. (3W6) Stufe erreichst. 34,2 KAP! Ui ZAUBER', 'Barde, Hexenmeister, Magier, Zauberer', '{"scaling":{"text":"Unabhängig davon, ob du gleißenden Schaden oder die normale Schadensart der Waffe bewirkst, bewirkt der Angriff zusätzlich gleißenden Schaden, wenn du die 5. (1W6), die 11. (2W6) und die 17. (3W6) Stufe erreichst. 34,2 KAP! Ui ZAUBER","type":"cantrip_scaling","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('zone-der-wahrheit', 'ZONE DER WAHRHEIT', 2, 'Verzauberungszauber', 'Aktion', '18 Meter', 'V, G', NULL, '10 Minuten', 0, 0, 'Du erschaffst in einer Kugel mit einem Radius von 4,5 Metern um einen Punkt in Reichweite eine magische Zone, die vor Täuschung schützt. Für die Wirkungsdauer führt jede Kreatur, die den Bereich des Zaubers in einem Zug erstmals betritt oder ihren Zug darin beginnt, einen Charismarettungswurf aus. Scheitert der Wurf, so kann die Kreatur innerhalb des Radius nicht lügen. Du weißt, ob eine Kreatur diesen Rettungswurf bestanden hat. Eine betroffene Kreatur ist sich des Zaubers bewusst und kann Antworten auf Fragen vermeiden, auf die sie sonst mit einer Lüge antworten würde. In diesem Fall kann sie ausweichend antworten, muss aber bei der Wahrheit bleiben. ZORNIGES NIEDERSTRECKEN Nekromantiezauber 1. Grades (Paladin) Zeitaufwand: Bonusaktion, die du sofort ausführst, wenn du eine Kreatur mit einer Nahkampfwaffe oder einem waffenlosen Angriff getroffen hast Reichweite: Selbst Komponenten: V Wirkungsdauer: 1 Minute Das Ziel erleidet zusätzlich 1W6 nekrotischen Schaden durch den Angriff und muss einen Weisheitsrettungswurf bestehen, oder es ist verängstigt, bis der Zauber endet. Das verängstigte Ziel wiederholt den Rettungswurf am Ende jedes seiner Züge. Bei einem Erfolg endet der Zauber.', 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht.', 'Barde, Kleriker, Paladin', '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('zungen', 'ZUNGEN', 3, 'Erkenntniszauber', 'Aktion', 'Berührung', 'V, M (eine Miniatur-Zikkurat)', NULL, '1 Stunde', 0, 0, 'Dieser Zauber gewährt einer von dir berührten Kreatur die Fähigkeit, jede gesprochene Sprache oder Gebärdensprache zu verstehen, die sie hört oder sieht. Wenn das Ziel außerdem durch Sprache oder Gebärden kommuniziert, wird es von jeder Kreatur verstanden, die mindestens eine Sprache versteht und das Ziel hören oder sehen kann. ZUTRITT VERWEHREN Bannzauber 6. Grades (Kleriker) Zeitaufwand: 10 Minuten oder Ritual Reichweite: Berührung Komponenten: V, G, M (Rubinstaub im Wert von mindestens 1.000 GM) Wirkungsdauer: 1 Tag Du erschaffst einen Schutz gegen magische Reisen, der einen Bereich von bis zu 3.700 Quadratmetern mit einer Höhe von bis zu neun Metern über dem Boden abdeckt. Für die Wirkungsdauer können sich Kreaturen weder in den Bereich teleportieren noch Portale wie durch den Zauber Tor verwenden, um in den Bereich zu gelangen. Der Zauber schützt den Bereich gegen Ebenenreisen und verhindert damit, dass Kreaturen aus der Astralebene, der Ätherebene, der Feenwildnis, dem Schattensaum oder mit dem Zauber Ebenenwechsel in den Bereich gelangen. Außerdem fügt der Zauber Kreaturentypen, die du beim Wirken bestimmst, Schaden zu. Wähle mindestens einen der folgenden Kreaturentypen aus: Aberrationen, celestische Wesen, Feenwesen, Unholde und Untote. Wenn eine Kreatur eines ausgewählten Typs den Bereich des Zaubers in einem Zug erstmals betritt oder ihren Zug darin beendet, erleidet sie 5W10 gleißenden oder nekrotischen Schaden (nach deiner Wahl beim Wirken des Zaubers). Du kannst ein Kennwort festlegen, wenn du den Zauber wirkst. Spricht eine Kreatur das Kennwort aus, wenn sie den Bereich betritt, so erleidet sie durch den Zauber keinen Schaden. Der Bereich des Zaubers kann sich nicht mit dem Bereich eines anderen Zaubers Zutritt verwehren überschneiden. Wenn du Zutritt verwehren 30 Tage lang am selben Ort wirkst, bleibt der Effekt bestehen, bis er gebannt wird. Die Materialkomponenten werden beim letzten Wirken verbraucht.', '', 'Barde, Hexenmeister, Kleriker, Magier, Zauberer', '{"source_page":239}');
INSERT INTO core_spells (id, name, level, school, casting_time, range, components, material_components, duration, concentration, ritual, description, higher_levels, classes, data)
VALUES ('zwang', 'ZWANG', 4, 'Verzauberungszauber', 'Aktion', '9 Meter', 'V, G', NULL, 'Konzentration, bis zu 1 Minute', 1, 0, 'Jede Kreatur deiner Wahl in Reichweite, die du sehen kannst, muss einen Weisheitsrettungswurf bestehen, oder sie ist bezaubert, bis der Zauber endet. Für die Wirkungsdauer kannst du eine Bonusaktion verwenden, um eine Richtung zu bestimmen, die horizontal von dir verläuft. Jedes bezauberte Ziel muss in seinem nächsten Zug so viel seiner Bewegungsrate wie möglich verwenden, um sich in diese Richtung zu bewegen. Dabei nimmt es die sicherste Route. Nachdem das Ziel sich auf diese Art bewegt hat, wiederholt es seinen Rettungswurf. Bei einem Erfolg endet der Zauber bei ihm. ANHANG A DAS MULTIVERSUM Dieser Anhang bietet eine Übersicht über das Multiversum, die übergreifende Wirklichkeit der zahllosen Welten und Abenteuer in D&D. Die Reiche werden im Spielleiterhandbuch eingehend behandelt, ebenso andere Ebenen wie das Ferne Reich, die Negative Ebene und die Positive Ebene. MATERIELLE REICHE Die philosophischen und elementaren Kräfte der Realität konvergieren und erschaffen folgende Existenzebenen: Materielle Ebene: Die meisten D&D-Abenteuer finden auf der materiellen Ebene statt - in Kampagnenwelten wie Dragonlance, Eberron, den Vergessenen Reichen und Greyhawk. Dort ist auch Raum für die Welten, die deine Gruppe möglicherweise erschafft. Die verschiedenen Welten dieser Ebene werden von Magie und physikalischen Gesetzen geformt. Feenwildnis: Dieses blühende idealisierte Reich brodelt vor Lebendigkeit und Emotion, besonders in den Domänen der Freude. Schattensaum: Dieses finstere Reich ist von tristen Einöden, Spukruinen und den berüchtigten Domänen des Schreckens geprägt. TRANSITIVE EBENEN Die Transitiven Ebenen dienen als Pfade zwischen den Ebenen: Astralebene: Diese endlose silbrige Weite verbindet die materielle Ebene mit den Äußeren Ebenen. Ganze Zivilisationen haben sich schon in der schimmernden Unermesslichkeit erhoben. Andere navigieren mit Spelljammer-Schiffen durch den magischen Kosmos. Ätherebene: Dieses geisterhafte Reich verbindet die materielle Ebene mit den Inneren Ebenen. Der Bereich, in dem die Ätherebene sich mit anderen Ebenen schneidet, wird Äthergrenze genannt. Dort können Kreaturen in die Ebenen spähen, die jenseits davon liegen. INNERE EBENEN Von hier stammen die elementaren Kräfte und die Bausteine der Realität: Elementarebene der Erde: Dieses Höhlenlabyrinth windet sich durch Stein und ungeahnte mineralische Schätze. Elementarebene des Feuers: In diesem ewigen Inferno brodelt das Leben auf vulkanischen Inseln. Elementarebene der Luft: Die Winde treiben Wolken und schwebende Inseln über diesen grenzenlosen Himmel. Elementarebene des Wassers: In diesem bodenlosen Ozean wimmelt es von geheimnisvollen Meereskreaturen. 344 ANHANG A i DAS MULTIVERSUM Elementares Chaos: Hier, am Rande der Inneren Ebenen, stoßen die elementaren Kräfte unberechenbar zusammen. Para-Elementarebenen: In diesen Grenzlanden zwischen den Elementarebenen finden sich die elementaren Kräfte zu den Reichen der Asche, des Eises, des Magmas und des Schlicks zusammen. ÄUSSERE EBENEN Die Äußeren Ebenen sind die Heimat der Unsterblichen. Dies ist das Jenseits, in dem Geister ihr Nachleben verbringen und Ideen Gestalt annehmen. Die Äußeren Ebenen setzen sich aus folgenden Orten zusammen: Sigil: Sigil, die Stadt der Türen, ist eher eine Metropole als eine Ebene. Sie befindet sich in der Mitte der Äußeren Ebenen. Von hier aus führen Portale ins ganze Multiversum, und die Herrschaft liegt in den Händen der rätselhaften Fürstin der Schmerzen. Außenlande: Die Einflüsse der anderen Äußeren Ebenen fließen in den Außenlanden zusammen, von wo aus Portale auf diese Ebenen führen. Im Zentrum der Außenlande erhebt sich die sogenannte Spitze, eine Säule, auf der Sigil schwebt. Sonstige Äußere Ebenen: Diese Ebenen sind in der Tabelle „Äußere Ebenen" aufgeführt. Manche sind paradiesisch, andere albtraumhaft, wieder andere einfach bizarr. Jede wird von Kräften dominiert, die mindestens eine der in Kapitel 2 beschriebenen Gesinnungen verkörpern. ÄUSSERE EBENEN Äußere Ebene Abyss Acheron Arborea Arcadia Außenlande Bestienlande Bytopia Carceri Celestia Elysium Gehenna Hades Limbus Mechanus Neun Höllen Pandämonium Ysga rd Gesinnung Chaotisch böse Rechtschaffen böse, rechtschaffen neutral Chaotisch gut Rechtschaffen gut, rechtschaffen neutral Neutral Chaotisch gut, neutral gut Rechtschaffen gut, neutral gut Chaotisch böse, neutral böse Rechtschaffen gut Neutral gut Rechtschaffen böse, neutral böse Neutral böse Chaotisch neutral Rechtschaffen neutral Rechtschaffen böse Chaotisch böse, chaotisch neutral Chaotisch gut, chaotisch neutral Ums ARCADIA YSGARD - Silvana GLORIUM MECHANUS EXCELSIOR . FORTITUDE. AUTOMAMA RIGUS EE lie•BRUSTKORR FACKEL (MT HOF FNUNCSLOO PANDAMONIUM ACHERON INNERE EBENEN EBENE DES N›, DER ABYSS ÄUSSERE EBENEN MATERIELLE EBENE SCHATTENSAUM EBENE DeZRS. -90111, *A. iii(4 Jet - - ELEMENTARES CHÄVS''Ir, ÄTHEREBENE ASTRALEBENE DIE BESTIENLANDE }j''. ARBOREA ELYSIUM BYTOPIA HANDELSTOR EtNROERI ANHANG B KREATUREN-WERTEKÄSTEN In diesem Anhang sind die Wertekästen der Kreaturen aufgeführt, die in diesem Buch erwähnt werden - vor allem in den Kapiteln „Charakterklassen", „Ausrüstung" und „Zauber". Im Regelglossar erfährst du, wie die Wertekästen verwendet werden. Das Monsterhandbuch enthält noch weitere Kreaturen. Die Wertekästen sind im Folgenden in alphabetischer Reihenfolge aufgeführt. Wenn der Spielleiter diese Wertekästen verwendet, kann er Details darin ändern. BRAUNBÄR Großes Tier, gesinnungslos', '', 'Barde', '{"summon":{"ac":null,"hp":null},"summon_text":"RK 11\tInitiative +1 (11)\nTP 22 (3W10+6)\nBewegungsrate 12 m, Klettern 9 m\nMOD RW\tMOD PW\tMOD RW\nSTÄ 17 +3 +3\tGES 12 +1 +1\tKON 15 +2 +2\nINT 2 -4 -4 WEI 13 .+1 +1\tCHA\nFertigkeiten Wahrnehmung +3\nSinne Dunkelsicht 18 m, Passive Wahrnehmung 13\nSprachen -\nHG 1 (EP 200, ÜB +2)\nAKTIONEN\nMehrfachangrijj''? Der Bär führt einen Bi