UPDATE core_spells SET 
    level = 4,
    school = 'Beschwörungs',
    casting_time = 'Aktion',
    range = '27 Meter',
    components = 'V, G, M (ein eingelegtes Tentakel und',
    material_components = NULL,
    duration = 'Konzentration, bis zu 1 Stunde',
    concentration = 1,
    ritual = 0,
    description = 'Du rufst eine geisterhafte Aberration herbei. Sie erscheint in einem freien Bereich in Reichweite, den du sehen kannst, und verwendet den Wertekasten einer Geisterhaften Aberration. Wenn du den Zauber wirkst, wähle zwischen Betrachter, Gedankenschinder oder Slaad aus. Die Kreatur sieht aus wie eine Aberration dieser Art. Einige Details im Wertekasten werden dadurch bestimmt. Die Kreatur verschwindet, wenn ihre Trefferpunkte auf 0 sinken oder der Zauber endet. Die Kreatur ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt sie deinen Initiativewert und ist direkt nach dir am Zug. Sie gehorcht deinen mündlichen Befehlen (keine Aktion deinerseits erforderlich). Befiehlst du ihr nichts, so führt sie die Ausweichaktion aus und nutzt ihre Bewegung, um Gefahren zu vermeiden.',
    higher_levels = 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.',
    classes = 'Hexenmeister, Magier',
    data = '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":"11 + Zaubergrad","hp":null},"summon_raw":"MERKMALE GEISTERHAFTE ABERRATION Mittelgroße Aberration, neutral RK 11 + Zaubergrad TP 40 + 10 für jeden Zaubergrad ab dem 5. Bewegungsrate 9 m, Fliegen 9 m (Schweben, nur Betrachter) MOD RW MOD RW MOD RW STÄ +3 +3 GES 10 +0 +0 KON 15 +2 +2 INT +3 +3 WEI 10 +0 +0 CHA 6 -2 -2 Immunitäten Psychisch Sinne Dunkelsicht 18 m, Passive Wahrnehmung 10 Sprachen Tiefensprache, versteht die Sprachen, die du kennst HG — (EP 0, ÜB entspricht deinem Übungsbonus) !I","source_page":239}'
WHERE id = 'aberration-herbeirufen';
UPDATE core_spells SET 
    level = 5,
    school = 'Illusions',
    casting_time = 'Aktion',
    range = 'Selbst',
    components = 'S',
    material_components = NULL,
    duration = 'Konzentration, bis zu 1 Stunde',
    concentration = 1,
    ritual = 0,
    description = 'Du wirst unsichtbar, und gleichzeitig erscheint in deinem Bereich ein illusionärer Doppelgänger von dir. Dieser bleibt für die Wirkungsdauer bestehen, doch deine Unsichtbarkeit endet, sobald du einen Angriffswurf ausführst, Schaden bewirkst oder einen Zauber wirkst. Als magische Aktion kannst du deinen illusionären Doppelgänger bis zu deiner doppelten Bewegungsrate bewegen und ihn gestikulieren, sprechen und auf eine beliebige Art handeln lassen. Er ist immateriell und unverwundbar. Du kannst durch seine Augen sehen und durch seine Ohren hören, als befändest du dich am selben Ort.',
    higher_levels = '',
    classes = 'Barde, Hexenmeister, Magier',
    data = '{"source_page":239}'
WHERE id = 'ablenkung';
UPDATE core_spells SET 
    level = 1,
    school = 'Bann',
    casting_time = '1 Minute oder Ritual',
    range = '9 Meter',
    components = 'V, G, M (Glocke und Silberdraht)',
    material_components = 'Glocke und Silberdraht',
    duration = '8 Stunden',
    concentration = 0,
    ritual = 1,
    description = 'Flüsternde Aura (nur Gedankenschinder): Zu Beginn jedes seiner Züge strahlt der Geist psionische Energie aus, sofern er nicht kampfunfähig ist. Weisheitsrettungswurf: Der SG entspricht deinem Zauberrettungswurf-SG, jede Kreatur (außer dir) im Abstand von bis zu 1,5 Metern vom Geist. Misserfolg: 2W6 psychischer Schaden. Regeneration (nur Slaad): Der Geist erhält zu Beginn seines Zugs 5 Trefferpunkte zurück, wenn er über mindestens 1 Trefferpunkt verfügt.',
    higher_levels = '',
    classes = 'Magier, Waldläufer',
    data = '{"summon_raw":"AKTIONEN Mehrfachangriff Der Geist führt eine Anzahl von Angriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet). Klaue (nur Slaad): Nahkampfangriffswurf? Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W10+3 + Zaubergrad Hiebschaden, und das Ziel kann bis zum Beginn des nächsten Zugs des Geists keine Trefferpunkte zurückerhalten. Psychischer Hieb (nur Gedankenschinder): Nahkampfangriffswurf? Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W8+3 + Zaubergrad psychischer Schaden. Augenstrahl (nur Betrachter): Fernkampfangriffswurf.• Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 45 Meter. Treffer: 1W8+3 + Zaubergrad psychischer Schaden. KAPITEL 7 ZAUBER Du erschaffst einen Alarm gegen Eindringen. Wähle eine Tür, ein Fenster oder einen Bereich in Reichweite aus, der nicht größer als ein Würfel mit sechs Metern Kantenlänge sein darf. Für die Wirkungsdauer warnt dich ein Alarm, wann immer eine Kreatur den geschützten Bereich berührt oder in ihn eindringt. Wenn du den Zauber wirkst, kannst du Kreaturen bestimmen, die den Alarm nicht auslösen. Außerdem wählst du aus, ob der Alarm akustisch oder mental erfolgt: Akustischer Alarm: Der Alarm erzeugt im Abstand von bis zu 18 Metern vom geschützten Bereich zehn Sekunden lang das Geräusch einer Handglocke. Mentaler Alarm: Du wirst von einem mentalen Ping gewarnt, wenn du dich im Abstand von bis zu 1,6 Kilometern vom geschützten Bereich befindest. Dieser Ping weckt dich, wenn du schläfst.","source_page":239}'
WHERE id = 'alarm';
UPDATE core_spells SET 
    level = 5,
    school = 'Nekromantie',
    casting_time = 'Aktion Reichweite: Berührung Komponenten: V, G',
    range = '',
    components = '',
    material_components = NULL,
    duration = '7 Tage',
    concentration = 0,
    ritual = 0,
    description = 'Deine Berührung verursacht eine magische Ansteckung. Das Ziel muss einen Konstitutionsrettungswurf bestehen, oder es erleidet 11W8 nekrotischen Schaden und ist vergiftet • Wähle außerdem ein Attribut aus, wenn du den Zauber wirkst. Das vergiftete Ziel ist bei Rettungswürfen mit dem ausgewählten Attribut im Nachteil. Das Ziel muss den Rettungswurf am Ende jedes seiner Züge wiederholen, bis es insgesamt drei Erfolge oder drei Misserfolge hatte. Bei drei Erfolgen endet der Zauber bei dem Ziel. Bei drei Misserfolgen wirkt der Zauber sieben Tage lang auf das Ziel. Wann immer das vergiftete Ziel einen Effekt erhält, der den Zustand Vergiftet beenden würde, muss es einen Konstitutionsrettungswurf bestehen, oder der Zustand Vergiftet endet nicht. ANTIMAGISCHES FELD Bannzauber 8. Grades (Kleriker, Magier) Zeitaufwand: Aktion Reichweite: Selbst Komponenten: V, G, M (Eisenspäne) Wirkungsdauer: Konzentration, bis zu 1 Stunde Du wirst von einer antimagischen Aura mit einer Aus­strömung von drei Metern umgeben. Im Bereich der Aura kann niemand Zauber wirken, magische Aktionen ausführen oder andere magische Effekte erzeugen, und Ziele innerhalb der Aura können nicht von Zaubern, magischen Aktionen und Effekten betroffen werden. Magische Eigenschaften von magischen Gegenständen funktionieren innerhalb der Aura nicht und haben auf nichts darin eine Wirkung. Wirkungsbereiche von Zaubern oder anderer Magie können sich nicht in die Aura erstrecken. Niemand kann sich in die Aura oder aus ihr heraus teleportieren, und auch Ebenenreisen sind in der Aura blockiert. Portale schließen sich, solange sie sich innerhalb der Aura befinden. Bereits wirksame Zauber werden im Bereich unterdrückt, sofern sie nicht von Artefakten oder Gottheiten gewirkt wurden. Solange ein Effekt unterdrückt wird, wirkt er nicht. Die Zeit, in der er unterdrückt ist, wird dennoch von seiner Wirkungsdauer abgezogen. Magie bannen hat innerhalb der Aura keinen Effekt. Auren, die durch mehrere Antimagisches Feld-Zauber erzeugt wurden, heben einander nicht auf.',
    higher_levels = '',
    classes = 'Druide, Kleriker',
    data = '{"source_page":239}'
WHERE id = 'ansteckung';
UPDATE core_spells SET 
    level = 8,
    school = 'Verungszauber',
    casting_time = '1 Stunde',
    range = '18 Meter',
    components = 'V, G, M (eine Mischung aus Essig und Honig)',
    material_components = 'eine Mischung aus Essig und Honig',
    duration = '10 Tage',
    concentration = 0,
    ritual = 0,
    description = 'Wähle beim Wirken des Zaubers aus, ob er Antipathie oder Sympathie erzeugen soll, und ziele auf eine Kreatur oder einen Gegenstand von höchstens riesiger Größe. Bestimme dann einen Kreaturentyp (beispielsweise rote Drachen, Goblins oder Vampire). Kreaturen der ausgewählten Art führen einen Weisheitsrettungswurf aus, wenn sie sich dem Ziel auf höchstens 36 Meter nähern. Deine Entscheidung (Antipathie oder Sympathie) bestimmt, was mit einer Kreatur geschieht, wenn der Rettungswurf scheitert: Antipathie: Die Kreatur ist verängstigt. Die verängstigte Kreatur muss in ihren Zügen ihre Bewegung nutzen, um sich so weit wie möglich vom Ziel zu entfernen. Dabei nimmt sie die sicherste Route. Sympathie: Die Kreatur ist bezaubert. Die bezauberte Kreatur muss in ihren Zügen ihre Bewegung nutzen, um sich dem Ziel so weit wie möglich zu nähern. Dabei nimmt sie die sicherste Route. Befindet sich die Kreatur im Abstand von bis zu 1,5 Metern vom Ziel, so kann sie sich nicht freiwillig entfernen. Wenn das Ziel der bezauberten Kreatur Schaden zufügt, kann diese einen Weisheitsrettungswurf ausführen, um den Effekt wie unten beschrieben zu beenden. Den Effekt beenden: Wenn die verängstigte oder bezauberte Kreatur ihren Zug weiter als 36 Meter vom Ziel entfernt beendet, führt sie einen Weisheitsrettungswurf aus. Bei einem erfolgreichen Rettungswurf endet der Effekt des Ziels auf die Kreatur. Nach einem erfolgreichen Rettungswurf ist eine Kreatur eine Minute lang gegen den Effekt immun. Danach kann er erneut auf sie gewirkt werden.',
    higher_levels = '',
    classes = 'Barde, Druide, Magier',
    data = '{"source_page":239}'
WHERE id = 'antipathie-sympathie';
UPDATE core_spells SET 
    level = 7,
    school = 'Illusions',
    casting_time = '10 Minuten Reichweite: Sicht',
    range = '',
    components = 'V, G',
    material_components = NULL,
    duration = '10 Tage',
    concentration = 0,
    ritual = 0,
    description = 'Du lässt Gelände in einem Bereich von 2,6 Quadrat­kilometern wie eine andere Art von Gelände aussehen, riechen und sich sogar anfühlen. Weite Felder oder eine Straße können somit wie ein Sumpf, ein Hügel, eine Erdspalte oder anderes unwegsames oder unpassierbares KAPITEL 7 ZAUBER Gelände erscheinen. Ein Tümpel kann wie eine grüne Wiese wirken, ein Steilhang wie ein sanftes Gefälle, ein Graben voller Felsbrocken wie eine breite Straße. Du kannst auch das Aussehen von Gebäuden ändern oder sie dort platzieren, wo es keine gibt. Der Zauber kann keine Kreaturen verändern, verbergen oder hinzufügen. Die Illusion umfasst hörbare, visuelle, berührbare und olfaktorische Elemente, sodass sie sicheres Gelände in schwieriges verwandeln (und umgekehrt) oder anderweitig die Bewegung durch das Gebiet beeinträchtigen kann. Jeder Teil des illusionären Geländes wie ein Fels oder ein Zweig, der aus dem Bereich des Zaubers entfernt wird, verschwindet sofort. Kreaturen mit Wahrer Blick können die Illusion durchschauen und sehen das echte Gelände. Alle anderen Elemente der Illusion bleiben jedoch bestehen. Selbst wenn sich eine Kreatur der Illusion bewusst ist, kann sie physisch mit ihr interagieren.',
    higher_levels = '',
    classes = 'Barde, Druide, Magier',
    data = '{"source_page":239}'
WHERE id = 'arkane-spiegelung';
