UPDATE core_spells SET 
    level = 4,
    school = 'Hervorrufungs',
    casting_time = '1 Stunde',
    range = 'Berührung',
    components = 'V, G, M (ein Diamant im Wert von',
    material_components = NULL,
    duration = 'Unmittelbar',
    concentration = 0,
    ritual = 0,
    description = 'Du erweckst mit einer Berührung eine tote Kreatur zum Leben, die nicht länger als ein Jahrhundert tot war, nicht an Altersschwäche gestorben ist und nicht untot war, als sie starb. KAPITEL 7 ZAUBER 243 Die Kreatur kehrt mit all ihren Trefferpunkten ins Leben zurück. Dieser Zauber neutralisiert zudem sämtliche Gifte, die zum Zeitpunkt des Todes auf das Ziel gewirkt haben. Dieser Zauber schließt alle tödlichen Wunden und stellt sämtliche abgetrennten Gliedmaßen wieder her. Von den Toten aufzuerstehen ist eine Tortur. Das Ziel erleidet einen Malus von -4 auf W20-Prüfungen. Nach jeder langen Rast wird der Malus um 1 verringert, bis er 0 beträgt. Wirkst du diesen Zauber, um eine Kreatur zum Leben zu erwecken, die seit mindestens 365 Tagen tot ist, so belastet dich das stark. Du kannst erst nach einer langen Rast wieder Zauber wirken und bist bis dahin bei W20-Prüfungen im Nachteil.',
    higher_levels = '',
    classes = 'Magier, Zauberer',
    data = '{"source_page":239}'
WHERE id = 'atzkugel';
UPDATE core_spells SET 
    level = 6,
    school = 'Verwandlungs',
    casting_time = 'Aktion',
    range = '18 Meter',
    components = 'V, G, M (ein Magnetstein und Staub)',
    material_components = 'ein Magnetstein und Staub',
    duration = 'Unmittelbar',
    concentration = 0,
    ritual = 0,
    description = 'Du verschießt einen dünnen grünen Strahl auf ein Ziel in Reichweite, das du sehen kannst. Das Ziel kann eine Kreatur, ein nichtmagischer Gegenstand oder eine Struktur aus magischer Energie sein, beispielsweise die Wand des Zaubers Energiewand. Eine Kreatur, die zum Ziel dieses Zaubers wird, führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet das Ziel 10W6+40 Energieschaden. Wenn seine Trefferpunkte durch diesen Schaden auf 0 sinken, zerfällt es mitsamt jeder nichtmagischen Ausrüstung, die es trägt oder hält, zu grauem Staub. Das Ziel kann nur durch die Zauber Wahre Auferstehung und Wunsch wiederbelebt werden. Nichtmagische Gegenstände von höchstens großer Größe sowie Strukturen aus magischer Energie werden von diesem Zauber automatisch aufgelöst. Wenn das Ziel von mindestens riesiger Größe ist, löst dieser Zauber einen Würfel mit drei Metern Kantenlänge davon auf.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 3W6 erhöht.',
    classes = 'Magier, Zauberer',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 6. wird der Schaden um 3W6 erhöht.","type":"damage","dice":"3W6"},"source_page":239}'
WHERE id = 'aufl-sung';
UPDATE core_spells SET 
    level = 3,
    school = 'Bann',
    casting_time = 'Aktion',
    range = 'Selbst',
    components = 'V',
    material_components = NULL,
    duration = 'Konzentration, bis zu 1 Minute',
    concentration = 1,
    ritual = 0,
    description = 'Für die Wirkungsdauer geht eine Aura mit einer Aus­strömung neun Metern von dir aus. Wenn du die Aura erzeugst, sowie zu Beginn jedes deiner Züge, solange sie besteht, kannst du 2W6 Trefferpunkte bei einer Kreatur innerhalb der Aura wiederherstellen. KAPITEL 7 I ZAUBER 24.4',
    higher_levels = '',
