UPDATE core_spells SET 
    level = 5,
    school = 'Illusions',
    casting_time = 'Aktion Reichweite: 9 Meter Komponenten: V, G Wirkungsdauer: 8 Stunden',
    range = '',
    components = '',
    material_components = NULL,
    duration = '',
    concentration = 0,
    ritual = 0,
    description = 'Zeitaufwand: 10 Minuten Reichweite: Selbst Komponenten: V, G, M (ein Fokus im Wert von mindestens 1.000 GM, beispielsweise eine Kristallkugel, ein Spiegel oder ein mit Wasser gefülltes Becken) Wirkungsdauer: Konzentration, bis zu 10 Minuten Du kannst eine Kreatur deiner Wahl, die sich auf derselben Existenzebene wie du befindet, hören und sehen. Das Ziel führt einen Weisheitsrettungswurf aus, der dadurch modifiziert wird, wie gut du das Ziel kennst und ob du einen Gegenstand hast, der dich mit ihm verbindet (siehe Tabellen unten). Das Ziel weiß nicht, wogegen es den Rettungswurf ausführt. Es fühlt sich nur vage unwohl. RettungswurfDein Wissen über das Ziel ist ... Modifikator aus zweiter Hand (du hast von dem Ziel gehört) +5 aus erster Hand (du bist dem Ziel begegnet) +0 umfassend (du kennst das Ziel gut) -5 RettungswurfDu hast etwas vom Ziel, und zwar ... Modifikator ein Abbild oder Porträt -2 ein Kleidungsstück oder anderen Besitz -4 ein Körperteil, Nagelstück oder eine Haarlocke -10 Bei einem erfolgreichen Rettungswurf ist das Ziel nicht betroffen, und du kannst diesen Zauber erst nach 24 Stunden erneut auf das Ziel wirken. Scheitert der Wurf, so erschafft der Zauber einen unsichtbaren, immateriellen Sensor im Abstand von bis zu drei Metern vom Ziel. Du kannst durch den Sensor hören und sehen, als ob du dich in seinem Bereich befändest. Der Sensor bewegt sich mit dem Ziel und bleibt für die Wirkungsdauer im Abstand von bis zu drei Metern von ihm. KAPITEL 7 1 ZAUBER 245 Wer den Sensor sehen kann, sieht ihn als leuchtende, etwa faustgroße Kugel. Anstelle einer Kreatur kannst du auf einen Ort zielen, den du schon einmal gesehen hast. In diesem Fall erscheint der Sensor an diesem Ort und bewegt sich nicht.',
    higher_levels = '',
    classes = 'Barde, Magier, Zauberer',
    data = '{"source_page":239}'
WHERE id = '-usserlichkeiten';
UPDATE core_spells SET 
    level = 5,
    school = 'Beschwörungs',
    casting_time = 'Aktion',
    range = 'Selbst',
    components = 'V, G',
    material_components = NULL,
    duration = 'Konzentration, bis zu 1 Minute',
    concentration = 1,
    ritual = 0,
    description = 'Du erhältst die Fähigkeit, einen Baum zu betreten und aus einem anderen Baum derselben Art im Abstand von bis zu 150 Metern wieder herauszusteigen. Beide Bäume müssen lebendig und mindestens so groß wie du sein. Es kostet 1,5 Meter Bewegung, den Baum zu betreten. Du erfährst sofort die Position aller anderen Bäume derselben Art im Abstand von bis zu 150 Metern. Als Teil der Bewegung, um den Baum zu betreten, kannst du entweder in einen dieser Bäume wechseln und oder aus dem Baum heraustreten, in dem du dich befindest. Du erscheinst in einem Bereich deiner Wahl im Abstand von bis zu 1,5 Metern vom Zielbaum, was dich weitere 1,5 Meter Bewegung kostet. Hast du keine Bewegungsrate übrig, so erscheinst du im Abstand von bis zu 1,5 Metern vom Baum, den du betreten hast. Du kannst diese Transportmöglichkeit nur einmal in jedem deiner Züge anwenden. Du musst jeden Zug außerhalb eines Baums beenden.',
    higher_levels = '',
    classes = 'Druide, Waldläufer',
    data = '{"source_page":239}'
WHERE id = 'baumwandeln';
