UPDATE core_spells SET 
    level = 5,
    school = 'Hervorrufungs',
    casting_time = 'Aktion',
    range = '36 Meter',
    components = 'V, G, M (eine Eierschale und ein Handschuh)',
    material_components = 'eine Eierschale und ein Handschuh',
    duration = 'Konzentration, bis zu 1 Minute',
    concentration = 1,
    ritual = 0,
    description = 'Du erschaffst eine große Hand aus schimmernder magischer Energie in einem freien Bereich in Reichweite, den du sehen kannst. Die Hand bleibt für die Wirkungsdauer bestehen. Sie bewegt sich auf deinen Befehl und imitiert die Bewegungen deiner eigenen Hand. Die Hand ist ein Gegenstand mit einer RK von 20. Ihre Trefferpunkte entsprechen deinem Trefferpunktemaximum. Wenn ihre Trefferpunkte auf 0 sinken, endet der Zauber. Die Hand besetzt ihren Bereich nicht. Nach dem Wirken des Zaubers sowie als Bonusaktion in deinen folgenden Zügen kannst du die Hand bis zu 18 Meter weit bewegen und einen der folgenden Effekte bewirken: Blockierende Hand: Die Hand gewährt dir Teildeckung gegen Angriffe und andere Effekte, die von ihrem Bereich ausgehen oder ihn durchqueren. Außerdem zählt ihr Bereich für deine Gegner als schwieriges Gelände. DER MAGIER ßIGBY NUTZT SEINEN LIEBLINGSZAUBER BIGBYS HAND, UM UNGESTÖRT FORSCHEN ZU KÖNNEN. Geballte Faust: Die Hand schlägt ein Ziel im Abstand von bis zu 1,5 Metern von ihr. Führe einen Nahkampf-Zauberangriff aus. Bei einem Treffer erleidet das Ziel 5W8 Energieschaden. Greifende Hand: Die Hand versucht, eine Kreatur von höchstens riesiger Größe im Abstand von bis zu 1,5 Metern von ihr zu packen. Das Ziel muss einen Geschicklichkeitsrettungswurf bestehen, oder es wird gepackt (der Rettungswurf-SG entspricht deinem Zauberrettungswurf-SG). Solange die Hand das Ziel gepackt hat, kannst du eine Bonusaktion verwenden, damit sie es zerquetscht. Dabei fügt sie ihm Wuchtschaden in Höhe von 4W6 plus deinem Zauberwirken-Attributsmodifikator zu. Kraftvolle Hand: Die Hand versucht, eine Kreatur von höchstens riesiger Größe im Abstand von bis zu 1,5 Metern von ihr zu stoßen. Das Ziel muss einen Stärkerettungswurf bestehen, oder die Hand schiebt es bis zu 1,5 Meter plus 1,5-mal dein Zauberwirken-Attributs-modifikator (in Metern) weit in eine Richtung deiner Wahl. Die Hand bewegt sich mit dem Ziel und bleibt im Abstand von bis zu 1,5 Metern von ihm.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 5. wird der Schaden der geballten Faust um 2W8 und der Schaden der greifenden Hand um 2W6 erhöht.',
    classes = 'Magier, Zauberer',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 5. wird der Schaden der geballten Faust um 2W8 und der Schaden der greifenden Hand um 2W6 erhöht.","type":"damage","dice":"2W8"},"source_page":239}'
WHERE id = 'bigbys-hand';
UPDATE core_spells SET 
