UPDATE core_spells SET 
    level = 3,
    school = 'Beschwörungs',
    casting_time = 'Aktion',
    range = '36 Meter',
    components = 'V, G',
    material_components = NULL,
    duration = 'Konzentration, bis zu 10 Minuten',
    concentration = 1,
    ritual = 0,
    description = 'An einem Punkt in Reichweite über dir, den du sehen kannst, erscheint eine Sturmwolke. Sie hat die Form eines drei Meter hohen Zylinders mit einem Radius von 18 Metern. Wenn du den Zauber wirkst, wähle einen Punkt unter der Wolke aus, den du sehen kannst. Ein Blitz schlägt von der Wolke in diesen Punkt ein. Jede Kreatur im Abstand von bis zu 1,5 Metern um diesem Punkt führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet sie 3W10 Blitzschaden, anderenfalls die Hälfte. Für die Wirkungsdauer kannst du eine magische Aktion verwenden, um einen weiteren Blitz dieser Art einschlagen zu lassen. Dabei kannst du denselben Punkt oder einen anderen als Ziel auswählen. Wenn du dich beim Wirken des Zaubers draußen aufhältst und ein Sturm herrscht, verleiht der Zauber dir Kontrolle über diesen Sturm, anstatt einen neuen zu erschaffen. Unter diesen Umständen wird der Schaden des Zaubers um 1W10 erhöht.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W10 erhöht.',
    classes = 'Druide',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden um 1W10 erhöht.","type":"damage","dice":"1W10"},"source_page":239}'
WHERE id = 'blitze-herbeirufen';
