UPDATE core_spells SET 
    level = 1,
    school = 'Hervorrufungs',
    casting_time = 'Aktion',
    range = '27 Meter',
    components = 'V, G',
    material_components = NULL,
    duration = 'Konzentration, bis zu 10 Minuten',
    concentration = 1,
    ritual = 0,
    description = 'Du beschwörst einen Geist aus den Oberen Ebenen, der sich als Lichtsäule in einem zwölf Meter hohen Zylinder mit einem Radius von drei Metern um einen Punkt in Reichweite manifestiert. Wähle bei jeder Kreatur im Zylinder, die du sehen kannst, eine der folgenden Arten von Licht aus, von der sie beschienen wird: Heilendes Licht: Das Ziel erhält Trefferpunkte in Höhe von 4W12 plus deinem Zauberwirken-Attributsmodifikator zurück. Sengendes Licht: Das Ziel führt einen Geschicklichkeits-rettungswurf aus. Scheitert der Wurf, so erleidet es 6W12 gleißenden Schaden, anderenfalls die Hälfte. Für die Wirkungsdauer ist der Zylinder von strahlendem Licht erfüllt. Wenn du dich in deinem Zug bewegst, kannst du auch den Zylinder bis zu neun Meter weit bewegen. Wann immer der Zylinder sich in den Bereich einer Kreatur bewegt, die du sehen kannst, und wann immer eine Kreatur, die du sehen kannst, den Zylinder betritt oder ihren Zug dort beendet, kannst du sie in einer der Lichtarten baden. Eine Kreatur kann von diesem Zauber nur einmal pro Zug betroffen sein.',
    higher_levels = 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. KAPITEL 7 1 ZAUBER CELESTISCHER GEIST Großes celestisches Wesen, neutral RK 11 + Zaubergrad + 2 (nur Verteidiger) TP 40 + 10 für jeden Zaubergrad ab dem 6. Bewegungsrate 9 m, Fliegen 12 m MOD 5W MOD RW MOD 5W STÄ 16 +3 +3 GES 14 +2 +2 KON 16 +3 +3 INT 10 +0 +0 WEI 14 +2 +2 CHA 16 +3 +3 Resistenzen Gleißend Immunitäten Bezaubert, Verängstigt Sinne Dunkelsicht 18 m, Passive Wahrnehmung 12 Sprachen Celestisch, versteht die Sprachen, die du kennst HG — (EP 0, ÜB entspricht deinem Übungsbonus)',
    classes = 'Magier, Zauberer',
    data = '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten. KAPITEL 7 1 ZAUBER CELESTISCHER GEIST Großes celestisches Wesen, neutral RK 11 + Zaubergrad + 2 (nur Verteidiger) TP 40 + 10 für jeden Zaubergrad ab dem 6. Bewegungsrate 9 m, Fliegen 12 m MOD 5W MOD RW MOD 5W STÄ 16 +3 +3 GES 14 +2 +2 KON 16 +3 +3 INT 10 +0 +0 WEI 14 +2 +2 CHA 16 +3 +3 Resistenzen Gleißend Immunitäten Bezaubert, Verängstigt Sinne Dunkelsicht 18 m, Passive Wahrnehmung 12 Sprachen Celestisch, versteht die Sprachen, die du kennst HG — (EP 0, ÜB entspricht deinem Übungsbonus)","type":"spell_level_ref","target":"creature_statblock"},"summon":{"ac":"11 + Zaubergrad","hp":null},"summon_raw":"AKTIONEN Mehrfachange Der Geist führt eine Anzahl von Angriffen aus, die der Hälfte des Zaubergrads entspricht (abgerundet). Gleißender Streitkolben (nur Verteidiger): Nahkampfangriffswwf Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 1,5 Meter. Treffer: 1W10+3 + Zaubergrad gleißender Schaden. Der Geist kann sich selbst oder eine andere Kreatur, die von ihm gesehen werden kann, im Abstand von bis zu drei Metern vom Ziel auswählen. Die ausgewählte Kreatur erhält 1W10 temporäre Trefferpunkte. Gleißender Bogen (nur Rächer): Fernkampfangriffswurf? Bonus in Höhe deines Angriffszauber-Modifikators, Reichweite 180 Meter. Treffer: 2W6+2 + Zaubergrad gleißender Schaden. Heilende Berührung (1-mal täglich): Der Geist berührt eine andere Kreatur. Das Ziel erhält Trefferpunkte zurück, die 2W8 + dem Zaubergrad entsprechen. KAPITEL 7 I ZAUBER --;","source_page":239}'
