UPDATE core_spells SET 
    level = 4,
    school = 'Hervorrufungs',
    casting_time = 'Aktion',
    range = '90 Meter',
    components = 'V, G, M (ein Fausthandschuh)',
    material_components = 'ein Fausthandschuh',
    duration = 'Unmittelbar',
    concentration = 0,
    ritual = 0,
    description = 'In einem zwölf Meter hohen Zylinder mit einem Radius von sechs Metern um einen Punkt in Reichweite fällt Hagel. Jede Kreatur im Zylinder führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 2W10 Wuchtschaden und 4W6 Kälteschaden, anderenfalls die Hälfte. Die Hagelkörner machen den Boden im Zylinder bis zum Ende deines nächsten Zugs zu schwierigem Gelände.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 4. wird der Wuchtschaden um 1W10 erhöht.',
    classes = 'Druide, Magier, Zauberer',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der Wuchtschaden um 1W10 erhöht.","type":"damage","dice":"1W10"},"source_page":239}'
