UPDATE core_spells SET 
    level = 3,
    school = 'Illusions',
    casting_time = '1 Minute oder Ritual Reichweite: 9 Meter Komponenten: V, G',
    range = '',
    components = '',
    material_components = NULL,
    duration = '1 Stunde',
    concentration = 0,
    ritual = 1,
    description = '270 KAPITEL 7 1 ZAUBER GEIS rERRO55, In einem freien Bereich deiner Wahl in Reichweite, den du sehen kannst, erscheint eine große, quasi reale, pferdeartige Kreatur. Du bestimmst das Aussehen der Kreatur. Sie ist stets mit einem Sattel und Zaumzeug ausgerüstet. Durch diesen Zauber erschaffene Ausrüstung löst sich in Rauch auf, wenn sie weiter als drei Meter vom Ross entfernt wird. Für die Wirkungsdauer kann eine Kreatur deiner Wahl (auch du selbst) auf dem Ross reiten. Die Kreatur verwendet den Wertekasten eines Reitpferds (siehe Anhang B), verfügt jedoch über eine Bewegungsrate von 30 Metern und kann in einer Stunde 20,8 Kilometer zurücklegen. Wenn der Zauber endet, verblasst das Ross allmählich, und der Reiter hat eine Minute zum Absteigen Zeit. Wenn das Ross Schaden erleidet, endet der Zauber vorzeitig.',
    higher_levels = '',
    classes = 'Magier',
    data = '{"source_page":239}'
WHERE id = 'geisterross';
UPDATE core_spells SET 
    level = 2,
    school = 'Verwandlungs',
    casting_time = 'Aktion',
    range = 'Selbst',
    components = 'V, G',
    material_components = NULL,
    duration = 'Konzentration, bis zu 1 Stunde',
    concentration = 1,
    ritual = 0,
    description = 'Du veränderst deine physische Gestalt. Entscheide dich für eine der folgenden Optionen. Die Effekte bleiben für die Wirkungsdauer bestehen. Währenddessen kannst du eine magische Aktion ausführen, um die ausgewählte Option durch eine andere zu ersetzen. Aquatische Anpassung: Dir wachsen Kiemen und Schwimmhäute. Du kannst unter Wasser atmen und erhältst eine Schwimmbewegungsrate in Höhe deiner Bewegungsrate. Aussehen ändern: Du veränderst dein Aussehen. Du bestimmst, wie du aussiehst: Größe, Gewicht, Gesichtszüge, Stimme, Haarlänge, Haarfarbe und andere besondere Merkmale. Du kannst dich auch als Mitglied einer anderen Spezies ausgeben. Deine Spielwerte bleiben jedoch unverändert. Du kannst nicht als Kreatur einer anderen Größenkategorie erscheinen. Deine grundlegende Gestalt bleibt ebenfalls gleich, beispielsweise kannst du dich mit diesem Zauber nicht von einem Zweibeiner in einen Vierbeiner verwandeln. Für die Wirkungsdauer kannst du eine magische Aktion ausführen, um dein Erscheinungsbild erneut auf diese Art zu ändern. Natürliche Waffen: Dir wachsen Klauen (Hieb), Fangzähne (Stich), Hörner (Stich) oder Hufe (Wucht). Verwendest du deinen waffenlosen Angriff, um mit diesen natürlichen Waffen Schaden zu bewirken, so bewirkt dieser Angriff 1W6 Schaden der in Klammern angegebenen Art statt des normalen Schadens deines waffenlosen Angriffs, und du verwendest für die Angriffsund Schadenswürfe deinen Zauberwirken-Attributsmodifikator statt Stärke.',
    higher_levels = '',
    classes = 'Magier, Zauberer',
    data = '{"source_page":239}'
WHERE id = 'gestalt-ver-ndern';
UPDATE core_spells SET 
    level = 9,
    school = 'Verwandlungs',
    casting_time = 'Aktion',
    range = 'Selbst',
    components = 'V, G, M (ein Jadediadem im Wert von',
    material_components = NULL,
    duration = 'Konzentration, bis zu 1 Stunde',
    concentration = 1,
    ritual = 0,
    description = 'Du nimmst die Gestalt einer anderen Kreatur an, bis der Zauber endet oder du eine magische Aktion ausführst, um eine andere mögliche Gestalt anzunehmen. Bei der neuen Gestalt muss es sich um eine Kreatur handeln, deren Herausforderungsgrad höchstens deiner Stufe oder deinem Herausforderungsgrad entspricht. Du musst diese Art von Kreatur mindestens einmal gesehen haben, und es darf sich weder um ein Konstrukt noch um einen Untoten handeln. Wenn du dich verwandelst, erhältst du eine Anzahl von temporären Trefferpunkten, die der Anzahl von Trefferpunkten der ersten neuen Gestalt entspricht. Wenn nach dem Gestaltwandel von diesen temporären Trefferpunkten noch welche übrig sind, verschwinden sie. Deine Spielwerte werden durch den Wertekasten der ausgewählten Gestalt ersetzt. Du behältst jedoch Folgendes bei: Kreaturentyp, Gesinnung, Persönlichkeit, Werte für Intelligenz, Weisheit und Charisma, Trefferpunkte, Trefferpunktewürfel, Übungen sowie die Fähigkeit zu kommunizieren. Auch das Merkmal Zauberwirken behältst du bei, sofern du darüber verfügst. Bei der Verwandlung entscheidest du, ob deine Ausrüstung zu Boden fällt oder ihre Form und Größe ändert, sodass sie zu deiner neuen Gestalt passt, solange du diese beibehältst. KAPITEL 7 1 ZAUBER 271 ^ ^ GIFT UND KRANKHEIT ENTDECKEN Erkenntniszauber I. Grades (Druide, Kleriker, Paladin, Waldläufer)',
    higher_levels = 'Der Schaden wird um jeweils 1W12 erhöht, wenn du die 5. (2W12), die 11. (3W12) und die 17. (4W12) Stufe erreichst. RIVALISIERENDE MAGIER NUTZEN GESTALTWANDEL, UM SICH ALS BETRACHTER UND ES EHIR MAGISCH 1U DUELLIEREN. Zeitaufwand: Aktion oder Ritual Reichweite: Selbst Komponenten: V, G, M (ein Eibenblatt) Wirkungsdauer: Konzentration, bis zu 10 Minuten Für die Wirkungsdauer nimmst du die Position von Giften, giftigen Kreaturen und magischen Ansteckungen im Abstand von bis zu neun Metern von dir wahr. Du erkennst auch die jeweilige Art des Gifts, der giftigen Kreatur oder der Ansteckung. Der Zauber wird von 30 Zentimetern Stein, Erde oder Holz sowie von 2,5 Zentimetern Metall oder einer dünnen Bleischicht blockiert.',
    classes = 'Druide, Magier',
    data = '{"scaling":{"text":"Der Schaden wird um jeweils 1W12 erhöht, wenn du die 5. (2W12), die 11. (3W12) und die 17. (4W12) Stufe erreichst. RIVALISIERENDE MAGIER NUTZEN GESTALTWANDEL, UM SICH ALS BETRACHTER UND ES EHIR MAGISCH 1U DUELLIEREN. Zeitaufwand: Aktion oder Ritual Reichweite: Selbst Komponenten: V, G, M (ein Eibenblatt) Wirkungsdauer: Konzentration, bis zu 10 Minuten Für die Wirkungsdauer nimmst du die Position von Giften, giftigen Kreaturen und magischen Ansteckungen im Abstand von bis zu neun Metern von dir wahr. Du erkennst auch die jeweilige Art des Gifts, der giftigen Kreatur oder der Ansteckung. Der Zauber wird von 30 Zentimetern Stein, Erde oder Holz sowie von 2,5 Zentimetern Metall oder einer dünnen Bleischicht blockiert.","type":"damage","dice":"1W12"},"source_page":239}'
WHERE id = 'gestaltwandel';
UPDATE core_spells SET 
    level = 0,
    school = 'Nekromantie',
    casting_time = '1 Stunde',
    range = 'Berührung',
    components = 'V, G, M (Diamantpulver im Wert von',
    material_components = NULL,
    duration = '',
    concentration = 0,
    ritual = 0,
    description = 'Auslöser bestimmen: Wenn du den Zauber wirkst, bestimmst du einen Auslöser für die Glyphe. Glyphen auf Oberflächen werden meist dadurch ausgelöst, dass jemand sie berührt oder betritt, einen Gegenstand bewegt, der die Glyphe verdeckt, oder einen Mindestabstand zur Glyphe unterschreitet. Glyphen in Gegenständen werden meist dadurch ausgelöst, dass jemand den Gegenstand öffnet oder die Glyphe sieht. Sobald die Glyphe ausgelöst wurde, endet der Zauber. Du kannst den Auslöser verfeinern, sodass die Glyphe nur von bestimmten Kreaturentypen ausgelöst wird (beispielsweise könnte die Glyphe nur auf Aberrationen wirken). Du kannst auch Bedingungen festlegen, unter denen die Glyphe nicht ausgelöst wird, beispielsweise wenn ein bestimmtes Kennwort ausgesprochen wird. Explosive Rune: Wird die Glyphe ausgelöst, so explodiert ihre magische Energie in einer Kugel mit einem Radius von sechs Metern um sie. Jede Kreatur in diesem Bereich führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 5W8 Blitz-, Feuer-, Kälte-, Säureoder Schallschaden (nach deiner Wahl beim Schreiben der Glyphe), anderenfalls die Hälfte. Zauberglyphe: Beim Erschaffen der Glyphe kannst du einen vorbereiteten Zauber des höchstens 3. Grades wirken und in der Glyphe speichern. Der Zauber muss eine einzelne Kreatur oder einen Bereich zum Ziel haben. Wenn er auf diese Weise gewirkt wird, hat der Zauber keinen unmittelbaren Effekt. Er wirkt erst, wenn die Glyphe ausgelöst wird. Wenn der Zauber ein Ziel braucht, wird die Kreatur ausgewählt, welche die Glyphe auslöst. Wirkt der Zauber auf einen Bereich, so ist dieser auf die Kreatur zentriert. Wenn der Zauber feindlich gesinnte Kreaturen beschwört oder schädliche Gegenstände oder Fallen erschafft, erscheinen sie so nahe wie möglich beim Eindringling und greifen ihn an. Erfordert der Zauber Konzentration, so hält diese bis zum Ende der vollen Wirkungsdauer an.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 3. wird der Schaden einer explosiven Rune um 1W8 erhöht. In einer Zauberglyphe kannst du Zauber bis zum Grad des Zauberplatzes speichern, den du zum Wirken der Glyphe des Schutzes verwendet hast.',
    classes = 'Druide, Hexenmeister, Magier,',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 3. wird der Schaden einer explosiven Rune um 1W8 erhöht. In einer Zauberglyphe kannst du Zauber bis zum Grad des Zauberplatzes speichern, den du zum Wirken der Glyphe des Schutzes verwendet hast.","type":"cantrip_scaling","dice":"1W8"},"source_page":239}'
