UPDATE core_spells SET 
    level = 1,
    school = 'Verwandlungs',
    casting_time = 'Bonusaktion Reichweite: Selbst',
    range = '',
    components = 'V, G',
    material_components = NULL,
    duration = '1 Minute',
    concentration = 0,
    ritual = 0,
    description = 'Für die Wirkungsdauer bewirkst du mit Angriffen, die du mit Waffen ausführst, bei jedem Treffer zusätzlich 1W4 gleißenden Schaden. GÖTTLICHES NIEDERSTRECKEN Hervorrufungszauber 1. Grades (Paladin) Zeitaufwand: Bonusaktion, die du sofort ausführst, wenn du ein Ziel mit einer Nahkampfwaffe oder einem waffenlosen Angriff getroffen hast Reichweite: Selbst Komponenten: V Wirkungsdauer: Unmittelbar Das Ziel erleidet zusätzlich 2W8 gleißenden Schaden durch den Angriff. Der zusätzliche Schaden wird um 1W8 erhöht, wenn das Ziel ein Unhold oder ein Untoter ist.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W8 erhöht.',
    classes = 'Paladin',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. wird der Schaden um 1W8 erhöht.","type":"damage","dice":"1W8"},"source_page":239}'
