UPDATE core_spells SET 
    level = 3,
    school = 'Verwandlungs',
    casting_time = 'Aktion oder Ritual Reichweite: Berührung',
    range = '',
    components = 'V, G',
    material_components = NULL,
    duration = '8 Stunden',
    concentration = 0,
    ritual = 1,
    description = 'Du betrittst einen steinernen Gegenstand oder eine Oberfläche von ausreichender Größe, um deinen Körper aufzunehmen, und verschmilzt für die Wirkungsdauer mitsamt deiner Ausrüstung mit dem Stein. Dazu musst du den Stein berühren. Deine Anwesenheit ist nicht erkennbar und kann nur auf magische Weise wahrgenommen werden. Während du mit dem Stein verschmolzen bist, kannst du nicht sehen, was außerhalb vor sich geht. Du bist zudem bei Weisheitswürfen (Wahrnehmung), um Geräusche von außen zu hören, im Nachteil. Du bemerkst, wie die Zeit vergeht, und kannst Zauber auf dich selbst wirken. Zudem 292 KAPITEL 7 ZAUBER kannst du 1,5 Meter deiner Bewegungsrate verwenden, um den Stein dort zu verlassen, wo du ihn betreten hast. Dadurch endet der Zauber. Ansonsten kannst du dich nicht bewegen. Geringer physischer Schaden am Stein verletzt dich nicht. Wenn er teilweise zerstört oder seine Form verändert wird, sodass du nicht mehr hineinpasst, wirst du ausgestoßen und erleidest 6W6 Energieschaden. Wird der Stein vollständig zerstört oder in eine andere Substanz verwandelt, so wirst du ausgestoßen und erleidest 50 Energieschaden. Wenn du ausgestoßen wirst, gelangst du in den freien Bereich, der deinem Eintrittspunkt am nächsten liegt, und bist liegend.',
    higher_levels = '',
    classes = 'Barde, Druide, Waldläufer',
    data = '{"source_page":239}'
WHERE id = 'mit-pflanzen-sprechen';
