UPDATE core_spells SET 
    level = 3,
    school = 'Nekromantie',
    casting_time = 'Aktion',
    range = '3 Meter',
    components = 'V, G, M (brennender Weihrauch)',
    material_components = 'brennender Weihrauch',
    duration = '10 Minuten',
    concentration = 0,
    ritual = 0,
    description = 'Du erfüllst einem Leichnam deiner Wahl in Reichweite mit einem Hauch von Leben, damit er Fragen beantworten kann, die du ihm stellst. Der Leichnam muss einen Mund besitzen. Wenn die Kreatur untot war, als sie starb, scheitert dieser Zauber. Er scheitert auch dann, wenn die Leiche innerhalb der letzten zehn Tage bereits Ziel dieses Zaubers war. Für die Wirkungsdauer kannst du der Leiche bis zu fünf Fragen stellen. Die Leiche besitzt nur das Wissen, das sie zu Lebzeiten hatte, und spricht nur die ihr zu Lebzeiten bekannten Sprachen. Die Antworten sind normalerweise kurz, kryptisch oder monoton. Der Leichnam muss dir nicht wahrheitsgemäß antworten, wenn du ihm feindselig gesinnt bist oder er dich als Feind erkennt. Dieser Zauber bringt nicht die Seele der Kreatur in ihren Körper zurück, nur ihren belebenden Geist. Daher kann die Leiche keine neuen Informationen erhalten, weiß nichts, was nach ihrem Tod geschehen ist, und kann nicht über zukünftige Ereignisse mutmaßen.',
    higher_levels = '',
    classes = 'Barde, Kleriker, Magier',
    data = '{"source_page":239}'
WHERE id = 'mit-toten-sprechen';
UPDATE core_spells SET 
    level = 2,
    school = 'Hervorrufungs',
    casting_time = 'Aktion',
    range = '36 Meter',
    components = 'V, G, M (Blatt einer Mondsamenpflanze)',
    material_components = 'Blatt einer Mondsamenpflanze',
    duration = 'Konzentration, bis zu 1 Minute',
    concentration = 1,
    ritual = 0,
    description = 'In einem zwölf Meter hohen Zylinder mit einem Radius von bis zu 1,5 Metern um einen Punkt in Reichweite scheint ein silbriger Strahl fahlen Lichts herab. Für die Wirkungsdauer ist der Zylinder von dämmrigem Licht erfüllt, und du kannst in deinen folgenden Zügen eine magische Aktion ausführen, um den Zylinder bis zu 18 Meter weit zu bewegen. Wenn der Zylinder erscheint, führt jede Kreatur darin einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet eine Kreatur 2W10 gleißenden Schaden. Wenn ihre Gestalt gewandelt ist (beispielsweise infolge des Zaubers Verwandlung), nimmt sie außerdem ihre wahre Gestalt an und kann sich erst wieder verwandeln, wenn sie den Zylinder verlässt. Bei einem erfolgreichen Rettungswurf erleidet die Kreatur nur halb so viel Schaden. Eine Kreatur führt diesen Rettungswurf auch dann aus, wenn der Bereich des Zaubers sich in ihren Bereich bewegt, wenn die Kreatur den Bereich des Zaubers betritt oder ihren Zug darin beendet. Eine Kreatur kann diesen Rettungswurf nur einmal pro Zug ausführen.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 2. wird der Schaden um 1W10 erhöht.',
    classes = 'Druide',
