UPDATE core_spells SET 
    level = 8,
    school = 'Verungszauber',
    casting_time = 'Aktion',
    range = '18 Meter',
    components = 'V, G',
    material_components = NULL,
    duration = 'Konzentration, bis zu 1 Stunde',
    concentration = 1,
    ritual = 0,
    description = 'Eine Kreatur deiner Wahl in Reichweite, die du sehen kannst, muss einen Weisheitsrettungswurf bestehen, oder sie ist für die Wirkungsdauer bezaubert. Wenn du oder deine Verbündeten gegen das Ziel kämpfen, ist es beim Rettungswurf im Vorteil. Wann immer das Ziel Schaden erleidet, wiederholt es den Rettungswurf. Bei einem Erfolg endet der Zauber bei ihm. Du verfügst über eine telepathische Verbindung mit dem bezauberten Ziel, solange ihr euch auf derselben Existenzebene befindet. Diese Verbindung kannst du in deinem Zug nutzen, um dem Ziel Befehle zu erteilen (keine Aktion erforderlich), beispielsweise „Greife diese Kreatur an", „Laufe dorthin" oder „Hole diesen Gegenstand". Das Ziel gehorcht in seinem Zug, so gut es kann. Wenn es den Befehl ausgeführt hat und keine weiteren Anweisungen von dir erhält, verhält es sich nach seinen Wünschen und versucht vor allem, sich zu schützen. KAPITEL 71 ZAUBER 293 DER MAGIER MORDENKAINEN BEGRÜSST SEINE GÄSTE IN SEINEM MAGISCHEN ANWESEN: MORDENKA INENS HERRLICHEM HERRENHAUS. Du kannst dem Ziel befehlen, eine Reaktion auszuführen, musst dazu allerdings selbst eine Reaktion ausführen.',
    higher_levels = 'Deine Konzentration kann länger andauern: bei einem Zauberplatz des 9. Grades bis zu acht Stunden.',
    classes = 'Barde, Hexenmeister, Magier,',
    data = '{"scaling":{"text":"Deine Konzentration kann länger andauern: bei einem Zauberplatz des 9. Grades bis zu acht Stunden."},"source_page":239}'
WHERE id = 'monster-beherrschen';
UPDATE core_spells SET 
    level = 4,
    school = 'Verungszauber',
    casting_time = 'Aktion Reichweite: 9 Meter Komponenten: V, G Wirkungsdauer: 1 Stunde',
    range = '',
    components = '',
    material_components = NULL,
    duration = '',
    concentration = 0,
    ritual = 0,
    description = 'Zauberer) Zeitaufwand: Aktion Reichweite: 27 Meter Komponenten: V, G, M (ein gerades Stück Eisen) Wirkungsdauer: Konzentration, bis zu 1 Minute Wähle eine Kreatur in Reichweite aus, die du sehen kannst. Das Ziel muss einen Weisheitsrettungswurf bestehen, oder es ist für die Wirkungsdauer gelähmt. Das Ziel wiederholt den Rettungswurf am Ende jedes seiner Züge. Bei einem Erfolg endet der Zauber.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 4. kannst du die Kantenlänge des Würfels um 30 Meter vergrößern. MORDENKAINENS SCHWERT Hervorrufungszauber 7. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: 27 Meter Komponenten: V, G, M (ein Miniaturschwert im Wert von mindestens 250 GM) Wirkungsdauer: Konzentration, bis zu 1 Minute Du erschaffst ein geisterhaftes Schwert, das in Reichweite schwebt. Die Kugel bleibt für die Wirkungsdauer bestehen. Wenn das Schwert erscheint, kannst du einen Nahkampf-Zauberangriff gegen ein Ziel im Abstand von bis zu 1,5 Metern vom Schwert ausführen. Bei einem Treffer erleidet das Ziel Energieschaden in Höhe von 4W12 plus deinem Zauberwirken-Attributsmodifikator. In deinen folgenden Zügen kannst du das Schwert mit einer Bonusaktion bis zu neun Meter weit an einen Ort bewegen, den du sehen kannst, und den Angriff gegen dasselbe oder ein anderes Ziel wiederholen. MORDENKAINENS TREUER HUND Beschwörungszauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G, M (eine Silberpfeife) Wirkungsdauer: 8 Stunden Du beschwörst in einem freien Bereich in Reichweite, den du sehen kannst, einen geisterhaften Wachhund. Dieser bleibt für die Wirkungsdauer bestehen, oder bis ihr euch weiter als 90 Meter voneinander entfernt. Niemand außer dir kann den Hund sehen, und er ist immateriell und unverwundbar. Wenn sich ihm eine Kreatur von mindestens kleiner Größe auf höchstens neun Meter nähert, ohne zuvor das Kennwort zu sprechen, das du beim Wirken des Zaubers festgelegt hast, bellt der Hund laut. Der Hund hat Wahrer Blick mit einer Reichweite von neun Metern. Zu Beginn jedes deiner Züge versucht der Hund, einen Gegner im Abstand von bis zu 1,5 Metern von sich zu beißen. Der Gegner muss einen Geschicklichkeitsrettungswurf bestehen, oder er erleidet 4W8 Energieschaden. In deinen folgenden Zügen kannst du eine magische Aktion ausführen, um den Hund bis zu neun Meter weit zu bewegen. 04 • KAPITEL 7 I ZAUBER ./ 295 -= • NAHRUNG UND WASSER ERSCHAFFEN Beschwörungszauber 3. Grades (Kleriker, Paladin) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du erschaffst 22,5 Kilogramm Nahrung und 120 Liter Wasser auf dem Boden oder in Behältern in Reichweite ­nützlich, um den Gefahren von Unterernährung und Dehydrierung vorzubeugen. Die Nahrung sieht aus wie Essen deiner Wahl und ist schlicht, aber nahrhaft. Das Wasser ist sauber. Die Nahrung verdirbt innerhalb von 24 Stunden, wenn sie nicht gegessen wird. NAHRUNG UND WASSER REINIGEN Verwandlungszauber 1. Grades (Druide, Kleriker, Paladin) Zeitaufwand: Aktion oder Ritual Reichweite: 3 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du entfernst Gifte und Verderbnis aus nichtmagischen Speisen und Getränken in einer Kugel mit einem Radius von 1,5 Metern um einen Punkt deiner Wahl in Reichweite.',
    classes = 'Barde, Druide, Hexenmeister,',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. kannst du die Kantenlänge des Würfels um 30 Meter vergrößern. MORDENKAINENS SCHWERT Hervorrufungszauber 7. Grades (Barde, Magier) Zeitaufwand: Aktion Reichweite: 27 Meter Komponenten: V, G, M (ein Miniaturschwert im Wert von mindestens 250 GM) Wirkungsdauer: Konzentration, bis zu 1 Minute Du erschaffst ein geisterhaftes Schwert, das in Reichweite schwebt. Die Kugel bleibt für die Wirkungsdauer bestehen. Wenn das Schwert erscheint, kannst du einen Nahkampf-Zauberangriff gegen ein Ziel im Abstand von bis zu 1,5 Metern vom Schwert ausführen. Bei einem Treffer erleidet das Ziel Energieschaden in Höhe von 4W12 plus deinem Zauberwirken-Attributsmodifikator. In deinen folgenden Zügen kannst du das Schwert mit einer Bonusaktion bis zu neun Meter weit an einen Ort bewegen, den du sehen kannst, und den Angriff gegen dasselbe oder ein anderes Ziel wiederholen. MORDENKAINENS TREUER HUND Beschwörungszauber 4. Grades (Magier) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G, M (eine Silberpfeife) Wirkungsdauer: 8 Stunden Du beschwörst in einem freien Bereich in Reichweite, den du sehen kannst, einen geisterhaften Wachhund. Dieser bleibt für die Wirkungsdauer bestehen, oder bis ihr euch weiter als 90 Meter voneinander entfernt. Niemand außer dir kann den Hund sehen, und er ist immateriell und unverwundbar. Wenn sich ihm eine Kreatur von mindestens kleiner Größe auf höchstens neun Meter nähert, ohne zuvor das Kennwort zu sprechen, das du beim Wirken des Zaubers festgelegt hast, bellt der Hund laut. Der Hund hat Wahrer Blick mit einer Reichweite von neun Metern. Zu Beginn jedes deiner Züge versucht der Hund, einen Gegner im Abstand von bis zu 1,5 Metern von sich zu beißen. Der Gegner muss einen Geschicklichkeitsrettungswurf bestehen, oder er erleidet 4W8 Energieschaden. In deinen folgenden Zügen kannst du eine magische Aktion ausführen, um den Hund bis zu neun Meter weit zu bewegen. 04 • KAPITEL 7 I ZAUBER ./ 295 -= • NAHRUNG UND WASSER ERSCHAFFEN Beschwörungszauber 3. Grades (Kleriker, Paladin) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du erschaffst 22,5 Kilogramm Nahrung und 120 Liter Wasser auf dem Boden oder in Behältern in Reichweite ­nützlich, um den Gefahren von Unterernährung und Dehydrierung vorzubeugen. Die Nahrung sieht aus wie Essen deiner Wahl und ist schlicht, aber nahrhaft. Das Wasser ist sauber. Die Nahrung verdirbt innerhalb von 24 Stunden, wenn sie nicht gegessen wird. NAHRUNG UND WASSER REINIGEN Verwandlungszauber 1. Grades (Druide, Kleriker, Paladin) Zeitaufwand: Aktion oder Ritual Reichweite: 3 Meter Komponenten: V, G Wirkungsdauer: Unmittelbar Du entfernst Gifte und Verderbnis aus nichtmagischen Speisen und Getränken in einer Kugel mit einem Radius von 1,5 Metern um einen Punkt deiner Wahl in Reichweite.","type":"damage","dice":"4W12"},"source_page":239}'
WHERE id = 'monster-bezaubern';
