UPDATE core_spells SET 
    level = 2,
    school = 'Verwandlungs',
    casting_time = 'Bonusaktion',
    range = 'Berührung',
    components = 'V, G, M (eine Handvoll Rinde)',
    material_components = 'eine Handvoll Rinde',
    duration = '1 Stunde',
    concentration = 0,
    ritual = 0,
    description = 'Du berührst eine bereitwillige Kreatur. Für die Wirkungsdauer nimmt die Haut des Ziels ein rindenartiges Erscheinungsbild an, und das Ziel erhält eine Rüstungsklasse von 17, sofern seine RK niedriger ist. Das Reittier sieht aus wie ein reittaugliches Tier deiner Wahl — beispielsweise ein Pferd, ein Kamel, ein Schreckenswolf oder ein Elch. Wann immer du diesen Zauber wirkst, wählst du den Kreaturentyp des Reittiers aus: celestisches Wesen, Feenwesen oder Unhold. Der Typ beeinflusst bestimmte Merkmale im Wertekasten. Kampf: Das Reittier ist mit dir und deinen Verbündeten verbündet. Im Kampf nutzt es deinen Initiativewert und fungiert als kontrolliertes Reittier, während du es reitest (wie in den Regeln zu berittenem Kampf definiert). Wenn du kampfunfähig bist, ist das Reittier unmittelbar nach dir am Zug, agiert unabhängig und fokussiert sich darauf, dich zu beschützen. Verschwinden des Reittiers: Das Reittier verschwindet, wenn seine Trefferpunkte auf 0 sinken oder wenn du stirbst. Wenn es verschwindet, hinterlässt es alles, was es getragen oder gehalten hat. Wenn du diesen Zauber erneut wirkst, entscheidest du, ob du erneut das verschwundene Reittier oder ein anderes beschwörst.',
    higher_levels = 'Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.',
    classes = 'Druide, Waldläufer',
    data = '{"scaling":{"text":"Verwende den Zauberplatzgrad als Zaubergrad im Wertekasten.","type":"spell_level_ref","target":"creature_statblock"},"source_page":239}'
WHERE id = 'rindenhaut';
