UPDATE core_spells SET 
    level = 2,
    school = 'Verwandlungs',
    casting_time = 'Aktion',
    range = '18 Meter',
    components = 'V, G, M (eine Metallfeder)',
    material_components = 'eine Metallfeder',
    duration = 'Konzentration, bis zu 10 Minuten',
    concentration = 1,
    ritual = 0,
    description = 'Ein Ziel (eine Kreatur oder ein loser Gegenstand) deiner Wahl in Reichweite, das du sehen kannst, steigt senkrecht sechs Meter empor und schwebt für die Wirkungsdauer in der Luft. Der Zauber kann Gegenstände mit einem Maximalgewicht von 250 Kilogramm anheben. Besteht eine nicht bereitwillige Kreatur einen Konstitutionsrettungswurf, so ist sie nicht betroffen. Das Ziel kann sich nur bewegen, indem es sich von festen Gegenständen oder Oberflächen wie Wänden oder Decken in Reichweite abstößt oder sich daran entlanghangelt. Dabei gilt die Kletterbewegungsrate. Während deines Zugs kannst du das Ziel um bis zu sechs Meter anheben oder absenken. Wenn du selbst das Ziel bist, kannst du dich im Rahmen deiner Bewegung anheben oder absenken. Anderenfalls kannst du das Ziel mit einer magischen Aktion bewegen. Dabei musst du die Reichweite des Zaubers einhalten. Wenn der Zauber endet und das Ziel noch schwebt, sinkt es sanft zu Boden. Der Raum fasst bis zu acht Kreaturen von höchstens mittelgroßer Größe. Angriffe, Zauber und andere Effekte können den extradimensionalen Raum weder von innen noch von außen durchdringen. Kreaturen im Raum können jedoch durch das Portal nach draußen blicken. Alles, was im Raum enthalten ist, fällt heraus, wenn der Zauber endet.',
    higher_levels = '',
    classes = 'Magier, Zauberer',
    data = '{"source_page":239}'
WHERE id = 'schweben';
