UPDATE core_spells SET 
    level = 6,
    school = 'Hervorrufungs',
    casting_time = 'Aktion',
    range = 'Selbst',
    components = 'V, G, M (eine Lupe)',
    material_components = 'eine Lupe',
    duration = 'Konzentration, bis zu 1 Minute',
    concentration = 1,
    ritual = 0,
    description = 'Du verschießt einen Sonnenstrahl in einer 1,5 Meter breiten und 18 Meter langen Linie. Jede Kreatur in der Linie führt einen Konstitutionsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur 6W8 gleißenden Schaden und ist bis zum Beginn deines nächsten Zugs blind. Bei einem erfolgreichen Rettungswurf erleidet das Ziel nur halb so viel Schaden. Bis der Zauber endet, kannst du eine magische Aktion ausführen, um eine neue gleißende Linie zu erschaffen. Für die Wirkungsdauer erstrahlt ein heller Funke über dir. Er spendet in einem Radius von neun Metern helles Licht und in einem Radius von weiteren neun Metern dämmriges Licht. Beim Licht handelt es sich um Sonnenlicht. SPÄTZÜNDENDER FEUERBALL Hervorrufungszauber 7. Grades (Magier, Zauberer) Zeitaufwand: Aktion Reichweite: 45 Meter Komponenten: V, G, M (eine Kugel aus Fledermaus-Guano und Schwefel) Wirkungsdauer: Konzentration, bis zu 1 Minute KAPITEL 7 I ZAUBER 3OS Du verschießt einen gelben Lichtstrahl, der sich an einem Punkt deiner Wahl in Reichweite zu einer leuchtenden Perle verdichtet und für die Wirkungsdauer dort verweilt. Wenn der Zauber endet, explodiert die Perle, und jede Kreatur in einer Kugel mit einem Radius von sechs Metern um diesen Punkt führt einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so erleidet die Kreatur Feuerschaden in Höhe des gesamten angesammelten Schadens, anderenfalls die Hälfte. Der Grundschaden des Zaubers ist 12W6, und der Schaden wird um 1W6 erhöht, wann immer während der Wirkungsdauer einer deiner Züge endet. Wenn eine Kreatur während der Wirkungsdauer die leuchtende Perle berührt, führt die Kreatur einen Geschicklichkeitsrettungswurf aus. Scheitert der Wurf, so endet der Zauber, und die Perle explodiert. Bei einem erfolgreichen Rettungswurf kann die Kreatur die Perle bis zu zwölf Meter weit werfen. Gelangt die geworfene Perle in den Bereich einer Kreatur oder kollidiert sie mit einem festen Gegenstand, so endet der Zauber, und die Perle explodiert. Wenn die Perle explodiert, beginnen brennbare Gegenstände im Explosionsbereich, die nicht getragen oder gehalten werden, zu brennen.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 7. wird der Grundschaden um 1W6 erhöht.',
    classes = 'Druide, Kleriker, Magier,',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 7. wird der Grundschaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}'
WHERE id = 'sonnenstrahl';
