UPDATE core_spells SET 
    level = 5,
    school = 'Verwandlungs',
    casting_time = 'Aktion',
    range = '9 Meter',
    components = 'V, G, M (eine Prise Sesamkörner)',
    material_components = 'eine Prise Sesamkörner',
    duration = '1 Stunde',
    concentration = 0,
    ritual = 0,
    description = 'An einem Punkt in Reichweite auf einer Oberfläche aus Holz, Gips oder Stein (beispielsweise eine Mauer, eine Decke oder ein Fußboden), den du sehen kannst, erscheint ein Durchgang, der für die Wirkungsdauer bestehen bleibt. Du bestimmst die Ausmaße des Durchgangs: höchstens 1,5 Meter breit, 2,4 Meter hoch und sechs Meter tief. Der Durchgang macht das umgebende Gebäude nicht instabil. Wenn der Durchgang verschwindet, gelangen sämtliche Kreaturen und Gegenstände, die sich noch darin befinden, in den freien Bereich, welcher der Oberfläche, auf die der Zauber gewirkt wurde, am nächsten liegt. KAPITEL 7 f ZAUBER WANKENDMACHENDES NIEDERSTRECKEN Verzauberungszauber 4. Grades (Paladin) Zeitaufwand: Bonusaktion, die du sofort ausführst, wenn du eine Kreatur mit einer Nahkampfwaffe oder einem waffenlosen Angriff getroffen hast Reichweite: Selbst Komponenten: V Wirkungsdauer: Unmittelbar Das Ziel erleidet durch den Angriff zusätzlich 4W6 psychischen Schaden und muss einen Weisheitsrettungswurf bestehen, oder es ist bis zum Ende deines nächsten Zugs betäubt.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 4. wird der zusätzliche Schaden um 1W6 erhöht.',
    classes = 'Magier',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 4. wird der zusätzliche Schaden um 1W6 erhöht.","type":"damage","dice":"1W6"},"source_page":239}'
