UPDATE core_spells SET 
    level = 3,
    school = 'Verwandlungs',
    casting_time = 'Aktion oder Ritual',
    range = '9 Meter',
    components = 'V, G, M (ein kurzes Schilfrohr)',
    material_components = 'ein kurzes Schilfrohr',
    duration = '24 Stunden',
    concentration = 0,
    ritual = 1,
    description = 'Bis zu zehn bereitwillige Kreaturen deiner Wahl in Reichweite können für die Wirkungsdauer unter Wasser atmen. Betroffene Kreaturen verfügen weiterhin über ihre normale Atmung. ZAUBER WIE WASSER ATMEN UND WASSERWANDELN SIND BEI ABSTECHERN iN AQUATISCHE GEGENDEN NÜTZLICH. WASSER ERSCHAFFEN ODER ZERSTÖREN Verwandlungszauber 1. Grades (Druide, Kleriker) Zeitaufwand: Aktion Reichweite: 9 Meter Komponenten: V, G, M (eine Mischung aus Wasser und Sand) Wirkungsdauer: Unmittelbar Du führst eine der folgenden Handlungen aus: Wasser erschaffen: Du erschaffst bis zu 40 Liter sauberes Wasser in einem offenen Behälter in Reichweite. Alternativ geht das Wasser als Regen in einem Würfel mit neun Metern Kantenlänge in Reichweite nieder und löscht dort offene Flammen. Wasser zerstören: Du zerstörst bis zu 40 Liter Wasser in einem offenen Behälter in Reichweite. Alternativ zerstörst du Nebel in einem Würfel mit neun Metern Kantenlänge in Reichweite.',
    higher_levels = 'Für jeden Zauberplatzgrad über dem 1. kannst du zusätzliche 40 Liter Wasser erschaffen oder zerstören oder die Kantenlänge des Würfels um 1,5 Meter erhöhen.',
    classes = 'Druide, Magier, Waldläufer,',
    data = '{"scaling":{"text":"Für jeden Zauberplatzgrad über dem 1. kannst du zusätzliche 40 Liter Wasser erschaffen oder zerstören oder die Kantenlänge des Würfels um 1,5 Meter erhöhen."},"source_page":239}'
