UPDATE core_spells SET 
    level = 8,
    school = 'Verwandlungs',
    casting_time = '10 Minuten',
    range = 'Selbst',
    components = 'V, G, M (brennender Weihrauch)',
    material_components = 'brennender Weihrauch',
    duration = 'Konzentration, bis zu 8 Stunden',
    concentration = 1,
    ritual = 0,
    description = 'KAPITEL 7 I ZAUBER 337 Für die Wirkungsdauer kontrollierst du das Wetter im Abstand von bis zu acht Kilometern. Du musst dich im Freien befinden, wenn du diesen Zauber wirkst. Der Zauber endet vorzeitig, wenn du nach drinnen gehst. Wenn du den Zauber wirkst, änderst du die aktuellen Wetterbedingungen, die vom SL bestimmt werden. Du kannst Niederschlag, Temperatur und Wind beeinflussen. Es dauert 1W4 mal zehn Minuten, bis die neuen Bedingungen in Kraft treten. Danach kannst du die Bedingungen erneut ändern. Wenn der Zauber endet, normalisiert sich das Wetter allmählich. Finde zum Ändern des Wetters eine aktuelle Wetterbedingung in der folgenden Tabelle und ändere sie eine Stufe nach oben oder unten. Wenn du den Wind änderst, kannst du auch seine Richtung ändern. NIEDERSCHLAG Phase Bedingung 1 Wolkenlos 2 Leicht bewölkt 3 Bewölkt oder Bodennebel 4 Regen, Hagel oder Schnee 5 Starkregen, Hagelsturm oder Schneesturm TEMPERATUR WIND Phase Bedingung Phase Bedingung Extrem heiß Flaute Heiß Mäßiger Wind Warm Starker Wind Kühl Starke Böen Kalt Sturm Eisig WIDERSTAND Zaubertrick des Banns (Druide, Kleriker) Zeitaufwand: Aktion Reichweite: Berührung Komponenten: V, G Wirkungsdauer: Konzentration, bis zu 1 Minute Du berührst eine bereitwillige Kreatur und wählst eine Schadensart aus: Blitz, Feuer, Gift, Gleißend, Hieb, Kälte, Nekrotisch, Säure, Schall, Stich oder Wucht. Wenn die Kreatur während der Wirkungsdauer Schaden des ausgewählten Typs erleidet, verringert sie den erlittenen Gesamtschaden um 1W4. Eine Kreatur kann von diesem Zauber nur einmal pro Zug profitieren.',
    higher_levels = '',
    classes = 'Druide, Kleriker, Magier',
    data = '{"source_page":239}'
WHERE id = 'wetterkontrolle';
