INSERT INTO weapon_properties (id, name, description, data) VALUES ('finesse', 'Finesse', 'Wenn du mit Finesse-Waffen angreifst, hast du bei Angriffs- und Schadenswürfen die Wahl zwischen deinem Stärke- und deinem Geschicklichkeitsmodifikator. Du musst allerdings bei beiden Würfen denselben Modifikator verwenden.', '{"mechanical_effect":"choice(STR, DEX)","affects":["attack_rolls","damage_rolls"]}');
INSERT INTO weapon_properties (id, name, description, data) VALUES ('ammunition', 'Geschosse', 'Du kannst Waffen mit der Eigenschaft Geschosse nur für Fernkampfangriffe verwenden, wenn du über entsprechende Geschosse verfügst. Jeder Angriff verbraucht ein Geschoss. Es ist Teil des Angriffs, die Waffe mit Geschossen zu laden.', '{"mechanical_effect":"requires_ammo","affects":["ranged_attacks"]}');
INSERT INTO weapon_properties (id, name, description, data) VALUES ('loading', 'Laden', 'Du kannst mit einer Aktion, Bonusaktion oder Reaktion immer nur ein Geschoss aus einer Waffe mit der Eigenschaft Laden abfeuern, egal, wie viele Angriffe dir zur Verfügung stehen.', '{"mechanical_effect":"limit_one_shot_per_action"}');
INSERT INTO weapon_properties (id, name, description, data) VALUES ('light', 'Leicht', 'Wenn du in deinem Zug die Angriffsaktion ausführst und mit einer leichten Waffe angreifst, kannst du später im selben Zug als Bonusaktion einen zusätzlichen Angriff ausführen. (Beachte: Einkerben Mastery kann dies ändern).', '{"mechanical_effect":"bonus_attack_eligible"}');
INSERT INTO weapon_properties (id, name, description, data) VALUES ('range', 'Reichweite', 'Die Reichweite umfasst zwei Werte: Grundreichweite und Maximalreichweite. Bei Angriffen außerhalb der Grundreichweite bist du im Nachteil. Ziele außerhalb der Maximalreichweite können nicht angegriffen werden.', '{"mechanical_effect":"range_penalty_and_limit"}');
INSERT INTO weapon_properties (id, name, description, data) VALUES ('heavy', 'Schwer', 'Du bist bei Angriffswürfen mit schweren Waffen im Nachteil, wenn du bei Nahkampfwaffen einen Stärkewert von weniger als 13 und bei Fernkampfwaffen einen Geschicklichkeitswert von weniger als 13 hast.', '{"mechanical_effect":"disadvantage_on_low_attr","min_attr":13}');
INSERT INTO weapon_properties (id, name, description, data) VALUES ('versatile', 'Vielseitig', 'Waffen mit der Eigenschaft Vielseitig können mit einer Hand oder mit zwei Händen geführt werden. Mit der Eigenschaft wird ein Schadenswert in Klammern genannt, der bei zweihändiger Führung gilt.', '{"mechanical_effect":"variable_damage"}');
INSERT INTO weapon_properties (id, name, description, data) VALUES ('reach', 'Weitreichend', 'Bei Waffen mit der Eigenschaft Weitreichend ist die normale Angriffsreichweite um 1,5 Meter erhöht. Dies gilt auch bei Gelegenheitsangriffen.', '{"mechanical_effect":"reach_bonus","bonus_m":1.5}');
INSERT INTO weapon_properties (id, name, description, data) VALUES ('thrown', 'Wurfwaffe', 'Waffen mit der Eigenschaft Wurfwaffe können geworfen werden, um Fernkampfangriffe auszuführen. Sie können als Teil des Angriffs gezogen werden.', '{"mechanical_effect":"thrown_ranged_attack"}');
INSERT INTO weapon_properties (id, name, description, data) VALUES ('two-handed', 'Zweihändig', 'Waffen mit der Eigenschaft Zweihändig müssen mit zwei Händen geführt werden.', '{"mechanical_effect":"requires_two_hands"}');
INSERT INTO weapon_masteries (id, name, description, data) VALUES ('sap', 'Auslaugen', 'Wenn du eine Kreatur mit dieser Waffe triffst, ist diese Kreatur bei ihrem nächsten Angriffswurf vor Beginn deines nächsten Zugs im Nachteil.', '{"trigger":"on_hit","effect":{"type":"disadvantage","target":"enemy","duration":"next_attack"}}');
INSERT INTO weapon_masteries (id, name, description, data) VALUES ('nick', 'Einkerben', 'Wenn du den zusätzlichen Angriff der Eigenschaft Leicht ausführst, kannst du dies als Teil der Angriffsaktion statt als Bonusaktion tun. Einmal pro Zug.', '{"trigger":"on_light_attack","effect":{"type":"action_efficiency","benefit":"use_attack_action_for_bonus_attack","limit":"1/turn"}}');
INSERT INTO weapon_masteries (id, name, description, data) VALUES ('vex', 'Plagen', 'Wenn du eine Kreatur mit dieser Waffe triffst und ihr Schaden zufügst, bist du beim nächsten Angriffswurf gegen diese Kreatur vor Ende deines nächsten Zugs im Vorteil.', '{"trigger":"on_hit_damage","effect":{"type":"advantage","on_next_attack":true,"against_same_target":true}}');
INSERT INTO weapon_masteries (id, name, description, data) VALUES ('cleave', 'Spalten', 'Wenn du eine Kreatur mit einem Nahkampfangriffswurf triffst, kannst du einen weiteren Nahkampfangriff auf eine zweite Kreatur im Abstand von bis zu 1,5 Metern ausführen. Einmal pro Zug, kein Attributsmodifikator auf Schaden.', '{"trigger":"on_hit","effect":{"type":"additional_attack","max_distance_m":1.5,"damage_mod":0}}');
INSERT INTO weapon_masteries (id, name, description, data) VALUES ('push', 'Stoßen', 'Wenn du eine Kreatur mit dieser Waffe triffst, kannst du sie bis zu drei Meter weit in gerader Linie von dir wegstoßen, sofern sie von höchstens großer Größe ist.', '{"trigger":"on_hit","effect":{"type":"push","distance_m":3,"max_size":"Large"}}');
INSERT INTO weapon_masteries (id, name, description, data) VALUES ('graze', 'Streifen', 'Wenn dein Angriffswurf mit dieser Waffe eine Kreatur verfehlt, kannst du der Kreatur Schaden in Höhe des Attributsmodifikators zufügen.', '{"trigger":"on_miss","effect":{"type":"damage_on_miss","amount":"attr_mod"}}');
INSERT INTO weapon_masteries (id, name, description, data) VALUES ('topple', 'Umstoßen', 'Wenn du eine Kreatur mit dieser Waffe triffst, kannst du sie zu einem Konstitutionsrettungswurf zwingen. Scheitert der Wurf, so wird die Kreatur umgestoßen.', '{"trigger":"on_hit","effect":{"type":"condition","condition":"prone","save_attr":"CON","save_dc":"8 + mod + proficiency"}}');
INSERT INTO weapon_masteries (id, name, description, data) VALUES ('slow', 'Verlangsamen', 'Wenn du eine Kreatur mit dieser Waffe triffst und ihr Schaden zufügst, kannst du ihre Bewegungsrate bis zum Beginn deines nächsten Zugs um drei Meter verringern.', '{"trigger":"on_hit_damage","effect":{"type":"speed_reduction","amount_m":3,"duration":"next_turn"}}');
