INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('flegel', 'Flegel', 'Kriegswaffen', 'Nahkampf', '1W8', 'Wucht', 1, 10, '{"properties":[],"mastery":"sap","source_page":213,"property_details":{},"mastery_details":{"id":"sap","name":"Auslaugen","description":"Wenn du eine Kreatur mit dieser Waffe triffst, ist diese Kreatur bei ihrem nächsten Angriffswurf vor Beginn deines nächsten Zugs im Nachteil.","data":{"trigger":"on_hit","effect":{"type":"disadvantage","target":"enemy","duration":"next_attack"}}}}');
INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('glefe', 'Glefe', 'Kriegswaffen', 'Nahkampf', '1W10', 'Hieb', 3, 20, '{"properties":["heavy","reach","two-handed"],"mastery":"graze","source_page":213,"property_details":{"heavy":{"id":"heavy","name":"Schwer","description":"Du bist bei Angriffswürfen mit schweren Waffen im Nachteil, wenn du bei Nahkampfwaffen einen Stärkewert von weniger als 13 und bei Fernkampfwaffen einen Geschicklichkeitswert von weniger als 13 hast.","data":{"mechanical_effect":"disadvantage_on_low_attr","min_attr":13}},"reach":{"id":"reach","name":"Weitreichend","description":"Bei Waffen mit der Eigenschaft Weitreichend ist die normale Angriffsreichweite um 1,5 Meter erhöht. Dies gilt auch bei Gelegenheitsangriffen.","data":{"mechanical_effect":"reach_bonus","bonus_m":1.5}},"two-handed":{"id":"two-handed","name":"Zweihändig","description":"Waffen mit der Eigenschaft Zweihändig müssen mit zwei Händen geführt werden.","data":{"mechanical_effect":"requires_two_hands"}}},"mastery_details":{"id":"graze","name":"Streifen","description":"Wenn dein Angriffswurf mit dieser Waffe eine Kreatur verfehlt, kannst du der Kreatur Schaden in Höhe des Attributsmodifikators zufügen.","data":{"trigger":"on_miss","effect":{"type":"damage_on_miss","amount":"attr_mod"}}}}');
INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('hellebarde', 'Hellebarde', 'Kriegswaffen', 'Nahkampf', '1W10', 'Hieb', 3, 20, '{"properties":["heavy","reach","two-handed"],"mastery":"cleave","source_page":213,"property_details":{"heavy":{"id":"heavy","name":"Schwer","description":"Du bist bei Angriffswürfen mit schweren Waffen im Nachteil, wenn du bei Nahkampfwaffen einen Stärkewert von weniger als 13 und bei Fernkampfwaffen einen Geschicklichkeitswert von weniger als 13 hast.","data":{"mechanical_effect":"disadvantage_on_low_attr","min_attr":13}},"reach":{"id":"reach","name":"Weitreichend","description":"Bei Waffen mit der Eigenschaft Weitreichend ist die normale Angriffsreichweite um 1,5 Meter erhöht. Dies gilt auch bei Gelegenheitsangriffen.","data":{"mechanical_effect":"reach_bonus","bonus_m":1.5}},"two-handed":{"id":"two-handed","name":"Zweihändig","description":"Waffen mit der Eigenschaft Zweihändig müssen mit zwei Händen geführt werden.","data":{"mechanical_effect":"requires_two_hands"}}},"mastery_details":{"id":"cleave","name":"Spalten","description":"Wenn du eine Kreatur mit einem Nahkampfangriffswurf triffst, kannst du einen weiteren Nahkampfangriff auf eine zweite Kreatur im Abstand von bis zu 1,5 Metern ausführen. Einmal pro Zug, kein Attributsmodifikator auf Schaden.","data":{"trigger":"on_hit","effect":{"type":"additional_attack","max_distance_m":1.5,"damage_mod":0}}}}');
INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('kriegshammer', 'Kriegshammer', 'Kriegswaffen', 'Nahkampf', '1W8', 'Wucht', 2.5, 15, '{"properties":["versatile"],"mastery":"push","source_page":213,"versatile_damage":"1W10","property_details":{"versatile":{"id":"versatile","name":"Vielseitig","description":"Waffen mit der Eigenschaft Vielseitig können mit einer Hand oder mit zwei Händen geführt werden. Mit der Eigenschaft wird ein Schadenswert in Klammern genannt, der bei zweihändiger Führung gilt.","data":{"mechanical_effect":"variable_damage"}}},"mastery_details":{"id":"push","name":"Stoßen","description":"Wenn du eine Kreatur mit dieser Waffe triffst, kannst du sie bis zu drei Meter weit in gerader Linie von dir wegstoßen, sofern sie von höchstens großer Größe ist.","data":{"trigger":"on_hit","effect":{"type":"push","distance_m":3,"max_size":"Large"}}}}');
INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('kriegspicke', 'Kriegspicke', 'Kriegswaffen', 'Nahkampf', '1W8', 'Stich', 1, 5, '{"properties":["versatile"],"mastery":"sap","source_page":213,"versatile_damage":"1W10","property_details":{"versatile":{"id":"versatile","name":"Vielseitig","description":"Waffen mit der Eigenschaft Vielseitig können mit einer Hand oder mit zwei Händen geführt werden. Mit der Eigenschaft wird ein Schadenswert in Klammern genannt, der bei zweihändiger Führung gilt.","data":{"mechanical_effect":"variable_damage"}}},"mastery_details":{"id":"sap","name":"Auslaugen","description":"Wenn du eine Kreatur mit dieser Waffe triffst, ist diese Kreatur bei ihrem nächsten Angriffswurf vor Beginn deines nächsten Zugs im Nachteil.","data":{"trigger":"on_hit","effect":{"type":"disadvantage","target":"enemy","duration":"next_attack"}}}}');
INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('krummsäbel', 'Krummsäbel', 'Kriegswaffen', 'Nahkampf', '1W6', 'Hieb', 1.5, 25, '{"properties":["finesse","light"],"mastery":"nick","source_page":213,"property_details":{"finesse":{"id":"finesse","name":"Finesse","description":"Wenn du mit Finesse-Waffen angreifst, hast du bei Angriffs- und Schadenswürfen die Wahl zwischen deinem Stärke- und deinem Geschicklichkeitsmodifikator. Du musst allerdings bei beiden Würfen denselben Modifikator verwenden.","data":{"mechanical_effect":"choice(STR, DEX)","affects":["attack_rolls","damage_rolls"]}},"light":{"id":"light","name":"Leicht","description":"Wenn du in deinem Zug die Angriffsaktion ausführst und mit einer leichten Waffe angreifst, kannst du später im selben Zug als Bonusaktion einen zusätzlichen Angriff ausführen. (Beachte: Einkerben Mastery kann dies ändern).","data":{"mechanical_effect":"bonus_attack_eligible"}}},"mastery_details":{"id":"nick","name":"Einkerben","description":"Wenn du den zusätzlichen Angriff der Eigenschaft Leicht ausführst, kannst du dies als Teil der Angriffsaktion statt als Bonusaktion tun. Einmal pro Zug.","data":{"trigger":"on_light_attack","effect":{"type":"action_efficiency","benefit":"use_attack_action_for_bonus_attack","limit":"1/turn"}}}}');
INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('kurzschwert', 'Kurzschwert', 'Kriegswaffen', 'Nahkampf', '1W6', 'Stich', 1, 10, '{"properties":["finesse","light"],"mastery":"vex","source_page":213,"property_details":{"finesse":{"id":"finesse","name":"Finesse","description":"Wenn du mit Finesse-Waffen angreifst, hast du bei Angriffs- und Schadenswürfen die Wahl zwischen deinem Stärke- und deinem Geschicklichkeitsmodifikator. Du musst allerdings bei beiden Würfen denselben Modifikator verwenden.","data":{"mechanical_effect":"choice(STR, DEX)","affects":["attack_rolls","damage_rolls"]}},"light":{"id":"light","name":"Leicht","description":"Wenn du in deinem Zug die Angriffsaktion ausführst und mit einer leichten Waffe angreifst, kannst du später im selben Zug als Bonusaktion einen zusätzlichen Angriff ausführen. (Beachte: Einkerben Mastery kann dies ändern).","data":{"mechanical_effect":"bonus_attack_eligible"}}},"mastery_details":{"id":"vex","name":"Plagen","description":"Wenn du eine Kreatur mit dieser Waffe triffst und ihr Schaden zufügst, bist du beim nächsten Angriffswurf gegen diese Kreatur vor Ende deines nächsten Zugs im Vorteil.","data":{"trigger":"on_hit_damage","effect":{"type":"advantage","on_next_attack":true,"against_same_target":true}}}}');
