INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('blasrohr', 'Blasrohr', 'Kriegswaffen', 'Fernkampf', '1', 'Stich', 0.5, 10, '{"properties":["ammunition","loading"],"mastery":"vex","source_page":213,"range":{"normal":7.5,"max":30},"ammunition_type":"Blasrohrpfeil","property_details":{"ammunition":{"id":"ammunition","name":"Geschosse","description":"Du kannst Waffen mit der Eigenschaft Geschosse nur für Fernkampfangriffe verwenden, wenn du über entsprechende Geschosse verfügst. Jeder Angriff verbraucht ein Geschoss. Es ist Teil des Angriffs, die Waffe mit Geschossen zu laden.","data":{"mechanical_effect":"requires_ammo","affects":["ranged_attacks"]}},"loading":{"id":"loading","name":"Laden","description":"Du kannst mit einer Aktion, Bonusaktion oder Reaktion immer nur ein Geschoss aus einer Waffe mit der Eigenschaft Laden abfeuern, egal, wie viele Angriffe dir zur Verfügung stehen.","data":{"mechanical_effect":"limit_one_shot_per_action"}}},"mastery_details":{"id":"vex","name":"Plagen","description":"Wenn du eine Kreatur mit dieser Waffe triffst und ihr Schaden zufügst, bist du beim nächsten Angriffswurf gegen diese Kreatur vor Ende deines nächsten Zugs im Vorteil.","data":{"trigger":"on_hit_damage","effect":{"type":"advantage","on_next_attack":true,"against_same_target":true}}}}');
INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('handarmbrust', 'Handarmbrust', 'Kriegswaffen', 'Fernkampf', '1W6', 'Stich', 1.5, 75, '{"properties":["ammunition","light","loading"],"mastery":"vex","source_page":213,"range":{"normal":9,"max":36},"ammunition_type":"Bolzen","property_details":{"ammunition":{"id":"ammunition","name":"Geschosse","description":"Du kannst Waffen mit der Eigenschaft Geschosse nur für Fernkampfangriffe verwenden, wenn du über entsprechende Geschosse verfügst. Jeder Angriff verbraucht ein Geschoss. Es ist Teil des Angriffs, die Waffe mit Geschossen zu laden.","data":{"mechanical_effect":"requires_ammo","affects":["ranged_attacks"]}},"light":{"id":"light","name":"Leicht","description":"Wenn du in deinem Zug die Angriffsaktion ausführst und mit einer leichten Waffe angreifst, kannst du später im selben Zug als Bonusaktion einen zusätzlichen Angriff ausführen. (Beachte: Einkerben Mastery kann dies ändern).","data":{"mechanical_effect":"bonus_attack_eligible"}},"loading":{"id":"loading","name":"Laden","description":"Du kannst mit einer Aktion, Bonusaktion oder Reaktion immer nur ein Geschoss aus einer Waffe mit der Eigenschaft Laden abfeuern, egal, wie viele Angriffe dir zur Verfügung stehen.","data":{"mechanical_effect":"limit_one_shot_per_action"}}},"mastery_details":{"id":"vex","name":"Plagen","description":"Wenn du eine Kreatur mit dieser Waffe triffst und ihr Schaden zufügst, bist du beim nächsten Angriffswurf gegen diese Kreatur vor Ende deines nächsten Zugs im Vorteil.","data":{"trigger":"on_hit_damage","effect":{"type":"advantage","on_next_attack":true,"against_same_target":true}}}}');
INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('langbogen', 'Langbogen', 'Kriegswaffen', 'Fernkampf', '1W8', 'Stich', 1, 50, '{"properties":["ammunition","heavy","two-handed"],"mastery":"slow","source_page":213,"range":{"normal":45,"max":180},"ammunition_type":"Pfeil","property_details":{"ammunition":{"id":"ammunition","name":"Geschosse","description":"Du kannst Waffen mit der Eigenschaft Geschosse nur für Fernkampfangriffe verwenden, wenn du über entsprechende Geschosse verfügst. Jeder Angriff verbraucht ein Geschoss. Es ist Teil des Angriffs, die Waffe mit Geschossen zu laden.","data":{"mechanical_effect":"requires_ammo","affects":["ranged_attacks"]}},"heavy":{"id":"heavy","name":"Schwer","description":"Du bist bei Angriffswürfen mit schweren Waffen im Nachteil, wenn du bei Nahkampfwaffen einen Stärkewert von weniger als 13 und bei Fernkampfwaffen einen Geschicklichkeitswert von weniger als 13 hast.","data":{"mechanical_effect":"disadvantage_on_low_attr","min_attr":13}},"two-handed":{"id":"two-handed","name":"Zweihändig","description":"Waffen mit der Eigenschaft Zweihändig müssen mit zwei Händen geführt werden.","data":{"mechanical_effect":"requires_two_hands"}}},"mastery_details":{"id":"slow","name":"Verlangsamen","description":"Wenn du eine Kreatur mit dieser Waffe triffst und ihr Schaden zufügst, kannst du ihre Bewegungsrate bis zum Beginn deines nächsten Zugs um drei Meter verringern.","data":{"trigger":"on_hit_damage","effect":{"type":"speed_reduction","amount_m":3,"duration":"next_turn"}}}}');
INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('muskete', 'Muskete', 'Kriegswaffen', 'Fernkampf', '1W12', 'Stich', 5, 500, '{"properties":["ammunition","loading","two-handed"],"mastery":"slow","source_page":213,"range":{"normal":12,"max":36},"ammunition_type":"Kugel","property_details":{"ammunition":{"id":"ammunition","name":"Geschosse","description":"Du kannst Waffen mit der Eigenschaft Geschosse nur für Fernkampfangriffe verwenden, wenn du über entsprechende Geschosse verfügst. Jeder Angriff verbraucht ein Geschoss. Es ist Teil des Angriffs, die Waffe mit Geschossen zu laden.","data":{"mechanical_effect":"requires_ammo","affects":["ranged_attacks"]}},"loading":{"id":"loading","name":"Laden","description":"Du kannst mit einer Aktion, Bonusaktion oder Reaktion immer nur ein Geschoss aus einer Waffe mit der Eigenschaft Laden abfeuern, egal, wie viele Angriffe dir zur Verfügung stehen.","data":{"mechanical_effect":"limit_one_shot_per_action"}},"two-handed":{"id":"two-handed","name":"Zweihändig","description":"Waffen mit der Eigenschaft Zweihändig müssen mit zwei Händen geführt werden.","data":{"mechanical_effect":"requires_two_hands"}}},"mastery_details":{"id":"slow","name":"Verlangsamen","description":"Wenn du eine Kreatur mit dieser Waffe triffst und ihr Schaden zufügst, kannst du ihre Bewegungsrate bis zum Beginn deines nächsten Zugs um drei Meter verringern.","data":{"trigger":"on_hit_damage","effect":{"type":"speed_reduction","amount_m":3,"duration":"next_turn"}}}}');
