INSERT INTO core_weapons (id, name, category, weapon_type, damage_dice, damage_type, weight_kg, cost_gp, data) VALUES ('schwere-armbrust', 'Schwere Armbrust', 'Kriegswaffen', 'Fernkampf', '1W10', 'Stich', 9, 50, '{"properties":["ammunition","loading","heavy","two-handed"],"mastery":"push","source_page":213,"range":{"normal":30,"max":120},"ammunition_type":"Bolzen","property_details":{"ammunition":{"id":"ammunition","name":"Geschosse","description":"Du kannst Waffen mit der Eigenschaft Geschosse nur für Fernkampfangriffe verwenden, wenn du über entsprechende Geschosse verfügst. Jeder Angriff verbraucht ein Geschoss. Es ist Teil des Angriffs, die Waffe mit Geschossen zu laden.","data":{"mechanical_effect":"requires_ammo","affects":["ranged_attacks"]}},"loading":{"id":"loading","name":"Laden","description":"Du kannst mit einer Aktion, Bonusaktion oder Reaktion immer nur ein Geschoss aus einer Waffe mit der Eigenschaft Laden abfeuern, egal, wie viele Angriffe dir zur Verfügung stehen.","data":{"mechanical_effect":"limit_one_shot_per_action"}},"heavy":{"id":"heavy","name":"Schwer","description":"Du bist bei Angriffswürfen mit schweren Waffen im Nachteil, wenn du bei Nahkampfwaffen einen Stärkewert von weniger als 13 und bei Fernkampfwaffen einen Geschicklichkeitswert von weniger als 13 hast.","data":{"mechanical_effect":"disadvantage_on_low_attr","min_attr":13}},"two-handed":{"id":"two-handed","name":"Zweihändig","description":"Waffen mit der Eigenschaft Zweihändig müssen mit zwei Händen geführt werden.","data":{"mechanical_effect":"requires_two_hands"}}},"mastery_details":{"id":"push","name":"Stoßen","description":"Wenn du eine Kreatur mit dieser Waffe triffst, kannst du sie bis zu drei Meter weit in gerader Linie von dir wegstoßen, sofern sie von höchstens großer Größe ist.","data":{"trigger":"on_hit","effect":{"type":"push","distance_m":3,"max_size":"Large"}}}}');
